{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ percent_font | font_face: font_display: 'swap' }}
  {{ feature_text_font | font_face: font_display: 'swap' }}
  {{ badge_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .before-after-heading-{{ section.id }} {
    text-align: {{ content_align_mobile }};
  }

  .before-after-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-wrap: break-word;
    font-weight: 700;
  }

  .before-after-text-{{ section.id }} {
    text-align: {{ content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;    
  }

  .before-after-text-{{ section.id }} * {
    margin: 0;   
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};  
    text-transform: unset;
    word-wrap: break-word;
  } 

  .before-after-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: center;
    margin-top: {{ body_mt_mobile }}px;
    gap: {{ body_gap_mobile }}px;
  }

  .before-after-features-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ features_gap_mobile }}px;
  }

  .before-after-feature-content-{{ section.id }} {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 100%;
  }

  .before-after-percent-{{ section.id }} {
    margin: 0; 
    width: 100%;       
    font-size: {{ percent_size_mobile }}px;
    line-height: {{ percent_height }}%;
    color: {{ percent_color }};  
    text-align: {{ feature_align_mobile }};
    text-transform: unset;
    word-wrap: break-word;
    font-weight: 700;
  }

  .before-after-feature-text-{{ section.id }} {    
    width: 100%;
    text-align: {{ feature_align_mobile }};   
  }

  .before-after-feature-text-{{ section.id }} * {
    margin: 0;   
    font-size: {{ feature_text_size_mobile }}px;
    line-height: {{ feature_text_height }}%;
    color: {{ feature_text_color }};  
    text-transform: unset;
    word-wrap: break-word;
  } 

  .before-after-progress-bar-{{ section.id }} {
    display: block !important;
    position: relative;
    z-index: 1;
    height: 6px;
    border-radius: 100px;
    background-color: {{ progress_color }};
    width: 100%;
    margin-top: {{ progress_mt_mobile }}px;
    overflow: hidden;
  }

  .before-after-progress-bar-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: var(--after-width, 0);
    background-color: {{ progress_active_color }};
    transition: all 0.5s linear 0s;
    z-index: 2;
  }

  .before-after-button-{{ section.id }} {
    width: {{ btn_size_mobile }}px;
    height: {{ btn_size_mobile }}px;
    border-radius: 50%;
    border: {{ btn_border_thickness }}px solid {{ btn_border_color }};
    backdrop-filter: blur(7px);
    -webkit-backdrop-filter: blur(7px);
    box-shadow: rgba(0, 0, 0, 0.35) 0px 0px 7px;
    display: flex !important;
    align-items: center;
    justify-content: center;
    z-index: 3;
    position: relative;
  }

  .before-after-button-{{ section.id }}::after {
    content: '';
    padding: 3px;
    display: inline-block;
    border: solid {{ btn_arrow_color }};
    border-width: 0 2px 2px 0;
    transform: rotate(-45deg) scale({{ btn_arrow_size_mobile }}%) translateY(-50%);
  }

  .before-after-button-{{ section.id }}::before {
    content: '';
    padding: 3px;
    display: inline-block;
    border: solid {{ btn_arrow_color }};
    border-width: 0 2px 2px 0;
    transform: rotate(135deg) scale({{ btn_arrow_size_mobile }}%) translateX(-50%);
  }

  .before-after-button-{{ section.id }}:before {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.50 | round: 0 }}px);
    top: 50%;
    left: 15%;
    width: 3px;
    color: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-button-{{ section.id }}:after {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.50 | round: 0 }}px);
    top: 50%;
    right: 15%;
    width: 3px;
    color: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-images-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: center;
    gap: {{ toggle_images_gap_mobile }}px;
  }

  .before-after-image-relative-{{ section.id }} {
    position: relative;
    height: 100%;
  }

  .before-after-image-relative-{{ section.id }} img,
  .before-after-image-relative-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .before-after-image-first-{{ section.id }} {
    display: none;
  }

  .before-after-image-second-{{ section.id }} {
    display: none;
  }

  .before-after-image-relative-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .before-after-image-wrapper-{{ section.id }} {
    display: none;
  }

  .before-after-image-wrapper-{{ section.id }}.active {
    display: block;
  }

  .before-after-image-inner-{{ section.id }} {    
    position: relative;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};  
    border-radius: {{ image_radius }}px;
    filter: drop-shadow(var(--shadow));
    user-select: none;
    overflow: hidden;
  }

  .before-after-image-cursor-{{ section.id }} {
    height: 100%;
    width: max-content;
    touch-action: none;
    --transform-logical-flip: 1;
    transform: translate(calc(var(--transform-logical-flip) * -50% + var(--clip-path-offset, 0px)),-50%);
    cursor: grab;
    will-change: transform;
    place-items: center;
    display: grid;
    position: relative;
    top: 50%;
    left: var(--before-after-initial-drag-position, 0px);
  }

  .before-after-image-cursor-{{ section.id }}:before {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.5 | round: 0 }}px);
    top: 0;
    width: {{ btn_border_thickness }}px;
    background: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-image-cursor-{{ section.id }}:after {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.5 | round: 0 }}px);
    bottom: 0;
    width: {{ btn_border_thickness }}px;
    background: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-badge-{{ section.id }} {
    position: absolute;
    top: 10px;
    padding: {{ badge_padding_vertical_mobile }}px {{ badge_padding_horizontal_mobile }}px;
    border-radius: {{ badge_radius }}px;
    background-color: {{ badge_bg_color | hex_to_rgba: 0.4 }};
    margin: 0;    
    text-align: center;
    text-transform: unset;
    word-wrap: break-word;
    font-size: {{ badge_size_mobile }}px;
    line-height: {{ badge_height }}%;
    color: {{ badge_color }};  
  }

  .before-after-before-badge-{{ section.id }} {
    left: 10px;
  }

  .before-after-after-badge-{{ section.id }} {
    right: 10px;
  }

  .before-after-image-toggles-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ toggle_images_view_mobile }}, 1fr);
    align-items: center;
    gap: {{ toggle_images_gap_mobile }}px;
  }

  .before-after-image-toggle-{{ section.id }} {
    position: relative;
    border: {{ toggle_image_border_thickness }}px solid {{ toggle_image_border_color }};
    border-radius: {{ toggle_image_radius }}px;
    overflow: hidden;
    cursor: pointer;
  }

  .before-after-image-toggle-{{ section.id }} img,
  .before-after-image-toggle-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .before-after-image-toggle-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .before-after-image-toggle-{{ section.id }}:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 2;
    background-color: {{ toggle_image_overlay_color | hex_to_rgba: 0.5 }};
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .before-after-image-toggle-{{ section.id }}.active:before {
    opacity: 0;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .before-after-heading-{{ section.id }} {
      text-align: {{ content_align }};
    }

    .before-after-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .before-after-text-{{ section.id }} {
      text-align: {{ content_align }};
      margin-top: {{ text_mt }}px;    
    }

    .before-after-text-{{ section.id }} * {
      font-size: {{ text_size }}px; 
    } 

    .before-after-body-{{ section.id }} {
      grid-template-columns: 0.5fr 1fr;
      margin-top: {{ body_mt }}px;
      gap: {{ body_gap }}px;
    }

    .before-after-features-{{ section.id }} {
      gap: {{ features_gap }}px;
    }

    .before-after-feature-content-{{ section.id }} {
      display: flex;
      flex-direction: column;
      width: 100%;
    }

    .before-after-percent-{{ section.id }} {  
      font-size: {{ percent_size }}px;
      text-align: {{ feature_align }};
    }

    .before-after-feature-text-{{ section.id }} {
      text-align: {{ feature_align }};
      margin-top: {{ feature_text_mt }}px;    
    }

    .before-after-feature-text-{{ section.id }} * {
      font-size: {{ feature_text_size }}px;
    }

    .before-after-progress-bar-{{ section.id }} {
      margin-top: {{ progress_mt }}px;
    }

    .before-after-images-{{ section.id }} {
      grid-template-columns: 1fr 0.5fr;
      gap: {{ toggle_images_gap }}px;
    }

    .before-after-image-first-{{ section.id }} {
      display: block;
    }

    .before-after-image-mobile-{{ section.id }} {
      display: none;
    }

    .before-after-image-second-{{ section.id }} {
      display: block;
    }

    .before-after-image-mobile-second-{{ section.id }} {
      display: none;
    }

    .before-after-button-{{ section.id }} {
      width: {{ btn_size }}px;
      height: {{ btn_size }}px;
    }    

    .before-after-button-{{ section.id }}::after {
      transform: rotate(-45deg) scale({{ btn_arrow_size }}%) translateY(-50%);
      height: calc(50% - {{ btn_size | times: 0.50 | round: 0 }}px);
    }

    .before-after-button-{{ section.id }}::before {
      transform: rotate(135deg) scale({{ btn_arrow_size }}%) translateX(-50%);
      height: calc(50% - {{ btn_size | times: 0.50 | round: 0 }}px);
    }

    .before-after-image-cursor-{{ section.id }}:before {
      height: calc(50% - {{ btn_size | times: 0.5 | round: 0 }}px);
    }

    .before-after-image-cursor-{{ section.id }}:after {
      height: calc(50% - {{ btn_size | times: 0.5 | round: 0 }}px);
    }

    .before-after-badge-{{ section.id }} {
      padding: {{ badge_padding_vertical }}px {{ badge_padding_horizontal }}px;
      font-size: {{ badge_size }}px; 
    }

    .before-after-image-toggles-{{ section.id }} {
      grid-template-columns: repeat({{ toggle_images_view }}, 1fr);
      gap: {{ toggle_images_gap }}px;
    }  
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .before-after-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .before-after-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if percent_custom %}
  <style>
    .before-after-percent-{{ section.id }} {
      font-family: {{ percent_font.family }}, {{ percent_font.fallback_families }};
      font-weight: {{ percent_font.weight }};
      font-style: {{ percent_font.style }};
    }
  </style>
{% endif %}

{% if feature_text_custom %}
  <style>
    .before-after-feature-text-{{ section.id }} * {
      font-family: {{ feature_text_font.family }}, {{ feature_text_font.fallback_families }};
      font-weight: {{ feature_text_font.weight }};
      font-style: {{ feature_text_font.style }};
    }
  </style>
{% endif %}

{% if badge_custom %}
  <style>
    .before-after-badge-{{ section.id }} {
      font-family: {{ badge_font.family }}, {{ badge_font.fallback_families }};
      font-weight: {{ badge_font.weight }};
      font-style: {{ badge_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .before-after-image-relative-{{ section.id }} {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .before-after-image-relative-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .before-after-image-relative-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
