    @media(min-width: 1024px) {
      .before-after-image-toggle-{{ section.id }} {
        aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .before-after-image-toggle-{{ section.id }} {
        aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} before-after-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="before-after-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      {% if text != blank %}
        <div class="before-after-text-{{ section.id }}">{{ text }}</div>
      {% endif %}            
      <div class="before-after-body-{{ section.id }}">
        <div class="before-after-features-{{ section.id }}">
          {% for block in section.blocks %}
            {% if block.type == 'feature' %}
              {% if block.settings.percent != 0 %}
                <div class="before-after-feature-{{ section.id }}">
                  <div class="before-after-feature-content-{{ section.id }}">
                    <div class="before-after-percent-{{ section.id }}" data-percent="{{ block.settings.percent }}">0 %</div>
                    {% if block.settings.text != blank %}
                      <div class="before-after-feature-text-{{ section.id }}">{{ block.settings.text }}</div>
                    {% endif %}   
                  </div>                 
                  <div class="before-after-progress-bar-{{ section.id }}" data-percent="{{ block.settings.percent }}"></div>                              
                </div>
              {% endif %}             
            {% endif %}
          {% endfor %}
        </div>
        <div class="before-after-images-{{ section.id }}">
          {% for block in section.blocks %}      
            {% if block.type == 'image' %}
              <div class="before-after-image-wrapper-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
                <div class="before-after-image-inner-{{ section.id }}" style="--before-after-initial-drag-position: 50%; --clip-path-offset: 0px;">
                  {% assign before_image = block.settings.before_image %}
                  {% assign before_image_mobile = block.settings.before_image_mobile %}
                  {% assign after_image = block.settings.after_image %}
                  {% assign after_image_mobile = block.settings.after_image_mobile %}
                  <div class="before-after-image-relative-{{ section.id }}" style=" position: relative;">
                    {% if before_image_mobile != blank %}
                      <img src="{{ before_image_mobile | image_url }}" alt="{{ before_image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} draggable="false" class="before-after-image-{{ section.id }} before-after-image-mobile-{{ section.id }}">
                    {% endif %}
                    {% if before_image != blank %}
                      <img src="{{ before_image | image_url }}" alt="{{ before_image.alt }}" {% if lazy %}loading="lazy"{% endif %} draggable="false" class="before-after-image-{{ section.id }} {% if before_image_mobile != blank %}before-after-image-first-{{ section.id }}{% endif %}">
                    {% else %}
                      {{  'image' | placeholder_svg_tag }}
                    {% endif %}
                    {% if block.settings.badge_before != blank %}
                      <p class="before-after-badge-{{ section.id }} before-after-before-badge-{{ section.id }}">{{ block.settings.badge_before }}</p>
                    {% endif %}                   
                  </div>
                  <div class="before-after-image-relative-{{ section.id }}" style="clip-path: inset(0 0 0 calc(var(--before-after-initial-drag-position, 50%) + var(--clip-path-offset, 0px))); position: absolute; top: 0; bottom: 0; left: 0; right: 0;">
                    {% if after_image_mobile != blank %}
                      <img src="{{ after_image_mobile | image_url }}" alt="{{ after_image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} draggable="false" class="before-after-image-{{ section.id }} before-after-image-mobile-second-{{ section.id }}">
                    {% endif %}
                    {% if after_image != blank %}
                      <img src="{{ after_image | image_url }}" alt="{{ after_image.alt }}" {% if lazy %}loading="lazy"{% endif %} draggable="false" class="before-after-image-{{ section.id }} {% if after_image_mobile != blank %}before-after-image-second-{{ section.id }}{% endif %}">
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                    {% if block.settings.badge_after != blank %}
                      <p class="before-after-badge-{{ section.id }} before-after-after-badge-{{ section.id }}">{{ block.settings.badge_after }}</p>
                    {% endif %}                  
                  </div>
                  {% comment %} Cursor: {% endcomment %}
                  <div style="position: absolute; top: 0; bottom: 0; left: 0; right: 0;">
                    <split-cursor class="before-after-image-cursor-{{ section.id }}">
                      <div class="before-after-button-{{ section.id }}"></div>
                    </split-cursor>
                  </div>          
                </div>
              </div> 
            {% endif %}                       
          {% endfor %}
          <div class="before-after-image-toggles-{{ section.id }}">
            {% for block in section.blocks %}
              {% if block.type == 'image' %}
                <div class="before-after-image-toggle-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}">
                  {% if block.settings.toggle_image != blank %}
                    <img src="{{ block.settings.toggle_image | image_url }}" alt="{{ block.settings.toggle_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}             
                </div>
              {% endif %}              
            {% endfor %}
          </div>
        </div>        
      </div>
    </div>
</div>

<script>
function animatePercentages(feature) {
  const sectionId = feature.getAttribute('data-section-id');
  const percentElement = feature.querySelector(`.before-after-percent-${sectionId}`);
  const progressBar = feature.querySelector(`.before-after-progress-bar-${sectionId}`);

  if (!percentElement || !progressBar) return;

  const duration = 2250;
  const targetPercent = parseInt(percentElement.getAttribute('data-percent'));
  let startTime = null;

  function animate(currentTime) {
    if (!startTime) startTime = currentTime;
    const elapsed = currentTime - startTime;
    const progress = Math.min(elapsed / duration, 1);

    const currentValue = Math.floor(targetPercent * progress);

    percentElement.textContent = `${currentValue}%`;

    const exactProgress = targetPercent * progress;
    progressBar.style.setProperty('--after-width', `${exactProgress}%`);

    if (progress < 1) {
      requestAnimationFrame(animate);
    } else {
      percentElement.textContent = `${targetPercent}%`;
      progressBar.style.setProperty('--after-width', `${targetPercent}%`);
    }
  }

  requestAnimationFrame(animate);
}

function initBeforeAfterImage8() {
  const section = this.closest('[id^="shopify-section"]');
  const sectionId = section ? section.id.replace('shopify-section-', '') : '';

  if (!sectionId) return;

  const toggles = document.querySelectorAll(`.before-after-image-toggle-${sectionId}`);
  const images = document.querySelectorAll(`.before-after-image-wrapper-${sectionId}`);
  const imageWrappers = document.querySelectorAll(`.before-after-image-inner-${sectionId}`);

  imageWrappers.forEach(imageWrapper => {
    const cursor = imageWrapper.querySelector(`.before-after-image-cursor-${sectionId}`);
    let xStart, xEnd, xDiff, originalOffset;

    function dragStart(event) {
      xStart = event.clientX;
      originalOffset = imageWrapper.style
        .getPropertyValue('--clip-path-offset')
        .slice(0, -2);
      document.addEventListener('pointermove', dragMove);
      document.addEventListener('pointerup', dragEnd);
    }

    function dragMove(event) {
      xEnd = event.clientX;
      xDiff = xEnd - xStart;
      let result = xDiff + parseInt(originalOffset);
      let cursorOffsetPercent = parseFloat(getComputedStyle(imageWrapper).getPropertyValue('--before-after-initial-drag-position'));
      let containerWidth = imageWrapper.offsetWidth;
      let pixelsOffset = (cursorOffsetPercent / 100) * containerWidth;
      const negativeMax = -1 * (pixelsOffset);
      const positiveMax = imageWrapper.offsetWidth - pixelsOffset;

      if (result < negativeMax) {
        result = negativeMax;
      } else if (result > positiveMax) {
        result = positiveMax;
      }
      imageWrapper.style.setProperty('--clip-path-offset', result + 'px');
    }

    function dragEnd(event) {
      document.removeEventListener('pointermove', dragMove);
      document.removeEventListener('pointerup', dragEnd);
    }

    cursor.addEventListener('pointerdown', dragStart);
  });

  if (toggles) {
    toggles.forEach(toggle => {
      toggle.addEventListener('click', () => {
        toggles.forEach(toggle => toggle.classList.remove('active'));
        if (images) {
          images.forEach(image => image.classList.remove('active'));
          images.forEach(image => {            
            if (toggle.id == image.getAttribute('data-id')) {
              image.classList.add('active');
              toggle.classList.add('active');
            }                     
          });
        }
      });  
    });
  }

  const features = document.querySelectorAll(`.before-after-feature-${sectionId}`);

  features.forEach(feature => {
    feature.setAttribute('data-section-id', sectionId);

    if (!feature.hasAttribute('data-animated')) {
      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            animatePercentages(feature);
            feature.setAttribute('data-animated', 'true');
            observer.disconnect();
          }
        });
      }, {
        threshold: 0.2,
        rootMargin: '0px'
      });

      observer.observe(feature);
    }
  });
}

function initAllSections() {
  const sections = document.querySelectorAll('[id^="shopify-section"]');
  sections.forEach(section => {
    initBeforeAfterImage8.call(section);
  });
}

document.addEventListener('DOMContentLoaded', initAllSections);

if (Shopify.designMode) {
  document.addEventListener('shopify:section:unload', function(event) {
    const sectionId = event.detail.sectionId.replace('shopify-section-', '');
    initBeforeAfterImage8.call(document.getElementById(`shopify-section-${sectionId}`));
  });

  document.addEventListener('shopify:section:load', function(event) {
    const sectionId = event.detail.sectionId.replace('shopify-section-', '');
    initBeforeAfterImage8.call(document.getElementById(`shopify-section-${sectionId}`));
  });
}
</script>
