liquid_code:
{%- style -%}
  .section-{{ section.id }}-padding {
    padding-top: {{ section.settings.padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ section.settings.padding_bottom | times: 0.75 | round: 0 }}px;
  }

  @media screen and (min-width: 750px) {
    .section-{{ section.id }}-padding {
      padding-top: {{ section.settings.padding_top }}px;
      padding-bottom: {{ section.settings.padding_bottom }}px;
    }
  }

  .collection-2-title {
    font-size: {{section.settings.section_title_font_size}}px;
    line-height: 1.32;
    letter-spacing: 0.5px;
    color: {{section.settings.section_title_color}};
    padding: 0 0 50px 0;
    text-align:center;
  }
  {% if section.settings.heading_custom %}
    .collection-2-title {
      font-family: {{section.settings.type_header_font.family}}, {{ section.settings.type_header_font.fallback_families }};
      font-weight: {{section.settings.type_header_font.weight}};
      font-style:  {{ section.settings.type_header_font.style }};
    }
  {% endif %}

  .collection-2-wrapper {
	display:flex;
  	flex-wrap:wrap;
    justify-content:center;
  }

  .box-{{ section.id }} {
    flex: 0 0 calc(33% - 40px);
  	position: relative;
    width: 100%;
    min-height: 1px;
  	margin-bottom:60px;
    margin-right: 20px;
    margin-left: 20px;
  	height: 115px;
    box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
  	background-color:#fafafa;
    transition: all 0.2s;
  }

    .box-{{ section.id }} img {
      max-height: 130px;
      max-width: 130px;
      position:absolute;
      left: -20px;
      top: -7.5px;
  }

  .collection-2-name-box {
    position: absolute;
    top: 50%;
    left: 65%;
    -moz-transform: translate(-50%,-50%);
    -o-transform: translate(-50%,-50%);
    -ms-transform: translate(-50%,-50%);
    -webkit-transform: translate(-50%,-50%);
    transform: translate(-50%,-50%);
    width: 70%;
    text-align: center;
    font-weight: 600;
    line-height:1.2;
  }

  @media (max-width: 1070px) {

     .box-{{ section.id }} {
       height:100px;
     }

    .box-{{ section.id }} img {
      max-height: 100px;
      max-width: 100px;
      left: -15px;
      top: 0px;
    }
  }

  @media (max-width: 767px) {
    .collection-2-title {
      padding: 0 0 20px 0;
    }

    .collection-2-name-box {
      left: 60%;
    }

    .box-{{ section.id }} {
      flex: 0 0 calc(50% - 20px);
      margin:20px 10px;
      height: 60px;
    }
    .collection-2-wrapper {
      justify-content:space-between;
    }

    .box-{{ section.id }} img {
      max-height: 50px;
      max-width: 50px;
      left: -10px;
      top: 5px;
    }
  }

  .box-{{ section.id }}:hover {
    transform: scale(1.1);
  }

   .box-{{ section.id }} svg {
    max-height: 130%;
    max-width: 100%;
    position: absolute;
    top: -15px;
    left: -15px;
  }



{%- endstyle -%}

<div style="background-color:{{section.settings.background-color}};">
  <div class="page-width section-{{ section.id }}-padding">
    <div class="collection-2-title">{{section.settings.header}}</div>
    <div class="collection-2-wrapper">
      {%- for block in section.blocks -%}
        <a style="display:contents;text-decoration:none;color:{{ section.settings.link-color }}" href="{{ block.settings.link }}">
        <div class="box-{{ section.id }}">
          <div>
             {%- if block.settings.collection.featured_image -%}
                {{ block.settings.collection.featured_image | image_url: width: block.settings.collection.featured_image.width | image_tag: loading: 'lazy', alt: block.settings.collection.title }}
             {%- elsif block.settings.image -%}
                 {{ block.settings.image | image_url: width: 500 | image_tag: loading: 'lazy', alt: block.settings.collection.title }}
             {%- else -%}
                {{ 'image' | placeholder_svg_tag }}
             {%- endif -%}

            <div class="collection-2-name-box"><p>{{ block.settings.collection_name }}</p></div>
          </div>
        </div></a>
      {%- endfor -%}
    </div>
  </div>
</div>
