{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign cards_gap = section.settings.cards_gap
  assign cards_gap_mobile = section.settings.cards_gap_mobile

  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_height = section.settings.card_height
  assign card_height_mobile = section.settings.card_height_mobile
  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_align = section.settings.card_align
  assign card_align_mobile = section.settings.card_align_mobile
  assign use_box_shadow = section.settings.use_box_shadow

  assign card_horizontal_align = ""
  if card_align == "center"
    assign card_horizontal_align = "center"
  elsif card_align == "right"
    assign card_horizontal_align = "end"
  else
    assign card_horizontal_align = "start"
  endif

  assign card_horizontal_align_mobile = ""
  if card_align_mobile == "center"
    assign card_horizontal_align_mobile = "center"
  elsif card_align_mobile == "right"
    assign card_horizontal_align_mobile = "end"
  else
    assign card_horizontal_align_mobile = "start"
  endif

  for block in section.blocks
    if block.type == 'card'
      assign card_count = card_count | plus: 1 
    endif
  endfor

  assign sub_heading_size = section.settings.sub_heading_size
  assign sub_heading_size_mobile = section.settings.sub_heading_size_mobile
  assign sub_heading_custom = section.settings.sub_heading_custom
  assign sub_heading_font = section.settings.sub_heading_font
  assign sub_heading_height = section.settings.sub_heading_height

  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_mt = section.settings.heading_mt
  assign heading_mt_mobile = section.settings.heading_mt_mobile

  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_style = section.settings.button_style
  assign button_mt = section.settings.button_mt
  assign button_mt_mobile = section.settings.button_mt_mobile

  assign icon_size = section.settings.icon_size
  assign icon_size_mobile = section.settings.icon_size_mobile
-%}

{%- style -%}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .bento-body-{{ section.id }} {
    display: grid;
    gap: {{ cards_gap_mobile }}px;
  }

  .bento-cards-column-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    gap: {{ cards_gap_mobile }}px;
  }

  .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }} {
    width: 100%;
  }

  .bento-card-{{ section.id }} {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: {{ card_horizontal_align_mobile }};
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    min-height: {{ card_height_mobile }}px;
    overflow: hidden;
    text-decoration: none;    
    {% if use_box_shadow %}
      box-shadow: rgba(0, 0, 0, 0.18) 0px 2px 4px;
      transition: all 0.25s ease 0s;
  {% endif %}
  }

  .bento-card-image-{{ section.id }} {
    position: absolute;
    left: 0;
    top: 0;
    z-index: 1;
    width: 100%;
    height: 100%;    
  }

  .bento-card-image-{{ section.id }} img,
  .bento-card-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bento-card-subheading-{{ section.id }} {
    position: relative;
    z-index: 2;
    text-align: {{ card_align_mobile }};
  }

  .bento-card-subheading-{{ section.id }} * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px; 
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .bento-card-heading-{{ section.id }} {
    position: relative;
    z-index: 2;
    text-align: {{ card_align_mobile }};
    margin-top: {{ heading_mt_mobile }}px;
  }

  .bento-card-heading-{{ section.id }} * {
    margin: 0;    
    font-size: {{ heading_size_mobile }}px; 
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .bento-card-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    position: relative;
    z-index: 2;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .bento-card-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bento-card-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bento-card-top-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
  }

  .bento-icon-{{ section.id }} {
    position: relative;
    z-index: 1;
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .bento-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .bento-body-{{ section.id }} {
      gap: {{ cards_gap }}px;
    }

    .bento-cards-column-{{ section.id }} {
      gap: {{ cards_gap }}px;
    }

    .bento-card-{{ section.id }} {
      align-items: {{ card_horizontal_align }};
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
      min-height: {{ card_height }}px;
    }

    .bento-card-subheading-{{ section.id }} {
      text-align: {{ card_align }};
    }

    .bento-card-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px; 
    }

    .bento-card-heading-{{ section.id }} {
      text-align: {{ card_align }};
      margin-top: {{ heading_mt }}px;
    }

    .bento-card-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px; 
    }

    .bento-card-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .bento-icon-{{ section.id }} {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if use_box_shadow %}
  <style>
    box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    transition: box-shadow 0.3s ease-in-out;
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .bento-card-subheading-{{ section.id }} {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .bento-card-heading-{{ section.id }} {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}


{% if button_custom %}
  <style>
    .bento-card-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if card_count == 5 %} 
  <style>
    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(4) {
      width: 100%;
    }

    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(2),
    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(3) {
      width: calc(50% - ({{ cards_gap_mobile }}px / 2)) !important;
    }

    @media (min-width: 1024px) {
      .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(2),
      .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(3) {
        width: calc(50% - ({{ cards_gap }}px / 2)) !important;
      }

      .bento-body-{{ section.id }} {
        grid-template-columns: repeat(2, 1fr);
      }
    }
  </style>
{% endif %}  

{% if card_count == 4 %} 
  <style>
    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(2),
    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(3) {
      width: calc(50% - ({{ cards_gap_mobile }}px / 2)) !important;
    }

    @media (min-width: 1024px) {
      .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(2),
      .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }}:nth-child(3) {
        width: calc(50% - ({{ cards_gap }}px / 2)) !important;
      }

      .bento-body-{{ section.id }} {
        grid-template-columns: repeat(2, 1fr);
      }
    }
  </style>
{% endif %}  

{% if card_count == 3 %} 
  <style>
    .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }} {
      width: 100%;
    }

    @media (min-width: 1024px) {
      .bento-body-{{ section.id }} {
        grid-template-columns: repeat(2, 1fr);
      }

      .bento-cards-column-{{ section.id }} .bento-card-{{ section.id }} {
        width: 100%;
      }
    }
  </style>
{% endif %}  

{% if card_count == 2 %} 
  <style>
    .bento-body-{{ section.id }} {
      grid-template-columns: 1fr;
    }
    @media (min-width: 1024px) {
      .bento-body-{{ section.id }} {
        grid-template-columns: repeat(2, 1fr);
      }
    }
  </style>
{% endif %} 

{% if card_count == 1 %} 
  <style>
    .faq-body-{{ section.id }} {
      grid-template-columns: 1fr;
    }
    @media (min-width: 1024px) {
      .faq-body-{{ section.id }} {
        grid-template-columns: 1fr;
      }
    }
  </style>
{% endif %} 

{% for block in section.blocks %}
  <style>
    .bento-card-{{ block.id }} {
      transition: all 0.25s ease 0s;
      background-color: {{ block.settings.card_bg_color }};
      border: {{ card_border_thickness }}px solid {{ block.settings.card_border_color }};
    }

    .bento-card-subheading-{{ block.id }} * {
      transition: all 0.25s ease 0s;
      color: {{ block.settings.sub_heading_color }};
    }

    .bento-card-heading-{{ block.id }} * {
      transition: all 0.25s ease 0s;
      color: {{ block.settings.heading_color }};
    }   

    .bento-card-button-{{ block.id }} {
      color: {{ block.settings.button_color }};
    }

    .bento-card-button-{{ block.id }}:hover {
      color: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .bento-card-button-{{ block.id }} svg path {
      fill: {{ block.settings.button_color }};
      transition: all 0.25s ease 0s;
    }

    .bento-card-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .bento-icon-{{ block.id }} svg path {
      transition: all 0.25s ease 0s;
      fill: {{ block.settings.icon_color }};
    }
  </style>
  {% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
    <style>
      .bento-card-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
      }

      .bento-card-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
      }
    </style>
  {% elsif button_style == "outline" or button_style == "outline_arrow" %}
    <style>
      .bento-card-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.button_border_color }};
      }

      .bento-card-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
      }
    </style>
  {% elsif button_style == "link" %}
    <style>
      .bento-card-button-{{ block.id }} {
        padding: 0px !important;
      }
    </style>
  {% endif %}
  {% if block.settings.button == blank %}
    <style>
      .bento-card-{{ block.id }} {
        justify-content: space-between;
      }

      .bento-card-{{ block.id }}:hover {
        transition: all 0.25s ease 0s;
        background-color: {{ block.settings.card_bg_hover_color }};
        border: {{ card_border_thickness }}px solid {{ block.settings.card_border_hover_color }};
      }

      .bento-card-{{ block.id }}:hover .bento-card-subheading-{{ block.id }} * {
        transition: all 0.25s ease 0s;
        color: {{ block.settings.sub_heading_hover_color }};
      }

      .bento-card-{{ block.id }}:hover .bento-card-heading-{{ block.id }} * {
        transition: all 0.25s ease 0s;
        color: {{ block.settings.heading_hover_color }};
      }   

      .bento-card-{{ block.id }}:hover  .bento-icon-{{ block.id }} svg path {
        transition: all 0.25s ease 0s;
        fill: {{ block.settings.icon_hover_color }};
      }
    </style>
  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} bento-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="bento-body-{{ section.id }}">          
        {% for block in section.blocks %}
          {% if forloop.first %}
            <{% if block.settings.button == blank and block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div {% endif %} class="bento-card-{{ section.id }} bento-card-{{ block.id }}">
              <div class="bento-card-image-{{ section.id }}">
                {% if block.settings.card_bg_image != blank %}
                  <img src="{{ block.settings.card_bg_image | image_url }}" alt="{{ block.settings.card_bg_image.alt }}" {% if lazy %}loading="lazy"{% endif %} draggable="false" class="before-after-image-{{ section.id }} {% if before_image_mobile != blank %}before-after-image-first-{{ section.id }}{% endif %}">
                {% endif %}
              </div>    
              {% if block.settings.button == blank %}
                <div class="bento-card-top-{{ section.id }}">
                  {% if block.settings.sub_heading != blank %}
                    <div class="bento-card-subheading-{{ section.id }} bento-card-subheading-{{ block.id }}">{{ block.settings.sub_heading }}</div>
                  {% endif %}   
                  {% unless block.settings.hide_arrow %}
                  <div class="bento-icon-{{ section.id }} bento-icon-{{ block.id }}">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="#000000" width="800px" height="800px" viewBox="-8.5 0 32 32" version="1.1">
                      <path d="M14.56 9.6c0-0.64-0.44-0.92-0.88-0.92v0h-9.64c-0.48 0-0.84 0.36-0.84 0.84s0.36 0.84 0.84 0.84h7.68l-11.48 11.52c-0.32 0.32-0.32 0.84 0 1.2 0.16 0.16 0.4 0.24 0.6 0.24s0.44-0.080 0.6-0.24l11.44-11.44v7.68c0 0.48 0.36 0.84 0.84 0.84s0.84-0.36 0.84-0.84v-9.72z"/>
                    </svg>
                  </div> 
                  {% endunless %}
                </div>
              {% else %}  
                {% if block.settings.sub_heading != blank %}
                  <div class="bento-card-subheading-{{ section.id }} bento-card-subheading-{{ block.id }}">{{ block.settings.sub_heading }}</div>
                {% endif %}   
              {% endif %}                               
              {% if block.settings.heading != blank %}
                <div class="bento-card-heading-{{ section.id }} bento-card-heading-{{ block.id }}">{{ block.settings.heading }}</div>
              {% endif %}   
              {% if block.settings.button != blank %}
                <{% if  block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="bento-card-button-{{ section.id }} bento-card-button-{{ block.id }}">
                  <p class="bento-card-button-inner-{{ section.id }}">
                  {{ block.settings.button }}
                    {% if button_style == "non_outline_arrow" or button_style == "outline_arrow" %}
