{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign cards_gap = section.settings.cards_gap
  assign cards_gap_mobile = section.settings.cards_gap_mobile

  assign big_card_padding_vertical = section.settings.big_card_padding_vertical
  assign big_card_padding_vertical_mobile = section.settings.big_card_padding_vertical_mobile
  assign big_card_padding_horizontal = section.settings.big_card_padding_horizontal
  assign big_card_padding_horizontal_mobile = section.settings.big_card_padding_horizontal_mobile
  assign big_card_radius = section.settings.big_card_radius
  assign big_card_bg_style = section.settings.big_card_bg_style
  assign big_card_bg_color = section.settings.big_card_bg_color
  assign big_card_bg_gradient = section.settings.big_card_bg_gradient
  assign big_card_bg_image = section.settings.big_card_bg_image
  assign big_card_overlay_color = section.settings.big_card_overlay_color
  assign big_card_overlay_opacity = section.settings.big_card_overlay_opacity
  assign big_card_use_overlay = section.settings.big_card_use_overlay
  assign big_card_border_color = section.settings.big_card_border_color
  assign big_card_border_thickness = section.settings.big_card_border_thickness
  assign big_card_align = section.settings.big_card_align
  assign big_card_align_mobile = section.settings.big_card_align_mobile

  assign big_card_heading_custom = section.settings.big_card_heading_custom
  assign big_card_heading_font = section.settings.big_card_heading_font
  assign big_card_heading_size = section.settings.big_card_heading_size
  assign big_card_heading_size_mobile = section.settings.big_card_heading_size_mobile
  assign big_card_heading_height = section.settings.big_card_heading_height
  assign big_card_heading_color = section.settings.big_card_heading_color

  assign big_card_sub_heading_custom = section.settings.big_card_sub_heading_custom
  assign big_card_sub_heading_font = section.settings.big_card_sub_heading_font
  assign big_card_sub_heading_size = section.settings.big_card_sub_heading_size
  assign big_card_sub_heading_size_mobile = section.settings.big_card_sub_heading_size_mobile
  assign big_card_sub_heading_mt = section.settings.big_card_sub_heading_mt
  assign big_card_sub_heading_mt_mobile = section.settings.big_card_sub_heading_mt_mobile
  assign big_card_sub_heading_height = section.settings.big_card_sub_heading_height
  assign big_card_sub_heading_color = section.settings.big_card_sub_heading_color

  assign big_card_text_custom = section.settings.big_card_text_custom
  assign big_card_text_font = section.settings.big_card_text_font
  assign big_card_text_size = section.settings.big_card_text_size
  assign big_card_text_size_mobile = section.settings.big_card_text_size_mobile
  assign card_text_mt = section.settings.card_text_mt
  assign card_text_mt_mobile = section.settings.card_text_mt_mobile
  assign big_card_text_height = section.settings.big_card_text_height
  assign big_card_text_color = section.settings.big_card_text_color
  assign big_card_text_highlight = section.settings.big_card_text_highlight

  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_gap = section.settings.card_gap
  assign card_gap_mobile = section.settings.card_gap_mobile
  assign card_gap_radius = section.settings.card_gap_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_bg_color = section.settings.card_bg_color
  assign card_border_color = section.settings.card_border_color
  assign card_align = section.settings.card_align
  assign card_align_mobile = section.settings.card_align_mobile

  assign card_image_ratio = section.settings.card_image_ratio
  assign card_image_ratio_mobile = section.settings.card_image_ratio_mobile

  assign card_heading_custom = section.settings.card_heading_custom
  assign card_heading_font = section.settings.card_heading_font
  assign card_heading_size = section.settings.card_heading_size
  assign card_heading_size_mobile = section.settings.card_heading_size_mobile
  assign card_heading_height = section.settings.card_heading_height
  assign card_heading_color = section.settings.card_heading_color

  assign card_text_custom = section.settings.card_text_custom
  assign card_text_font = section.settings.card_text_font
  assign card_text_size = section.settings.card_text_size
  assign card_text_size_mobile = section.settings.card_text_size_mobile
  assign card_text_height = section.settings.card_text_height
  assign card_text_color = section.settings.card_text_color
  
  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif

  assign card_sizes = 0
  for block in section.blocks
    if block.type == 'card'
      assign card_sizes = card_sizes | plus: 1
    endif
  endfor
-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    grid-template-rows: auto;
    gap: {{ cards_gap_mobile }}px 0;
  }

  .bento-big-card-{{ section.id }} {
    display: flex;
    position: relative;
    overflow: hidden;
    flex-direction: column;
    align-items: stretch;
    grid-row: 1;
    align-self: stretch;
    min-height: 260px;
    padding: {{ big_card_padding_vertical_mobile }}px {{ big_card_padding_horizontal_mobile }}px;
    gap: 10px;
    border-radius: {{ big_card_radius }}px;
    border: {{ big_card_border_thickness }}px solid {{ big_card_border_color }};
  }

  .bento-big-card-content-top-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    align-items: flex-start;
  }

  .bento-big-card-heading-{{ section.id }}  {
    text-align: {{ big_card_align_mobile }};
    width: 100%;
  }

  .bento-big-card-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ big_card_heading_size_mobile }}px;
    color: {{ big_card_heading_color }};
    line-height: {{ big_card_heading_height }}%;
    font-style: normal;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .bento-big-card-sub-heading-{{ section.id }} {
    margin-top: {{ big_card_sub_heading_mt_mobile }}px;
    text-align: {{ big_card_align_mobile }};
    width: 100%;
  }

  .bento-big-card-sub-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ big_card_sub_heading_size_mobile }}px;
    color: {{ big_card_sub_heading_color }};
    line-height: {{ big_card_sub_heading_height }}%;
    font-style: normal;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .bento-big-card-content-bottom-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    align-items: flex-start;
    flex: 1 0;
    align-self: stretch;
  }

  .bento-big-card-text-{{ section.id }}  {
    text-align: {{ big_card_align_mobile }};
    width: 100%;
  }

  .bento-big-card-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ big_card_text_size_mobile }}px;
    line-height: {{ big_card_text_height }}%;
    color: {{ big_card_text_color }};
    font-style: normal;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .bento-big-card-text-{{ section.id }} strong {
    font-weight: 400;
    color: {{ big_card_text_highlight }};
  }

  .bento-card-{{ section.id }} {
    overflow: hidden;
    border-radius: {{ card_gap_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background: {{ card_bg_color }};
  }

  .bento-card-image-wrap-{{ section.id }} {
    width: 100%;
  }

  .bento-card-image-wrap-{{ section.id }} img,
  .bento-card-image-wrap-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bento-card-image-wrap-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .bento-card-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: {{ card_gap_mobile }}px;
    align-self: stretch;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
  }

  .bento-card-heading-{{ section.id }} {
    text-align: {{ card_align_mobile }};
    width: 100%;
  }

  .bento-card-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ card_heading_size_mobile }}px;
    color: {{ card_heading_color }};
    line-height: {{ card_heading_height }}%;
    font-style: normal;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .bento-card-text-{{ section.id }} {
    text-align: {{ card_align_mobile }};
    margin-top: {{ card_text_mt_mobile }}px;
    width: 100%;
  }

  .bento-card-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ card_text_size_mobile }}px;
    color: {{ card_text_color }};
    line-height: {{ card_text_height }}%;
    font-style: normal;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
      gap: {{ cards_gap }}px;
      grid-template-columns: repeat(3, 1fr);
    }

    .bento-big-card-{{ section.id }} {
      grid-column: 1 / span 1;
      grid-row: 1 / span 2;
      padding: {{ big_card_padding_vertical }}px {{ big_card_padding_horizontal }}px;
    }

    .bento-big-card-heading-{{ section.id }}  {
      text-align: {{ big_card_align }};
    }

    .bento-big-card-heading-{{ section.id }} * {
      font-size: {{ big_card_heading_size }}px;
    }

    .bento-big-card-sub-heading-{{ section.id }} {
      text-align: {{ big_card_align }};
      margin-top: {{ big_card_sub_heading_mt }}px;
    }

    .bento-big-card-sub-heading-{{ section.id }} * {
      font-size: {{ big_card_sub_heading_size }}px;
    }

    .bento-big-card-text-{{ section.id }} {
      text-align: {{ big_card_align }};
    }

    .bento-big-card-text-{{ section.id }} * {
      font-size: {{ big_card_text_size }}px;
    }

    .bento-card-content-{{ section.id }} {
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
      gap: {{ card_gap }}px;
    }

    .bento-card-heading-{{ section.id }} * {
      font-size: {{ card_heading_size }}px;
    }

    .bento-card-heading-{{ section.id }} {
      text-align: {{ card_align }};
    }

    .bento-card-text-{{ section.id }} {
      text-align: {{ card_align }};
      margin-top: {{ card_text_mt }}px;
    }

    .bento-card-text-{{ section.id }} * {
      font-size: {{ card_text_size }}px;
    }
  }

{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}


{% if big_card_bg_style == 'solid' %}
  <style>
    .bento-big-card-{{ section.id }} {
      background-color: {{ big_card_bg_color }};
    }
  </style>
{% elsif big_card_bg_style == 'gradient' %}
    <style>
    .bento-big-card-{{ section.id }} {
      background-image: {{ big_card_bg_gradient }};
    }
  </style>
{% else %}
  <style>
    .bento-big-card-{{ section.id }} {
      background-image: url('{{ big_card_bg_image | image_url }}');
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
      position: relative;
    }
  </style>
{% endif %}

{% if big_card_bg_style == 'image' %}
  <style>
    .bento-big-card-{{ section.id }} {
      position: relative;
      overflow: hidden;
    }

    {% if big_card_overlay_opacity > 0 %}
      .bento-big-card-{{ section.id }}::after {
        content: "";
        position: absolute;
        inset: 0;
        background: {{ big_card_overlay_color }};
        opacity: {{ big_card_overlay_opacity | times: 0.01 }};
        pointer-events: none;
        z-index: 1;
      }
    {% endif %}

    {% if big_card_use_overlay %}
      .bento-big-card-{{ section.id }}::before {
        content: "";
        position: absolute;
        left: 0;
        right: 0;
        bottom: 0;
        top: unset;
        height: 50%;
        background: linear-gradient(180deg, transparent 0%, {{ big_card_overlay_color }} 100%);
        pointer-events: none;
        z-index: 2;
      }
    {% endif %}

    .bento-big-card-{{ section.id }} > * {
      position: relative;
      z-index: 2;
    }
  </style>
{% endif %}


{% if big_card_heading_custom %}
  <style>
    {{ big_card_heading_font | font_face: font_display: 'swap' }}

    .bento-big-card-heading-{{ section.id }} * {
      font-family: {{ big_card_heading_font.family }}, {{ big_card_heading_font.fallback_families }};
      font-weight: {{ big_card_heading_font.weight }};
      font-style: {{ big_card_heading_font.style }};
    }
  </style>
{% endif %}

{% if big_card_sub_heading_custom %}
  <style>
    {{ big_card_sub_heading_font | font_face: font_display: 'swap' }}

    .bento-big-card-sub-heading-{{ section.id }} * {
      font-family: {{ big_card_sub_heading_font.family }}, {{ big_card_sub_heading_font.fallback_families }};
      font-weight: {{ big_card_sub_heading_font.weight }};
      font-style: {{ big_card_sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if big_card_text_custom %}
  <style>
    {{ big_card_text_font | font_face: font_display: 'swap' }}

    .bento-big-card-text-{{ section.id }} * {
      font-family: {{ big_card_text_font.family }}, {{ big_card_text_font.fallback_families }};
      font-weight: {{ big_card_text_font.weight }};
      font-style: {{ big_card_text_font.style }};
    }
    .bento-big-card-text-{{ section.id }} strong {
      font-weight: {{ big_card_text_font.weight }};
    }
  </style>
{% endif %}

{% if card_heading_custom %}
  <style>
    {{ card_heading_font | font_face: font_display: 'swap' }}

    .bento-card-heading-{{ section.id }} * {
      font-family: {{ card_heading_font.family }}, {{ card_heading_font.fallback_families }};
      font-weight: {{ card_heading_font.weight }};
      font-style: {{ card_heading_font.style }};
    }
  </style>
{% endif %}

{% if card_text_custom %}
  <style>
    {{ card_text_font | font_face: font_display: 'swap' }}

    .bento-card-text-{{ section.id }} * {
      font-family: {{ card_text_font.family }}, {{ card_text_font.fallback_families }};
      font-weight: {{ card_text_font.weight }};
      font-style: {{ card_text_font.style }};
    }
  </style>
{% endif %}

{% if card_image_ratio_mobile == "portrait" %}
    <style>
        .bento-card-image-wrap-{{ section.id }} {
            aspect-ratio: 9.6/12; 
        }
    </style>
{% elsif card_image_ratio_mobile == "landscape" %}
    <style>
        .bento-card-image-wrap-{{ section.id }}{
            aspect-ratio: 12/8; 
        }
    </style>
{% elsif card_image_ratio_mobile == "square" %}
    <style>
        .bento-card-image-wrap-{{ section.id }} {
            aspect-ratio: 12/12; 
        }
    </style>
{% else %}
    <style>
        .bento-card-image-wrap-{{ section.id }} {
            aspect-ratio: auto; 
        }
    </style>
{% endif %}

{% if card_image_ratio == "portrait" %}
    <style>
        @media(min-width: 1024px) {
            .bento-card-image-wrap-{{ section.id }} {
                aspect-ratio: 9.6/12; 
            } 
        }
    </style>
{% elsif card_image_ratio == "landscape" %}
    <style>
        @media(min-width: 1024px) {
            .bento-card-image-wrap-{{ section.id }} {
                aspect-ratio: 12/8; 
            }
        }
    </style>
{% elsif card_image_ratio == "square" %}
    <style>
        @media(min-width: 1024px) {
            .bento-card-image-wrap-{{ section.id }} {
                aspect-ratio: 12/12; 
            }
        }
    </style>
{% else %}
    <style>
        @media(min-width: 1024px) {
            .bento-card-image-wrap-{{ section.id }} {
                aspect-ratio: auto; 
            }
        }
    </style>
{% endif %}

{% if card_sizes < 3 %}
  <style>
    @media(min-width: 1024px) {
      .bento-big-card-{{ section.id }} {
        grid-row: 1 / span 1;
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} bento-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
  <div class="section-{{ section.id }}-settings">
    {% for block in section.blocks %}
      {% if block.type == 'big_card' %}
        <div class="bento-big-card-{{ section.id }}">
          <div class="bento-big-card-content-top-{{ section.id }}">
            {% if block.settings.heading %}
              <div class="bento-big-card-heading-{{ section.id }}">{{ block.settings.heading }}</div>
            {% endif %}
            {% if block.settings.sub_heading %}
              <div class="bento-big-card-sub-heading-{{ section.id }}">{{ block.settings.sub_heading }}</div>
            {% endif %}
          </div>
          <div class="bento-big-card-content-bottom-{{ section.id }}">
            {% if block.settings.text %}
              <div class="bento-big-card-text-{{ section.id }}">{{ block.settings.text }}</div>
            {% endif %}
          </div>
        </div>
      {% endif %}
    {% endfor %}
    {% for block in section.blocks %}
      {% if block.type == 'card' %}
        <div class="bento-card-{{ section.id }}">
          <div class="bento-card-image-wrap-{{ section.id }}">
            {% if block.settings.image != blank %}
              {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
              {{ block.settings.image | image_url: width: 1000 | image_tag:
                loading: loading_attribute,
                width: block.settings.image.width,
                height: block.settings.image.height,
                sizes: sizes,
                widths: '360, 535, 750, 1070, 1500'
              }}
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
          <div class="bento-card-content-{{ section.id }}">
            {% if block.settings.heading %}
              <div class="bento-card-heading-{{ section.id }}">{{ block.settings.heading }}</div>
            {% endif %}
            {% if block.settings.text %}
              <div class="bento-card-text-{{ section.id }}">{{ block.settings.text }}</div>
            {% endif %}
          </div>
        </div>
      {% endif %}
    {% endfor %}
  </div>
</div>
