{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign grid_row_mobile = section.settings.grid_row_mobile
  assign grid_row = section.settings.grid_row
  assign grid_gap = section.settings.grid_gap
  assign grid_gap_mobile = section.settings.grid_gap_mobile
  assign grid_mt = section.settings.grid_mt

  assign articles = section.settings.articles

  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_radius = section.settings.card_radius
  assign card_hover = section.settings.card_hover
  assign card_shadow_color = section.settings.card_shadow_color

  assign image_ratio = section.settings.image_ratio
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_bottom_border = section.settings.image_bottom_border

  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_bg_color = section.settings.content_bg_color
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_align = section.settings.content_align

  assign date_size = section.settings.date_size
  assign date_size_mobile = section.settings.date_size_mobile
  assign date_color = section.settings.date_color
  assign date_custom = section.settings.date_custom
  assign date_font = section.settings.date_font
  assign date_height = section.settings.date_height

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_mt = section.settings.title_mt

  assign description_size = section.settings.description_size
  assign description_size_mobile = section.settings.description_size_mobile
  assign description_color = section.settings.description_color
  assign description_custom = section.settings.description_custom
  assign description_font = section.settings.description_font
  assign description_height = section.settings.description_height
  assign description_mt = section.settings.description_mt

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_arrow = section.settings.button_arrow
  assign button_style = section.settings.button_style
  assign button_mt = section.settings.button_mt
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color

  assign arrow_hover_color = section.settings.arrow_hover_color
  assign arrow_color = section.settings.arrow_color

  if content_align == "center"
    assign text_align = "center"
  elsif content_align == "end"
    assign text_align = "right"
  endif

  if content_align_mobile == "center"
    assign text_align_mobile = "center"
  elsif content_align_mobile == "end"
    assign text_align_mobile = "right"
  endif

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  date_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  description_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .blog-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .blog-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .blog-grid-{{ section.id }} {
    display: grid;
    gap: {{ grid_gap_mobile }}px;
    grid-template-columns: repeat({{ grid_row_mobile }}, 1fr);
    margin-top: {{ grid_mt | times: 0.75 | round: 0 }}px;
  }

  .blog-card-{{ section.id }} {
    display: flex;
    flex-direction: column;
    text-decoration: none;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    overflow: hidden;
  }

  .blog-image-{{ section.id }} {
    display: block;
    min-height: 300px;
    border-top-left-radius: {{ card_radius }}px;
    border-top-right-radius: {{ card_radius }}px;
  }

  .blog-image-{{ section.id }} img,
  .blog-image-{{ section.id }} svg {
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
  }

  .blog-image-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  .blog-content-{{ section.id }} {
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: {{ content_align_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    background-color: {{ content_bg_color }};
  }

  .blog-date-{{ section.id }} {
    margin: 0;
    font-size: {{ date_size_mobile }}px;
    color: {{ date_color }};
    line-height: {{ date_height }}%;
    text-transform: unset;
  }

  .blog-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    text-align: {{ text_align_mobile }};
    text-decoration: none;
  }

  .blog-description-{{ section.id }} {
    margin-top: {{ description_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .blog-description-{{ section.id }} * {
    margin: 0;
    font-size: {{ description_size_mobile }}px;
    color: {{ description_color }};
    line-height: {{ description_height }}%;
    text-transform: unset;
  }

  .blog-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 5px;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .blog-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .blog-button-{{ section.id }} svg {
    display: block;
    width: 20px;
    height: 20px;
    fill: {{ arrow_color }};
    transition: all 0.25s ease 0s;
  }

  .blog-button-{{ section.id }}:hover svg {
    fill: {{ arrow_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .blog-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .blog-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .blog-grid-{{ section.id }} {
      margin-top: {{ grid_mt }}px;
      gap: {{ grid_gap }}px;
      grid-template-columns: repeat({{ grid_row }}, 1fr);
    }

    .blog-content-{{ section.id }} {
      align-items: {{ content_align }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .blog-date-{{ section.id }} {
      font-size: {{ date_size }}px;
    }

    .blog-title-{{ section.id }} {
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
      text-align: {{ text_align }};
    }

    .blog-description-{{ section.id }} {
      margin-top: {{ description_mt }}px;
      text-align: {{ text_align }};
    }

    .blog-description-{{ section.id }} * {
      font-size: {{ description_size }}px;
    }

    .blog-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      font-size: {{ button_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .blog-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if date_custom %}
  <style>
    .blog-date-{{ section.id }} {
      font-family: {{ date_font.family }}, {{ date_font.fallback_families }};
      font-weight: {{ date_font.weight }};
      font-style: {{ date_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .blog-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if description_custom %}
  <style>
    .blog-description-{{ section.id }} * {
      font-family: {{ description_font.family }}, {{ description_font.fallback_families }};
      font-weight: {{ description_font.weight }};
      font-style: {{ description_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .blog-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if card_hover %}
  <style>
    .blog-card-{{ section.id }} {
      transition: all 0.25s ease 0s;
    }

    .blog-card-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      box-shadow: 6px 6px 0 0 {{ card_shadow_color }};
      transform: translate(-6px, -6px);
    }
  </style>
{% endif %}

{% if button_style == "button" %}
  <style>

    .blog-button-{{ section.id }} {
      padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
      border-radius: {{ button_radius }}px;
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
      background-color: {{ button_bg_color }};
    }

    .blog-button-{{ section.id }}:hover {
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
      background-color: {{ button_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }

    @media(min-width: 1024px ) {
      .blog-button-{{ section.id }} {
        padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      }
    }

  </style>
{% endif %}

{% if image_bottom_border %}
  <style>
    .blog-image-{{ section.id }} {
      border-bottom: {{ image_border_thickness }}px solid {{ image_border_color }};
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    .blog-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    .blog-image-{{ section.id }} {
     aspect-ratio: 12/7; 
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    .blog-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} blog-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="blog-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}

      <div class="blog-grid-{{ section.id }}">
        {% if articles != blank %}
          {% for article in articles.articles %}
            <div class="blog-card-{{ section.id }}">

              <a href="{{ article.url }}" class="blog-image-{{ section.id }}">
                {% if article.image != blank %}
                  <img src="{{ article.image | image_url }}" alt="{{ article.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}
              </a>
              <div class="blog-content-{{ section.id }}">

                <p class="blog-date-{{ section.id }}">{{ article.created_at | date: '%B %d, %Y' }}</p>

                <a href="{{ article.url }}" class="blog-title-{{ section.id }}">{{ article.title }}</a>


                {% if article.excerpt != blank %}
                  <div class="blog-description-{{ section.id }}">{{ article.excerpt }}</div>
                {% endif %}

                  <a href="{{ article.url }}" class="blog-button-{{ section.id }}">
                    {{ button }}
                    {% if button_arrow %}
                        <svg fill="{{ arrow_color }}" height="800px" width="800px" version="1.1" id="Layer_1" viewBox="0 0 330 330" >
                        	<path id="XMLID_27_" d="M15,180h263.787l-49.394,49.394c-5.858,5.857-5.858,15.355,0,21.213C232.322,253.535,236.161,255,240,255
                        		s7.678-1.465,10.606-4.394l75-75c5.858-5.857,5.858-15.355,0-21.213l-75-75c-5.857-5.857-15.355-5.857-21.213,0
                        		c-5.858,5.857-5.858,15.355,0,21.213L278.787,150H15c-8.284,0-15,6.716-15,15S6.716,180,15,180z"/>
                        </svg>
                    {% endif %}
                  </a>

              </div>

            </div>
          {% endfor %}
        {% else %}
          {% for item in (1..3) %}
            <a href="#" class="blog-card-{{ section.id }}">

                <div class="blog-image-{{ section.id }}">
                  {{ 'image' | placeholder_svg_tag }}
                </div>
                <div class="blog-content-{{ section.id }}">

                  <p class="blog-date-{{ section.id }}">{{ "now" | date: "%Y-%m-%d" }}</p>

                  <p class="blog-title-{{ section.id }}">Article #{{ forloop.index }}</p>

                  <div class="blog-description-{{ section.id }}">
                    <p>Short description article</p>
                  </div>

                  <p class="blog-button-{{ section.id }}">
                      {{ button }}
                      {% if button_arrow %}
                          <svg fill="{{ arrow_color }}" height="800px" width="800px" version="1.1" id="Layer_1" viewBox="0 0 330 330" >
                          	<path id="XMLID_27_" d="M15,180h263.787l-49.394,49.394c-5.858,5.857-5.858,15.355,0,21.213C232.322,253.535,236.161,255,240,255
                          		s7.678-1.465,10.606-4.394l75-75c5.858-5.857,5.858-15.355,0-21.213l-75-75c-5.857-5.857-15.355-5.857-21.213,0
                          		c-5.858,5.857-5.858,15.355,0,21.213L278.787,150H15c-8.284,0-15,6.716-15,15S6.716,180,15,180z"/>
                          </svg>
                      {% endif %}
                    </p>

                </div>

              </a>
            {% endfor %}
        {% endif %}
      </div>
    </div>
</div>
