{%- style -%}
  {{ blog_title_font | font_face: font_display: 'swap' }}
  {{ tab_font | font_face: font_display: 'swap' }}
  {{ article_title_font | font_face: font_display: 'swap' }}
  {{ article_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .blog-top-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 20px;
    justify-content: space-between;
    align-items: center;
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
  }

  .blog-title-{{ section.id }} {
    margin: 0;
    max-width: {{ blog_title_max_width_mobile }}%;
    width: 100%;
    font-size: {{ blog_title_size_mobile }}px; 
    line-height: {{ blog_title_height }}%;
    color: {{ blog_title_color }};
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .blog-tabs-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: {{ tabs_gap_mobile }}px;    
    overflow-x: scroll;
    scrollbar-width: none;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    width: 100%;
  }

  .blog-tab-{{ section.id }} {
    margin: 0;
    position: relative;
    font-size: {{ tab_size_mobile }}px; 
    line-height: {{ tab_height }}%;
    color: {{ tab_color }};
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    cursor: pointer;
    transition: all 0.25s ease 0s;
    font-weight: 700;
    min-width: max-content;
  }

  .blog-tab-{{ section.id }}.active {
    color: {{ tab_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .blog-tab-{{ section.id }}::after.active {
    width: 100%;
    transition: all 0.25s ease 0s;
  }

  .blog-tab-{{ section.id }}::after {
    content: "";
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: {{ tab_border_thickness }}px;
    background-color: {{ tab_border_color }};
    transition: all 0.25s ease 0s;
  }

  .blog-blog-{{ section.id }} {
    display: none;
    grid-template-columns: 1fr;
    gap: 0px;
    margin-top: {{ layout_mt_mobile }}px;
  }

  .blog-blog-{{ section.id }}.active {
    display: grid;
  }

  .blog-big-article-{{ section.id }} {
    display: flex;
    justify-content: space-between;
    padding: {{ article_padding_vertical_mobile }}px 0px;
    border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
    border-bottom: 0px;
    gap: 10px;
    grid-column-end: initial;
    align-items: start;
  }

  .blog-article-{{ section.id }} {
    display: flex;
    justify-content: space-between;
    padding: {{ article_padding_vertical_mobile }}px 0px;   
    border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
    border-bottom: 0px;
    gap: 10px;
  }

  .blog-article-{{ section.id }}::last-child {
    border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
    border-bottom: {{ article_border_thickness }}px solid {{ article_border_color }};
  }

  .blog-article-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    flex: 1 1 0;
    height: 100%;
  }

  .blog-article-title-wrap-{{ section.id }} {
    display: flex;
    gap: 6px;
    align-items: center;
    flex-wrap: wrap;
  }

  .blog-article-title-{{ section.id }},
  .blog-article-tag-{{ section.id }} {
    margin: 0;
    font-size: {{ article_title_size_mobile }}px; 
    line-height: {{ article_title_height }}%;
    color: {{ article_title_color }};
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    transition: all 0.5s ease 0s;
  }

  .blog-article-title-{{ section.id }} {
    font-weight: 700;
  }

  .blog-article-tag-{{ section.id }} {
    color: {{ article_tag_color }};
    transition: all 0.25s ease 0s;
  }

  .blog-article-text-{{ section.id }} {
    margin: 0;
    font-size: {{ article_text_size_mobile }}px; 
    line-height: {{ article_text_height }}%;
    color: {{ article_text_color }};
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    opacity: 1;
    transition: all 0.5s ease 0s;
  }

  .blog-article-image-{{ section.id }} {
    flex: 1 1 0;
    overflow: hidden;
  }

  .blog-article-main-image-{{ section.id }} {
    border: {{ main_image_border_thickness }}px solid {{ main_image_border_color }};
    border-radius: {{ main_image_radius }}px;
  }

  .blog-article-article-image-{{ section.id }} {
    border: {{ article_image_border_thickness }}px solid {{ article_image_border_color }};
    border-radius: {{ article_image_radius }}px;
  }

  .blog-article-image-{{ section.id }} img,
  .blog-article-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.5s ease 0s;
  }

  .blog-article-image-{{ section.id }} svg {
    background-color: #CECECE;
  }

  .blog-right-articles-wrapper-{{ section.id }} {
    grid-column-end: initial;
  }

  .blog-right-articles-{{ section.id }} {
    display: none;
    grid-template-columns: 1fr;    
  }

  .blog-right-articles-{{ section.id }}.active {
    display: grid;
  }

  .blog-right-articles-{{ section.id }} .blog-article-image-{{ section.id }} {
    flex: 1 1 0;
    width: 100%;
  }

  .blog-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .blog-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .blog-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .blog-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .blog-top-{{ section.id }} {
      flex-direction: row;
      margin-left: 0px;
      margin-right: 0px;
    }

    .blog-title-{{ section.id }} {
      max-width: {{ blog_title_max_width }}%;
      font-size: {{ blog_title_size }}px; 
      padding-left: 0px;
      padding-right: 0px;
    }

    .blog-tabs-{{ section.id }} {
      gap: {{ tabs_gap }}px;    
      overflow-x: visible;
      padding-left: 0px;
      padding-right: 0px;
      width: auto;
    }

    .blog-tab-{{ section.id }} {
      font-size: {{ tab_size }}px; 
    }

    .blog-tab-{{ section.id }}:hover {
      color: {{ tab_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .blog-tab-{{ section.id }}:hover::after {
      width: 100%;
      transition: all 0.25s ease 0s;
    }

    .blog-blog-{{ section.id }} {
      grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
      gap: {{ layout_gap }}px;
      margin-top: {{ layout_mt }}px;
    }

    .blog-big-article-{{ section.id }} {
      border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
      border-bottom: {{ article_border_thickness }}px solid {{ article_border_color }};
      padding: {{ article_padding_vertical }}px 0px;
      grid-column-end: span 3;
    }

    .blog-article-{{ section.id }} {
      padding: {{ article_padding_vertical }}px 0px;
    }

    .blog-article-title-{{ section.id }},
    .blog-article-tag-{{ section.id }} {
      font-size: {{ article_title_size }}px; 
    }

    .blog-article-tag-{{ section.id }}:hover {
      color: {{ article_tag_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .blog-article-text-{{ section.id }} {
      font-size: {{ article_text_size }}px; 
      opacity: 0;
      transform: translateY(-10%);
    }    

    .blog-big-article-{{ section.id }}:hover .blog-article-text-{{ section.id }} {
      opacity: 1;
      transform: translateY(0%);
      transition: all 0.5s ease 0s;
    }

    .blog-article-{{ section.id }}:hover .blog-article-text-{{ section.id }} {
      opacity: 1;
      transform: translateY(0%);
      transition: all 0.5s ease 0s;
    }

    .blog-right-articles-wrapper-{{ section.id }} {
      grid-column-end: span 2;
    }

    .blog-article-{{ section.id }}:not(:last-child) {
      border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
    }

    .blog-article-{{ section.id }}:last-child {
      border-top: {{ article_border_thickness }}px solid {{ article_border_color }};
      border-bottom: {{ article_border_thickness }}px solid {{ article_border_color }};
    }

    .blog-big-article-{{ section.id }}:hover .blog-article-image-{{ section.id }} img,
    .blog-big-article-{{ section.id }}:hover .blog-article-image-{{ section.id }} svg {
      transition: all 0.5s ease 0s;
      transform: scale(1.1);
    }

    .blog-article-{{ section.id }}:hover .blog-article-image-{{ section.id }} img,
    .blog-article-{{ section.id }}:hover .blog-article-image-{{ section.id }} svg {
      transition: all 0.5s ease 0s;
      transform: scale(1.1);
    }

    .blog-right-articles-{{ section.id }} .blog-article-image-{{ section.id }} {
      flex: initial;
      width: 20%;
    }

    .blog-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .blog-button-{{ section.id }}:hover {
      color: {{ button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .blog-button-{{ section.id }}:hover svg path {
      fill: {{ button_hover_color }};
      transition: all 0.25s ease 0s;
    }  
  }

{%- endstyle -%}

