  <style>
    .blog-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .blog-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .blog-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .image-btn-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .image-btn-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .image-btn-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .image-btn-{{ section.id }} {
        margin-left: auto;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .image-btn-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} blog-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="blog-top-{{ section.id }}">
        {% if blog_title != blank %}
          <{% if blog_url != blank %}a href="{{ blog_url.url }}"{% else %}div{% endif %} class="blog-title-{{ section.id }}">
            {{ blog_title }}
          </{% if blog_url != blank %}a{% else %}div{% endif %}>
        {% endif %}
        <div class="blog-tabs-{{ section.id }}">
          {% for block in section.blocks %}
            {% if block.type == 'tab' %}
              <p class="blog-tab-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}">{{ block.settings.tab }}</p>
            {% endif %}
          {% endfor %}
        </div>       
      </div>
      <div class="blog-blogs-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.type == 'tab' %}
            <div class="blog-blog-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
              <div class="blog-big-article-{{ section.id }}">
                <div class="blog-article-content-{{ section.id }}">
                  <div class="blog-article-title-wrap-{{ section.id }}">
                    {% if block.settings.title != blank %}
                      <span>
                        <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}span{% endif %} class="blog-article-title-{{ section.id }}">
                          <span>{{ block.settings.title }}</span>      
                          {% if block.settings.tag != blank %}
                            <span>
                              <{% if block.settings.tag_url != blank %}a href="{{ block.settings.tag_url }}"{% else %}span{% endif %} class="blog-article-tag-{{ section.id }}">{{ block.settings.tag }}</{% if block.settings.tag_url != blank %}a{% else %}span{% endif %}>                   
                            </span>                         
                          {% endif %}                      
                        </{% if block.settings.url != blank %}a{% else %}span{% endif %}>    
                      </span>                                                         
                    {% endif %}                 
                  </div>
                  {% if block.settings.text != blank %}
                    <div class="blog-article-text-{{ section.id }}">{{ block.settings.text }}</div>
                  {% endif %}
                </div>
                <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% elsif block.settings.tag_url != blank%}a href="{{ block.settings.tag_url }}"{% else %}div{% endif %} class="blog-article-image-{{ section.id }} blog-article-main-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | img_url: '1000x1000' }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </{% if block.settings.url != blank or block.settings.tag_url != blank %}a{% else %}div{% endif %}>
              </div>

              <div class="blog-right-articles-wrapper-{{ section.id }}">
                {% assign inside_item = false %}
                {% for block in section.blocks %}
                  {% if block.type == 'tab' %}
                    {% if inside_item %}
                      </div>
                    {% endif %}
                    <div class="blog-right-articles-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
                  {% assign inside_item = true %}
                  {% elsif block.type == 'article' %}
                    {% if inside_item %}
                      <div class="blog-article-{{ section.id }}">
                        <div class="blog-article-content-{{ section.id }}">
                          <div class="blog-article-title-wrap-{{ section.id }}">
                            {% if block.settings.custom_title != blank %}
                              <span>
                                <{% if block.settings.custom_url != blank %}a href="{{ block.settings.custom_url }}"{% else %}span{% endif %} class="blog-article-title-{{ section.id }}">
                                  <span>{{ block.settings.custom_title }}</span>
                                  {% if block.settings.tag != blank %}
                                    <span>
                                      <{% if block.settings.tag_url != blank %}a href="{{ block.settings.tag_url }}"{% else %}span{% endif %} class="blog-article-tag-{{ section.id }}">{{ block.settings.tag }}</{% if block.settings.tag_url != blank %}a{% else %}span{% endif %}>
                                    </span>                                  
                                  {% endif %}
                                </{% if block.settings.custom_url != blank %}a{% else %}span{% endif %}>
                              </span>                             
                            {% elsif block.settings.article != blank %}
                              <span>
                                <a href="{{ block.settings.article.url }}" class="blog-article-title-{{ section.id }}">
                                  <span>{{ block.settings.article.title }}</span>
                                  {% if block.settings.tag != blank %}
                                    <span>
                                    <{% if block.settings.tag_url != blank %}a href="{{ block.settings.tag_url }}"{% else %}span{% endif %} class="blog-article-tag-{{ section.id }}">{{ block.settings.tag }}</{% if block.settings.tag_url != blank %}a{% else %}span{% endif %}>
                                    </span>                                 
                                  {% endif %}
                                </a>
                              </span>


                            {% endif %}                          
                          </div>
                          {% if block.settings.custom_text != blank %}
                            <div class="blog-article-text-{{ section.id }}">{{ block.settings.custom_text }}</div>                          
                          {% endif %}
                        </div>
                        <{% if block.settings.custom_url != blank %}a href="{{ block.settings.custom_url}}"{% elsif block.settings.article != blank %}a href="{{ block.settings.article.url}}"{% else %}div{% endif %} class="blog-article-image-{{ section.id }} blog-article-article-image-{{ section.id }}">
                          {% if block.settings.custom_image != blank %}
                            <img src="{{ block.settings.custom_image | img_url: '400x400' }}" alt="{{ block.settings.custom_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                          {% elsif block.settings.article.image != blank %}
                            <img src="{{ block.settings.article.image | img_url: '400x400' }}" alt="{{ block.settings.article.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                          {% else %}
                            {{ 'image' | placeholder_svg_tag }}
                          {% endif %}
                        </{% if block.settings.custom_url != blank or block.settings.article != blank %}a{% else %}div{% endif %}>
                      </div>
                    {% endif %}
                  {% endif %}
                {% endfor %}
                {% if inside_item %}
                  </div>
                {% endif %}
              </div>
            </div>
          {% endif %}

        {% endfor %}
      </div>
      {% if button != blank %}
        <{% if button_url %}a href="{{ button_url }}"{% else %}div{% endif %} class="blog-button-{{ section.id }}">
          <p class="blog-button-inner-{{ section.id }}">
          {{ button }}
          {% if button_style == "non_outline_arrow" or button_style == "outline_arrow" %}
              <svg width="192" height="192" viewBox="0 0 192 192" fill="none" xmlns="http://www.w3.org/2000/svg">
