{%- style -%}

  {{  title_font | font_face: font_display: 'swap' }}
  {{  description_font | font_face: font_display: 'swap' }}
  {{  selector_font | font_face: font_display: 'swap' }}
  {{  product_button_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  variant_title_font | font_face: font_display: 'swap' }}
  {{  variant_price_font | font_face: font_display: 'swap' }}
  {{  sidebar_products_text_font | font_face: font_display: 'swap' }}
  {{  sidebar_product_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  status_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .bundle-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ body_gap_mobile }}px;
  }

  .bundle-products-{{ section.id }} {
    order: 2;
    display: grid;
    grid-template-columns: repeat({{ products_row_mobile }}, 1fr);
    gap: {{ products_gap_mobile }}px;
  }

  .bundle-product-{{ section.id }} {
    display: flex;
    flex-direction: column;
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};
  }

  .bundle-product-image-{{ section.id }} {
    border-radius: {{ card_image_radius }}px;
    overflow: hidden;
  }

  .bundle-product-image-{{ section.id }} img,
  .bundle-product-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bundle-product-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .bundle-product-content-{{ section.id }} {
    flex: 1 1 auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: {{ product_content_horizontal_align_mobile }};
  }

  .bundle-product-content-top-{{ section.id }},
  .bundle-product-content-bottom-{{ section.id }} {
    width: 100%;
  }

  .bundle-product-content-bottom-{{ section.id }} {
    margin-top: {{ product_button_mt_mobile }}px;
  }

  .bundle-product-title-{{ section.id }} {
    margin: 0;
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .bundle-product-description-{{ section.id }} {
    margin: 0;
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ description_mt_mobile }}px;
    font-size: {{ description_size_mobile }}px;
    color: {{ description_color }};
    line-height: {{ description_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .bundle-product-select-wrapper-{{ section.id }} {
    position: relative;
    margin-top: {{ selector_mt_mobile }}px;
  }

  .bundle-product-select-wrapper-{{ section.id }} > span {
    pointer-events: none;
    position: absolute;
    right: {{ selector_padding_horizontal_mobile | minus: 6 }}px;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    align-items: center;
    justify-content: center;
    width: 25px;
    height: 100%;
  }

  .bundle-product-select-wrapper-{{ section.id }} > span svg {
    stroke: unset;
    fill: {{ selector_icon_color }};
    display: block;
    width: 100%;
    height: 100%;
  }

  .bundle-product-select-{{ section.id }} {
    margin: 0px;
    word-wrap: break-word;
    font-size: {{ selector_size_mobile }}px;
    line-height: {{ selector_height }}%;
    color: {{ selector_color }};
    position: relative;
    cursor: pointer;
    padding: {{ selector_padding_vertical_mobile }}px {{ selector_padding_horizontal_mobile | plus: 18 }}px {{ selector_padding_vertical_mobile }}px {{ selector_padding_horizontal_mobile }}px;
    width: 100%;
    appearance: none;
    outline: none;
    box-shadow: none;
    box-sizing: border-box;
    border: {{ selector_border_thickness }}px solid {{ selector_border_color }};
    border-radius: {{ selector_radius }}px;
    background: {{ selector_bg_color }};
  }

  .bundle-product-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    margin: 0;
    font-size: {{ product_button_size_mobile }}px;
    font-weight: 700;
    color: {{ product_button_color }};
    line-height: {{ product_button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ product_button_padding_vertical_mobile }}px {{ product_button_padding_horizontal_mobile }}px;
    border-radius: {{ product_button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .bundle-product-{{ section.id }}.active .bundle-product-button-{{ section.id }} {
    display: none;
  }

  .bundle-product-button-{{ section.id }}[disabled] {
    cursor: no-drop;
    opacity: .6;
  }

  .bundle-product-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bundle-product-button-{{ section.id }}:hover {
    color: {{ product_button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bundle-product-button-{{ section.id }} svg path {
    fill: {{ product_button_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-button-{{ section.id }}:hover svg path {
    fill: {{ product_button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-wrapper-{{ section.id }} {
    order: 1;
  }

  .bundle-sidebar-{{ section.id }} {
    border-radius: {{ sidebar_radius }}px;
    overflow: hidden;
    border: {{ sidebar_border_thickness }}px solid {{ sidebar_border_color }};
  }

  .bundle-sidebar-header-{{ section.id }} {
    padding: {{ header_padding_vertical_mobile }}px {{ header_padding_horizontal_mobile }}px;
    background-color: {{ header_bg_color_mobile }};
  }

  .bundle-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .bundle-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color_mobile }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .bundle-sidebar-body-{{ section.id }} {
    padding: {{ sidebar_body_padding_vertical_mobile }}px {{ sidebar_body_padding_horizontal_mobile }}px;
  }

  .bundle-text-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .bundle-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .bundle-variants-{{ section.id }} {
    margin-top: {{ variants_mt_mobile }}px;
    display: flex;
    align-items: stretch;
    border-radius: {{ variants_radius }}px;
    border: {{ variants_border_thickness }}px solid {{ variants_border_color }};
    overflow: hidden;
  }

  .bundle-variant-{{ section.id }} {
    flex: 1 0;
    padding: 12px;
    display: flex;
    flex-direction: column;
    gap: 5px;
    align-items: center;
    justify-content: center;
    text-align: center;
    transition: all 0.25s ease 0s;
    cursor: pointer;
  }

  .bundle-variant-{{ section.id }}.active {
    transition: all 0.25s ease 0s;
    background-color: {{ variants_active_bg_color }};
  }

  .bundle-variant-title-{{ section.id }} {
    margin: 0;
    font-size: {{ variant_title_size_mobile }}px;
    color: {{ variant_title_color }};
    line-height: {{ variant_title_height }}%;
    text-transform: unset;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .bundle-variant-{{ section.id }}.active .bundle-variant-title-{{ section.id }} {
    transition: all 0.25s ease 0s;
    color: {{ variant_title_active_color }};
  }

  .bundle-variant-price-{{ section.id }} {
    margin: 0;
    font-size: {{ variant_price_size_mobile }}px;
    color: {{ variant_price_color }};
    line-height: {{ variant_price_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
  }

  .bundle-variant-{{ section.id }}.active .bundle-variant-price-{{ section.id }} {
    transition: all 0.25s ease 0s;
    color: {{ variant_price_active_color }};
  }

  .bundle-sidebar-products-text-{{ section.id }} {
    margin-top: {{ sidebar_products_text_mt_mobile }}px;
    text-align: {{ sidebar_products_text_align_mobile }};
  }

  .bundle-sidebar-products-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ sidebar_products_text_size_mobile }}px;
    color: {{ sidebar_products_text_color }};
    line-height: {{ sidebar_products_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .bundle-sidebar-product-{{ section.id }} {
    margin-top: {{ sidebar_product_mt_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 20px;
  }

  .bundle-sidebar-product-left-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .bundle-sidebar-product-image-{{ section.id }} {
    flex: 0 0 {{ sidebar_product_image_width_mobile }}px;
  }

  .bundle-sidebar-product-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bundle-sidebar-product-title-{{ section.id }},
  .bundle-sidebar-product-count-{{ section.id }},
  .bundle-sidebar-product-empty-{{ section.id }} {
    margin: 0;
    font-size: {{ sidebar_product_size_mobile }}px;
    color: {{ sidebar_product_color }};
    line-height: {{ sidebar_product_height }}%;
    text-transform: unset;
  }

  .bundle-sidebar-product-empty-{{ section.id }} {
    margin-top: {{ sidebar_product_mt_mobile }}px;
  }

  .bundle-sidebar-product-empty-{{ section.id }}.active {
    display: none;
  }

  .bundle-sidebar-product-right-{{ section.id }} {
    flex-shrink: 0;
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .bundle-sidebar-product-remove-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: {{ remove_bg_color }};
    width: {{ remove_size_mobile }}px;
    height: {{ remove_size_mobile }}px;
    border-radius: 50%;
    overflow: hidden;
    cursor: pointer;
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-product-remove-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    background-color: {{ remove_bg_hover_color }};
  }

  .bundle-sidebar-product-remove-{{ section.id }} svg {
    display: block;
    width: 80%;
    height: 80%;
  }

  .bundle-sidebar-product-remove-{{ section.id }} svg path {
    fill: {{ remove_icon_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-product-remove-{{ section.id }}:hover svg path {
    fill: {{ remove_icon_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .bundle-button-{{ section.id }}[disabled] {
    cursor: no-drop;
    opacity: .6;
  }

  .bundle-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bundle-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bundle-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-quantity-wrapper-{{ section.id }} {
    display: none;
    grid-template-columns: 1fr 2fr 1fr;
    border-radius: {{ product_button_radius }}px;
    overflow: hidden;
    border: 1px solid {{ product_button_border_color }};
    width: 100%;
    height: 100%;
  }

  .bundle-product-{{ section.id }}.active .bundle-product-quantity-wrapper-{{ section.id }} {
    display: grid;
  }

  .bundle-product-quantity-minus-{{ section.id }},
  .bundle-product-quantity-plus-{{ section.id }} {
    background-color: transparent;
    border: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: {{ product_button_bg_color }};
    cursor: pointer;
    transition: all 0.25s ease 0s;
  }

  .bundle-product-quantity-minus-{{ section.id }} svg path,
  .bundle-product-quantity-plus-{{ section.id }} svg path {
    fill: {{ product_button_color }};
  }

  .bundle-product-quantity-{{ section.id }} {
    margin: 0;
    padding: {{ product_button_padding_vertical_mobile }}px 0px;
    font-size: {{ product_button_size_mobile }}px;
    color: {{ quantity_color }};
    line-height: {{ product_button_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-align: center;
  }

  .bundle-product-quantity-plus-{{ section.id }}[disabled] {
    cursor: no-drop;
    opacity: .6;
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-product-default-{{ section.id }} {
    display: none;
  }

  .bundle-status-{{ section.id }} {
    margin: 0px;
    font-size: {{ status_size }}px;
    color: {{ status_color }};
    line-height: {{ status_height }}%;
    text-transform: unset;
    text-align: center;
  }

  .bundle-fixed-{{ section.id }} {
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
    z-index: 999;
    background-color: rgba(255,255,255,0.6);
    padding: 20px;
  }

