                        {% endif %}
                      </p>
                  </button>
                {% endif %}

              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
</div>

<script>
  var formayMoneyForBuyItWith = function(cents, format) {
    if (typeof cents == 'string') { cents = cents.replace('.',''); }
    var value = '';
    var placeholderRegex = /\{\{\s*(\w+)\s*\}\}/;
    var formatString = (format || this.money_format);

    function defaultOption(opt, def) {
       return (typeof opt == 'undefined' ? def : opt);
    }

    function formatWithDelimiters(number, precision, thousands, decimal) {
      precision = defaultOption(precision, 2);
      thousands = defaultOption(thousands, ',');
      decimal   = defaultOption(decimal, '.');

      if (isNaN(number) || number == null) { return 0; }

      number = (number/100.0).toFixed(precision);

      var parts   = number.split('.'),
          dollars = parts[0].replace(/(\d)(?=(\d\d\d)+(?!\d))/g, '$1' + thousands),
          cents   = parts[1] ? (decimal + parts[1]) : '';

      return dollars + cents;
    }

    switch(formatString.match(placeholderRegex)[1]) {
      case 'amount':
        value = formatWithDelimiters(cents, 2);
        break;
      case 'amount_no_decimals':
        value = formatWithDelimiters(cents, 0);
        break;
      case 'amount_with_comma_separator':
        value = formatWithDelimiters(cents, 2, '.', ',');
        break;
      case 'amount_no_decimals_with_comma_separator':
        value = formatWithDelimiters(cents, 0, '.', ',');
        break;
    }

    return formatString.replace(placeholderRegex, value);
  };

  function initBundle3() {

    let addedCount = 0;
    let limitCount = Number("{{ limitsArray[0] }}");
    let mainButton = document.querySelector('.bundle-button-{{ section.id }}');
    let products = document.querySelectorAll('.bundle-product-{{ section.id }}');
    let variants = document.querySelectorAll('.bundle-variant-{{ section.id }}');
    let emptyProduct = document.querySelector('.bundle-sidebar-product-empty-{{ section.id }}');
    let bundleInputProperty = document.querySelector('.bundle-input-property-{{ section.id }}');
    let status = document.querySelector('.bundle-status-{{ section.id }}');
    let fixed = document.querySelector('.bundle-fixed-{{ section.id }}');
    let section = document.querySelector('.bundle-{{ section.id }}');
    let form = document.querySelector('#{{ product_form_id }}');

    if (fixed && section) {  
      if (window.innerWidth < 1023) {
          fixed.style.display = 'none';
          window.addEventListener('scroll', () => {
            const sectionRect = section.getBoundingClientRect();  

            if (sectionRect.top < 0 && sectionRect.bottom > 0) {   
                fixed.style.display = 'block';  
            } else if (sectionRect.bottom <= 0) {   
                fixed.style.display = 'none';  
            } else {  
                fixed.style.display = 'none';  
            }   
          });   
      }  
    }  

    // UPDATE STATUS
    function updateStatus() {
      let defaultText = status.getAttribute('data-default');
      status.innerText = `${addedCount}/${limitCount} ${defaultText}`;
    }

    // UPDATE PROPERTY
    function updateBundleInputProperty() {
      let sidebarProducts = document.querySelectorAll('.bundle-sidebar-product-{{ section.id }}');
      let productList = Array.from(sidebarProducts).map(product => {
        let title = product.querySelector('.bundle-sidebar-product-title-{{ section.id }}').innerText;
        let count = product.querySelector('.count-{{ section.id }}').innerText;
        return `${title} (x${count})`;
      }).join(', ');
      bundleInputProperty.value = productList;
    }

    if(products) {
      products.forEach(el => {
        let btn = el.querySelector('.bundle-product-button-{{ section.id }}');
        let quantity = el.querySelector('.bundle-product-quantity-wrapper-{{ section.id }}');
        let selector = el.querySelector('.bundle-product-select-{{ section.id }}');
        let productTitle = el.querySelector('.bundle-product-title-{{ section.id }}');

        if(selector) {
          let productInitialTtitle = el.getAttribute('data-title');
          selector.addEventListener('change', () => {
            let title = selector.value;
            let newId = selector.selectedOptions[0].id;

            let newTitle = `${productInitialTtitle} - ${title}`;
            productTitle.innerText = newTitle;

            el.setAttribute('data-title', newTitle);
            el.setAttribute('data-id', newId);

            let sidebarProducts = document.querySelectorAll('.bundle-sidebar-product-{{ section.id }}');

            if(sidebarProducts) {
              let matchedElement = Array.from(sidebarProducts).find(element => {  
                return element.getAttribute('data-id') === newId;  
              });  

              if (matchedElement) {  
                let elementCount = matchedElement.querySelector('.count-{{ section.id }}');  
                if (quantity) {  
                  let count = quantity.querySelector('.bundle-product-quantity-{{ section.id }}');  
                  count.innerText = `${elementCount.innerText}`;  
                }  
              } else {  
                el.classList.remove('active');  
                if (quantity) {  
                  let count = quantity.querySelector('.bundle-product-quantity-{{ section.id }}');  
                  count.innerText = "1";  
                }  
              }  
            }

          });

        }

        if(btn) {
          btn.addEventListener('click', () => {
            const dataId = el.getAttribute('data-id');
            let image = el.querySelector('.bundle-product-image-{{ section.id }} img');
            let imageSrc;
            let imageAlt;
            if(image) {
              imageSrc = image.src;
              imageAlt = image.alt;
            }
            el.classList.add('active');
            addedCount++;

            const newDiv = document.createElement('div');  
            newDiv.className = `bundle-sidebar-product-{{ section.id }}`;  
            newDiv.setAttribute('data-id', dataId);

            const leftDiv = document.createElement('div');  
            leftDiv.className = `bundle-sidebar-product-left-{{ section.id }}`;

            const imageDiv = document.createElement('div');  
            imageDiv.className = `bundle-sidebar-product-image-{{ section.id }}`;

            const imageElement = document.createElement('img');  
            imageElement.src = imageSrc;
            imageElement.alt = imageAlt;

            const titleSpan = document.createElement('p');  
            titleSpan.className = `bundle-sidebar-product-title-{{ section.id }}`;  
            titleSpan.innerText = productTitle.innerText;

            const rightDiv = document.createElement('div');  
            rightDiv.className = `bundle-sidebar-product-right-{{ section.id }}`;  

            const countParagraph = document.createElement('p');  
            countParagraph.className = `bundle-sidebar-product-count-{{ section.id }}`;  
            countParagraph.innerHTML = `x<span class="count-{{ section.id }}">1</span>`;  

            const removeDiv = document.createElement('div');  
            removeDiv.className = `bundle-sidebar-product-remove-{{ section.id }}`;  

            removeDiv.innerHTML = `<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M14.3053 7.64357L14.0953 13.981C14.0774 14.5238 13.8481 15.0382 13.4559 15.4152C13.0638 15.7921 12.5397 16.0019 11.995 16H8.00446C7.46008 16.0019 6.93634 15.7924 6.54426 15.4159C6.15218 15.0394 5.9226 14.5255 5.90417 13.9831L5.69415 7.64357C5.68955 7.50474 5.74046 7.36978 5.83568 7.26837C5.9309 7.16696 6.06263 7.10742 6.20189 7.10284C6.34115 7.09826 6.47653 7.14901 6.57825 7.24394C6.67997 7.33887 6.73969 7.47019 6.74429 7.60902L6.95432 13.948C6.96478 14.2187 7.08002 14.4747 7.27584 14.6624C7.47165 14.8501 7.73281 14.9549 8.00446 14.9546H11.995C12.267 14.9548 12.5285 14.8498 12.7243 14.6616C12.9202 14.4735 13.0352 14.2169 13.0452 13.946L13.2552 7.60902C13.2598 7.47019 13.3195 7.33887 13.4212 7.24394C13.5229 7.14901 13.6583 7.09826 13.7976 7.10284C13.9368 7.10742 14.0686 7.16696 14.1638 7.26837C14.259 7.36978 14.3099 7.50474 14.3053 7.64357ZM15 5.53457C15 5.6734 14.9447 5.80654 14.8462 5.90471C14.7477 6.00287 14.6142 6.05802 14.4749 6.05802H5.52507C5.38581 6.05802 5.25226 6.00287 5.15379 5.90471C5.05532 5.80654 5 5.6734 5 5.53457C5 5.39574 5.05532 5.2626 5.15379 5.16443C5.25226 5.06627 5.38581 5.01112 5.52507 5.01112H7.1528C7.31916 5.01156 7.47975 4.95029 7.6033 4.83921C7.72685 4.72814 7.80452 4.57521 7.82121 4.41019C7.85996 4.02309 8.04188 3.66423 8.33153 3.40354C8.62118 3.14285 8.9978 2.99899 9.38803 3.00001H10.6114C11.0017 2.99899 11.3783 3.14285 11.6679 3.40354C11.9576 3.66423 12.1395 4.02309 12.1783 4.41019C12.195 4.57521 12.2726 4.72814 12.3962 4.83921C12.5197 4.95029 12.6803 5.01156 12.8467 5.01112H14.4744C14.6137 5.01112 14.7472 5.06627 14.8457 5.16443C14.9442 5.2626 14.9995 5.39574 14.9995 5.53457H15ZM8.73274 5.01112H11.2678C11.1988 4.85394 11.1537 4.6874 11.1339 4.51698C11.1209 4.38795 11.0603 4.26833 10.9639 4.18129C10.8674 4.09425 10.742 4.04599 10.612 4.04587H9.38855C9.25848 4.04599 9.13308 4.09425 9.03665 4.18129C8.94023 4.26833 8.87964 4.38795 8.86663 4.51698C8.8467 4.68743 8.80192 4.85397 8.73274 5.01112ZM9.26149 12.942V8.48476C9.26149 8.34593 9.20617 8.21279 9.1077 8.11462C9.00923 8.01645 8.87567 7.9613 8.73641 7.9613C8.59716 7.9613 8.4636 8.01645 8.36513 8.11462C8.26666 8.21279 8.21134 8.34593 8.21134 8.48476V12.9441C8.21134 13.0829 8.26666 13.216 8.36513 13.3142C8.4636 13.4124 8.59716 13.4675 8.73641 13.4675C8.87567 13.4675 9.00923 13.4124 9.1077 13.3142C9.20617 13.216 9.26149 13.0829 9.26149 12.9441V12.942ZM11.7892 12.942V8.48476C11.7892 8.34593 11.7339 8.21279 11.6354 8.11462C11.5369 8.01645 11.4034 7.9613 11.2641 7.9613C11.1249 7.9613 10.9913 8.01645 10.8928 8.11462C10.7944 8.21279 10.739 8.34593 10.739 8.48476V12.9441C10.739 13.0829 10.7944 13.216 10.8928 13.3142C10.9913 13.4124 11.1249 13.4675 11.2641 13.4675C11.4034 13.4675 11.5369 13.4124 11.6354 13.3142C11.7339 13.216 11.7892 13.0829 11.7892 12.9441V12.942Z" fill="#FEF4FF"></path></svg>`;  

            rightDiv.appendChild(countParagraph);  
            rightDiv.appendChild(removeDiv);
            leftDiv.appendChild(imageDiv);  
            leftDiv.appendChild(titleSpan);
            imageDiv.appendChild(imageElement); 
            newDiv.appendChild(leftDiv);  
            newDiv.appendChild(rightDiv);  

            const container = document.querySelector(`.bundle-sidebar-products-{{ section.id }}`);  
            container.appendChild(newDiv);

            emptyProduct.classList.add('active');

            updateBundleInputProperty();
            updateStatus();

            if(addedCount == limitCount) {
              products.forEach(element => {
                let btn = element.querySelector('.bundle-product-button-{{ section.id }}');
                if(btn) {
                  btn.setAttribute('disabled', true);
                }
              });

              if(mainButton) {
                mainButton.removeAttribute('disabled',);
              }

              let pluses = document.querySelectorAll('.bundle-product-quantity-plus-{{ section.id }}');
              if(pluses) {
                pluses.forEach(plus => {
                 plus.setAttribute('disabled', true);   
                });
              }
            } else {
              if(mainButton) {
                mainButton.setAttribute('disabled', true);
              }
            }

            removeDiv.addEventListener('click', () => {
              let thisCount = newDiv.querySelector('.count-{{ section.id }}');
              addedCount = addedCount - Number(thisCount.innerText);
              newDiv.remove();

              let elementDataId = newDiv.getAttribute('data-id');

              if(addedCount != limitCount) {
                if(mainButton) {
                  mainButton.setAttribute('disabled', true);
                } 
              }

              if(products) {
                products.forEach(element => {

                  if(element.getAttribute('data-id') == elementDataId) {
                    let quantity = element.querySelector('.bundle-product-quantity-{{ section.id }}');
                    element.classList.remove('active');
                    quantity.innerText = "1";
                  }
                })
              }

              if(addedCount == 0) {
                emptyProduct.classList.remove('active');
              }

              products.forEach(element => {
                let btn = element.querySelector('.bundle-product-button-{{ section.id }}');
                if(btn) {
                  btn.removeAttribute('disabled');
                }
              });

              let pluses = document.querySelectorAll('.bundle-product-quantity-plus-{{ section.id }}');
              if(pluses) {
                pluses.forEach(plus => {
                 plus.removeAttribute('disabled');   
                })
              }

              updateBundleInputProperty();
              updateStatus();
            });
          }) 
        }

        if(quantity) {
          let minus = quantity.querySelector('.bundle-product-quantity-minus-{{ section.id }}');
          let plus = quantity.querySelector('.bundle-product-quantity-plus-{{ section.id }}');
          let count = quantity.querySelector('.bundle-product-quantity-{{ section.id }}');

          if(minus) {
            minus.addEventListener('click', () => {
              let dataId = el.getAttribute('data-id');
              if(Number(count.innerText) > 1) {
                count.innerText = Number(count.innerText) - 1;
              } else {
                el.classList.remove('active');
              }
              addedCount--;
              if(addedCount != limitCount) {
                if(mainButton) {
                  mainButton.setAttribute('disabled', true);
                } 
              }

              if(addedCount == 0) {
                emptyProduct.classList.remove('active');
              }

              let sidebarProducts = document.querySelectorAll('.bundle-sidebar-product-{{ section.id }}');
              if(sidebarProducts) {
                sidebarProducts.forEach(element => {
                  let elementDataId = element.getAttribute('data-id');

                  if(dataId == elementDataId) {
                    let elementCount = element.querySelector('.count-{{ section.id }}');
                    if(Number(elementCount.innerText) > 1) {
                      elementCount.innerText = Number(elementCount.innerText) - 1;
                    } else {
                      element.remove();
                    }
                  }
                })
              }

              products.forEach(element => {
                let btn = element.querySelector('.bundle-product-button-{{ section.id }}');
                if(btn) {
                  btn.removeAttribute('disabled');
                }
              });

              let pluses = document.querySelectorAll('.bundle-product-quantity-plus-{{ section.id }}');
              if(pluses) {
                pluses.forEach(plus => {
                 plus.removeAttribute('disabled');   
                })
              }

              updateBundleInputProperty();
              updateStatus();
            })
          }

          if(plus) {
            plus.addEventListener('click', () => {
              let dataId = el.getAttribute('data-id');
              if(Number(count.innerText) < limitCount) {
                count.innerText = Number(count.innerText) + 1;
              }

              if(addedCount < limitCount) {
                addedCount++;
              }

              let sidebarProducts = document.querySelectorAll('.bundle-sidebar-product-{{ section.id }}');

              if(sidebarProducts) {
                sidebarProducts.forEach(element => {
                  let elementDataId = element.getAttribute('data-id');
                  if(dataId == elementDataId) {
                    let elementCount = element.querySelector('.count-{{ section.id }}');
                    if(Number(elementCount.innerText) < limitCount) {
                      elementCount.innerText = Number(elementCount.innerText) + 1;
                    }
                  }
                })
              }

              if(addedCount == limitCount) {

                if(mainButton) {
                  mainButton.removeAttribute('disabled');
                }

                products.forEach(element => {
                  let btn = element.querySelector('.bundle-product-button-{{ section.id }}');
                  if(btn) {
                    btn.setAttribute('disabled', true);
                  }
                });

                let pluses = document.querySelectorAll('.bundle-product-quantity-plus-{{ section.id }}');
                if(pluses) {
                  pluses.forEach(plus => {
                   plus.setAttribute('disabled', true);   
                  })
                }
              }

              updateBundleInputProperty();
              updateStatus();

            })
          }
        }
      })
    }

    if(variants) {
      variants.forEach(el => {
        el.addEventListener('click', () => {
          variants.forEach(element => {
            element.classList.remove('active');
          });
          el.classList.add('active');
          addedCount = 0;
          limitCount = Number(el.getAttribute('data-limit'));

          let dataId = el.getAttribute('data-id');

          if(form) {
            let inputId = form.querySelector('input[name="id"]');
            if(inputId) {
              inputId.value = dataId;
            }
          }

          let sidebarProducts = document.querySelectorAll('.bundle-sidebar-product-{{ section.id }}');
          if(sidebarProducts) {
            sidebarProducts.forEach((element,index) => {
              if(!element.classList.contains('.bundle-sidebar-product-default-{{ section.id }}')) {
                element.remove();
              }
            })
          }

          let quantities = document.querySelectorAll('.bundle-product-quantity-{{ section.id }}');
          if(quantities) {
            quantities.forEach(element => {
              element.innerText = "1";
            })
          }

          if(products) {
            products.forEach(element => {
              element.classList.remove('active');

              let btn = element.querySelector('.bundle-product-button-{{ section.id }}');
              if(btn && !btn.classList.contains('out-of-stock')) {
                btn.removeAttribute('disabled');
              }
            })
          }

          emptyProduct.classList.remove('active');

          if(mainButton) {
            mainButton.setAttribute('disabled', true);
          }

           let pluses = document.querySelectorAll('.bundle-product-quantity-plus-{{ section.id }}');
            if(pluses) {
              pluses.forEach(plus => {
               plus.removeAttribute('disabled');   
              })
            }

          updateBundleInputProperty();
          updateStatus();
        })
      })
    }

  }

  document.addEventListener('DOMContentLoaded', initBundle3);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initBundle3);
     document.addEventListener('shopify:section:load', initBundle3);
  }
</script>
