  assign sizes = ''

  for block in section.blocks
    if block.type == 'bundle'
      if block.settings.sizes_value != blank 
       unless forloop.first 
         assign sizes = sizes | append: ',' 
       endunless 
       assign sizes = sizes | append: block.settings.sizes_value 
      endif 
    endif
  endfor
-%}

{%- style -%}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}
  {{  product_button_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}  
  {{  step_text_font | font_face: font_display: 'swap' }}
  {{  step_number_font | font_face: font_display: 'swap' }}
  {{  tab_font | font_face: font_display: 'swap' }}
  {{  total_price_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .bundle-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: start;
    gap: {{ layout_gap_mobile }}px;
  }

  .bundle-products-{{ section.id }} {
    order: 2;
    display: grid;
    grid-template-columns: repeat({{ products_row_mobile }}, 1fr);
    gap: {{ products_gap_mobile }}px;
    padding: 2rem;
    border-radius: {{ sidebar_radius }}px;
    border: {{ sidebar_border_thickness }}px solid {{ sidebar_border_color }};
    background-color: {{ sidebar_bg_color }};
  }

  .bundle-product-{{ section.id }} {
    display: flex;
    flex-direction: column;
  }

  .bundle-product-image-{{ section.id }} {
    border-radius: {{ product_image_radius }}px;
    border: {{ product_image_border_thickness }}px solid {{ product_image_border_color }};
    overflow: hidden;
  }

  .bundle-sidebar-gift-product-{{ section.id }} {
    margin-top: 27px;
    border-radius: {{ gift_product_image_radius }}px;
    border: {{ gift_product_image_border_thickness }}px solid {{ gift_product_image_border_color }};
    overflow: hidden;
    width: 100px;
  }

  .bundle-sidebar-gift-product-{{ section.id }} img,
  .bundle-sidebar-gift-product-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bundle-sidebar-gift-product-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .bundle-product-image-{{ section.id }} img,
  .bundle-product-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bundle-product-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .bundle-product-content-{{ section.id }} {
    width: 100%;
    flex: 1 1 0;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: {{ product_content_horizontal_align_mobile }};
  }

  .bundle-product-content-top-{{ section.id }},
  .bundle-product-content-bottom-{{ section.id }} {
    width: 100%;
  }

  .bundle-product-button-{{ section.id }} {
    margin-top: {{ product_button_mt_mobile }}px;
  }

  .bundle-product-button-{{ section.id }}.out-of-stock {
    pointer-events: none;
  }

  .bundle-product-title-{{ section.id }} {
    margin: 0;
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    word-break: break-word;
    font-weight: 700;
  }

  .bundle-product-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;  
    width: 100%;
    display: flex;
    align-items: center;
    gap: 5px;
    justify-content: {{ product_content_horizontal_align_mobile }};
  }

  .bundle-product-price-{{ section.id }} * {
    margin: 0;        
    text-align: {{ product_content_align_mobile }};
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    word-break: break-word;
    transition: all 0.25s ease 0s;
  }   

  .bundle-product-compare-price-{{ section.id }} {
    text-decoration: line-through;
    color: {{ price_original_color }};
  }

  .bundle-product-link-{{ section.id }} {
    margin: 0;
    display: block;
    text-align: {{ product_content_align_mobile }};
    margin-top: {{ link_mt_mobile }}px;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    word-break: break-word;
    text-decoration: underline;
    font-weight: 700;
  }

  .bundle-product-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    font-size: {{ product_button_size_mobile }}px;
    font-weight: 700;
    color: {{ product_button_color }};
    line-height: {{ product_button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ product_button_padding_vertical_mobile }}px {{ product_button_padding_horizontal_mobile }}px;
    border-radius: {{ product_button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .bundle-product-{{ section.id }}.active .bundle-product-button-{{ section.id }} {
    display: none;
  }

  .bundle-product-button-{{ section.id }}[disabled] {
    cursor: no-drop;
    opacity: .6;
  }

  .bundle-product-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bundle-product-button-{{ section.id }}:hover {
    color: {{ product_button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bundle-product-button-{{ section.id }} svg path {
    fill: {{ product_button_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-button-{{ section.id }}:hover svg path {
    fill: {{ product_button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-wrapper-{{ section.id }} {
    order: 1;
  }

  .bundle-sidebar-{{ section.id }} {
    border-radius: {{ sidebar_radius }}px;
    border: {{ sidebar_border_thickness }}px solid {{ sidebar_border_color }};
    overflow: hidden;
    background-color: {{ sidebar_bg_color }};
    padding: 24px;
  }

  .bundle-submit-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .bundle-submit-button-{{ section.id }}[disabled] {
    cursor: no-drop;
    color: {{ button_color | hex_to_rgba: 0.8 }}
  }

  .bundle-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .bundle-submit-button-{{ section.id }}:not([disabled]):hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-submit-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .bundle-submit-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-submit-button-{{ section.id }}:not([disabled]):hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-product-quantity-wrapper-{{ section.id }} {
    display: none;
    position: relative;
    justify-content: space-between;
    border-radius: 50px;
    width: 100%;
    margin-top: {{ product_button_mt_mobile }}px;
    height: calc({{ product_button_padding_vertical_mobile | times: 2 }}px + {{ product_button_size_mobile }}px);
    background: {{ quantity_wrapper_bg_color }};
    padding: 0px 10px;
  }

  .bundle-product-{{ section.id }}.active .bundle-product-quantity-wrapper-{{ section.id }} {
    display: flex;
  }

  .bundle-product-quantity-minus-{{ section.id }},
  .bundle-product-quantity-plus-{{ section.id }} {
    background-color: transparent;
    border: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.25s ease 0s;
  }

  .bundle-product-quantity-minus-{{ section.id }} svg path,
  .bundle-product-quantity-plus-{{ section.id }} svg path {
    fill: {{ quantity_controls_color }};
  }

  .bundle-product-quantity-{{ section.id }} {
    margin: 0;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 100%;
    height: 100%;
    max-width: fit-content;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: {{ product_button_size_mobile }}px;
    color: {{ quantity_color }};
    border-radius: 50%;
    background-color: {{ quantity_bg_color }};
    line-height: {{ product_button_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-align: center;
    box-shadow: 0 0 transparent,0 0 transparent,0 0 transparent,0 0 transparent,0px 10px 10px rgba(0,0,0,.08);
    aspect-ratio: 1/1;
  }

  .bundle-product-quantity-plus-{{ section.id }}[disabled] {
    cursor: no-drop;
    opacity: .6;
    transition: all 0.25s ease 0s;
  }

  .bundle-sidebar-product-default-{{ section.id }} {
    display: none;
  }

  .bundle-step-{{ section.id }} {
    margin-top: 20px;
    display: flex;
    align-items: center;
    gap: 8px;
    justify-content: {{ sidebar_horizontal_align_mobile }};
  }

  .bundle-step-circle-{{ section.id }} {
    margin: 0;
    font-size: {{ step_number_size_mobile }}px;
    color: {{ step_number_color }};
    line-height: {{ step_number_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    border-radius: 50%;
    padding: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    width: {{ step_circle_size_mobile }}px;
    height: {{ step_circle_size_mobile }}px;
    background-color: {{ step_circle_bg_color }};
    border: {{ step_circle_border_thickness }}px solid {{ step_circle_border_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-step-{{ section.id }}.active .bundle-step-circle-{{ section.id }} {
    color: {{ step_number_active_color }};
    background-color: {{ step_circle_bg_active_color }};
    border-color: {{ step_circle_border_active_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-step-text-{{ section.id }} {
    margin: 0;
    text-align: {{ sidebar_align_mobile }};
    font-size: {{ step_text_size_mobile }}px;
    color: {{ step_text_color }};
    line-height: {{ step_text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
  }

  .bundle-tabs-{{ section.id }} {
    display: flex;
    align-items: stretch;
    flex-wrap: wrap;
    gap: {{ tabs_gap_mobile }}px;
    margin-top: 20px;
  }

  .bundle-tab-{{ section.id }} {
    margin: 0;
    flex: 1 1 0;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: {{ tab_padding_vertical_mobile }}px {{ tab_padding_horizontal_mobile }}px;
    background-color: {{ tab_bg_color }};
    border-radius: {{ tab_radius }}px;
    border: {{ tab_border_thickness }}px solid {{ tab_border_color }};
    font-size: {{ tab_size_mobile }}px;
    color: {{ tab_color }};
    line-height: {{ tab_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    text-align: center;
    transition: all 0.25s ease 0s;
  }

  .bundle-tab-comment-{{ section.id }} {
    font-size: {{ tab_size_mobile | times: 0.6 }}px;
    color: {{ tab_color }};
    line-height: {{ tab_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .bundle-tab-comment-{{ section.id }}.hide {
    display: none;
  }

  .bundle-tab-{{ section.id }}:hover {
    border: {{ tab_border_thickness }}px solid {{ tab_border_hover_color }};
    background-color: {{ tab_bg_hover_color }};
    color: {{ tab_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .bundle-tab-{{ section.id }}:hover  .bundle-tab-comment-{{ section.id }} {
    color: {{ tab_hover_color }};
    transition: all 0.25s ease 0s;
  }  

  .bundle-tab-{{ section.id }}.active {
    border: {{ tab_border_thickness }}px solid {{ tab_border_hover_color }};
    background-color: {{ tab_bg_hover_color }};
    color: {{ tab_hover_color }};
    transition: all 0.25s ease 0s;
  }

    .bundle-tab-{{ section.id }}.active  .bundle-tab-comment-{{ section.id }} {
    color: {{ tab_hover_color }};
    transition: all 0.25s ease 0s;
  }


  .bundle-product-block-{{ section.id }} {
    width: 36px;
    height: 36px;
  }

  .bundle-product-block-{{ section.id }} img,
  .bundle-product-block-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .bundle-product-block-{{ section.id }}.empty {
    width: 36px;
    height: 36px;
  }

  .bundle-product-block-{{ section.id }}.empty svg rect {
    stroke: {{ empty_icon_border_color }};
  }

  .bundle-product-block-{{ section.id }}.empty svg path {
    stroke: {{ empty_icon_color }};
  }

  .bundle-sidebar-products-{{ section.id }} {
    margin-top: 20px;
    display: flex;
    align-items: center;
    gap: 15px;
    flex-wrap: wrap;
    justify-content: {{ sidebar_horizontal_align_mobile }};
  }

  .bundle-total-price-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    justify-content: space-between;
    padding-top: 15px;
    margin-top: 20px;
    border-top: 1px solid {{ total_price_color }};
  }

  .bundle-total-price-text-{{ section.id }},
  .bundle-total-price-{{ section.id }},
  .bundle-total-compare-price-{{ section.id }} {
    margin: 0;
    font-size: {{ total_price_size_mobile }}px;
    color: {{ total_price_color }};
    line-height: {{ total_price_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
    text-align: center;
  }

  .bundle-total-compare-price-{{ section.id }} {
    text-decoration: line-through;
     font-size: {{ total_price_size_mobile | minus: 5 }}px;
     opacity: 0.5;
  }

  .bundle-sidebar-products-gift-{{ section.id }} {
    display: flex;
    align-items: start;
    gap: 20px;    
    justify-content: {{ sidebar_horizontal_align_mobile }};
  }

  .bundle-sidebar-gift-text-{{ section.id }} {

    margin-top: 20px;
  }

  .bundle-second-step-accent-text-{{ section.id }} {
    color: {{ step_text_accent_color }};
  }

  .bundle-sidebar-product-image-{{ section.id }} {
    aspect-ratio: 1/1;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .bundle-body-{{ section.id }} {
      grid-template-columns: 1fr {{ sidebar_width }}%;
      gap: {{ layout_gap }}px;
    }

    .bundle-products-{{ section.id }} {
      order: 1;
      grid-template-columns: repeat({{ products_row }}, 1fr);
      gap: {{ products_gap }}px;
    }

    .bundle-product-content-{{ section.id }} {;
      align-items: {{ product_content_horizontal_align }};
    }

    .bundle-product-title-{{ section.id }} {
      text-align: {{ product_content_align }};
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
    }

    .bundle-product-price-{{ section.id }} {    
      margin-top: {{ price_mt }}px;       
      justify-content: {{ product_content_horizontal_align }};
    }

    .bundle-product-price-{{ section.id }} * {        
      text-align: {{ product_content_align }};
      font-size: {{ price_size }}px;    
    }   

    .bundle-product-link-{{ section.id }} {    
      text-align: {{ product_content_align }};
      margin-top: {{ link_mt }}px;
      font-size: {{ link_size }}px;    
    }

    .bundle-product-button-{{ section.id }} {
      margin-top: {{ product_button_mt }}px;
    }

    .bundle-product-button-{{ section.id }} {
      padding: {{ product_button_padding_vertical }}px {{ product_button_padding_horizontal }}px;
      font-size: {{ product_button_size }}px;
    }

    .bundle-sidebar-wrapper-{{ section.id }} {
      order: 2;
    }

    .bundle-sidebar-products-gift-{{ section.id }} {      
      justify-content: {{ sidebar_horizontal_align }};
    }

    .bundle-step-{{ section.id }} {    
      justify-content: {{ sidebar_horizontal_align }};
    }

    .bundle-product-quantity-wrapper-{{ section.id }} {
      margin-top: {{ product_button_mt }}px;
      height: calc({{ product_button_padding_vertical | times: 2 }}px + {{ product_button_size }}px);  
    }

    .bundle-step-circle-{{ section.id }} {
      width: {{ step_circle_size }}px;
      height: {{ step_circle_size }}px;
      font-size: {{ step_number_size }}px;
    }

    .bundle-step-text-{{ section.id }} {
      text-align: {{ sidebar_align }};
      font-size: {{ step_text_size }}px;
    }

    .bundle-submit-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .bundle-product-quantity-{{ section.id }} {
      font-size: {{ product_button_size }}px;
    }

    .bundle-sidebar-products-gift-{{ section.id }} {
      flex-wrap: wrap;
    }

    .bundle-fixed-{{ section.id }} {
      position: static;
      padding: 0px;
      background-color: transparent;
    }

    .bundle-tabs-{{ section.id }} {     
      gap: {{ tabs_gap }}px;
    }

    .bundle-sidebar-products-{{ section.id }} {    
      justify-content: {{ sidebar_horizontal_align }};
    }

    .bundle-tab-{{ section.id }} {     
      padding: {{ tab_padding_vertical }}px {{ tab_padding_horizontal }}px;    
      font-size: {{ tab_size }}px;     
    }

    .bundle-tab-comment-{{ section.id }} {
      display: block !important;
      font-size: {{ tab_size | times: 0.6 }}px;
    }

    .bundle-total-price-text-{{ section.id }},
    .bundle-total-price-{{ section.id }} {   
      font-size: {{ total_price_size }}px;      
    }

    .bundle-total-compare-price-{{ section.id }} {
       font-size: {{ total_price_size | minus: 5 }}px;      
    }
  } 

  @media(min-width: 1324px) {
    .bundle-sidebar-products-gift-{{ section.id }} {
      flex-wrap: nowrap;
    }
  }
{%- endstyle -%}

