{% for block in section.blocks %}
  {% case block.type %}
    {% when 'features' %}
      <style>
        {{  block.settings.feature_font | font_face: font_display: 'swap' }}    
        .bundle-features-{{ section.id }} {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 10px;
          margin-top: {{ block.settings.feature_mt_mobile }}px;
        }

        .bundle-feature-{{ section.id }} {
          display: flex;
          align-items: center;
          gap: 10px;
          justify-content: {{ sidebar_horizontal_align_mobile }};
        }

        .bundle-feature-{{ section.id }} svg {
          flex-shrink: 0;
          display: block;
          width: 25px;
          height: 15px;
          object-fit: cover;
        }

        .bundle-feature-{{ section.id }} svg path {
          stroke: {{ block.settings.feature_icon_color }};
        }

        .bundle-feature-text-{{ section.id }} {
          margin: 0;
          text-align: {{ sidebar_align_mobile }};
          font-size: {{ block.settings.feature_size_mobile }}px;
          color: {{ block.settings.feature_color }};
          line-height: {{ block.settings.feature_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {          
          .bundle-features-{{ section.id }} {          
            margin-top: {{ block.settings.feature_mt }}px;
          }

          .bundle-feature-{{ section.id }} {
            justify-content: {{ sidebar_horizontal_align }};
          }

          .bundle-feature-text-{{ section.id }} {
            text-align: {{ sidebar_align }};
            font-size: {{ block.settings.feature_size }}px;
          }
        }          
      </style>

      {% if block.settings.feature_custom %}
        <style>
          .bundle-feature-text-{{ section.id }} {
            font-family: {{ block.settings.feature_font.family }}, {{ block.settings.feature_font.fallback_families }};
            font-weight: {{ block.settings.feature_font.weight }};
            font-style: {{ block.settings.feature_font.style }};
          }
        </style>
      {% endif %}

      {% if sidebar_align_mobile == 'center' %}
        <style>
          .bundle-feature-{{ section.id }} {
            flex-direction: column;
            align-items: center;
          }
        </style>
      {% endif %}

      {% if sidebar_align_mobile == 'right' %}
        <style>
          .bundle-feature-{{ section.id }} {
            flex-direction: row-reverse;
          }
        </style>
      {% endif %}

      {% if sidebar_align == 'center' %}
        <style>
          @media(min-width: 1024px) {
            .bundle-feature-{{ section.id }} {
              flex-direction: column;
              align-items: center;
            }
          }

        </style>
      {% endif %}

      {% if sidebar_align == 'right' %}
        <style>
          @media(min-width: 1024px) {
            .bundle-feature-{{ section.id }} {
              flex-direction: row-reverse;
            }
          }          
        </style>
      {% endif %}


    {% else %}

  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} bundle-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  <div class="section-{{ section.id }}-settings">
    <div class="bundle-body-{{ section.id }}">
      <div class="bundle-products-{{ section.id }}">
        {% if collection != blank %}
          {% for product in collections[collection].products %}
            <div class="bundle-product-{{ section.id }}" data-title="{{ product.title }}" data-id="{{ product.selected_or_first_available_variant.id }}"  data-price="{{ product.selected_or_first_available_variant.price }}" data-compare-price="{{ product.selected_or_first_available_variant.compare_at_price | default: product.selected_or_first_available_variant.price }}">
              <div class="bundle-product-image-{{ section.id }}">
                {% if product.featured_image != blank %}
                  {%- liquid assign img_alt = product.featured_image.alt | escape -%}
                  {%- capture sizes -%}, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                    {{ product.featured_image | image_url: width: 1000 | image_tag:
                      loading: loading_attribute,
                      width: product.featured_image.width,
                      height: product.featured_image.height,
                      sizes: sizes,
                      widths: '450, 535, 750, 1070, 1500',
                      alt: img_alt
                    }}
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}
              </div>
              <div class="bundle-product-content-{{ section.id }}">
                <div class="bundle-product-content-top-{{ section.id }}">
                  <h3 class="bundle-product-title-{{ section.id }}">
                    {% if product_title_limit %}
                      {{ product.title | truncate: product_title_count }}
                    {% else %}
                      {{ product.title }}
                    {% endif %}
                  </h3>                 
                </div>              
                <div class="bundle-product-content-bottom-{{ section.id }}">
                  <div class="bundle-product-price-{{ section.id }}">
                    {% if price_compare and product.compare_at_price != blank %}
                      <p class="bundle-product-compare-price-{{ section.id }}">{{ product.compare_at_price | money }}</p>
                    {% endif %}
                    <p class="bundle-product-regular-price-{{ section.id }}">
                      {{ product.price | money }}
                    </p>
                  </div>
                  <div class="bundle-product-quantity-wrapper-{{ section.id }}">
                    <button class="bundle-product-quantity-minus-{{ section.id }}">
                      <svg width="10" height="3" viewBox="0 0 10 3" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M10 3H0V0H10V3Z" fill="#432953"></path></svg>
                    </button>
                    <p class="bundle-product-quantity-{{ section.id }}">1</p>
                    <button class="bundle-product-quantity-plus-{{ section.id }}">
                      <svg width="12" height="12" viewBox="0 0 12 12" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M4.5 7.5V12H7.5V7.5H12V4.5H7.5V0H4.5V4.5H0V7.5H4.5Z" fill="#432953"></path></svg>
                    </button>
                  </div>
                  <button class="bundle-product-button-{{ section.id }} {% unless product.available %}out-of-stock{% endunless %}" {% unless product.available %}disabled{% endunless %}>
                    <p class="bundle-product-button-inner-{{ section.id }}">
                      {% if product.available %}
                        {{ product_button }}
                      {% else %}
                        {{ product_button_soldout }}
                      {% endif %}
                        {% if product_button_style == "non_outline_arrow" or product_button_style == "outline_arrow" %}
                          <svg width="192" height="192" viewBox="0 0 192 192" fill="none" xmlns="http://www.w3.org/2000/svg">
