<script>
var formayMoneyForBuyItWith = function(cents, format) {
  if (typeof cents == 'string') {
    cents = cents.replace('.', '');
  }
  var value = '';
  var placeholderRegex = /\{\{\s*(\w+)\s*\}\}/;
  var formatString = format || (typeof Shopify !== 'undefined' && Shopify.money_format);

  if (typeof formatString !== 'string') {
    return '';
  }

  function defaultOption(opt, def) {
    return (typeof opt == 'undefined' ? def : opt);
  }

  function formatWithDelimiters(number, precision, thousands, decimal) {
    precision = defaultOption(precision, 2);
    thousands = defaultOption(thousands, ',');
    decimal = defaultOption(decimal, '.');

    if (isNaN(number) || number == null) {
      return 0;
    }

    number = (number / 100.0).toFixed(precision);

    var parts = number.split('.'),
      dollars = parts[0].replace(/(\d)(?=(\d\d\d)+(?!\d))/g, '$1' + thousands),
      cents = parts[1] ? (decimal + parts[1]) : '';

    return dollars + cents;
  }

  const matchResult = formatString.match(placeholderRegex);
  if (!matchResult || !matchResult[1]) {
    return formatWithDelimiters(cents, 2);
  }

  switch (matchResult[1]) {
    case 'amount':
      value = formatWithDelimiters(cents, 2);
      break;
    case 'amount_no_decimals':
      value = formatWithDelimiters(cents, 0);
      break;
    case 'amount_with_comma_separator':
      value = formatWithDelimiters(cents, 2, '.', ',');
      break;
    case 'amount_no_decimals_with_comma_separator':
      value = formatWithDelimiters(cents, 0, '.', ',');
      break;
  }

  return formatString.replace(placeholderRegex, value);
};

function initBundle4() {
  let mainButtonText = document.querySelector('.bundle-submit-button-{{ section.id }} span');
  let mainButton = document.querySelector('.bundle-submit-button-{{ section.id }}');
  let products = document.querySelectorAll('.bundle-product-{{ section.id }}');
  let bundleSidebarProductsContainer = document.querySelector(`.bundle-sidebar-products-{{ section.id }}`);
  let bundleTabs = document.querySelectorAll('.bundle-tab-{{ section.id }}');
  let totalValueElement = document.querySelector('.bundle-total-price-{{ section.id }}');
  let totalComparePriceElement = document.querySelector('.bundle-total-compare-price-{{ section.id }}');
  let bundleStepTextElement = document.querySelector('.bundle-second-step-text-{{ section.id }}');
  let initialBundleStepText = bundleStepTextElement ? bundleStepTextElement.textContent.trim() : '';
  let bundleSecondStepCounterElement = document.querySelector('.bundle-second-step-counter-{{ section.id }}');

  // New: Get gift product variant ID
  const giftProductElement = document.querySelector('.bundle-sidebar-gift-product-{{ section.id }}');
  const giftProductVariantId = giftProductElement ? giftProductElement.dataset.variantId : null;

  let selectedProducts = {};

  function updateTotalAndButton() {
    let total = 0;
    let totalCompare = 0;
    let totalSelectedItems = 0;

    for (const productId in selectedProducts) {
      if (selectedProducts.hasOwnProperty(productId)) {
        const productCount = selectedProducts[productId].count;
        const productPrice = selectedProducts[productId].price;
        const productComparePrice = selectedProducts[productId].comparePrice;
        total += productCount * productPrice;
        totalCompare += productCount * productComparePrice;
        totalSelectedItems += productCount;
      }
    }

    totalValueElement.textContent = formayMoneyForBuyItWith(total, "{{ shop.money_format }}");

    if (totalComparePriceElement) {
        if (totalCompare > total) {
            totalComparePriceElement.textContent = formayMoneyForBuyItWith(totalCompare, "{{ shop.money_format }}");
            totalComparePriceElement.style.display = 'inline-block';
        } else {
            totalComparePriceElement.textContent = '';
            totalComparePriceElement.style.display = 'none';
        }
    }

    const activeTab = document.querySelector('.bundle-tab-{{ section.id }}.active');
    const currentLimitMatch = activeTab.textContent.match(/\d+/);
    const currentLimit = currentLimitMatch ? Number(currentLimitMatch[0]) : 0;

    if (bundleSecondStepCounterElement) {
      bundleSecondStepCounterElement.innerHTML = `<span class="bundle-second-step-accent-text-{{ section.id }}">${totalSelectedItems}</span>/${currentLimit}`;
    }

    // New: Logic for gift product and button state
    if (totalSelectedItems === currentLimit) {
      mainButton.disabled = false;
      mainButtonText.textContent = '{{ button }}';
    } else {
      mainButton.disabled = true;
      const remaining = currentLimit - totalSelectedItems;
      let productWord;

      if (remaining === 1) {
        productWord = 'product';
      } else {
        productWord = 'products';
      }
      mainButtonText.textContent = `Add ${remaining} ${productWord}`;
    }

    const form = document.querySelector('.bundle-form-{{ section.id }}');
    const oldInputs = form.querySelectorAll('input[name^="items["]');
    oldInputs.forEach(input => input.remove());

    let itemIndex = 0;
    for (const productId in selectedProducts) {
      if (selectedProducts.hasOwnProperty(productId) && selectedProducts[productId].count > 0) {
        var productIdInput = document.createElement('input');
        productIdInput.type = 'hidden';
        productIdInput.name = `items[${itemIndex}][id]`;
        productIdInput.value = productId;
        form.appendChild(productIdInput);

        var quantityInput = document.createElement('input');
        quantityInput.type = 'hidden';
        quantityInput.name = `items[${itemIndex}][quantity]`;
        quantityInput.value = selectedProducts[productId].count;
        form.appendChild(quantityInput);
        itemIndex++;
      }
    }

    // New: Add gift product to form if limit is met
    if (totalSelectedItems === currentLimit && giftProductVariantId) {
        var giftIdInput = document.createElement('input');
        giftIdInput.type = 'hidden';
        giftIdInput.name = `items[${itemIndex}][id]`;
        giftIdInput.value = giftProductVariantId;
        form.appendChild(giftIdInput);

        var giftQuantityInput = document.createElement('input');
        giftQuantityInput.type = 'hidden';
        giftQuantityInput.name = `items[${itemIndex}][quantity]`;
        giftQuantityInput.value = 1; // Always add 1 quantity of the gift
        form.appendChild(giftQuantityInput);
    }
  }

  bundleTabs.forEach(tab => {
    tab.addEventListener('click', () => {
      bundleTabs.forEach(t => t.classList.remove('active'));
      tab.classList.add('active');

      const newLimitMatch = tab.textContent.match(/\d+/);
      const newLimit = newLimitMatch ? Number(newLimitMatch[0]) : 0;

      selectedProducts = {};
      products.forEach(el => {
        el.classList.remove('active');
        const quantityDisplay = el.querySelector('.bundle-product-quantity-{{ section.id }}');
        if (quantityDisplay) {
          quantityDisplay.textContent = '1';
        }
      });
      updateSidebarProducts(newLimit);
      updateTotalAndButton();
    });
  });

  function updateSidebarProducts(targetCount) {
    while (bundleSidebarProductsContainer.firstChild) {
      bundleSidebarProductsContainer.removeChild(bundleSidebarProductsContainer.firstChild);
    }

    for (const productId in selectedProducts) {
      if (selectedProducts.hasOwnProperty(productId)) {
        const productInfo = selectedProducts[productId];
        for (let i = 0; i < productInfo.count; i++) {
          const newDiv = document.createElement('div');
          newDiv.className = `bundle-product-block-{{ section.id }}`;
          newDiv.setAttribute('data-id', productId);
          newDiv.setAttribute('data-variant-id', productInfo.variantId);

          const imageDiv = document.createElement('div');
          imageDiv.className = `bundle-sidebar-product-image-{{ section.id }}`;

          const imageElement = document.createElement('img');
          imageElement.src = productInfo.imageSrc;
          imageElement.alt = productInfo.imageAlt;

          imageDiv.appendChild(imageElement);
          newDiv.appendChild(imageDiv); // Changed this line from newDiv.appendChild(newDiv);
          bundleSidebarProductsContainer.appendChild(newDiv);
        }
      }
    }

    const currentSelectedCount = Object.values(selectedProducts).reduce((sum, prod) => sum + prod.count, 0);
    const emptyBlocksToAdd = targetCount - currentSelectedCount;

    if (emptyBlocksToAdd > 0) {
      for (let i = 0; i < emptyBlocksToAdd; i++) {
        const emptyDiv = document.createElement('div');
        emptyDiv.className = `bundle-product-block-{{ section.id }} empty`;
        emptyDiv.innerHTML = `
          <svg class="bundle-icon build-a-bundle__pack-image-default" data-id="empty" width="36" height="29" viewBox="0 0 36 29" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="4.5" y="1.30005" width="27" height="27" rx="13.5" stroke="#AFAFAF" stroke-dasharray="1.5 1.5"></rect>
            <path d="M18.0005 8.80005V20.8" stroke="#241F21" stroke-width="1.5"></path>
            <path d="M24 14.8005L12 14.8005" stroke="#241F21" stroke-width="1.5"></path>
          </svg>
        `;
        bundleSidebarProductsContainer.appendChild(emptyDiv);
      }
    }
    updateTotalAndButton();
  }

  if (products) {
    products.forEach(el => {
      let btn = el.querySelector('.bundle-product-button-{{ section.id }}');
      let quantityDisplay = el.querySelector('.bundle-product-quantity-{{ section.id }}');
      let minusButton = el.querySelector('.bundle-product-quantity-minus-{{ section.id }}');
      let plusButton = el.querySelector('.bundle-product-quantity-plus-{{ section.id }}');

      const dataId = el.getAttribute('data-id');
      const productPrice = Number(el.getAttribute('data-price'));
      const productComparePrice = Number(el.getAttribute('data-compare-price'));
      let image = el.querySelector('.bundle-product-image-{{ section.id }} img');
      let imageSrc = image ? image.src : '';
      let imageAlt = image ? image.alt : '';

      if (btn) {
        btn.addEventListener('click', () => {
          const activeTab = document.querySelector('.bundle-tab-{{ section.id }}.active');
          const currentLimitMatch = activeTab.textContent.match(/\d+/);
          const currentLimit = currentLimitMatch ? Number(currentLimitMatch[0]) : 0;
          const currentSelectedCount = Object.values(selectedProducts).reduce((sum, prod) => sum + prod.count, 0);

          if (currentSelectedCount < currentLimit) {
            if (!selectedProducts[dataId]) {
              selectedProducts[dataId] = {
                count: 0,
                price: productPrice,
                comparePrice: productComparePrice,
                imageSrc: imageSrc,
                imageAlt: imageAlt
              };
            }

            selectedProducts[dataId].count++;
            quantityDisplay.textContent = selectedProducts[dataId].count;
            el.classList.add('active');

            const firstEmptyBlock = document.querySelector(`.bundle-product-block-{{ section.id }}.empty`);
            if (firstEmptyBlock) {
              bundleSidebarProductsContainer.removeChild(firstEmptyBlock);
            }

            const newDiv = document.createElement('div');
            newDiv.className = `bundle-product-block-{{ section.id }}`;
            newDiv.setAttribute('data-id', dataId);

            const newImageDiv = document.createElement('div');
            newImageDiv.className = `bundle-sidebar-product-image-{{ section.id }}`;
            const newImageElement = document.createElement('img');
            newImageElement.src = imageSrc;
            newImageElement.alt = imageAlt;
            newImageDiv.prepend(newImageElement);
            newDiv.prepend(newImageDiv);
            bundleSidebarProductsContainer.prepend(newDiv);

            updateTotalAndButton();
          }
        });
      }

      if (minusButton) {
        minusButton.addEventListener('click', () => {
          if (selectedProducts[dataId] && selectedProducts[dataId].count > 0) {
            selectedProducts[dataId].count--;
            quantityDisplay.textContent = selectedProducts[dataId].count;

            const productBlocks = document.querySelectorAll(`.bundle-product-block-{{ section.id }}[data-id="${dataId}"]`);
            if (productBlocks.length > 0) {
              bundleSidebarProductsContainer.removeChild(productBlocks[productBlocks.length - 1]);
            }

            const emptyDiv = document.createElement('div');
            emptyDiv.className = `bundle-product-block-{{ section.id }} empty`;
            emptyDiv.innerHTML = `
              <svg class="bundle-icon build-a-bundle__pack-image-default" data-id="empty" width="36" height="29" viewBox="0 0 36 29" fill="none" xmlns="http://www.w3.org/2000/svg">
                <rect x="4.5" y="1.30005" width="27" height="27" rx="13.5" stroke="#AFAFAF" stroke-dasharray="1.5 1.5"></rect>
                <path d="M18.0005 8.80005V20.8" stroke="#241F21" stroke-width="1.5"></path>
                <path d="M24 14.8005L12 14.8005" stroke="#241F21" stroke-width="1.5"></path>
              </svg>
            `;
            bundleSidebarProductsContainer.appendChild(emptyDiv);

            if (selectedProducts[dataId].count === 0) {
              el.classList.remove('active');
              delete selectedProducts[dataId];
            }
            updateTotalAndButton();
          }
        });
      }

      if (plusButton) {
        plusButton.addEventListener('click', () => {
          const activeTab = document.querySelector('.bundle-tab-{{ section.id }}.active');
          const currentLimitMatch = activeTab.textContent.match(/\d+/);
          const currentLimit = currentLimitMatch ? Number(currentLimitMatch[0]) : 0;
          const currentSelectedCount = Object.values(selectedProducts).reduce((sum, prod) => sum + prod.count, 0);

          if (currentSelectedCount < currentLimit) {
            if (!selectedProducts[dataId]) {
              selectedProducts[dataId] = {
                count: 0,
                price: productPrice,
                comparePrice: productComparePrice,
                imageSrc: imageSrc,
                imageAlt: imageAlt
              };
            }

            selectedProducts[dataId].count++;
            quantityDisplay.textContent = selectedProducts[dataId].count;
            el.classList.add('active');

            const firstEmptyBlock = document.querySelector(`.bundle-product-block-{{ section.id }}.empty`);
            if (firstEmptyBlock) {
              bundleSidebarProductsContainer.removeChild(firstEmptyBlock);
            }

            const newDiv = document.createElement('div');
            newDiv.className = `bundle-product-block-{{ section.id }}`;
            newDiv.setAttribute('data-id', dataId);

            const newImageDiv = document.createElement('div');
            newImageDiv.className = `bundle-sidebar-product-image-{{ section.id }}`;
            const newImageElement = document.createElement('img');
            newImageElement.src = imageSrc;
            newImageElement.alt = imageAlt;
            newImageDiv.prepend(newImageElement);
            newDiv.prepend(newImageDiv);
            bundleSidebarProductsContainer.prepend(newDiv);

            updateTotalAndButton();
          }
        });
      }
    });
  }

  // Initial update
  const initialTab = document.querySelector('.bundle-tab-{{ section.id }}.active');
  const initialLimitMatch = initialTab.textContent.match(/\d+/);
  const initialLimit = initialLimitMatch ? Number(initialLimitMatch[0]) : 0;
  updateSidebarProducts(initialLimit);
  updateTotalAndButton();
}

document.addEventListener('DOMContentLoaded', initBundle4);
</script>
