  <style>
    @media(min-width: 1024px) {
      .bundle-step-{{ section.id }} {
        flex-direction: row-reverse;
      }
    }          
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .bundle-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ block.settings.heading_align_mobile }};
        }
      
        .bundle-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        .bundle-heading-{{ block.id }} * strong {
          text-decoration: underline {{ block.settings.heading_border_color }};
        }

        @media(min-width: 1024px) {
          
          .bundle-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ block.settings.heading_align }};
          }
        
          .bundle-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>
        
      {% if block.settings.heading_custom %}
        <style>
          .bundle-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}   
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        
        .bundle-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ block.settings.text_align_mobile }};
          position: relative;
          z-index: 2;
        }
      
        .bundle-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .bundle-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{ block.settings.text_align }};
          }
      
          .bundle-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
          
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .bundle-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}       
  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} bundle-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
  <div class="section-{{ section.id }}-settings">
    <div class="bundle-body-{{ section.id }}">
      <div class="bundle-product-wrapper-{{ section.id }}">
        {% for block in section.blocks %}
          {% case block.type %}
            {% when 'heading'%}
              {% if block.settings.heading != blank %}
                <div class="bundle-heading-{{ block.id }}">
                  {{ block.settings.heading }}
                </div>
              {% endif %}
            {% when 'text' %}
              {% if block.settings.text != blank %}
                <div class="bundle-text-{{ block.id }}">
                  {{ block.settings.text }}
                </div>
              {% endif %}  
          {% endcase %}
        {% endfor %}

        <div class="bundle-products-{{ section.id }}">
          {% if collection != blank %}
            {% for product in collections[collection].products %}
              {% if product.available %}
                <div class="bundle-product-{{ section.id }}" data-title="{{ product.title }}" data-id="{{ product.selected_or_first_available_variant.id }}"  data-price="{{ product.selected_or_first_available_variant.price }}" data-compare-price="{{ product.selected_or_first_available_variant.compare_at_price | default: product.selected_or_first_available_variant.price }}" {% if product.selected_or_first_available_variant.inventory_management != nil %}data-limit="{{ product.selected_or_first_available_variant.inventory_quantity }}"{% endif %}>
                  <div class="bundle-product-image-{{ section.id }}">
                    {% if product.featured_image != blank %}
                      {%- liquid assign img_alt = product.featured_image.alt | escape -%}
                      {%- capture sizes -%}, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                        {{ product.featured_image | image_url: width: 1000 | image_tag:
                          loading: loading_attribute,
                          width: product.featured_image.width,
                          height: product.featured_image.height,
                          sizes: sizes,
                          widths: '450, 535, 750, 1070, 1500',
                          alt: img_alt
                        }}
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                    <div class="bundle-product-image-icon-{{ section.id }}">
                      <svg class="bundle-product-add-icon-{{ section.id }}" width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M9.35938 12.35H0.459375V9.15H9.35938V0.199998H12.6094V9.15H21.5594V12.35H12.6094V21.3H9.35938V12.35Z" fill="currentColor"></path>
                      </svg>
                    </div>
                  </div>
                  <div class="bundle-product-content-{{ section.id }}">
                    <a href="{{ product.url }}" {% if new_tab %}target="_blank"{% endif %} class="bundle-product-title-{{ section.id }}">
                      {% if product_title_limit %}
                        {{ product.title | truncate: product_title_count }}
                      {% else %}
                        {{ product.title }}
                      {% endif %}
                    </a>                          
                    {% if product.metafields.custom.detail_line_1.value != blank %}
                      <div class="featured-text-{{ section.id }}"><p>{{ product.metafields.custom.detail_line_1.value }}</p></div>
                    {% endif %}
                    {% if product.metafields.custom.detail_line_2.value != blank %}
                      <p class="featured-secondary-text-{{ section.id }}">{{ product.metafields.custom.detail_line_2.value }}</p>
                    {% endif %}
                    <div class="bundle-product-price-{{ section.id }}">
                      {% if price_compare and product.compare_at_price != blank %}
                        <p class="bundle-product-compare-price-{{ section.id }}">{{ product.compare_at_price | money }}</p>
                      {% endif %}
                      <p class="bundle-product-regular-price-{{ section.id }}">
                        {{ product.price | money }}
                      </p>
                    </div>             
                  </div>
                </div>
              {% endif %}
            {% endfor %}
          {% else %}
            {% for item in (1..8) %}
              <div class="bundle-product-{{ section.id }}">
                <div class="bundle-product-image-{{ section.id }}">
                  {{ 'image' | placeholder_svg_tag }}
                </div>
                <div class="bundle-product-content-{{ section.id }}">
                  <h3 class="bundle-product-title-{{ section.id }}">Product title</h3>                 
                  <div class="featured-text-{{ section.id }}"><p>Product details</p></div>
                  <div class="bundle-product-price-{{ section.id }}"><p>$19.99</p></div>
                </div>
              </div>
            {% endfor %}
          {% endif %}
        </div>
      </div>   
      
      <div class="bundle-sidebar-wrapper-{{ section.id }}">       
        <div class="bundle-sidebar-{{ section.id }}">
          {% if sidebar_heading != blank %}
            <div class="bundle-sidebar-heading-{{ section.id }}">{{ sidebar_heading }}</div>
          {% endif %}
          {% if sidebar_sub_heading != blank %}
            <div class="bundle-sidebar-subheading-{{ section.id }}">{{ sidebar_sub_heading }}</div>
          {% endif %}
         <div class="bundle-discount-wrapper-{{ section.id }}">
          <p class="bundle-discount-title-{{ section.id }}">{{ discount_title }}</p>
            <div class="bundle-discount-bar-{{ section.id }}">
              {%- assign last_discount_percent = -1 -%}
              {% for i in (1..max_products) %}
                {% assign discount_variable = 'discount_' | append: i | append: '_percent' %}
                {% assign current_discount = section.settings[discount_variable] | plus: 0 %}

                {%- if current_discount > last_discount_percent or i == 1 -%}
                  <div class="bundle-discount-level-{{ section.id }}" data-discount-level="{{ i }}" data-discount-percent="{{ current_discount }}">
                    {{ current_discount }}%
                  </div>
                  {%- assign last_discount_percent = current_discount -%}
                {%- endif -%}
              {% endfor %}
            </div>
          </div>
          <div class="bundle-sidebar-body-{{ section.id }}">         
            <div class="bundle-sidebar-products-wrap-{{ section.id }}">    
              {% assign num_blocks = max_products %}
              <div class="bundle-sidebar-products-{{ section.id }}">                   
                {% for i in (1..num_blocks) %}
                  <div class="bundle-product-block-{{ section.id }} empty">
                    <svg width="173" height="200" viewBox="0 0 173 200" fill="none" xmlns="http://www.w3.org/2000/svg"><rect x="1.25" y="1.25" width="170.482" height="197.347" fill="white" stroke="black" stroke-width="2.5" stroke-linecap="round" stroke-dasharray="13 13"></rect></svg>
                  </div>
                {% endfor %}
              </div>
              </div>         
            <div class="bundle-total-price-wrapper-{{ section.id }}">
              {% if total_price != blank %}
                <p class="bundle-total-price-text-{{ section.id }}">{{ total_price }}</p>
              {% endif %}
              <div style="display: flex; align-items: center; gap: 5px;">
                <p class="bundle-total-compare-price-{{ section.id }} bundle-compare-price-strikethrough"></p>
                <p class="bundle-total-price-{{ section.id }}">{{ 0 | money }}</p>
              </div>            
            </div>
          </div>
          <form class="bundle-form-{{ section.id }}" method="post" action="/cart/add" accept-charset="UTF-8" enctype="multipart/form-data" novalidate="novalidate" data-type="add-to-cart-form">
            <button type="submit" disabled data-all="{{ button }}" class="bundle-submit-button-{{ section.id }}">
              <span>{{ button }}</span>
              {% if button_style == "non_outline_arrow" or button_style == "outline_arrow" %}
                <svg width="192" height="192" viewBox="0 0 192 192" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M187.827 98.7858C188.123 98.4749 188.123 98.4749 188.123 98.1761C188.419 97.8652 188.419 97.5663 188.704 97.5663C189 96.9566 189 96.6458 189 96.0361C189 95.4263 189 94.8166 188.704 94.5058C188.704 94.195 188.408 93.8961 188.123 93.5852C188.123 93.2744 187.827 93.2744 187.827 92.9755L103.287 4.21945C102.41 3.29889 101.533 3 100.668 3C99.791 3 98.6295 3.31083 98.0488 4.21945C97.1719 5.14 96.8872 6.06058 96.8872 6.96919C96.8872 7.88974 97.1833 9.10918 98.0488 9.7189L175.587 92.0539H6.79206C4.75371 92.0539 3 93.895 3 96.0351C3 98.1751 4.75365 100.016 6.79206 100.016H175.575L97.7543 182.042C96.2967 183.572 96.2967 186.322 97.7543 187.852C99.2119 189.383 101.831 189.383 103.288 187.852L187.827 98.7858Z" fill="black"></path>
                  <path fill-rule="evenodd" clip-rule="evenodd" d="M188.607 97.5657C188.432 97.5657 188.365 97.6788 188.27 97.8382C188.211 97.9378 188.141 98.0554 188.027 98.1748C188.027 98.4734 188.027 98.4734 187.731 98.7839L103.281 187.759C101.825 189.287 99.2085 189.287 97.7524 187.759C96.2963 186.23 96.2963 183.483 97.7524 181.954L175.492 100.013L6.88489 100.013C4.8486 100.013 3.09677 98.1739 3.09677 96.036C3.09677 93.8982 4.84866 92.059 6.88489 92.059L175.504 92.059L98.0465 9.80878C97.182 9.19968 96.8862 7.9815 96.8862 7.0619C96.8862 6.15422 97.1706 5.2346 98.0465 4.315C98.6267 3.40732 99.787 3.0968 100.663 3.0968C101.527 3.0968 102.403 3.39539 103.279 4.315L187.731 92.9796C187.731 93.1274 187.804 93.2021 187.877 93.2774C187.952 93.3543 188.027 93.4319 188.027 93.5887C188.046 93.6098 188.066 93.6308 188.085 93.6518C188.338 93.9267 188.584 94.1935 188.606 94.4689C188.607 94.482 188.607 94.4951 188.607 94.5083C188.903 94.8188 188.903 95.4279 188.903 96.037C188.903 96.6461 188.903 96.9566 188.607 97.5657ZM191.489 93.2767C191.79 93.8661 191.89 94.4204 191.934 94.7363C192.001 95.2226 192 95.7194 192 95.9856L192 96.037C192 96.0544 192 96.0729 192 96.0926C192 96.3523 192.001 96.8096 191.924 97.2931C191.828 97.8884 191.64 98.41 191.393 98.9184L190.546 100.663H190.212C190.127 100.759 190.038 100.852 189.988 100.905L189.974 100.92L105.527 189.891C102.85 192.701 98.1865 192.704 95.51 189.895C94.1476 188.464 93.5636 186.587 93.5636 184.857C93.5636 183.128 94.1468 181.252 95.5071 179.822M191.489 93.2767C191.316 92.7908 191.078 92.4357 190.938 92.2406C190.903 92.1912 190.866 92.142 190.828 92.0939V91.7408L105.522 2.17912C104.076 0.661813 102.397 0 100.663 0C99.3914 0 97.0569 0.401401 95.6212 2.37737C94.3151 3.83819 93.7895 5.45521 93.7895 7.0619C93.7895 8.26663 94.1183 10.6061 95.9608 12.111L168.333 88.9622L6.88489 88.9622C2.9981 88.9622 0 92.3316 0 96.036C0 99.7405 2.99801 103.11 6.88489 103.11L168.285 103.11" fill="black"></path>
                  <path d="M169.5 104L16.5 102.5V90H171L97.5 10.5V7L107.5 4.5C131.167 29.6667 180.2 81.5 187 87.5C193.8 93.5 191.5 99 189.5 101L105.5 184L94 181.5L169.5 104Z" fill="black"></path>
                </svg>
              {% endif %}
            </button>
            {% if mobile_sticky_button %}
              <div class="bundle-mobile-sticky-button-{{ section.id }}" id="bundle-sticky-button-{{ section.id }}">
                <button type="button" class="bundle-mobile-sticky-button-inner-{{ section.id }}" id="bundle-sticky-scroll-{{ section.id }}">
                  {{ mobile_sticky_button_text }}
                </button>
              </div>
            {% endif %}
          </form>
          {% if sidebar_disclaimer != blank %}
            <div class="bundle-sidebar-disclaimer-{{ section.id }}">{{ sidebar_disclaimer }}</div>
          {% endif %}
        </div>
      </div>
    </div>
  </div>
</div>

<script>  
  var formayMoneyForBuyItWith = function(cents, format) {
    if (typeof cents == 'string') {
      cents = cents.replace('.', '');
    }
    var value = '';
    var placeholderRegex = /\{\{\s*(\w+)\s*\}\}/;
    var formatString = format || (typeof Shopify !== 'undefined' && Shopify.money_format);

    if (typeof formatString !== 'string') {
      return '';
    }

    function defaultOption(opt, def) {
      return (typeof opt == 'undefined' ? def : opt);
    }

    function formatWithDelimiters(number, precision, thousands, decimal) {
      precision = defaultOption(precision, 2);
      thousands = defaultOption(thousands, ',');
      decimal = defaultOption(decimal, '.');

      if (isNaN(number) || number == null) {
        return 0;
      }

      number = (number / 100.0).toFixed(precision);

      var parts = number.split('.'),
        dollars = parts[0].replace(/(\d)(?=(\d\d\d)+(?!\d))/g, '$1' + thousands),
        cents = parts[1] ? (decimal + parts[1]) : '';

      return dollars + cents;
    }

    const matchResult = formatString.match(placeholderRegex);
    if (!matchResult || !matchResult[1]) {
      return formatWithDelimiters(cents, 2);
    }

    switch (matchResult[1]) {
      case 'amount':
        value = formatWithDelimiters(cents, 2);
        break;
      case 'amount_no_decimals':
        value = formatWithDelimiters(cents, 0);
        break;
      case 'amount_with_comma_separator':
        value = formatWithDelimiters(cents, 2, '.', ',');
        break;
      case 'amount_no_decimals_with_comma_separator':
        value = formatWithDelimiters(cents, 0, '.', ',');
        break;
    }

    return formatString.replace(placeholderRegex, value);
  };

  function initBundle4() {
    const MAX_PRODUCTS_LIMIT = Number('{{ section.settings.max_products }}');
    const REMOVE_ICON_SVG = '<svg width="16" height="22" viewBox="0 0 16 22" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M13.918 6.45312V19.8821C13.918 20.9341 13.058 21.7941 12.0059 21.7941H3.33331C2.28129 21.7941 1.42126 20.9341 1.42126 19.8821V6.45312H13.918ZM10.5356 9.22475C10.5356 9.00609 10.7121 8.82936 10.9273 8.82936C11.1424 8.82936 11.3227 9.00609 11.3227 9.22475V18.2428C11.3227 18.4576 11.1462 18.6344 10.9273 18.6344C10.7083 18.6344 10.5356 18.4576 10.5356 18.2428V9.22475ZM7.27611 9.22475C7.27611 9.00609 7.45257 8.82936 7.66772 8.82936C7.88287 8.82936 8.06316 9.00609 8.06316 9.22475V18.2428C8.06316 18.4576 7.88639 18.6344 7.66772 18.6344C7.44905 18.6344 7.27611 18.4576 7.27611 18.2428V9.22475ZM4.01658 9.22475C4.01658 9.00609 4.19333 8.82936 4.412 8.82936C4.63067 8.82936 4.80361 9.00609 4.80361 9.22475V18.2428C4.80361 18.4576 4.62715 18.6344 4.412 18.6344C4.19685 18.6344 4.01658 18.4576 4.01658 18.2428V9.22475Z" fill="black"></path><path d="M13.5496 2.82889H10.6087V2.34146C10.6087 1.94224 10.5204 1.5697 10.3478 1.29741C10.1558 0.986419 9.86766 0.794434 9.49921 0.794434H5.84425C5.47199 0.794434 5.18385 0.986419 4.99185 1.29741C4.8192 1.57 4.73068 1.94224 4.73068 2.34146V2.82889H1.78977C0.898969 2.82889 0.169678 3.55463 0.169678 4.44509V5.6697H15.1697V4.44509C15.1697 3.55434 14.4401 2.82889 13.5496 2.82889ZM5.5139 2.34146C5.5139 2.08411 5.56373 1.85783 5.65607 1.70805C5.7059 1.62745 5.77126 1.57762 5.84425 1.57762H9.49921C9.56838 1.57762 9.63346 1.62745 9.68358 1.70805C9.77562 1.85783 9.82575 2.0844 9.82575 2.34146V2.82889H5.51419L5.5139 2.34146Z" fill="black"></path></svg>';

    let mainButton = document.querySelector('.bundle-submit-button-{{ section.id }}');
    let products = document.querySelectorAll('.bundle-product-{{ section.id }}');
    let bundleSidebarProductsContainer = document.querySelector(`.bundle-sidebar-products-{{ section.id }}`);
    let totalValueElement = document.querySelector('.bundle-total-price-{{ section.id }}');
    let totalComparePriceElement = document.querySelector('.bundle-total-compare-price-{{ section.id }}');
    let discountLevels = document.querySelectorAll('.bundle-discount-level-{{ section.id }}');
    
    let sidebarHeading = document.querySelector('.bundle-sidebar-heading-{{ section.id }}');
    let sidebarSubheading = document.querySelector('.bundle-sidebar-subheading-{{ section.id }}');
    
    let originalHeadingHTML = sidebarHeading ? sidebarHeading.innerHTML : '';
    let originalSubheadingHTML = sidebarSubheading ? sidebarSubheading.innerHTML : '';

    let stickyButton = document.getElementById('bundle-sticky-button-{{ section.id }}');
    let stickyScrollButton = document.getElementById('bundle-sticky-scroll-{{ section.id }}');
    let sidebarWrapper = document.querySelector('.bundle-sidebar-wrapper-{{ section.id }}');

    let selectedProducts = {};
    
    function calculateTotalItems() {
        let count = 0;
        for (const productId in selectedProducts) {
            if (selectedProducts.hasOwnProperty(productId)) {
                count += selectedProducts[productId].count;
            }
        }
        return count;
    }

    function checkSidebarVisibility() {
      if (!stickyButton || !sidebarWrapper) return;
      
      if (window.innerWidth >= 1024) {
        stickyButton.classList.remove('visible');
        return;
      }

      const sidebarRect = sidebarWrapper.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      
      if (sidebarRect.top < windowHeight && sidebarRect.bottom > 0) {
        stickyButton.classList.remove('visible');
      } else {
        stickyButton.classList.add('visible');
      }
    }

    if (stickyScrollButton && sidebarWrapper) {
      stickyScrollButton.addEventListener('click', function() {
        sidebarWrapper.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'start'
        });
      });
    }

    if (stickyButton) {
      let scrollTimeout;
      window.addEventListener('scroll', function() {
        clearTimeout(scrollTimeout);
        scrollTimeout = setTimeout(checkSidebarVisibility, 10);
      });

      window.addEventListener('resize', function() {
        clearTimeout(scrollTimeout);
        scrollTimeout = setTimeout(checkSidebarVisibility, 100);
      });

      checkSidebarVisibility();
    }

    function removeProduct(productId) {
      if (selectedProducts[productId] && selectedProducts[productId].count > 0) {
        selectedProducts[productId].count--;
        
        if (selectedProducts[productId].count === 0) {
          delete selectedProducts[productId];
          
          const productElement = document.querySelector(`.bundle-product-{{ section.id }}[data-id="${productId}"]`);
          if (productElement) {
            productElement.classList.remove('active');
          }
        }

        updateProductAddIcon(productId);
        
        updateSidebarProducts();
      }
    }

    function calculateDiscount(count) {
      if (count <= 0) return 0;

      let discountPercent = 0;
      let targetLevel = count;

      if (count > 8) {
        if (count >= 9 && count <= 10) {
            targetLevel = 9;
        } else if (count >= 11 && count <= 12) {
            targetLevel = 11;
        } else if (count >= 13 && count <= 14) {
            targetLevel = 13;
        } else if (count >= 15) {
            targetLevel = 15;
        }
      }

      discountLevels.forEach(level => {
        const levelCount = Number(level.dataset.discountLevel);
        const levelPercent = Number(level.dataset.discountPercent);

        if (levelCount <= targetLevel && levelPercent > discountPercent) {
          discountPercent = levelPercent;
        }
      });
      
      if (targetLevel > MAX_PRODUCTS_LIMIT) {
          targetLevel = MAX_PRODUCTS_LIMIT;
      }
      
      let finalDiscountLevel = discountLevels[targetLevel - 1];
      if (finalDiscountLevel) {
          discountPercent = Number(finalDiscountLevel.dataset.discountPercent);
      }
      
      return discountPercent / 100.0;
    }

     function updateProductAddIcon(productId) {
      const productElement = document.querySelector(`.bundle-product-{{ section.id }}[data-id="${productId}"]`);
      if (!productElement) return;

      const addIconWrapper = productElement.querySelector('.bundle-product-image-icon-{{ section.id }}');
      if (!addIconWrapper) return;

      const limitAttr = productElement.getAttribute('data-limit');
      const productLimit = limitAttr !== null ? parseInt(limitAttr, 10) : null;
      const currentCount = selectedProducts[productId]?.count || 0;

      if (productLimit !== null && currentCount >= productLimit) {
        addIconWrapper.style.display = 'none';
        productElement.classList.add('product-disabled');
      } else {
        addIconWrapper.style.display = '';
        productElement.classList.remove('product-disabled');
      }
    }

    function updateSidebarHeadings(totalItems, savingsAmount) {
      if (sidebarHeading) {
        let headingHTML = originalHeadingHTML;
        
        if (totalItems > 0) {
          const lastClosingTag = headingHTML.lastIndexOf('</');
          if (lastClosingTag !== -1) {
            headingHTML = headingHTML.substring(0, lastClosingTag) + 
                          ` (${totalItems})` + 
                          headingHTML.substring(lastClosingTag);
          } else {
            headingHTML += ` (${totalItems})`;
          }
        }
        
        sidebarHeading.innerHTML = headingHTML;
      }

      if (sidebarSubheading && savingsAmount > 0) {
        const formattedSavings = formayMoneyForBuyItWith(savingsAmount, "{{ shop.money_format }}");
        
        let subheadingHTML = originalSubheadingHTML;
        
        const lastClosingTag = subheadingHTML.lastIndexOf('</');
        if (lastClosingTag !== -1) {
          subheadingHTML = subheadingHTML.substring(0, lastClosingTag) + 
                           ` ${formattedSavings}` + 
                           subheadingHTML.substring(lastClosingTag);
        } else {
          subheadingHTML += ` ${formattedSavings}`;
        }
        
        sidebarSubheading.innerHTML = subheadingHTML;
      } else if (sidebarSubheading && savingsAmount === 0) {
        sidebarSubheading.innerHTML = originalSubheadingHTML;
      }
    }

    function updateTotalAndButton() {
      let subtotal = 0;
      let totalCompare = 0;
      let totalSelectedItems = calculateTotalItems();

      for (const productId in selectedProducts) {
        if (selectedProducts.hasOwnProperty(productId)) {
          const productInfo = selectedProducts[productId];
          subtotal += productInfo.count * productInfo.price;
          totalCompare += productInfo.count * productInfo.comparePrice;
        }
      }

      const discountRate = calculateDiscount(totalSelectedItems);
      const total = subtotal * (1 - discountRate);
      
      const savingsAmount = subtotal - total;

      mainButton.disabled = totalSelectedItems === 0;

      totalValueElement.textContent = formayMoneyForBuyItWith(total, "{{ shop.money_format }}");

      if (totalComparePriceElement) {
        if (subtotal > total || totalCompare > total) {
          const priceToStrikethrough = Math.max(subtotal, totalCompare);
          totalComparePriceElement.textContent = formayMoneyForBuyItWith(priceToStrikethrough, "{{ shop.money_format }}");
          totalComparePriceElement.style.display = 'inline-block';
        } else {
          totalComparePriceElement.textContent = '';
          totalComparePriceElement.style.display = 'none';
        }
      }

      updateSidebarHeadings(totalSelectedItems, savingsAmount);

      discountLevels.forEach(level => {
        const levelCount = Number(level.dataset.discountLevel);
        let activeCheckLevel = totalSelectedItems;
        
        if (activeCheckLevel > 8) {
            if (activeCheckLevel >= 9 && activeCheckLevel <= 10) {
                activeCheckLevel = 9;
            } else if (activeCheckLevel >= 11 && activeCheckLevel <= 12) {
                activeCheckLevel = 11;
            } else if (activeCheckLevel >= 13 && activeCheckLevel <= 14) {
                activeCheckLevel = 13;
            } else if (activeCheckLevel >= 15) {
                activeCheckLevel = 15;
            }
        }
        
        if (activeCheckLevel >= levelCount) {
          level.classList.add('active-discount');
        } else {
          level.classList.remove('active-discount');
        }
      });

      const form = document.querySelector('.bundle-form-{{ section.id }}');
      const oldInputs = form.querySelectorAll('input[name^="items["]');
      oldInputs.forEach(input => input.remove());

      let itemIndex = 0;
      for (const productId in selectedProducts) {
        if (selectedProducts.hasOwnProperty(productId) && selectedProducts[productId].count > 0) {
          var productIdInput = document.createElement('input');
          productIdInput.type = 'hidden';
          productIdInput.name = `items[${itemIndex}][id]`;
          productIdInput.value = productId;
          form.appendChild(productIdInput);

          var quantityInput = document.createElement('input');
          quantityInput.type = 'hidden';
          quantityInput.name = `items[${itemIndex}][quantity]`;
          quantityInput.value = selectedProducts[productId].count;
          form.appendChild(quantityInput);
          itemIndex++;
        }
      }
    }

    function updateSidebarProducts() {
      while (bundleSidebarProductsContainer.firstChild) {
        bundleSidebarProductsContainer.removeChild(bundleSidebarProductsContainer.firstChild);
      }

      let currentSelectedCount = calculateTotalItems();

      for (const productId in selectedProducts) {
        if (selectedProducts.hasOwnProperty(productId)) {
          const productInfo = selectedProducts[productId];
          for (let i = 0; i < productInfo.count; i++) {
            const newDiv = document.createElement('div');
            newDiv.className = `bundle-product-block-{{ section.id }}`;
            newDiv.setAttribute('data-id', productId);

            const imageDiv = document.createElement('div');
            imageDiv.className = `bundle-sidebar-product-image-{{ section.id }}`;

            const imageElement = document.createElement('img');
            imageElement.src = productInfo.imageSrc;
            imageElement.alt = productInfo.imageAlt;

            imageDiv.appendChild(imageElement);
            newDiv.appendChild(imageDiv);
            bundleSidebarProductsContainer.appendChild(newDiv);
            currentSelectedCount++;

            const removeButton = document.createElement('div');
            removeButton.className = `bundle-sidebar-remove-icon-wrapper-{{ section.id }}`;
            removeButton.innerHTML = REMOVE_ICON_SVG;
            removeButton.setAttribute('data-product-id', productId);

            removeButton.addEventListener('click', (e) => {
              e.stopPropagation();
              const idToRemove = e.currentTarget.getAttribute('data-product-id');
              removeProduct(idToRemove);
            });

            newDiv.appendChild(removeButton);
          }
        }
      }

      const emptyBlocksToAdd = MAX_PRODUCTS_LIMIT - calculateTotalItems();

      if (emptyBlocksToAdd > 0) {
        for (let i = 0; i < emptyBlocksToAdd; i++) {
          const emptyDiv = document.createElement('div');
          emptyDiv.className = `bundle-product-block-{{ section.id }} empty`;
          emptyDiv.innerHTML = `<svg width="173" height="200" viewBox="0 0 173 200" fill="none" xmlns="http://www.w3.org/2000/svg"><rect x="1.25" y="1.25" width="170.482" height="197.347" fill="white" stroke="black" stroke-width="2.5" stroke-linecap="round" stroke-dasharray="13 13"></rect></svg>`;
          bundleSidebarProductsContainer.appendChild(emptyDiv);
        }
      }
      updateTotalAndButton();
    }

    if (products) {
      products.forEach(el => {
        const dataId = el.getAttribute('data-id');
        const productPrice = Number(el.getAttribute('data-price'));
        const productComparePrice = Number(el.getAttribute('data-compare-price'));

        const limitAttr = el.getAttribute('data-limit');
        const productLimit = limitAttr !== null ? parseInt(limitAttr, 10) : null;

        let image = el.querySelector('.bundle-product-image-{{ section.id }} img');
        let imageSrc = image ? image.src : '';
        let imageAlt = image ? image.alt : '';

        el.addEventListener('click', () => {
          const currentProductCount = selectedProducts[dataId]?.count || 0;

          if (productLimit !== null && currentProductCount >= productLimit) {
            updateProductAddIcon(dataId);
            console.log(`Product limit of ${productLimit} reached for product ${dataId}.`);
            return;
          }

          let currentTotal = calculateTotalItems();
          if (currentTotal >= MAX_PRODUCTS_LIMIT) {
            console.log(`Max products limit of ${MAX_PRODUCTS_LIMIT} reached. Not adding product.`);
            return; 
          }

          if (selectedProducts[dataId]) {
            selectedProducts[dataId].count++;
          } else {
            selectedProducts[dataId] = {
              count: 1,
              price: productPrice,
              comparePrice: productComparePrice,
              imageSrc: imageSrc,
              imageAlt: imageAlt
            };
          }

          el.classList.add('active');

          //       
          updateProductAddIcon(dataId);

          updateSidebarProducts();
        });

        if (selectedProducts[dataId]) {
          el.classList.add('active');
        }

        updateProductAddIcon(dataId);
      });
    }

    updateTotalAndButton();
  }

  document.addEventListener('DOMContentLoaded', initBundle4);
</script>
