<script>
  function initCollection11() {
      let lazyLoadInstance = new LazyLoad();

      window.addEventListener(
        "LazyLoad::Initialized",
        function (event) {
          window.lazyLoadInstance = event.detail.instance;
        },
        false
      );

      const videoItems = document.querySelectorAll('.collection-card-{{ section.id }}');

      if(videoItems) {
        videoItems.forEach(item => {        
          const video = item.querySelector('video');
          const iframe = item.querySelector('iframe');      
          const videoButton = item.querySelector('.collection-media-play-{{ section.id }}');    
          const muteButton = item.querySelector('.collection-media-mute-{{ section.id }}');

          if(videoButton) {
            videoButton.addEventListener('click', function() {
              if(videoButton.classList.contains('active')) {
                if(video) {
                  video.pause();
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src2');
                } 
              } else {
                if(video) {
                  video.play();
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src1');
                }      
              }  
              videoButton.classList.toggle('active'); 
            })  
          }   

          if(muteButton) {
            muteButton.addEventListener('click', function() {
              if(muteButton.classList.contains('active')) {
                if(video) {
                  video.muted = true;
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src1');
                } 
              } else {
                if(video) {
                  video.muted = false;
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src');
                }      
              }  
              muteButton.classList.toggle('active'); 
            })
          }
        });
      }

  }

  document.addEventListener('DOMContentLoaded', initCollection11);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initCollection11);
    document.addEventListener('shopify:section:load', initCollection11);
  }
</script>
