  assign collection_bg_color = section.settings.collection_bg_color
  assign collection_border_color = section.settings.collection_border_color
  assign collection_padding_vertical = section.settings.collection_padding_vertical
  assign collection_padding_horizontal = section.settings.collection_padding_horizontal
