{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  :root {
    --desktop-height: ;
  }

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .collection-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .collection-grid-{{ section.id }} {
    margin-top: {{ grid_mt | times: 0.75 | round: 0 }}px;
    display: block;
    gap: {{ grid_gap_mobile }}px;
    align-items: stretch;
    padding: {{ grid_padding_vertical | times: 0.75 | round: 0 }}px {{ grid_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ grid_radius }}px;
    border: {{ grid_border_thickness }}px solid {{ grid_border_color }};
    background-color: {{ grid_bg_color }};
  }

  .collection-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    position: relative;
    flex: 0 0 calc((100% / {{ section.blocks.size | plus: 2 }}) - {{ grid_gap_mobile }}px);
    transition: all 0.25s ease-out 0s;
    overflow: hidden;
    padding: {{ collection_padding_vertical_mobile }}px {{ collection_padding_horizontal_mobile }}px;
    border-radius: {{ collection_radius }}px;
    border: {{ collection_border_thickness }}px solid {{ collection_border_color }};
    background-color: {{ collection_bg_color }};
    cursor: pointer;
    max-height: 88px;
    height: 100%;
  }

  .collection-item-{{ section.id }}.active {
    max-height: 100vh;
    transition: all 0.25s ease-out 0s;
  }

  .collection-item-{{ section.id }}:not(:first-child) {
    margin-top: {{ grid_gap_mobile }}px;
  }

  /* .collection-item-{{ section.id }}.active {
    transition: all 0.25s ease-out 0s;
    flex: 0 0 calc(((100% / {{ section.blocks.size | plus: 2 }}) * 3) - {{ grid_gap_mobile }}px);
  } */

  .collection-image-{{ section.id }} {
    position: absolute;
    width: 100%;
    /* width: calc((((100vw-{{ padding_horizontal_mobile | times: 2 }}rem - {{ grid_padding_horizontal | times: 0.75 | times: 2 | round: 0 }}px) / {{ section.blocks.size | plus: 2 }}) * 3) - {{ grid_gap_mobile }}px); */
    height: 100%;
    left: 0;
    top: 0;
    border-radius: {{ collection_radius }}px;
  }

  .collection-image-{{ section.id }}:after {
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    border-radius: {{ collection_radius }}px;
    background-color: {{ collection_overlay_color | hex_to_rgba: collection_overlay }};
  }

  .collection-image-{{ section.id }} img,
  .collection-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ collection_radius }}px;
  }

  .collection-image-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  .collection-reverse-{{ section.id }} {
    position: relative;
    z-index: 2;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    width: 100%;
    height: 100%;
  }

  .collection-reverse-{{ section.id }} .collection-title-{{ section.id }} {
    padding-bottom: 0px;
  }

  .collection-reverse-{{ section.id }} .collection-title-{{ section.id }}:after {
    display: none;
  }

  .collection-item-{{ section.id }}.active .collection-reverse-{{ section.id }} {
    display: none;
  }

  .collection-content-{{ section.id }} {
    position: relative;
    z-index: 2;
    display: none;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
    gap: 20px;
  }

  .collection-item-{{ section.id }}.active .collection-content-{{ section.id }} {
    display: flex;
  }

  .collection-title-{{ section.id }},
  .collection-title-{{ section.id }} * {
    text-align: {{ title_align_mobile }};
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: uppercase;
    font-weight: 700;
  }

  .collection-stars-{{ section.id }} {
    margin-top: {{ stars_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    gap: 5px;
    justify-content: {{ stars_align_mobile }};
  }

  .collection-stars-{{ section.id }} svg,
  .collection-stars-{{ section.id }} img {
    display: block;
    width: {{ stars_size }}px;
    height: {{ stars_size }}px;
    object-fit: cover;
    flex: 0 0 {{ stars_size }}px;
  }

  .collection-text-{{ section.id }} {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
  }

  .collection-text-{{ section.id }} * {
    margin: 0;
  }

  .collection-text-{{ section.id }},
  .collection-text-{{ section.id }} * {
    text-align: {{ text_align_mobile }};
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;=
  }

  .collection-btn-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .collection-btn-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .collection-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .collection-grid-{{ section.id }} {
      display: flex;
      margin-top: {{ grid_mt }}px;
      gap: {{ grid_gap }}px;
      padding: {{ grid_padding_vertical }}px {{ grid_padding_horizontal | times: 0.75 | round: 0 }}px;
    }

    .collection-item-{{ section.id }} {
      display: block;
      flex: 0 0 calc((100% / {{ section.blocks.size | plus: 2 }}) - {{ grid_gap }}px);
      padding: {{ collection_padding_vertical }}px {{ collection_padding_horizontal }}px;
      max-height: 100vh;
      height: var(--desktop-height);
    }

    .collection-item-{{ section.id }}:not(:first-child) {
      margin-top: 0px;
    }

    .collection-item-{{ section.id }}.active {
      height: var(--desktop-height);
      flex: 0 0 calc(((100% / {{ section.blocks.size | plus: 2 }}) * 3) - {{ grid_gap }}px);
    }

    .collection-image-{{ section.id }} {
        width: calc(((100vw - {{ padding_horizontal | times: 2 }}rem - {{ grid_padding_horizontal | times: 2 }}px) / 7 * 3) - {{ grid_gap }}px);
    }

    .collection-content-{{ section.id }} {
      width: calc(((100vw - {{ padding_horizontal | times: 2 }}rem - {{ grid_padding_horizontal | times: 2 }}px - {{ collection_padding_horizontal | times: 5 | round: 0 }}px) / 7 * 3) - {{ grid_gap }}px);
    }

    .collection-reverse-{{ section.id }} {
      transform: rotate(180deg);
      writing-mode: vertical-rl;
    }

    .collection-title-{{ section.id }},
    .collection-title-{{ section.id }} * {
      text-align: {{ title_align }};
      font-size: {{ title_size }}px;
    }

    .collection-stars-{{ section.id }} {
      margin-top: {{ stars_mt }}px;
      justify-content: {{ stars_align }};
    }

    .collection-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
    }

    .collection-text-{{ section.id }},
    .collection-text-{{ section.id }} * {
      text-align: {{ text_align }};
      font-size: {{ text_size }}px;
    }

    .collection-btn-{{ section.id }} {
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }

    @media(min-width: 1024px) {
      .collection-image-{{ section.id }} {
        width: calc(((({{ content_width }}rem - {{ padding_horizontal | times: 2 }}rem - {{ grid_padding_horizontal | times: 2 | round: 0 }}px) / {{ section.blocks.size | plus: 2 }}) * 3) - {{ grid_gap }}px);
      }
      .collection-content-{{ section.id }} {
        width: calc(((({{ content_width }}rem - {{ padding_horizontal | times: 2 }}rem - {{ grid_padding_horizontal | times: 2 | round: 0 }}px - {{ collection_padding_horizontal | times: 4 | round: 0 }}px) / {{ section.blocks.size | plus: 2 }}) * 3) - {{ grid_gap }}px);
      }
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .collection-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .collection-title-{{ section.id }},
    .collection-title-{{ section.id }} * {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .collection-text-{{ section.id }},
    .collection-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .collection-btn-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if grid_shadow %}
  <style>
    .collection-grid-{{ section.id }} {
      box-shadow: 0px 0px 10px 0px {{ grid_shadow_color | hex_to_rgba: 0.3 }}; 
    }
  </style>
{% endif %}

{% if collection_shadow %}
  <style>
    .collection-item-{{ section.id }} {
      box-shadow: 0px 0px 10px 0px {{ collection_shadow_color | hex_to_rgba: 0.3 }}; 
    }
  </style>
{% endif %}

{% if collection_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .collection-item-{{ section.id }} {
       aspect-ratio: 9.6/12 !important; 
      } 
    }
  </style>
{% elsif collection_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
       aspect-ratio: 12/8 !important; 
      }
    }
  </style>
{% elsif collection_ratio == "square" %}
  <style>
     @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
       aspect-ratio: 12/12 !important; 
      } 
     }
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
       aspect-ratio: auto !important;  
      } 
     }
  </style>
{% endif %}

{% if title_underline %}
  <style>
    .collection-title-{{ section.id }} {
      position: relative;
      padding-bottom: {{ title_mb | times: 0.75 | round: 0 }}px;
    }

    .collection-title-{{ section.id }}:after {
      content: '';
      position: absolute;
      left: 0;
      bottom: 0;
      height: 2px;
      width: 100%;
      background-color: {{ title_underline_color }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .collection-btn-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .collection-btn-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .collection-btn-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .collection-btn-{{ section.id }} {
        margin-left: auto;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .collection-btn-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">

      {% if heading != blank %}
        <div class="collection-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}

      <div class="collection-grid-{{ section.id }}">
        {% for block in section.blocks %}
          {% assign collection = block.settings.collection %}
          <div class="collection-item-{{ section.id }} {% if forloop.first %}active{% endif %}" data-link="{% if block.settings.button_url != blank %}{{ block.settings.button_url }}{% else %}{{ collection.url }}{% endif %}">
            <div class="collection-image-{{ section.id }}">
              {% if block.settings.image !=  blank %}
                {%- capture sizes -%}(min-width: {{ content_width }}rem) {{ content_width | times: 16 | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {{ block.settings.image | image_url: width: 1000 | image_tag:
                  loading: lazy,
                  sizes: sizes,
                  alt: block.settings.image.alt,
                  widths: '375, 550, 750, 1100, 1500'
                }}
              {% elsif collection.featured_image != blank %}
                {%- capture sizes -%}(min-width: {{ content_width }}rem) {{ content_width | times: 16 | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {{ collection.featured_image | image_url: width: 1000 | image_tag:
                  loading: lazy,
                  sizes: sizes,
                  alt: collection.title,
                  widths: '375, 550, 750, 1100, 1500'
                }}
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
            </div>
            <div class="collection-reverse-{{ section.id }}">
              <div class="collection-title-{{ section.id }}">{% if block.settings.title != blank %}{{ block.settings.title }}{% else %}<h3>{{ collection.title }}</h3>{% endif %}</div>
            </div>
            <div class="collection-content-{{ section.id }}">
              <div class="collection-content-top-{{ section.id }}">
                <div class="collection-title-{{ section.id }}">{% if block.settings.title != blank %}{{ block.settings.title }}{% else %}<h3>{{ collection.title }}</h3>{% endif %}</div>
                {% if block.settings.stars_count != 0 %}
                  <div class="collection-stars-{{ section.id }}">
                      {% if stars_icon != blank %}
                        {% for item in (1..block.settings.stars_count) %}
                          <img src="{{ stars_icon | image_url }}" alt="{{ stars_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                        {% endfor %}
                      {% else %}
                        {% for item in (1..block.settings.stars_count) %}
                          <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="50" height="50" viewBox="0,0,256,256"
                            style="fill:#000000;">
                            <g fill="{{ stars_color }}" fill-rule="nonzero" stroke="none" stroke-width="1" stroke-linecap="butt" stroke-linejoin="miter" stroke-miterlimit="10" stroke-dasharray="" stroke-dashoffset="0" font-family="none" font-weight="none" font-size="none" text-anchor="none" style="mix-blend-mode: normal"><g transform="scale(5.12,5.12)"><path d="M10.2,48.6c-0.2,0 -0.4,-0.1 -0.6,-0.2c-0.3,-0.2 -0.5,-0.7 -0.4,-1.1l4.4,-16.4l-13.2,-10.7c-0.4,-0.2 -0.5,-0.7 -0.4,-1.1c0.1,-0.4 0.5,-0.7 0.9,-0.7l17,-0.9l6.1,-15.9c0.2,-0.3 0.6,-0.6 1,-0.6c0.4,0 0.8,0.3 0.9,0.6l6.1,15.9l17,0.9c0.4,0 0.8,0.3 0.9,0.7c0.1,0.4 0,0.8 -0.3,1.1l-13.2,10.7l4.4,16.4c0.1,0.4 0,0.8 -0.4,1.1c-0.3,0.2 -0.8,0.3 -1.1,0l-14.3,-9.2l-14.3,9.2c-0.2,0.2 -0.3,0.2 -0.5,0.2z"></path></g></g>
                          </svg>
                        {% endfor %}
                      {% endif %}
                  </div>
                {% endif %}
                <div class="collection-text-{{ section.id }}">{% if block.settings.text != blank %}{{ block.settings.text }}{% else %}{{ collection.description }}{% endif %}</div>
              </div>
              <a href="{% if block.settings.button_url != blank %}{{ block.settings.button_url }}{% else %}{{ collection.url }}{% endif %}" class="collection-btn-{{ section.id }}">{{ block.settings.button }}</a>
            </div>
          </div>
        {% endfor %}
      </div>

    </div>
</div>

<script>
  function initCollection4() {
    let items = document.querySelectorAll('.collection-item-{{ section.id }}');
    items.forEach(el => {
      if(window.innerWidth > 1023) {
        el.addEventListener('mouseover', () => {
          let height = el.offsetHeight + "px";
          document.documentElement.style.setProperty('--desktop-height', height);
          items.forEach(element => {
            element.classList.remove('active');
          })
          el.classList.add('active');
        }) 
        el.addEventListener('click', (e) => {
          window.location = el.getAttribute('data-link');
        });
      } else {
        el.addEventListener('click', (e) => {
          if(window.innerWidth < 1023) {
            if(!el.classList.contains('active')) {
             e.preventDefault(); 
            } else {
              window.location = el.getAttribute('data-link');
            }
            items.forEach(element => {
              element.classList.remove('active');
            })
            el.classList.add('active'); 
          }
        });
      }
    })
  }

  document.addEventListener('DOMContentLoaded', initCollection4);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initCollection4);
     document.addEventListener('shopify:section:load', initCollection4);
  }
</script>
