liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign body_gap = section.settings.body_gap

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align_mobile = section.settings.heading_align_mobile
  assign heading_align = section.settings.heading_align

  assign items_mt_mobile = section.settings.items_mt_mobile
  assign items_mt = section.settings.items_mt
  assign items_gap_mobile = section.settings.items_gap_mobile
  assign items_gap = section.settings.items_gap
  assign items_align_mobile = section.settings.items_align_mobile
  assign items_align = section.settings.items_align

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_hover_color = section.settings.text_hover_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height

  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
  }

  .collection-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .collection-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .collection-items-{{ section.id }} {
    margin-top: {{ items_mt_mobile }}px;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: {{ items_align_mobile }};
    gap: {{ items_gap_mobile }}px;
  }

  .collection-item-{{ section.id }} {
    margin: 0;
    display: block;
    max-width: fit-content;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    position: relative;
    transition: all 0.25s ease 0s;
    font-weight: 700;
    cursor: pointer;
  }

  .collection-item-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ text_hover_color }};
  }

  .collection-item-{{ section.id }}:after {
    content: 0;
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 0px;
    height: 1px;
    background-color: {{ text_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .collection-item-{{ section.id }}:hover:after {
    width: 100%;
    transition: all 0.25s ease 0s;
  }

  .collection-images-{{ section.id }} {
    position: relative;
    display: none;
  }

  .collection-image-absolute-{{ section.id }} {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
  }

  .collection-image-{{ section.id }} {
    display: none;
    border-radius: {{ image_radius }}px;
    outline: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    position: relative;
  }

  .collection-image-{{ section.id }}.active {
    display: block;
  }

  .collection-image-absolute-{{ section.id }} .collection-image-{{ section.id }} {
    display: block !important;
  }

  .collection-image-{{ section.id }} img,
  .collection-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .collection-image-{{ section.id }} svg {
    background-color: #AEAEAE;
  }

  .collection-static-image-{{ section.id }} {
    position: relative;
    z-index: 2;
  }

  .collection-animate-image-{{ section.id }} {
    transform: rotate(6deg);
    transition: all 0.35s ease 0s;
  }

  .collection-animate-image-{{ section.id }}.active {
    transform: rotate(-6deg);
    transition: all 0.35s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-body-{{ section.id }} {
      grid-template-columns: 2.5fr 1fr;
      gap: {{ body_gap }}px;
    }

    .collection-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .collection-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .collection-items-{{ section.id }} {
      margin-top: {{ items_mt }}px;
      justify-content: {{ items_align }};
      gap: {{ items_gap }}px;
    }

    .collection-item-{{ section.id }} {
      font-size: {{ text_size }}px;
    }

    .collection-images-{{ section.id }} {
      display: block;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
      .collection-heading-{{ section.id }} * {
          font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
          font-weight: {{ heading_font.weight }};
          font-style: {{ heading_font.style }};
      }
  </style>
{% endif %}

{% if text_custom %}
  <style>
      .collection-item-{{ section.id }} {
          font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
          font-weight: {{ text_font.weight }};
          font-style: {{ text_font.style }};
      }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .collection-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .collection-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .collection-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .collection-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="collection-body-{{ section.id }}">
        <div class="collection-content-{{ section.id }}">
          {% if heading != blank %}
            <div class="collection-heading-{{ section.id }}">
              {{ heading }}
            </div>
          {% endif %}
          <div class="collection-items-{{ section.id }}">
            {% for block in section.blocks %}
              <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}p{% endif %} class="collection-item-{{ section.id }}" id="{{ block.id }}">{{ block.settings.text }}</{% if block.settings.url != blank %}a{% else %}p{% endif %}>
            {% endfor %}
          </div>
        </div>
        <div class="collection-images-{{ section.id }}">
          {% for block in section.blocks %}
            <div class="collection-image-absolute-{{ section.id }}">
              <div class="collection-image-{{ section.id }}">
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}
              </div>
            </div>
          {% endfor %}
          <div class="collection-animate-image-{{ section.id }}">
            {% for block in section.blocks %}
              <div class="collection-image-static-{{ section.id }}">
                <div class="collection-image-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </div>
              </div>
            {% endfor %}
          </div>
        </div>
      </div>
    </div>
</div>

<script>
  function initCollection7() {
    let toggles = document.querySelectorAll('.collection-item-{{ section.id }}');
    let images = document.querySelectorAll('.collection-image-{{ section.id }}');
    let imageWrapper = document.querySelector('.collection-animate-image-{{ section.id }}');
    if(toggles) {
      toggles.forEach(toggle => {
        toggle.addEventListener('mouseover', () => {
          let toggleId = toggle.id;

          images.forEach(image => {
            image.classList.remove('active');
            if (image.dataset.id === toggleId) {
              image.classList.add('active');
              imageWrapper.classList.toggle('active');
            }
          });

        });
      });
    }
  }

  document.addEventListener('DOMContentLoaded', initCollection7);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initCollection7);
     document.addEventListener('shopify:section:load', initCollection7);
  }
</script>
