liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign menu = section.settings.menu

  assign collections_number = section.settings.collections_number
  assign collections_display = section.settings.collections_display
  assign collections_separator = section.settings.collections_separator
  assign collections_superscript = section.settings.collections_superscript

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_hover_color = section.settings.text_hover_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_align = section.settings.text_align
  assign text_align_mobile = section.settings.text_align_mobile

  assign show_image = section.settings.show_image

-%}

{%- style -%}

  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-list-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .collection-link-{{ section.id }} {
    display: inline-block;
  }

  .collection-link-{{ section.id }},
  .collection-text-{{ section.id }},
  .collection-superscript-{{ section.id }},
  .collection-separator-{{ section.id }} {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    text-decoration: none;
  }

  .collection-superscript-{{ section.id }} {
    font-size: 75% !important;
    line-height: 0;
  }

  .collection-item-{{ section.id }} {
    max-width: fit-content;
  }

  .collection-separator-{{ section.id }} {
    margin-left: 7px;
    margin-right: 7px;
  }

  .collection-item-{{ section.id }}:hover .collection-text-{{ section.id }},
  .collection-item-{{ section.id }}:hover .collection-superscript-{{ section.id }} {
    transition: all 0.25s ease 0s;
    color: {{ text_hover_color }};
  }

  .collection-item-{{ section.id }}:hover .collection-text-{{ section.id }} {
    text-decoration: underline;
  }

  .collection-image-{{ section.id }} {
    display: none;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    width: calc(100vw / 3);
    z-index: 2;
    pointer-events: none;
  }

  .collection-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .collection-item-{{ section.id }}:hover .collection-image-{{ section.id }} {
    display: block;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-list-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .collection-link-{{ section.id }},
    .collection-text-{{ section.id }},
    .collection-superscript-{{ section.id }},
    .collection-separator-{{ section.id }} {
      font-size: {{ text_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .collection-item-{{ section.id }},
    .collection-item-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if text_align_mobile == "center" %}
  <style>
    .collection-item-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif text_align_mobile == "right" %}
  <style>
    .collection-item-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if text_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif text_align == "right" %}
  <style>
    @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      } 
    }
  </style>
{% elsif text_align == "left" %}
  <style>
    @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

{% if collections_display == "paragraph" %}
  <style>
    .collection-item-{{ section.id }} {
      display: inline;
    }
  </style>
{% elsif collections_display == "list" %}
  <style>
    .collection-item-{{ section.id }} {
      display: block;
    }

    .collection-separator-{{ section.id }} {
      display: none;
    }
  </style>
{% elsif collections_display == "list_paragraph" %}
  <style>
    .collection-item-{{ section.id }} {
      display: block;
    }

    .collection-separator-{{ section.id }} {
      display: none;
    }

    @media(min-width: 1024px) {
      .collection-item-{{ section.id }} {
        display: inline;
      }

      .collection-separator-{{ section.id }} {
        display: inline;
      }
    }
  </style>
{% endif %}

{% if collections_separator == "none" %}
  <style>
    .collection-link-{{ section.id }} {
      margin-right: 16px;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <ul class="collection-list-{{ section.id }}">
        {% if menu != blank %}
          {% for link in menu.links %}
            {% assign link_handle = link.handle | handleize %}
            {% assign menu_collection = collections[link_handle] %}

            <li class="collection-item-{{ section.id }}">
              <a href="{{ link.url }}" class="collection-link-{{ section.id }}">
                <span class="collection-text-{{ section.id }}">{% if collections_superscript == "index" %}<sup class="collection-superscript-{{ section.id }}">{{ forloop.index }}</sup>{% endif %}{{ link.title | escape }}{% if collections_superscript == "products" %}{% if menu_collection != blank %}<sup class="collection-superscript-{{ section.id }}">{{ menu_collection.products_count }}</sup>{% elsif link_handle contains "all" or link_handle contains "shop" %}<sup class="collection-superscript-{{ section.id }}">{{ shop.products_count }}</sup>{% endif %}{% endif %}</span>
              </a>
              {% unless forloop.last %}
                {% if collections_separator != "none" %}
                  <span class="collection-separator-{{ section.id }}">{% if collections_separator == "emdash" %}—{% elsif collections_separator == "hyphen" %}-{% elsif collections_separator == "dot" %}·{% elsif collections_separator == "bullet" %}•{% endif %}</span>
                {% endif %}
              {% endunless %}

              {% if show_image and menu_collection.featured_image != blank%}
                <div class="collection-image-{{ section.id }}">
                  <img src="{{ menu_collection.featured_image | image_url }}" alt="{{ menu_collection.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                </div>
              {% endif %}
            </li>
          {% endfor %}
        {% else %}
          {% for collection in collections limit: collections_number %}
            <li class="collection-item-{{ section.id }}">
              <a href="{{ collection.url }}" class="collection-link-{{ section.id }}">
                <span class="collection-text-{{ section.id }}">{% if collections_superscript == "index" %}<sup class="collection-superscript-{{ section.id }}">{{ forloop.index }}</sup>{% endif %}{{ collection.title | escape }}{% if collections_superscript == "products" %}<sup class="collection-superscript-{{ section.id }}">{{ collection.products_count }}</sup>{% endif %}</span>
              </a>
              {% unless forloop.last %}
                {% if collections_separator != "none" %}
                  <span class="collection-separator-{{ section.id }}">{% if collections_separator == "emdash" %}—{% elsif collections_separator == "hyphen" %}-{% elsif collections_separator == "dot" %}·{% elsif collections_separator == "bullet" %}•{% endif %}</span>
                {% endif %}
              {% endunless %}

              {% if show_image and collection.featured_image != blank%}
                <div class="collection-image-{{ section.id }}">
                  <img src="{{ collection.featured_image | image_url }}" alt="{{ collection.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                </div>
              {% endif %}
            </li>
          {% endfor %}
        {% endif %}
      </ul>
    </div>
</div>
