{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    font-weight: 700;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .comparison-body-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    margin-top: {{ body_mt_mobile }}px;
    gap: {{ body_gap_mobile }}px;
    position: relative;
  }

  .comparison-versus-icon-{{ section.id }} {
    position: absolute;
    left: 50%;
    top: 14%;
    transform: translateX(-50%);    
    width: {{ versus_icon_width_mobile }}px;
    overflow: hidden;
  }

  .comparison-versus-icon-{{ section.id }} img,
  .comparison-versus-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-versus-icon-{{ section.id }} svg circle {
    fill: {{ versus_icon_bg_color }};
  }

  .comparison-versus-icon-{{ section.id }} svg path {
    fill: {{ versus_icon_color }};
  }

  .comparison-image-{{ section.id }} {
    flex: 0 1 calc(50% - ({{ body_gap_mobile }}px / 2));
    overflow: hidden;
    border-radius: {{ image_radius }}px;
  }

  .comparison-image-{{ section.id }} img,
  .comparison-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-image-{{ section.id }} svg {
    background: #AFAFAF;
  }

  .comparison-table-{{ section.id }} {
    flex: 1 1 auto;
    order: 3;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: {{ rows_gap_mobile }}px;
  }

  .comparison-value-{{ section.id }} {
    display: flex;
    gap: 5px;
    width: 100%
  }

  .comparison-right-value-{{ section.id }},
  .comparison-left-value-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    background: {{ value_bg_color }};
    border: {{ value_border_thickness }}px solid {{ value_border_color }};
    padding: {{ value_padding_vertical_mobile }}px {{ value_padding_horizontal_mobile }}px;
    border-radius: {{ value_radius }}px;
    width: 100%;
  }

  .comparison-icon-{{ section.id }} {
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
    flex-shrink: 0;
  }

  .comparison-icon-{{ section.id }} img,
  .comparison-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-text-{{ section.id }} {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-align: center;
    text-decoration: none;
  }

  .comparison-button-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ button_horizontal_align_mobile }};
  }

  .comparison-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .comparison-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .comparison-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .comparison-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-body-{{ section.id }} {      
      flex-wrap: nowrap;
      gap: {{ body_gap }}px;
      margin-top: {{ body_mt }}px;
    }

    .comparison-table-{{ section.id }} {
      gap: {{ rows_gap }}px;
    }

    .comparison-image-{{ section.id }} {
      flex: 0 1 {{ image_width }}px;
    }

    .comparison-table-{{ section.id }} {
      order: unset;
    }

    .comparison-right-value-{{ section.id }},
    .comparison-left-value-{{ section.id }} {
      padding: {{ value_padding_vertical }}px {{ value_padding_horizontal }}px;
    }

    .comparison-icon-{{ section.id }} {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .comparison-text-{{ section.id }} {
      font-size: {{ text_size }}px;
    }

    .comparison-versus-icon-{{ section.id }} {
      top: 50% !important;      
      transform: translate(-50%, -50%);
      width: {{ versus_icon_width }}px;
    }

    .comparison-button-wrapper-{{ section.id }} {
      justify-content: {{ button_horizontal_align }};
    }

    .comparison-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .comparison-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .comparison-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .comparison-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }

    .comparison-versus-icon-{{ section.id }} {
      top: 16%;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .comparison-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
    .comparison-versus-icon-{{ section.id }} {
      top: 10%;
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .comparison-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }    
    .comparison-versus-icon-{{ section.id }} {
      top: 14%;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .comparison-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .comparison-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}


