  assign border_thickness_between_product = section.settings.border_thickness_between_product
  assign border_color_between_product = section.settings.border_color_between_product
  assign row_border_color = section.settings.row_border_color

  assign product_title_size = section.settings.product_title_size
  assign product_title_size_mobile = section.settings.product_title_size_mobile
  assign product_title_first_color = section.settings.product_title_first_color
  assign product_title_second_color = section.settings.product_title_second_color
  assign product_title_first_hover_color = section.settings.product_title_first_hover_color
  assign product_title_second_hover_color = section.settings.product_title_second_hover_color
  assign product_title_custom = section.settings.product_title_custom
  assign product_title_font = section.settings.product_title_font
  assign product_title_height = section.settings.product_title_height
  assign product_title_padding_horizontal = section.settings.product_title_padding_horizontal
  assign product_title_padding_horizontal_mobile = section.settings.product_title_padding_horizontal_mobile
  assign product_title_padding_vertical = section.settings.product_title_padding_vertical
  assign product_title_padding_vertical_mobile = section.settings.product_title_padding_vertical_mobile
  assign product_title_radius = section.settings.product_title_radius
  assign product_title_first_bg_color = section.settings.product_title_first_bg_color
  assign product_title_first_bg_gradient = section.settings.product_title_first_bg_gradient
  assign product_second_title_bg_color = section.settings.product_second_title_bg_color
