  assign value_heading_font = section.settings.value_heading_font
  assign value_heading_size = section.settings.value_heading_size
  assign value_heading_size_mobile = section.settings.value_heading_size_mobile
  assign value_heading_color = section.settings.value_heading_color
  assign value_heading_height = section.settings.value_heading_height

  assign true_icon = section.settings.true_icon
  assign false_icon = section.settings.false_icon
