{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  value_font | font_face: font_display: 'swap' }}
  {{  value_heading_font | font_face: font_display: 'swap' }}  
  {{  product_title_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0 0 10px 0;
    font-size: {{ heading_size_mobile }}px;
    font-weight: 700;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .comparison-row-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    padding: {{ row_padding_vertical_mobile }}px 0px;
  }

  .comparison-row-{{ section.id }}:not(:first-child) {
    border-bottom: {{ row_border_thickness }}px {{ row_border_style }} {{ row_border_color }};
  }

  .comparison-row-products-{{ section.id }} {
    grid-template-columns: repeat(2, 1fr);
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }}:not(:last-child)  {
    border-right: {{ border_thickness_between_product }}px solid {{ border_color_between_product }};
  }

  .comparison-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: 0px 16px;
  }

  .comparison-item-product-image-{{ section.id }} {
    text-decoration: none;
    overflow: hidden;
    box-sizing: border-box !important;
    border-radius: {{ image_radius }}px;
    margin-top: {{ image_mt_mobile }}px;
    width: 100%;
    max-width: {{ image_width_mobile }}px;
  }

  .comparison-item-product-image-{{ section.id }} img,
  .comparison-item-product-image-{{ section.id }} svg {  
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    margin: 0; 
  }

  .comparison-item-product-image-{{ section.id }} svg {
    background: #cecece;
  }

  .comparison-item-{{ section.id }} {
    text-align: center;
  }

  .comparison-body-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 5px;
  }

  .comparison-body-item-{{ section.id }}:first-child {
    align-items: start;
  }

  .comparison-body-item-{{ section.id }}:last-child {
    align-items: end;
  }

  .comparison-item-product-title-{{ section.id }} {
    display: inline-block;
    margin: 0px;
    font-size: {{ product_title_size_mobile }}px;
    line-height: {{ product_title_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    font-weight: 700;
    text-align: center;
    text-decoration: none;
  }   

  .comparison-item-product-title-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ product_title_hover_color }};
  }

  .comparison-row-products-{{ section.id }} .comparison-item-product-title-{{ section.id }} {
    padding: {{ product_title_padding_vertical_mobile }}px {{ product_title_padding_horizontal_mobile }}px;
    border-radius: {{ product_title_radius }}px;
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }}:first-child .comparison-item-product-title-{{ section.id }} {
    color: {{ product_title_first_color }};
    background-color: {{ product_title_first_bg_color }};
    background-image: {{ product_title_first_bg_gradient }};
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }}:last-child .comparison-item-product-title-{{ section.id }}  {
    color: {{ product_title_second_color }};
    background-color: {{ product_second_title_bg_color }};
    background-image: {{ product_second_title_bg_gradient }};
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }}:first-child .comparison-item-product-title-{{ section.id }}:hover {
    color: {{ product_title_first_hover_color }};
  }

  .comparison-row-products-{{ section.id }} .comparison-item-{{ section.id }}:last-child .comparison-item-product-title-{{ section.id }}:hover {
    color: {{ product_title_second_hover_color }};
  }  

  .comparison-value-icon-{{ section.id }} {
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .comparison-value-icon-{{ section.id }} img,
  .comparison-value-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-value-{{ section.id }} {
    margin: 0px;
    font-size: {{ value_size_mobile }}px;
    color: {{ value_color }};
    line-height: {{ value_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    text-align: center;
  }

  .comparison-value-heading-{{ section.id }} {
    margin: 0px;
    font-size: {{ value_heading_size_mobile }}px;
    color: {{ value_heading_color }};
    line-height: {{ value_heading_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    text-align: center;
    font-weight: 700;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-row-{{ section.id }} {
      padding: {{ row_padding_vertical }}px 0px;
    }

    .comparison-value-icon-{{ section.id }} {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }


    .comparison-value-{{ section.id }} {
      font-size: {{ value_size }}px;
    }

    .comparison-value-heading-{{ section.id }} {
      font-size: {{ value_heading_size }}px;
    }

    .comparison-row-products-{{ section.id }} .comparison-item-product-title-{{ section.id }} {
      padding: {{ product_title_padding_vertical }}px {{ product_title_padding_horizontal }}px;
    }

    .comparison-item-product-title-{{ section.id }} {
      font-size: {{ product_title_size }}px;
    }

    .comparison-item-product-image-{{ section.id }} {
      margin-top: {{ image_mt }}px;
      max-width: {{ image_width }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if product_title_custom %}
  <style>
    .comparison-item-product-title-{{ section.id }} {
      font-family: {{ product_title_font.family }}, {{ product_title_font.fallback_families }};
      font-weight: {{ product_title_font.weight }};
      font-style: {{ product_title_font.style }};
    }
  </style>
{% endif %}

{% if value_custom %}
  <style>
    .comparison-value-{{ section.id }} {
      font-family: {{ value_font.family }}, {{ value_font.fallback_families }};
      font-weight: {{ value_font.weight }};
