  <style>
    @media (min-width: 1024px) {
      .comparison-item-product-image-{{ section.id }} {
        aspect-ratio: 9.6/13 !important;
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media (min-width: 1024px) {
      .comparison-item-product-image-{{ section.id }} {
        aspect-ratio: 12/8 !important;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media (min-width: 1024px) {
      .comparison-item-product-image-{{ section.id }} {
        aspect-ratio: 12/12 !important; 
      }
    }

  </style>
{% else %}
  <style>
    @media (min-width: 1024px) {
      .comparison-item-product-image-{{ section.id }} {
        aspect-ratio: auto !important;
      }
    }
  </style>
{% endif %} 


