{%- style -%}

  {{ heading_font | font_face: font_display: 'swap' }}
  {{ table_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_text_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    display: block;
    width: 100%;
  }

  .comparison-layout-{{ section.id }} {
    margin-top: {{ layout_mt_mobile }}px;
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile | plus: 10 }}px;
  }

  .comparison-media-{{ section.id }} {
    grid-area: video;
    overflow: hidden;
    border-radius: {{ media_radius }}px;
    border: {{ media_border_thickness }}px solid {{ media_border_color }};
    position: relative;
  }

  .comparison-media-{{ section.id }} video,
  .comparison-media-{{ section.id }} iframe,
  .comparison-media-{{ section.id }} img,
  .comparison-media-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    box-shadow: none;
    border: 0px;
  }

  .comparison-media-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .comparison-table-{{ section.id }} {
    grid-area: table;
    padding: {{ table_padding_vertical_mobile }}px {{ table_padding_horizontal_mobile }}px;
    border-radius: {{ table_radius }}px;
    background-color: {{ table_bg_color }};
  }

  .comparison-grid-{{ section.id }} {
    display: grid;
    grid-template-columns: {{ table_row_heading_width_mobile }}% {% if table_columns > 0 %}{{ 100 | minus: table_row_heading_width_mobile | divided_by: table_columns }}%{% endif %} {% if table_columns > 1 %}{{ 100 | minus: table_row_heading_width_mobile | divided_by: table_columns }}%{% endif %} {% if table_columns > 2 %}{{ 100 | minus: table_row_heading_width_mobile | divided_by: table_columns }}%{% endif %} {% if table_columns > 3 %}{{ 100 | minus: table_row_heading_width_mobile | divided_by: table_columns }}%{% endif %};
  }

  .comparison-item-{{ section.id }} {
    display: flex !important;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .comparison-item-{{ section.id }}:first-child {
    flex-direction: row;
    gap: 20px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: {{ table_heading_padding_vertical_mobile }}px {{ table_heading_padding_horizontal_mobile }}px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active {
    background-color: {{ table_heading_bg_active_color }};
    position: relative;
    border-left: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-right: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active:before {
    content: '';
    position: absolute;
    left: -{{ table_column_active_border_thickness }}px;
    top: calc(({{ table_padding_vertical_mobile }}px + 10px) * -1);
    display: block;
    width: calc(100% + {{ table_column_active_border_thickness | times: 2 }}px);
    height: {{ table_padding_vertical_mobile | plus: 10 }}px;
    border-top-left-radius: {{ table_column_active_radius }}px;
    border-top-right-radius: {{ table_column_active_radius }}px;
    background-color: {{ table_heading_bg_active_color }};
    border: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-bottom: 0px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
    margin: 0;
    font-size: {{ table_heading_size_mobile }}px;
    color: {{ table_heading_color }};
    line-height: {{ table_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_heading_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active h3 {
    color: {{ table_heading_active_color }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} img {
    display: block;
    width: {{ table_heading_image_size_mobile }}px;
    height: 100%;
    object-fit: contain;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
     padding: {{ table_row_padding_vertical_mobile }}px {{ table_row_padding_horizontal_mobile }}px;
  }

  .comparison-grid-{{ section.id }}:not(:last-child) .comparison-item-{{ section.id }} {
    border-bottom: {{ table_row_border_thickness }}px solid {{ table_row_border_color }};
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}.active {
    background-color: {{ table_column_active_bg_color }};
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}.active {
    border-left: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-right: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }}.active {
    position: relative;
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }}.active:before {
    content: '';
    background-color: {{ table_column_active_bg_color }};
    position: absolute;
    left: -{{ table_column_active_border_thickness }}px;
    top: 100%;
    display: block;
    width: calc(100% + {{ table_column_active_border_thickness | times: 2 }}px);
    height: {{ table_padding_vertical_mobile | plus: 10 }}px;
    border: {{ table_column_active_border_thickness }}px solid {{ table_column_active_border_color }};
    border-top: 0px;
    border-bottom-left-radius: {{ table_column_active_radius }}px;
    border-bottom-right-radius: {{ table_column_active_radius }}px;
  }

  .comparison-item-heading-image-{{ section.id }} {
    width: {{ table_row_heading_image_size_mobile }}px;
    height: {{ table_row_heading_image_size_mobile }}px;
  }

  .comparison-item-heading-image-{{ section.id }} img {
    display: block;
    object-fit: contain;
    width: 100%;
    height: 100%;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
    margin: 0;
    font-size: {{ table_row_heading_size_mobile }}px;
    color: {{ table_row_heading_color }};
    line-height: {{ table_row_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_row_heading_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-item-text-{{ section.id }} {
    margin: 0;
    font-size: {{ table_row_text_size_mobile }}px;
    color: {{ table_row_text_color }};
    line-height: {{ table_row_text_height }}%;
    text-transform: unset;
    text-align: {{ table_row_text_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-item-{{ section.id }}.active .comparison-item-text-{{ section.id }} {
    color: {{ table_row_active_text_color }};
  }

  .comparison-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 10px;
    box-sizing: border-box;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    font-weight: 700;
  }

  .comparison-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .comparison-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .comparison-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-icon-{{ section.id }} {
    flex-shrink: 0;
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .comparison-item-{{ section.id }}.active .comparison-icon-{{ section.id }} {
    width: {{ icon_size_mobile | plus: 10 }}px;
    height: {{ icon_size_mobile | plus: 10 }}px;
  }

  .comparison-icon-{{ section.id }} img,
  .comparison-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-yes-icon-{{ section.id }} circle,
  .comparison-yes-icon-{{ section.id }} path {
    fill: {{ icon_yes_bg_color }};
  }

  .comparison-yes-icon-{{ section.id }} polygon {
    fill: {{ icon_yes_color }}
  }

  .comparison-no-icon-{{ section.id }} path {
    stroke: {{ icon_no_color }}
  }

