      grid-template-columns: 1fr {% if table_columns > 0 %}1fr{% endif %} {% if table_columns > 1 %}1fr{% endif %} {% if table_columns > 2 %}1fr{% endif %} {% if table_columns > 3 %}1fr{% endif %};
