{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign table_columns = section.settings.table_columns
  assign table_columns_mobile = section.settings.table_columns_mobile
  assign table_gap = section.settings.table_gap
  assign table_gap_mobile = section.settings.table_gap_mobile

  assign table_column_bg_color = section.settings.table_column_bg_color
  assign table_column_active_bg_color = section.settings.table_column_active_bg_color
  assign table_radius = section.settings.table_radius
  assign table_column_border_thickness = section.settings.table_column_border_thickness
  assign table_column_border_active_color = section.settings.table_column_border_active_color
  assign table_column_border_color = section.settings.table_column_border_color

  assign row_image = section.settings.row_image
  assign row_heading = section.settings.row_heading
  assign first_heading = section.settings.first_heading
  assign first_image = section.settings.first_image
  assign second_heading = section.settings.second_heading
  assign second_image = section.settings.second_image
  assign third_heading = section.settings.third_heading
  assign third_image = section.settings.third_image
  assign fourth_heading = section.settings.fourth_heading
  assign fourth_image = section.settings.fourth_image
  assign fifth_heading = section.settings.fifth_heading
  assign fifth_image = section.settings.fifth_image

  assign image_width_mobile = section.settings.image_width_mobile
  assign image_width = section.settings.image_width   
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_radius = section.settings.image_radius

  assign row_image_width_mobile = section.settings.row_image_width_mobile
  assign row_image_width = section.settings.row_image_width   
  assign row_image_border_thickness = section.settings.row_image_border_thickness
  assign row_image_radius = section.settings.row_image_radius

  assign value_image_width_mobile = section.settings.value_image_width_mobile
  assign value_image_width = section.settings.value_image_width  
  assign value_image_border_thickness = section.settings.value_image_border_thickness
  assign value_image_radius = section.settings.value_image_radius 

  assign column_heading_size = section.settings.column_heading_size
  assign column_heading_size_mobile = section.settings.column_heading_size_mobile
  assign column_heading_color = section.settings.column_heading_color
  assign column_heading_active_color = section.settings.column_heading_active_color
  assign column_heading_custom = section.settings.column_heading_custom
  assign column_heading_font = section.settings.column_heading_font
  assign column_heading_height = section.settings.column_heading_height
  assign column_heading_align = section.settings.column_heading_align
  assign column_heading_align_mobile = section.settings.column_heading_align_mobile
  assign column_heading_padding_horizontal = section.settings.column_heading_padding_horizontal
  assign column_heading_padding_horizontal_mobile = section.settings.column_heading_padding_horizontal_mobile
  assign column_heading_padding_vertical = section.settings.column_heading_padding_vertical
  assign column_heading_padding_vertical_mobile = section.settings.column_heading_padding_vertical_mobile

  assign column_heading_horizontal_align = ""
  if column_heading_align == "center"
    assign column_heading_horizontal_align = "center"
  elsif column_heading_align == "right"
    assign column_heading_horizontal_align = "end"
  else
    assign column_heading_horizontal_align = "start"
  endif

  assign column_heading_horizontal_align_mobile = ""
  if column_heading_align_mobile == "center"
    assign column_heading_horizontal_align_mobile = "center"
  elsif column_heading_align_mobile == "right"
    assign column_heading_horizontal_align_mobile = "end"
  else
    assign column_heading_horizontal_align_mobile = "start"
  endif

  assign row_padding_horizontal = section.settings.row_padding_horizontal
  assign row_padding_horizontal_mobile = section.settings.row_padding_horizontal_mobile
  assign row_padding_vertical = section.settings.row_padding_vertical
  assign row_padding_vertical_mobile = section.settings.row_padding_vertical_mobile
  assign row_border_color = section.settings.row_border_color

  assign value_size = section.settings.value_size
  assign value_size_mobile = section.settings.value_size_mobile
  assign value_color = section.settings.value_color
  assign value_custom = section.settings.value_custom
  assign value_font = section.settings.value_font
  assign value_height = section.settings.value_height
  assign value_align = section.settings.value_align
  assign value_align_mobile = section.settings.value_align_mobile

  assign value_horizontal_align = ""
  if value_align == "center"
    assign value_horizontal_align = "center"
  elsif value_align == "right"
    assign value_horizontal_align = "end"
  else
    assign value_horizontal_align = "start"
  endif

  assign value_horizontal_align_mobile = ""
  if value_align_mobile == "center"
    assign value_horizontal_align_mobile = "center"
  elsif value_align_mobile == "right"
    assign value_horizontal_align_mobile = "end"
  else
    assign value_horizontal_align_mobile = "start"
  endif

  assign row_heading_size = section.settings.row_heading_size
  assign row_heading_size_mobile = section.settings.row_heading_size_mobile
  assign row_heading_color = section.settings.row_heading_color
  assign row_heading_custom = section.settings.row_heading_custom
  assign row_heading_font = section.settings.row_heading_font
  assign row_heading_height = section.settings.row_heading_height
  assign row_heading_align = section.settings.row_heading_align
  assign row_heading_align_mobile = section.settings.row_heading_align_mobile

  assign row_heading_horizontal_align = ""
  if row_heading_align == "center"
    assign row_heading_horizontal_align = "center"
  elsif row_heading_align == "right"
    assign row_heading_horizontal_align = "end"
  else
    assign row_heading_horizontal_align = "start"
  endif

  assign row_heading_horizontal_align_mobile = ""
  if row_heading_align_mobile == "center"
    assign row_heading_horizontal_align_mobile = "center"
  elsif row_heading_align_mobile == "right"
    assign row_heading_horizontal_align_mobile = "end"
  else
    assign row_heading_horizontal_align_mobile = "start"
  endif

  assign value_icon_size = section.settings.value_icon_size
  assign value_icon_size_mobile = section.settings.value_icon_size_mobile
  assign value_cross_color = section.settings.value_cross_color
  assign value_check_color = section.settings.value_check_color
-%}

{%- style -%} 
  {{ column_heading_font | font_face: font_display: 'swap' }}
  {{ row_heading_font | font_face: font_display: 'swap' }}
  {{ value_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }    

  .comparison-grid-{{ section.id }} {
    display: flex;
    gap: {{ table_gap_mobile }}px;
  }

  .comparison-table-wrapper-{{ section.id }} {
    overflow-x: scroll;
    scrollbar-width: none;
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
  }

  .comparison-table-inner-{{ section.id }} {
    min-width: 724px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-table-wrapper-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .comparison-table-{{ section.id }} {
    margin-top: {{ table_mt_mobile }}px;
  }

  .comparison-item-{{ section.id }} {
    position: relative;
    display: flex !important;
    flex: 1;
    gap: 20px;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-decoration: none;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
    justify-content: space-between;
    padding: {{ column_heading_padding_vertical_mobile }}px {{ column_heading_padding_horizontal_mobile }}px;  
    align-items: {{ column_heading_horizontal_align_mobile }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
    border-top-right-radius: {{ table_radius }}px;
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }} {
    border-bottom-right-radius: {{ table_radius }}px;
    border-bottom-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-header-{{ section.id }}:not(:first-child) .comparison-item-{{ section.id }} {
    border: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_color }}
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:not(:first-child),
  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
    background-color: {{ table_column_bg_color }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}.active {    
    background-color: {{ table_column_active_bg_color }};    
    border: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }}
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {    
    border-top-right-radius: {{ table_radius }}px;
    border-top-left-radius: {{ table_radius }}px;
    background-color: {{ table_column_active_bg_color }};    
    border: {{ table_column_border_thickness }}px solid {{ table_column_border_color }}
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
    border-left: {{ table_column_border_thickness }}px solid {{ table_column_border_color }};
    border-right: {{ table_column_border_thickness }}px solid {{ table_column_border_color }};
    border-bottom: {{ table_column_border_thickness }}px solid {{ table_column_border_color }}
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }} {
    border-left: {{ table_column_border_thickness }}px solid {{ table_column_border_color }};
    border-right: {{ table_column_border_thickness }}px solid {{ table_column_border_color }};
    border-bottom: {{ table_column_border_thickness }}px solid {{ table_column_border_color }}
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}.active {
    border-left: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }};
    border-right: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }};
    border-bottom: 0px;
  }

  .comparison-grid-rows-{{ section.id }}:not(:last-child) .comparison-item-{{ section.id }}.active::after {
    content: '';
    position: absolute;
    width: 100%;
    height: {{ table_column_border_thickness }}px;
    background-color: {{ table_column_border_color }};
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }}.active {
    border-left: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }};
    border-right: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }};
    border-bottom: {{ table_column_border_thickness | plus: 2 }}px solid {{ table_column_border_active_color }}
  }

  .comparison-column-heading-{{ section.id }} {
    margin: 0;
    width: 100%;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
    text-align: {{ column_heading_align_mobile }};
    text-decoration: none;
    font-weight: 700;
  }

  .comparison-item-{{ section.id }}.active .comparison-column-heading-{{ section.id }} {
    color: {{ column_heading_active_color }};
  }

  .comparison-item-{{ section.id }}.active .comparison-value-{{ section.id }} {
    font-size: {{ value_size_mobile | plus: 2 }}px;
    font-weight: 700;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: {{ row_padding_vertical_mobile }}px {{ row_padding_horizontal_mobile }}px;
    display: flex;
    flex-direction: column;
    align-items: {{ value_horizontal_align_mobile }};
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(1) {
    border-top-left-radius: {{ table_radius }}px;
    {% if table_columns > 1 %}
    border-top-right-radius: {{ table_radius }}px;
    {% endif %}
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(3) {
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }}:last-child:after {
    width: calc(100% + 25px);
    border-bottom-right-radius: {{ table_radius }}px;
  }

  .comparison-item-heading-image-{{ section.id }} {
    display: block;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    width: 100%;
    max-width: {{ image_width_mobile }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .comparison-item-image-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: {{ value_image_width_mobile }}px;
    border: {{ value_image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ value_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .comparison-row-image-{{ section.id }} {
    display: block;
    border: {{ row_image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ row_image_radius }}px;
    width: 100%;
    max-width: {{ row_image_width_mobile }}px;
    overflow: hidden;
    aspect-ratio: 1/1;
  }

  .comparison-item-heading-image-{{ section.id }} img,
  .comparison-item-heading-image-{{ section.id }} svg,
  .comparison-item-image-{{ section.id }} img,
  .comparison-item-image-{{ section.id }} svg,
  .comparison-row-image-{{ section.id }} img,
  .comparison-row-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-row-image-{{ section.id }} svg {
    background-color: #AFAFAF
  }

  .comparison-item-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .comparison-item-heading-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .comparison-row-heading-{{ section.id }} {
    margin: 0;
    text-align: {{ row_heading_align_mobile }};
    font-size: {{ row_heading_size_mobile }}px;
    color: {{ row_heading_color }};
    line-height: {{ row_heading_height }}%;
    word-break: break-word;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .comparison-value-{{ section.id }} {
    margin: 0;
    text-align: {{ value_align_mobile }};
    font-size: {{ value_size_mobile }}px;
    color: {{ value_color }};
    line-height: {{ value_height }}%;
    word-break: break-word;
    text-transform: unset;
  }

  .comparison-item-{{ section.id }}.active .comparison-value-{{ section.id }} {
    font-size: {{ value_size_mobile | plus: 2 }}px;
    font-weight: 400;
  }

  .comparison-value-icon-{{ section.id }} svg {
    width: {{ value_icon_size_mobile }}px !important;
    height: {{ value_icon_size_mobile }}px !important;    
  }

  .comparison-value-icon-{{ section.id }} img,
  .comparison-value-icon-{{ section.id }} svg {
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
  }

  .comparison-row-item-{{ section.id }} {
    align-items: {{ row_heading_horizontal_align_mobile }} !important;
  }

  .comparison-value-check-{{ section.id }} path {
    stroke: {{ value_check_color }};
  }

  .comparison-value-cross-{{ section.id }} path {
    stroke: {{ value_cross_color }};
  }

  @media(max-width: 1024px) {      
    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(n+{{ table_columns_mobile | plus: 1 }}) {
      display: none !important;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(n+{{ table_columns_mobile | plus: 1 }}) {
      display: none !important;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }  

    .comparison-grid-{{ section.id }} {
      gap: {{ table_gap }}px;
    }

    .comparison-table-wrapper-{{ section.id }} {
      margin-left: 0rem;
      margin-right: 0rem;
    }

    .comparison-table-inner-{{ section.id }} {
      padding-left: 0rem;
      padding-right: 0rem;
    }

    .comparison-item-heading-image-{{ section.id }} {
      max-width: {{ image_width }}px;
    }

    .comparison-item-image-{{ section.id }} {
      max-width: {{ value_image_width }}px;
    }

    .comparison-row-image-{{ section.id }} {
      max-width: {{ row_image_width }}px;
    }

    .comparison-row-item-{{ section.id }} {
      align-items: {{ row_heading_horizontal_align }} !important;
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ column_heading_padding_vertical }}px {{ column_heading_padding_horizontal }}px;
      align-items: {{ column_heading_horizontal_align }};
    }

    .comparison-column-heading-{{ section.id }} {
      text-align: {{ column_heading_align }};
      font-size: {{ column_heading_size }}px;
    }

     .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(n+{{ table_columns | plus: 1 }}) {
      display: none !important;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(n+{{ table_columns | plus: 1 }}) {
      display: none !important;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ row_padding_vertical }}px {{ row_padding_horizontal }}px;
      align-items: {{ value_horizontal_align }};
    }

    .comparison-row-heading-{{ section.id }}{
      font-size: {{ row_heading_size }}px;
    }

    .comparison-value-{{ section.id }} {
      text-align: {{ value_align }};
      font-size: {{ value_size }}px;
    }

    .comparison-item-{{ section.id }}.active .comparison-value-{{ section.id }} {
      font-size: {{ value_size | plus: 2 }}px;
    }

    .comparison-row-heading-{{ section.id }} {
      text-align: {{ row_heading_align }};
      font-size: {{ row_heading_size }}px;
    }

    .comparison-value-icon-{{ section.id }} svg {
      width: {{ value_icon_size }}px !important;
      height: {{ value_icon_size }}px !important;
    }


  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .comparison-grid-header-{{ section.id }} .comparison-column-heading-{{ section.id }} {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if row_heading_custom %}
  <style>
    .comparison-row-heading-{{ section.id }} {
      font-family: {{ row_heading_font.family }}, {{ row_heading_font.fallback_families }};
      font-weight: {{ row_heading_font.weight }};
      font-style: {{ row_heading_font.style }};
    }
  </style>
{% endif %}

{% if value_custom %}
  <style>
    .comparison-value-{{ section.id }} {
      font-family: {{ value_font.family }}, {{ value_font.fallback_families }};
      font-weight: {{ value_font.weight }};
      font-style: {{ value_font.style }};
    }
  </style>
{% endif %}

{% if table_columns_mobile <= 2 %}
  <style>
    .comparison-table-inner-{{ section.id }} {
      min-width: auto;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  <div class="section-{{ section.id }}-settings">
    <div class="comparison-table-wrapper-{{ section.id }}">
      <div class="comparison-table-inner-{{ section.id }}">
        <div class="comparison-table-{{ section.id }}">
          <div class="comparison-grid-{{ section.id }} comparison-grid-header-{{ section.id }}">
            <div class="comparison-item-{{ section.id }} active">
              {% if row_image != blank %}    
                <div class="comparison-item-heading-image-{{ section.id }}">              
                  <img src="{{ row_image | img_url: "400x400" }}" alt="{{ row_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>              
                </div>  
              {% endif %}  
              <h3 class="comparison-column-heading-{{ section.id }}">{{ row_heading }}</h3>
            </div>
            {% if first_heading != blank or first_image != blank %}
              <div class="comparison-item-{{ section.id }}">
                {% if first_image != blank %}    
                  <div class="comparison-item-heading-image-{{ section.id }}">              
                    <img src="{{ first_image | img_url: "400x400" }}" alt="{{ first_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>              
                  </div>  
                {% endif %}  
                <h3 class="comparison-column-heading-{{ section.id }}">{{ first_heading }}</h3>
              </div>
            {% endif %}
            {% if second_heading != blank %}
              <div class="comparison-item-{{ section.id }}">          
                {% if second_image != blank %}    
                  <div class="comparison-item-heading-image-{{ section.id }}">              
                    <img src="{{ second_image | img_url: "400x400" }}" alt="{{ second_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>              
                  </div>  
                {% endif %}             
                <h3 class="comparison-column-heading-{{ section.id }}">{{ second_heading }}</h3>                  
