{%- style -%}
  {{ table_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_heading_font | font_face: font_display: 'swap' }}
  {{ table_row_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-layout-{{ section.id }} {
    border-radius: {{ layout_radius }}px;
    background-color: {{ layout_bg_color }};
    padding: {{ layout_padding_vertical_mobile }}px {{ layout_padding_horizontal_mobile }}px;
  }

  .comparison-table-{{ section.id }} {
    grid-area: table;
    margin-top: {{ table_mt_mobile }}px;
  }

  .comparison-grid-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr {% if table_columns > 0 and first_heading_hide_mobile == false %}1fr{% endif %} {% if table_columns > 1 and second_heading_hide_mobile == false %}1fr{% endif %} {% if table_columns > 2 and third_heading_hide_mobile == false %}1fr{% endif %} {% if table_columns > 3 and fourth_heading_hide_mobile == false %}1fr{% endif %} {% if table_columns > 4 and fifth_heading_hide_mobile == false %}1fr{% endif %} {% if table_columns > 5 and sixth_heading_hide_mobile == false %}1fr{% endif %};
  }

  .comparison-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: all 0.25s ease 0s;
  }

  .comparison-item-{{ section.id }}:first-child {
    gap: 20px;
    background-color: transparent !important;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: {{ table_heading_padding_vertical_mobile }}px {{ table_heading_padding_horizontal_mobile }}px;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
    margin: 0;
    font-size: {{ table_heading_size_mobile }}px;
    color: {{ table_heading_color }};
    line-height: {{ table_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_heading_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} img {
    display: block;
    width: {{ table_heading_image_size_mobile }}px;
    height: 100%;
    object-fit: contain;
  }

  .comparison-grid-{{ section.id }}:not(:first-child) .comparison-item-{{ section.id }}.first-visible-item {
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}.first-visible-item {
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}.first-visible-item {
    border-bottom-left-radius: {{ table_radius }}px;
  }

  .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}.last-visible-item {
    border-top-right-radius: {{ table_radius }}px;
  }

  .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}.last-visible-item {
    border-bottom-right-radius: {{ table_radius }}px;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:not(:first-child) {
    border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-right: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:nth-child(2) {
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-rows-{{ section.id }}:last-child .comparison-item-{{ section.id }}:not(:first-child) {
    border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
    padding: {{ table_row_padding_vertical_mobile }}px {{ table_row_padding_horizontal_mobile }}px;
    text-decoration: none;
  }

  .comparison-grid-rows-{{ section.id }}.active .comparison-item-{{ section.id }}:not(:first-child) {
    background-color: {{ table_row_active_bg_color }};
  }

  .comparison-item-heading-image-{{ section.id }} img {
    display: block;
    object-fit: contain;
    width: 100%;
    height: 100%;
  }

  .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
    margin: 0;
    font-size: {{ table_row_heading_size_mobile }}px;
    color: {{ table_row_heading_color }};
    line-height: {{ table_row_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_row_heading_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-item-text-{{ section.id }} {
    margin: 0;
    font-size: {{ table_row_text_size_mobile }}px;
    color: {{ table_row_text_color }};
    line-height: {{ table_row_text_height }}%;
    text-transform: unset;
    text-align: {{ table_row_text_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .comparison-grid-rows-{{ section.id }}.active .comparison-item-text-{{ section.id }} {
    color: {{ table_row_active_text_color }};
  }

  .comparison-icon-{{ section.id }} {
    flex-shrink: 0;
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .comparison-icon-{{ section.id }} img,
  .comparison-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-yes-icon-{{ section.id }} path {
    stroke: {{ icon_yes_color }}
  }

  .comparison-no-icon-{{ section.id }} path {
    stroke: {{ icon_no_color }}
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-item-heading-image-{{ section.id }} img {
      width: {{ row_heading_image_size }}px;
    }

    .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}.first-visible-item {
      border-top-left-radius: 0px;
    }

    .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}.first-visible-item {
      border-bottom-left-radius: 0px;
    }

    .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}.last-visible-item {
      border-top-right-radius: 0px;
    }

    .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}.last-visible-item {
      border-bottom-right-radius: 0px;
    }

    .comparison-grid-{{ section.id }}:nth-child(2) {
      border-top-right-radius: {{ table_radius }}px;
    }

    .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}:last-child {
      border-top-right-radius: {{ table_radius }}px;
    }

    .comparison-grid-{{ section.id }}:nth-child(2) .comparison-item-{{ section.id }}:nth-child(2) {
      border-top-left-radius: {{ table_radius }}px;
    }

    .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}:nth-child(2) {
      border-bottom-left-radius: {{ table_radius }}px;
    }

    .comparison-grid-{{ section.id }}:last-child .comparison-item-{{ section.id }}:last-child {
      border-bottom-right-radius: {{ table_radius }}px !important;
    }

    .comparison-grid-{{ section.id }}:not(:first-child) .comparison-item-{{ section.id }}:last-child {
      border-right: {{ table_border_thickness }}px solid {{ table_border_color }};
    } 

    .comparison-layout-{{ section.id }} {
      padding: {{ layout_padding_vertical }}px {{ layout_padding_horizontal }}px;
    }

    .comparison-table-{{ section.id }} {
      margin-top: {{ table_mt }}px;
    }

    .comparison-item-{{ section.id }} {
      display: flex !important;
    }

    .comparison-grid-{{ section.id }} {
      grid-template-columns: 1fr {% if table_columns > 0 %}1fr{% endif %} {% if table_columns > 1 %}1fr{% endif %} {% if table_columns > 2 %}1fr{% endif %} {% if table_columns > 3 %}1fr{% endif %} {% if table_columns > 4 %}1fr{% endif %} {% if table_columns > 5 %}1fr{% endif %};
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} img {
      width: {{ table_heading_image_size }}px;
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ table_heading_padding_vertical }}px {{ table_heading_padding_horizontal }}px;
    }

    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
      text-align: {{ table_heading_align }};
      font-size: {{ table_heading_size }}px;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }} {
      padding: {{ table_row_padding_vertical }}px {{ table_row_padding_horizontal }}px;
    }

    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
      text-align: {{ table_row_heading_align }};
      font-size: {{ table_row_heading_size }}px;
    }

    .comparison-item-text-{{ section.id }} {
      font-size: {{ table_row_text_size }}px;
      text-align: {{ table_row_text_align }};
    }

    .comparison-icon-{{ section.id }} {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .comparison-grid-rows-{{ section.id }}:not(.active):hover .comparison-item-{{ section.id }}:not(:first-child) {
      background-color: {{ table_row_other_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .comparison-grid-rows-{{ section.id }}:not(.active) .comparison-item-{{ section.id }}:hover {
      background-color: {{ table_row_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .comparison-grid-rows-{{ section.id }}.active .comparison-item-{{ section.id }}:hover {
      background-color: {{ table_row_active_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if table_heading_custom %}
  <style>
    .comparison-grid-header-{{ section.id }} .comparison-item-{{ section.id }} h3 {
      font-family: {{ table_heading_font.family }}, {{ table_heading_font.fallback_families }};
      font-weight: {{ table_heading_font.weight }};
      font-style: {{ table_heading_font.style }};
    }
  </style>
{% endif %}

{% if table_row_heading_custom %}
  <style>
    .comparison-grid-rows-{{ section.id }} .comparison-item-{{ section.id }}:first-child h3 {
      font-family: {{ table_row_heading_font.family }}, {{ table_row_heading_font.fallback_families }};
      font-weight: {{ table_row_heading_font.weight }};
      font-style: {{ table_row_heading_font.style }};
    }
  </style>
{% endif %}

{% if table_row_text_custom %}
  <style>
    .comparison-item-text-{{ section.id }} {
      font-family: {{ table_row_text_font.family }}, {{ table_row_text_font.fallback_families }};
      font-weight: {{ table_row_text_font.weight }};
      font-style: {{ table_row_text_font.style }};
    }
  </style>
{% endif %}

{% if first_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(2) {
      display: none;
    }
  </style>
{% endif %}

{% if second_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(3) {
      display: none;
    }
  </style>
{% endif %}

{% if third_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(4) {
      display: none;
    }
  </style>
{% endif %}

{% if fourth_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(5) {
      display: none;
    }
  </style>
{% endif %}

{% if fifth_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(6) {
      display: none;
    }
  </style>
{% endif %}

{% if sixth_heading_hide_mobile %}
  <style>
    .comparison-item-{{ section.id }}:nth-child(7) {
      display: none;
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == "heading" %}
    <style>

      {{ block.settings.heading_font | font_face: font_display: 'swap' }}

      .comparison-heading-{{ section.id }} {
        text-align: {{ block.settings.heading_align_mobile }};
      }

      .comparison-heading-{{ section.id }} * {
        margin: 0;
        font-size: {{ block.settings.heading_size_mobile }}px;
        color: {{ block.settings.heading_color }};
        line-height: {{ block.settings.heading_height }}%;
        text-transform: unset;
        display: block;
        width: 100%;
      }

      @media(min-width: 1024px) {
        .comparison-heading-{{ section.id }} {
          text-align: {{ block.settings.heading_align }};
        }

        .comparison-heading-{{ section.id }} * {
          font-size: {{ block.settings.heading_size }}px;
        } 
      }

    </style>

    {% if block.settings.heading_custom %}
      <style>
        .comparison-heading-{{ section.id }} * {
          font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
          font-weight: {{ block.settings.heading_font.weight }};
          font-style: {{ block.settings.heading_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "text" %}
    <style>

      {{ block.settings.text_font | font_face: font_display: 'swap' }}

      .comparison-text-{{ section.id }} {
        margin-left: {{ block.settings.text_ml_mobile }}px;
        margin-top: {{ block.settings.text_mt_mobile }}px;
        text-align: {{ block.settings.text_align_mobile }};
      }

      .comparison-text-{{ section.id }} * {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
        display: block;
        width: 100%;
      }

      @media(min-width: 1024px) {
        .comparison-text-{{ section.id }} {
          margin-left: {{ block.settings.text_ml }}px;
          margin-top: {{ block.settings.text_mt }}px;
          text-align: {{ block.settings.text_align }};
        }

        .comparison-text-{{ section.id }} * {
          font-size: {{ block.settings.text_size }}px;
        } 
      }

    </style>

    {% if block.settings.text_custom %}
      <style>
        .comparison-text-{{ section.id }} * {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="comparison-layout-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.type == "heading" %}
            {% if block.settings.heading != blank %}
              <div class="comparison-heading-{{ section.id }}">{{ block.settings.heading }}</div>
            {% endif %}
          {% endif %}
        {% endfor %}
        <div class="comparison-table-{{ section.id }}">
          <div class="comparison-grid-{{ section.id }} comparison-grid-header-{{ section.id }}">
            <div class="comparison-item-{{ section.id }}" style="display: block !important;"></div>
            {% if first_heading != blank or first_heading_image != blank %}
              <div class="comparison-item-{{ section.id }}">
                {% if first_heading_image != blank %}
                    <img src="{{ first_heading_image | image_url }}" alt="{{ first_heading_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    <h3>{{ first_heading }}</h3>
                  {% endif %}
              </div>
            {% endif %}
            {% if table_columns > 1 and second_heading != blank or second_heading_image != blank %}
              <div class="comparison-item-{{ section.id }}">
                {% if second_heading_image != blank %}
                    <img src="{{ second_heading_image | image_url }}" alt="{{ second_heading_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    <h3>{{ second_heading }}</h3>
                  {% endif %}
              </div>
