<script>
  function initComparisonTable19() {
    const rows = document.querySelectorAll('.comparison-grid-rows-{{ section.id }}');

    rows.forEach(row => {
      const items = row.querySelectorAll('.comparison-item-{{ section.id }}');

      items.forEach((item, index) => {
        if (index === 0) return;

        item.addEventListener('mouseenter', () => {
          const isActiveRow = row.classList.contains('active');

          rows.forEach(otherRow => {
            const sameColumnItem = otherRow.querySelectorAll('.comparison-item-{{ section.id }}')[index];
            if (sameColumnItem) {
              if (otherRow === row) {
                sameColumnItem.style.backgroundColor = isActiveRow ? 
                  '{{ table_row_active_bg_hover_color }}' : 
                  '{{ table_row_bg_hover_color }}';
              } else if (otherRow.classList.contains('active')) {
                sameColumnItem.style.backgroundColor = '{{ table_row_active_bg_hover_color }}';
              } else {
                sameColumnItem.style.backgroundColor = '{{ table_row_other_bg_hover_color }}';
              }
            }
          });
        });

        item.addEventListener('mouseleave', () => {
          rows.forEach(otherRow => {
            const sameColumnItem = otherRow.querySelectorAll('.comparison-item-{{ section.id }}')[index];
            if (sameColumnItem) {
              sameColumnItem.style.backgroundColor = '';
            }
          });
        });
      });
    });

    const updateVisibleItems = () => {
      const grids = document.querySelectorAll('.comparison-grid-{{ section.id }}');

      grids.forEach(grid => {
        const items = grid.querySelectorAll('.comparison-item-{{ section.id }}');
        const visibleItems = Array.from(items).filter(item => {
          return window.getComputedStyle(item).display !== 'none';
        });

        items.forEach(item => {
          item.classList.remove('first-visible-item', 'last-visible-item');
        });

        if (visibleItems.length > 0) {
          const firstVisibleItem = visibleItems[1];
          firstVisibleItem.classList.add('first-visible-item');
          const lastVisibleItem = visibleItems[visibleItems.length - 1];
          lastVisibleItem.classList.add('last-visible-item');
        }
      });
    };

    updateVisibleItems();
    window.addEventListener('resize', updateVisibleItems);
  }

  document.addEventListener('DOMContentLoaded', initComparisonTable19);
</script>
