{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius  
  assign background_style = section.settings.background_style

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile  
  assign table_width = section.settings.table_width

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign table_radius = section.settings.table_radius
  assign table_border_thickness = section.settings.table_border_thickness
  assign table_border_style = section.settings.table_border_style
  assign table_border_color = section.settings.table_border_color
  assign table_bg_color = section.settings.table_bg_color
  assign table_bg_opacity = section.settings.table_bg_opacity

  assign column_width_mobile = section.settings.column_width_mobile

  assign column_heading_size = section.settings.column_heading_size
  assign column_heading_size_mobile = section.settings.column_heading_size_mobile
  assign column_heading_color = section.settings.column_heading_color
  assign column_heading_active_color = section.settings.column_heading_active_color
  assign column_heading_custom = section.settings.column_heading_custom
  assign column_heading_font = section.settings.column_heading_font
  assign column_heading_height = section.settings.column_heading_height
  assign column_heading_align = section.settings.column_heading_align
  assign column_heading_align_mobile = section.settings.column_heading_align_mobile

  assign column_header_height = section.settings.column_header_height
  assign column_header_height_mobile = section.settings.column_header_height_mobile

  assign row_height = section.settings.row_height
  assign row_height_mobile = section.settings.row_height_mobile

  assign first_row_heading = section.settings.first_row_heading
  assign second_row_heading = section.settings.second_row_heading
  assign third_row_heading = section.settings.third_row_heading
  assign fourth_row_heading = section.settings.fourth_row_heading
  assign fifth_row_heading = section.settings.fifth_row_heading

  assign row_heading_size = section.settings.row_heading_size
  assign row_heading_size_mobile = section.settings.row_heading_size_mobile
  assign row_heading_color = section.settings.row_heading_color
  assign row_heading_custom = section.settings.row_heading_custom
  assign row_heading_font = section.settings.row_heading_font
  assign row_heading_height = section.settings.row_heading_height
  assign row_heading_align = section.settings.row_heading_align
  assign row_heading_align_mobile = section.settings.row_heading_align_mobile

  assign row_text_size = section.settings.row_text_size
  assign row_text_size_mobile = section.settings.row_text_size_mobile
  assign row_text_color = section.settings.row_text_color
  assign row_active_text_color = section.settings.row_active_text_color
  assign row_text_custom = section.settings.row_text_custom
  assign row_text_font = section.settings.row_text_font
  assign row_text_height = section.settings.row_text_height
  assign row_text_align = section.settings.row_text_align
  assign row_text_align_mobile = section.settings.row_text_align_mobile

  assign row_text_horizontal_align = ""
  if row_text_align == "center"
    assign row_text_horizontal_align = "center"
  elsif row_text_align == "right"
    assign row_text_horizontal_align = "end"
  else
    assign row_text_horizontal_align = "start"
  endif

  assign row_text_horizontal_align_mobile = ""
  if row_text_align_mobile == "center"
    assign row_text_horizontal_align_mobile = "center"
  elsif row_text_align_mobile == "right"
    assign row_text_horizontal_align_mobile = "end"
  else
    assign row_text_horizontal_align_mobile = "start"
  endif

  assign value_icon_size = section.settings.value_icon_size
  assign value_icon_size_mobile = section.settings.value_icon_size_mobile
  assign value_cross_color = section.settings.value_cross_color
  assign value_cross_active_color = section.settings.value_cross_active_color
  assign value_check_color = section.settings.value_check_color
  assign value_check_active_color = section.settings.value_check_active_color

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}

  {{ column_heading_font | font_face: font_display: 'swap' }}
  {{ row_heading_font | font_face: font_display: 'swap' }}
  {{ row_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    position: relative;
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
    z-index: 1;
  }

  .comparison-body-{{ section.id }} {
    display: grid;
    gap: {{ layout_gap_mobile }}px;
    grid-template-columns: 1fr;
  }

  .comparison-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};
    grid-area: content;
  }

  .comparison-table-wrapper-{{ section.id }} {
    grid-area: table;
    overflow-x: scroll;
    scrollbar-width: none;
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    padding-top: 0;
    padding-bottom: 0;
  }

  .comparison-table-{{ section.id }} { 
    display: flex;
    max-width: fit-content;
    margin: 0 auto;
    flex-wrap: nowrap;  
  }

  .comparison-column-{{ section.id }}:first-child {
    padding-left: 15px;
    padding-bottom: 15px;
    border-bottom-left-radius: {{ table_radius }}px;
  }

  .comparison-column-{{ section.id }}.active:nth-child(2) {
    border-bottom-right-radius: {{ table_radius }}px;
  }

  .comparison-column-{{ section.id }}:not(:first-child) {
    position: relative;
  }

  .comparison-column-{{ section.id }} {
    width: {{ column_width_mobile }}px;
    flex-shrink: 0;
  }

  .comparison-column-heading-text-{{ section.id }} {
    margin: 0;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
    text-align: {{ column_heading_align_mobile }};
    display: block;
    width: 100%;
    font-weight: 700;
    word-break: break-word;
  }

  .comparison-column-heading-image-{{ section.id }} {
    flex-shrink: 0;
    width: auto;
    height: 100%;
    overflow: hidden;
    margin: 5px 5px;
  }

  .comparison-column-heading-image-{{ section.id }} img,
  .comparison-column-heading-image-{{ section.id }} svg {
    display: block;
    height: 100%;
    width: 100%;
    object-fit: contain;
  }

  .comparison-column-heading-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .comparison-column-{{ section.id }}.active .comparison-column-heading-text-{{ section.id }} {
    font-size: {{ column_heading_size_mobile | times: 1.2 }}px;
     color: {{ column_heading_active_color }};
  }

  .comparison-row-{{ section.id }} {
    height: {{ row_height_mobile }}px;
    display: flex !important;
    align-items: center;
    justify-content: {{ row_text_horizontal_align_mobile }};
    overflow: hidden;
    position: relative;
    z-index: 2;
    gap: 8px;
    padding: 8px;
  }

  .comparison-column-heading-{{ section.id }} {
    justify-content: end;
  }

  .comparison-row-{{ section.id }} {
    border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-column-{{ section.id }}:first-child .comparison-row-{{ section.id }}:first-child {
    border: 0px;
  }

  .comparison-row-{{ section.id }}:last-child {
    border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .comparison-column-{{ section.id }}.active {   
    overflow: hidden;   
    background-color: {{ table_bg_color | hex_to_rgba: table_bg_opacity }};
  }

  .comparison-column-{{ section.id }}.active:first-child {
    border-top-left-radius: {{ table_radius }}px;
  }

  .comparison-column-{{ section.id }}.active:nth-child(2) {
    border-top-right-radius: {{ table_radius }}px;
  }

  .comparison-column-{{ section.id }} .comparison-row-{{ section.id }}:first-child {
    border-top: 0px !important;
  }

  .comparison-row-heading-{{ section.id }} {
    margin: 0;
    font-size: {{ row_heading_size_mobile }}px;
    color: {{ row_heading_color }};
    line-height: {{ row_heading_height }}%;
    text-transform: unset;
    text-align: {{ row_heading_align_mobile }};
    width: 100%;
    word-break: break-word;
  }

  .comparison-value-{{ section.id }} {
    margin: 0;
    font-size: {{ row_text_size_mobile }}px;
    color: {{ row_text_color }};
    line-height: {{ row_text_height }}%;
    text-transform: unset;
    text-align: {{ row_text_align_mobile }};
    display: block;
    word-break: break-word;
  }

  .comparison-column-{{ section.id }}.active .comparison-value-{{ section.id }} {
    color: {{ row_active_text_color }};
  }

  .comparison-value-icon-{{ section.id }} {
    flex-shrink: 0;
    width: {{ value_icon_size_mobile }}px !important;
    height: {{ value_icon_size_mobile }}px !important;    
  }

  .comparison-value-icon-{{ section.id }} img,
  .comparison-value-icon-{{ section.id }} svg {
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
  }

  .comparison-value-check-{{ section.id }} path {
    fill: {{ value_check_color }};
  }

  .comparison-column-{{ section.id }}.active .comparison-value-check-{{ section.id }} path {
    fill: {{ value_check_active_color }};
  }

  .comparison-value-cross-{{ section.id }} path {
    fill: {{ value_cross_color }};
  }

  .comparison-column-{{ section.id }}.active .comparison-value-cross-{{ section.id }} path {
    fill: {{ value_cross_active_color }};
  }

  .comparison-sticker-{{ section.id }} {
    position: absolute;   
    z-index: 2;  
  }

  .comparison-sticker-{{ section.id }} img, 
  .comparison-sticker-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-sticker-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-body-{{ section.id }} {
      gap: {{ layout_gap }}px;    
    }

    .comparison-table-wrapper-{{ section.id }} {
      overflow-x: visible;
      margin-left: 0rem;
      margin-right: 0rem;
      padding-left: 0rem;
      padding-right: 0rem;
    }

    .comparison-table-{{ section.id }} { 
      max-width: 100%;
      margin: 0;
    }

    .comparison-content-{{ section.id }} {
      justify-content: {{ content_vertical_align }};
      align-items: {{ content_horizontal_align }};
    }

    .comparison-column-{{ section.id }} {
      width: auto !important;
    }

    .comparison-column-{{ section.id }}:first-child {
      width: 30%;
      flex-shrink: 0;
    }

    .comparison-column-{{ section.id }}:not(:first-child) {
      flex: 1 0;
    }

    .comparison-column-{{ section.id }}:last-child {
      padding-right: 0px;
    }

    .comparison-column-heading-text-{{ section.id }} {
      font-size: {{ column_heading_size }}px;
      text-align: {{ column_heading_align }};
    }

    .comparison-row-{{ section.id }} {
      height: {{ row_height }}px;
      padding: 15px;
    }

    .comparison-row-heading-{{ section.id }} {
      font-size: {{ row_heading_size }}px;
      text-align: {{ row_heading_align }};
    }

    .comparison-row-{{ section.id }} {
      justify-content: {{ row_text_horizontal_align }};
    }

    .comparison-value-{{ section.id }} {
      font-size: {{ row_text_size }}px;
      text-align: {{ row_text_align }};
    }

    .comparison-value-icon-{{ section.id }} {
      width: {{ value_icon_size }}px !important;
      height: {{ value_icon_size }}px !important;    
    }

    .comparison-column-{{ section.id }}.active .comparison-column-heading-text-{{ section.id }} {
      font-size: {{ column_heading_size | times: 1.2 }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .comparison-column-heading-text-{{ section.id }} {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if row_heading_custom %}
  <style>
    .comparison-row-heading-{{ section.id }} {
      font-family: {{ row_heading_font.family }}, {{ row_heading_font.fallback_families }};
      font-weight: {{ row_heading_font.weight }};
      font-style: {{ row_heading_font.style }};
    }
  </style>
{% endif %}

{% if row_text_custom %}
  <style>
    .comparison-value-{{ section.id }} {
      font-family: {{ row_text_font.family }}, {{ row_text_font.fallback_families }};
      font-weight: {{ row_text_font.weight }};
      font-style: {{ row_text_font.style }};
    }
  </style>
{% endif %}

{% if layout_mobile == "table_content" %}
  <style>
    .comparison-body-{{ section.id }} {
      grid-template-areas: 
        "table" 
        "content"; 
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_table" %}
    <style>
      .comparison-body-{{ section.id }} {
        grid-template-areas: 
          "content" 
          "table";    
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "table_content" %}
  <style>
    @media (min-width: 1024px) {
      .comparison-body-{{ section.id }} {
        grid-template-areas: "table content";
        grid-template-columns: {{ table_width }}% 1fr;
      }
    }
  </style>
{% else %}  
  {% if layout == "content_table" %}
    <style>
      @media (min-width: 1024px) {
        .comparison-body-{{ section.id }} {
          grid-template-areas: "content table";
          grid-template-columns: 1fr {{ table_width }}%;
        }
      }
    </style>
  {% endif %}  
{% endif %}

{% if column_width_mobile > 80 %}
  <style>
    @media(max-width: 1024px) {
      .comparison-column-{{ section.id }}:last-child {
        padding-right: 10px;
      }  
    } 
  </style>
{% endif %}

{% if table_border_style == "dashed" %}
  <style>
    .comparison-row-{{ section.id }} {
      border-top: {{ table_border_thickness }}px dashed {{ table_border_color }};
    }

    .comparison-column-{{ section.id }}:first-child .comparison-row-{{ section.id }}:first-child {
      border: 0px;
    }

    .comparison-row-{{ section.id }}:last-child {
      border-bottom: {{ table_border_thickness }}px dashed {{ table_border_color }};
    }
  </style>
{% else %}
  <style>
    .comparison-row-{{ section.id }} {
      border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
    }

    .comparison-column-{{ section.id }}:first-child .comparison-row-{{ section.id }}:first-child {
      border: 0px;
    }

    .comparison-row-{{ section.id }}:last-child {
      border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .comparison-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
          position: relative;
          z-index: 2;
        }

        .comparison-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {

          .comparison-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }

          .comparison-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .comparison-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}


    {% when 'column' %}
      {% if block.settings.column_heading_image != blank or block.settings.column_heading != blank %}
        <style>
          .comparison-column-{{ section.id }} .comparison-row-{{ section.id }}:first-child {
            height: {{ column_header_height_mobile }}px;
          }

          @media(min-width: 1024px) {
            .comparison-column-{{ section.id }} .comparison-row-{{ section.id }}:first-child {
              height: {{ column_header_height }}px;
            }
          }
        </style>
      {% endif %}

    {% when 'text' %}
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        .comparison-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .comparison-text-{{ block.id }} * {
          margin: 0;          
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {

          .comparison-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_align }};
          }         

          .comparison-text-{{ block.id }} * {       
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .comparison-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}

      {% when 'sticker' %}
      <style>        
        .comparison-sticker-{{ block.id }} {  
          position: absolute;
          z-index: 3;             
          left: {{ block.settings.horizontal_position_mobile }}%;
          top: {{ block.settings.vertical_position_mobile }}%;
          transform: translate(-50%, -50%);
          width: 100%;
          max-width: {{ block.settings.sticker_width_mobile }}px;
          display: {% if block.settings.show_mobile %} block {% else %} none {% endif %};
        }

        .comparison-sticker-wrap-{{ block.id }} {
          width: 100%;
          height: 100%;
          transform: rotate({{ block.settings.rotate }}deg);
        }

        @media(min-width: 1024px) {
          .comparison-sticker-{{ block.id }} {            
            left: {{ block.settings.horizontal_position }}%;
            top: {{ block.settings.vertical_position }}%;
            max-width: {{ block.settings.sticker_width }}px;
            display: block;
          }
        }
      </style>

    {% when 'rating' %}
      <style>   
        {{ block.settings.rating_font | font_face: font_display: 'swap' }}    

        .comparison-rating-wrap-{{ block.id }} {
          display: flex;
          align-items: center;
          margin-top: {{ block.settings.rating_mt_mobile }}px;
          gap: 5px;
          justify-content: {{ content_horizontal_align_mobile }};
        }

        .comparison-rating-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.rating_size_mobile }}px;
          color: {{ block.settings.rating_color }};
          line-height: {{ block.settings.rating_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          text-align: {{ content_horizontal_align_mobile }};
        }

        .comparison-rating-text-{{ block.id }} a {
          text-decoration: underline;
          color: {{ block.settings.rating_color }};
        }

        .comparison-stars-{{ block.id }} {
          display: flex;
          align-items: center;
          gap: {{ block.settings.stars_gap }}px;
          max-width: fit-content;
        }

        .comparison-stars-{{ block.id }} svg,
        .comparison-stars-{{ block.id }} img {
          display: block;
          width: {{ block.settings.stars_size_mobile }}px;
