{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile  

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign table_radius = section.settings.table_radius
  assign table_border_thickness = section.settings.table_border_thickness
  assign table_border_color = section.settings.table_border_color
  assign table_border_active_color = section.settings.table_border_active_color
  assign table_bg_color = section.settings.table_bg_color
  assign table_bg_active_color = section.settings.table_bg_active_color
  assign table_bg_opacity = section.settings.table_bg_opacity
  assign column_mt = section.settings.column_mt
  assign column_mt_mobile = section.settings.column_mt_mobile
  assign table_width = section.settings.table_width

  assign reverse_content = section.settings.reverse_content

  assign column_heading_size = section.settings.column_heading_size
  assign column_heading_size_mobile = section.settings.column_heading_size_mobile
  assign column_heading_color = section.settings.column_heading_color
  assign column_heading_active_color = section.settings.column_heading_active_color
  assign column_heading_custom = section.settings.column_heading_custom
  assign column_heading_font = section.settings.column_heading_font
  assign column_heading_height = section.settings.column_heading_height
  assign column_heading_align = section.settings.column_heading_align
  assign column_heading_align_mobile = section.settings.column_heading_align_mobile

  assign column_text_size = section.settings.column_text_size
  assign column_text_size_mobile = section.settings.column_text_size_mobile
  assign column_text_color = section.settings.column_text_color
  assign column_active_text_color = section.settings.column_active_text_color
  assign column_text_custom = section.settings.column_text_custom
  assign column_text_font = section.settings.column_text_font
  assign column_text_height = section.settings.column_text_height
  assign column_text_align = section.settings.column_text_align
  assign column_text_align_mobile = section.settings.column_text_align_mobile
  assign column_text_mt = section.settings.column_text_mt
  assign column_text_mt_mobile = section.settings.column_text_mt_mobile

  assign column_image_ratio = section.settings.column_image_ratio
  assign column_image_ratio_mobile = section.settings.column_image_ratio_mobile
  assign column_image_radius = section.settings.column_image_radius
  assign column_image_border_thickness = section.settings.column_image_border_thickness
  assign column_image_border_color = section.settings.column_image_border_color
-%}

{%- style -%}  
  {{ column_heading_font | font_face: font_display: 'swap' }}
  {{ column_text_font | font_face: font_display: 'swap' }}
  
  .section-{{ section.id }} {
    position: relative;
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
    z-index: 1;
  }

  .comparison-body-{{ section.id }} {
    display: grid;
    gap: {{ layout_gap_mobile }}px;
    grid-template-columns: 1fr;
  }

  .comparison-table-column-content-{{ section.id }} {
    position: relative;
    z-index: 2;
  }

  .comparison-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};
    grid-area: content;
  }

  .comparison-table-wrapper-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ column_mt_mobile }}px;
    position: relative;
    padding-left: 0px !important;
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
    border-right: {{ table_border_thickness }}px solid {{ table_border_color }};
    padding-top: {{ column_mt_mobile }}px;
    padding-bottom: {{ column_mt_mobile }}px;
  }

  .comparison-table-line-{{ section.id }} {
    display: block !important;
    width: {{ table_border_thickness }}px;
    background-color: {{ table_border_color }};
    height: 100%;
    position: absolute;
  }

  .comparison-table-column-{{ section.id }} {
    display: flex;
    align-items: center;
    width: 100%;
    justify-content: space-between;
    gap: 20px;
    position: relative;
    padding: 20px 10px 20px 20px;  
    z-index: 2; 
  }

  .comparison-column-heading-text-{{ section.id }} {
    margin: 0;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
    display: block;
    width: 100%;
    font-weight: 700;
    word-break: break-word;
  }

  .comparison-table-column-{{ section.id }}.active .comparison-column-heading-text-{{ section.id }} {
    font-size: {{ column_heading_size_mobile | plus: 4 }}px;
    color: {{ column_heading_active_color }} !important;
  }

  .comparison-column-text-{{ section.id }} {
    margin: 0;
    margin-top: {{ column_text_mt_mobile }}px;
    font-size: {{ column_text_size_mobile }}px;
    color: {{ column_text_color }};
    line-height: {{ column_text_height }}%;
    text-transform: unset;
    display: block;
    width: 100%;
    word-break: break-word;
  }

  .comparison-table-column-{{ section.id }}.active .comparison-column-text-{{ section.id }} {
    font-size: {{ column_text_size_mobile | plus: 2 }}px;
    color: {{ column_active_text_color }} !important;
  }

  .comparison-table-column-image-{{ section.id }} {
    position: relative;
    z-index: 2;
    width: 22%;
    border: {{ column_image_border_thickness }}px solid {{ column_image_border_color }};
    border-radius: {{ column_image_radius }}px;
    overflow: hidden;
  }

  .comparison-table-column-image-{{ section.id }} img,
  .comparison-table-column-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-table-column-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }
  
  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-body-{{ section.id }} {
      gap: {{ layout_gap }}px;    
    }

    .comparison-table-{{ section.id }} { 
      max-width: 100%;
      margin: 0;
    }

    .comparison-table-wrapper-{{ section.id }} {
      gap: {{ column_mt }}px;
      padding-top: {{ column_mt }}px;
      padding-bottom: {{ column_mt }}px;
    }

    .comparison-content-{{ section.id }} {
      justify-content: {{ content_vertical_align }};
      align-items: {{ content_horizontal_align }};
    }  

    .comparison-column-heading-text-{{ section.id }} {
      font-size: {{ column_heading_size }}px;
    }

    .comparison-table-column-{{ section.id }}.active .comparison-column-heading-text-{{ section.id }} {
      font-size: {{ column_heading_size | plus: 4 }}px;
    }

    .comparison-column-text-{{ section.id }} {
      margin-top: {{ column_text_mt }}px;
      font-size: {{ column_text_size }}px;
    }

    .comparison-table-column-{{ section.id }}.active .comparison-column-text-{{ section.id }} {
      font-size: {{ column_text_size | plus: 2 }}px;
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: var(--page-width);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .comparison-column-heading-text-{{ section.id }} {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if column_text_custom %}
  <style>
    .comparison-column-text-{{ section.id }} {
      font-family: {{ column_text_font.family }}, {{ column_text_font.fallback_families }};
      font-weight: {{ column_text_font.weight }};
      font-style: {{ column_text_font.style }};
    }
  </style>
{% endif %}

{% if reverse_content %}
  <style>
    .comparison-table-column-{{ section.id }} {
      flex-direction: row-reverse;
      padding-left: 10px !important;
      padding-right: 20px !important;
    }

    .comparison-table-wrapper-{{ section.id }} {
      padding-left: 0px !important;
      padding-right: 0px !important;
    }
  </style>
{% endif %}

{% if column_image_ratio_mobile == "portrait" %}
  <style>
    .comparison-table-column-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif column_image_ratio_mobile == "landscape" %}
  <style>
  .comparison-table-column-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif column_image_ratio_mobile == "square" %}
  <style>
    .comparison-table-column-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .comparison-table-column-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if column_image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-table-column-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
    
  </style>
{% elsif column_image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-table-column-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif column_image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-table-column-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .comparison-table-column-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if layout_mobile == "table_content" %}
  <style>
    .comparison-body-{{ section.id }} {
      grid-template-areas: 
        "table" 
        "content"; 
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_table" %}
    <style>
      .comparison-body-{{ section.id }} {
        grid-template-areas: 
          "content" 
          "table";    
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "table_content" %}
  <style>
    @media (min-width: 1024px) {
      .comparison-body-{{ section.id }} {
        grid-template-areas: "table content";
        grid-template-columns: {{ table_width }}% 1fr;
      }
    }
  </style>
{% else %}  
  {% if layout == "content_table" %}
    <style>
      @media (min-width: 1024px) {
        .comparison-body-{{ section.id }} {
          grid-template-areas: "content table";
          grid-template-columns: 1fr {{ table_width }}%;
        }
      }
    </style>
  {% endif %}  
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .comparison-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
          position: relative;
          z-index: 2;
        }
      
        .comparison-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .comparison-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }
        
          .comparison-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>
        
      {% if block.settings.heading_custom %}
        <style>
          .comparison-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}
    

    {% when 'column' %}
      {% if block.settings.column_heading_image != blank or block.settings.column_heading != blank %}
        <style>
          .comparison-column-{{ section.id }} .comparison-row-{{ section.id }}:first-child {
            height: {{ column_header_height_mobile }}px;
          }

          @media(min-width: 1024px) {
            .comparison-column-{{ section.id }} .comparison-row-{{ section.id }}:first-child {
              height: {{ column_header_height }}px;
            }
          }
        </style>
      {% endif %}

    {% when 'text' %}
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        .comparison-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .comparison-text-{{ block.id }} * {
          margin: 0;          
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .comparison-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_align }};
          }         
          
          .comparison-text-{{ block.id }} * {       
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .comparison-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}  
    
    {% when 'column' %}
      <style>
        .comparison-table-column-{{ block.id }}:after {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 0%;
          height: 100%;          
          border-top-right-radius: {{ table_radius }}px;
          border-bottom-right-radius: {{ table_radius }}px;
          border: {{ table_border_thickness }}px solid {{ table_border_color }};
          background-color: {{ table_bg_color }};
          border-left: 0px !important;
        }

        .comparison-table-column-{{ block.id }}.visible:after {
          width: {{ block.settings.column_progress }}%;
          transition: width 0.6s ease-out 0s;
        }

        .comparison-table-column-{{ block.id }}.active:after {
          background-color: {{ table_bg_active_color }};
          border: {{ table_border_thickness }}px solid {{ table_border_active_color }};
        }
      </style>

      {% if block.settings.column_progress == 100 %}
        <style>
          .comparison-table-column-{{ block.id }}.visible:after {
            {% if reverse_content %}
              width: 104%;
            {% else %}
              width: 102%;
            {% endif %}
            transition: width 0.6s ease-out 0s;
          }
        </style>        
      {% endif %}

      {% if reverse_content %}
        <style>
          .comparison-table-column-{{ block.id }}:after {
            left: inherit;
            right: 0;
            border-top-right-radius: 0px;
            border-bottom-right-radius: 0px;
            border-top-left-radius: {{ table_radius }}px;
            border-bottom-left-radius: {{ table_radius }}px;
            border-left: {{ table_border_thickness }}px solid {{ table_border_color }} !important;
            border-right: 0px !important;
          }

          .comparison-column-heading-text-{{ section.id }} {
            text-align: right;
          }

          .comparison-column-text-{{ section.id }} {
            text-align: right;
          }
        </style>
      {% endif %}

    {% when 'button' %}
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .comparison-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .comparison-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .comparison-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .comparison-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .comparison-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .comparison-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .comparison-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.button_custom %}
        <style>
          .comparison-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .comparison-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }
      
          .comparison-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .comparison-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }
      
          .comparison-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .comparison-button-{{ block.id }} {
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;
          }
        </style>
      {% endif %}
  {% endcase %}
{% endfor %}


<div class="section-{{ section.id }} comparison-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">
      <div class="comparison-body-{{ section.id }}">
        <div class="comparison-content-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type %}
              {% when 'heading' %}  
                {% if block.settings.heading != blank %}
                  <div class="comparison-heading-{{ block.id }}">
                    {{ block.settings.heading }}
                  </div>
                {% endif %}
  
              {% when 'text' %}  
                {% if block.settings.text != blank %}
                  <div class="comparison-text-{{ block.id }}">
                    {{ block.settings.text }}
                  </div>
                {% endif %}  
  
              {% when 'button' %}  
