<script>
function initComparisonTable25() {
  const sections = document.querySelectorAll('.comparison-{{ section.id }}');
  const observerOptions = {
    root: null,
    threshold: 0.2
  };

  const observerCallback = (entries, observer) => {
    entries.forEach(entry => {
      const items = entry.target.querySelectorAll('.comparison-table-column-{{ section.id }}');
      
      if (entry.isIntersecting) {
        items.forEach((item, index) => {
          setTimeout(() => {
            item.classList.add('visible');
          }, index * 100);
        });
      } else {
        items.forEach(item => {
          item.classList.remove('visible');
        });
      }
    });
  };

  const observer = new IntersectionObserver(observerCallback, observerOptions);

  sections.forEach(section => {
    observer.observe(section);
  });
}

document.addEventListener('DOMContentLoaded', initComparisonTable25);

if (Shopify.designMode) {
  document.addEventListener('shopify:section:unload', initComparisonTable25);
  document.addEventListener('shopify:section:load', initComparisonTable25);
}
</script>
