-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  column_heading_font | font_face: font_display: 'swap' }}
  {{  property_heading_font | font_face: font_display: 'swap' }}
  {{  property_text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .comparison-body-{{ section.id }} {
    margin-top: {{ body_mt_mobile }}px;
    display: flex;
    align-items: start;
    gap: {{  body_gap_mobile }}px;
    flex-direction: column;
  }

  .comparison-column-{{ section.id }} {
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    border-radius: {{ item_radius }}px;
    border: {{ item_border_thickness }}px solid {{ item_border_color }};
    background-color: {{ item_bg_color }};
  }

  .comparison-column-{{ section.id }}.active {
    background-color: {{ item_bg_active_color }};
  }

  .comparison-column-desktop-{{ section.id }} {
    display: none;
  }

  .comparison-product-heading-{{ section.id }} {
    text-align: {{ column_heading_align_mobile }};
    padding-bottom: {{ column_heading_padding_bottom | times: 0.75 | round: 0 }}px;
    border-bottom: {{ property_border_thickness }}px solid {{ property_border_color }};
    padding-left: {{ property_padding_horizontal_mobile }}px;
  }

  .comparison-product-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
  }

  .comparison-item-{{ section.id }} {
    padding: {{ property_padding_vertical_mobile }}px {{ property_padding_horizontal_mobile }}px;
    border-bottom: {{ property_border_thickness }}px solid {{ property_border_color }};
  }

  .comparison-top-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 8px;
  }

  .comparison-icon-{{ section.id }} {
    flex: 0 0 {{ property_icon_size_mobile }}px;
    height: {{ property_icon_size_mobile }}px;
  }

  .comparison-icon-{{ section.id }} svg,
  .comparison-icon-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: contain;
  }

  .comparison-title-{{ section.id }} {
    text-align: {{ property_heading_align_mobile }};
    margin: 0;
    font-size: {{ property_heading_size_mobile }}px;
    color: {{ property_heading_color }};
    line-height: {{ property_heading_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .comparison-text-{{ section.id }} {
    display: block;
    width: 100%;
    text-align: {{ property_text_align_mobile }};
    margin: 0;
    margin-top: 4px;
    padding-left: {{ property_icon_size_mobile | plus: 8 }}px;
    font-size: {{ property_text_size_mobile }}px;
    color: {{ property_text_color }};
    line-height: {{ property_text_height }}%;
    text-transform: unset;
  }

  .comparison-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: {{ button_width_mobile }}px;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .comparison-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .comparison-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-body-{{ section.id }} {
      margin-top: {{ body_mt }}px;
      gap: {{  body_gap }}px;
      flex-direction: row;
    }

    .comparison-column-{{ section.id }} {
      flex: 1 0;
    }

    .comparison-column-desktop-{{ section.id }} {
      display: block;
    }

    .comparison-column-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
    }

    .comparison-product-heading-{{ section.id }} {
      text-align: {{ column_heading_align }};
      padding-left: {{ property_padding_horizontal }}px;
      padding-bottom: {{ column_heading_padding_bottom }}px;
    }

    .comparison-product-heading-{{ section.id }} * {
      font-size: {{ column_heading_size }}px;
    }

    .comparison-item-{{ section.id }} {
      padding: {{ property_padding_vertical }}px {{ property_padding_horizontal }}px;
    }

    .comparison-icon-{{ section.id }} {
      flex: 0 0 {{ property_icon_size }}px;
      height: {{ property_icon_size }}px;
    }

    .comparison-title-{{ section.id }} {
      text-align: {{ property_heading_align }};
      font-size: {{ property_heading_size }}px;
    }

    .comparison-text-{{ section.id }} {
      text-align: {{ property_text_align }};
      padding-left: {{ property_icon_size | plus: 8 }}px;
      font-size: {{ property_text_size }}px;
    }

    .comparison-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      max-width: {{ button_width }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .comparison-product-heading-{{ section.id }} * {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if property_heading_custom %}
  <style>
    .comparison-title-{{ section.id }} {
      font-family: {{ property_heading_font.family }}, {{ property_heading_font.fallback_families }};
      font-weight: {{ property_heading_font.weight }};
      font-style: {{ property_heading_font.style }};
    }
  </style>
{% endif %}

{% if property_text_custom %}
  <style>
    .comparison-text-{{ section.id }} {
      font-family: {{ property_text_font.family }}, {{ property_text_font.fallback_families }};
      font-weight: {{ property_text_font.weight }};
      font-style: {{ property_text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .comparison-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if property_heading_align_mobile == "center" %}
  <style>
    .comparison-top-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif property_heading_align_mobile == "right" %}
  <style>
    .comparison-top-{{ section.id }} {
      justify-content: end;
    }
  </style>
{% endif %}

{% if property_heading_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-top-{{ section.id }} {
        justify-content: center;
      } 
    }
  </style>
{% elsif property_heading_align == "right" %}
  <style>
     @media(min-width: 1024px) {
      .comparison-top-{{ section.id }} {
        justify-content: end;
      } 
    }
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .comparison-top-{{ section.id }} {
        justify-content: start;
      } 
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .comparison-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .comparison-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .comparison-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .comparison-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .comparison-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% endif %}

{% if button_hide_desktop %}
  <style>
    @media(min-width: 1024px) {
      .comparison-button-{{ section.id }} {
        display: none !important;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}

  {% if block.type == "button" %}

    <style>

      {{  block.settings.button_font | font_face: font_display: 'swap' }}

      .comparison-button-{{ block.id }} {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 10px;
        width: 100%;
        max-width: {{ block.settings.button_width_mobile }}px;
        margin: 0;
        margin-top: {{ block.settings.button_mt_mobile }}px;
        font-size: {{ block.settings.button_size_mobile }}px;
        color: {{ block.settings.button_color }};
        line-height: {{ block.settings.button_height }}%;
        text-align: center;
        text-transform: unset;
        text-decoration: none;
        padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
        border-radius: {{ block.settings.button_radius }}px;
        transition: all 0.25s ease 0s;
        position: relative;
        font-weight: 700;
        z-index: 2;
      }

      .comparison-button-{{ block.id }}:hover {
        color: {{ block.settings.button_hover_color }};
        transition: all 0.25s ease 0s;
      }

      .comparison-button-{{ block.id }} svg {
        width: 14px;
        height: 14px;
      }

      .comparison-button-{{ block.id }} svg path {
        fill: {{ block.settings.button_color }};
        transition: all 0.25s ease 0s;
      }

      .comparison-button-{{ block.id }}:hover svg path {
        fill: {{ block.settings.button_hover_color }};
        transition: all 0.25s ease 0s;
      }

      @media(min-width: 1024px) {
        .comparison-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            max-width: {{ block.settings.button_width }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
      }

    </style>

    {% if  block.settings.button_custom %}
      <style>
        .comparison-button-{{ block.id }} {
          font-family: {{  block.settings.button_font.family }}, {{  block.settings.button_font.fallback_families }};
          font-weight: {{  block.settings.button_font.weight }};
          font-style: {{  block.settings.button_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.button_align_mobile == "center" %}
      <style>
       .comparison-button-{{ block.id }} {
          margin-left: auto;
          margin-right: auto;
        } 
      </style>
    {% elsif block.settings.button_align_mobile == "right" %}
      <style>
       .comparison-button-{{ block.id }} {
          margin-left: auto;
        } 
      </style>
    {% endif %}

    {% if block.settings.button_align == "center" %}
      <style>
        @media(min-width: 1024px) {
         .comparison-button-{{ block.id }} {
            margin-left: auto !important;
            margin-right: auto !important;
          } 
        }
      </style>
    {% elsif block.settings.button_align == "right" %}
      <style>
        @media(min-width: 1024px) {
         .comparison-button-{{ block.id }} {
            margin-left: auto !important;
            margin-right: 0px !important;
          } 
        }
      </style>
    {% else %}
      <style>
        @media(min-width: 1024px) {
         .comparison-button-{{ block.id }} {
            margin-left: 0px !important;
            margin-right: 0px !important;
          } 
        }
      </style>
    {% endif %}

    {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
      <style>
        .comparison-button-{{ block.id }} {
          background-color: {{ button_bg_color }};
        }

        .comparison-button-{{ block.id }}:hover {
          background-color: {{ button_bg_hover_color }};
        }
      </style>
    {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
      <style>
        .comparison-button-{{ block.id }} {
          background-color: {{ button_bg_color }};
          border: {{ button_border_thickness }}px solid {{ button_border_color }};
        }

        .comparison-button-{{ block.id }}:hover {
          background-color: {{ button_bg_hover_color }};
          border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
        }
      </style>
    {% endif %}

    {% if block.settings.button_hide_mobile %}
      <style>
        .comparison-button-{{ block.id }} {
          display: none !important;
        }

        @media(min-width: 1024px ){
          .comparison-button-{{ block.id }} {
            display: flex !important;
          }
        }
      </style>
    {% endif %}

  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="comparison-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}
      <div class="comparison-body-{{ section.id }}">
        {% assign inside_item = false %}
        {% for block in section.blocks %}
          {% if block.type == "column" %}
            {% if inside_item %}
              </div>
            {% endif %}

            <div class="comparison-column-{{ section.id }} {% if block.settings.active %}active{% endif %} {% if block.settings.hide_mobile %}comparison-column-desktop-{{ section.id }}{% endif %}">
              {% assign inside_item = true %}

              {% if block.settings.heading != blank %}
                <div class="comparison-product-heading-{{ section.id }}">{{ block.settings.heading }}</div>
              {% endif %}

          {% elsif block.type == "property" %}
            <div class="comparison-item-{{ section.id }}">
              <div class="comparison-top-{{ section.id }}">
                <div class="comparison-icon-{{ section.id }}">
                  {% if block.settings.icon == "worse" %}
                    {% if cross_icon != blank %}
                      <img src="{{ cross_icon | image_url }}" alt="{{ cross_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle cx="12" cy="12" r="12" fill="#E2255E"></circle><path fill="#fff" fill-rule="evenodd" d="M7.2 7.7c.2-.3.7-.3 1 0l3.8 3.9 3.9-4a.7.7 0 1 1 1 1l-4 3.9 4 3.9a.7.7 0 1 1-1 1l-3.9-4-3.9 4a.7.7 0 1 1-1-1l4-3.9-4-3.9a.7.7 0 0 1 0-1Z" clip-rule="evenodd"></path></svg>
                    {% endif %}
                  {% else %}
                    {% if tick_icon != blank %}
