                {% if first_heading_image != blank %}
                  {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                    {{ first_heading_image | image_url: width: 165 | image_tag:
                      loading: loading_attribute,
                      width: first_heading_image.width,
                      height: first_heading_image.height,
                      sizes: sizes,
                      widths: '165, 360, 535, 750, 1070, 1500'
                    }}  
                  {% else %}
                    <h3>{{ first_heading }}</h3>
                  {% endif %}
              </div>
            {% endif %}
            {% if table_columns > 1 and second_heading != blank or second_heading_image != blank %}
