  assign values_column_count_index = values_column_count | minus: 1

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign price_size_mobile = section.settings.price_size_mobile
  assign price_size = section.settings.price_size
  assign price_color = section.settings.price_color
  assign price_original_color = section.settings.price_original_color
  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_height = section.settings.price_height
  assign price_mt = section.settings.price_mt
  assign price_mt_mobile = section.settings.price_mt_mobile
  assign price_compare = section.settings.price_compare

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_style = section.settings.button_style
  assign button_mt = section.settings.button_mt
  assign button_mt_mobile = section.settings.button_mt_mobile
  assign button_hide_mobile = section.settings.button_hide_mobile

  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height

  assign value_size = section.settings.value_size
  assign value_size_mobile = section.settings.value_size_mobile
  assign value_color = section.settings.value_color
  assign value_custom = section.settings.value_custom
  assign value_font = section.settings.value_font
  assign value_height = section.settings.value_height

  assign tag = section.settings.tag
  assign tag_size = section.settings.tag_size
  assign tag_size_mobile = section.settings.tag_size_mobile
  assign tag_color = section.settings.tag_color
  assign tag_custom = section.settings.tag_custom
  assign tag_font = section.settings.tag_font
  assign tag_height = section.settings.tag_height
  assign tag_padding_vertical = section.settings.tag_padding_vertical
  assign tag_padding_vertical_mobile = section.settings.tag_padding_vertical_mobile
  assign tag_padding_horizontal = section.settings.tag_padding_horizontal
  assign tag_padding_horizontal_mobile = section.settings.tag_padding_horizontal_mobile
  assign tag_radius = section.settings.tag_radius
  assign tag_border_thickness = section.settings.tag_border_thickness
  assign tag_border_color = section.settings.tag_border_color
  assign tag_bg_color = section.settings.tag_bg_color

-%}

{%- style -%}

  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  value_font | font_face: font_display: 'swap' }}
  {{  tag_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ body_gap_mobile }}px;
  }

  .comparison-table-{{ section.id }} {
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
    padding: {{ table_padding_vertical_mobile }}px {{ table_padding_horizontal_mobile }}px;
    border: {{ table_border_thickness }}px solid {{ table_border_color }};
    background-color: {{ table_bg }};
    scroll-padding-top: 104px;
    display: grid;
    overflow: hidden;
  }

  .comparison-inner-table-{{ section.id }} {
    scrollbar-width: none;
    scroll-snap-type: x mandatory;
    overscroll-behavior-x: contain;
    overflow: auto hidden;
    margin-left: -{{ table_padding_horizontal_mobile }}px;
    padding: {{ table_padding_vertical_mobile }}px {{ table_padding_horizontal_mobile }}px;
    display: grid;
  }

  .comparison-row-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ values_column_count }}, 150px);
    gap: {{ row_gap_mobile }}px;
    min-width: min-content;
  }

  .comparison-row-{{ section.id }}[hidden] {
    display: none;
  }

  .comparison-product-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
  }

  .comparison-product-content-{{ section.id }} {
    flex: 1 1 auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .comparison-image-{{ section.id }} {
    /* background-color: #F7F7F7; */
    position: relative;
  }

  .comparison-image-{{ section.id }} img,
  .comparison-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-decoration: none;
    word-break: break-word;
  }

  .comparison-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;
    display: flex;
    align-items: center;
    gap: 5px;
    z-index: 1;
    position: relative;
    flex-wrap: wrap;
  }

  .comparison-price-{{ section.id }} * {
    margin: 0;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    font-style: normal;
  }

  .comparison-compare-price-{{ section.id }} {
    font-size: {{ price_size | times: 0.8 }}px;
    color: {{ price_original_color }};
    text-decoration: line-through !important;
  }

  .comparison-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    font-weight: 700;
  }

  .comparison-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .comparison-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-mobile-button-{{ section.id }} {
    display: flex;
  }

  .comparison-row-body-{{ section.id }} {
    padding: {{ row_padding_vertical_mobile }}px 0px;
    border-top: {{ row_border_thickness }}px solid {{ row_border_color }};
  }

  .comparison-row-body-{{ section.id }}:last-child {
    border-bottom: {{ row_border_thickness }}px solid {{ row_border_color }};
  }

  .comparison-row-{{ section.id }}--product {
    padding-bottom: {{ row_padding_vertical_mobile }}px;
  }

  .comparison-heading-{{ section.id }} {
    position: sticky;
    left: 0;
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .comparison-value-{{ section.id }} {
    grid-row: 2;
  }

  .comparison-value-{{ section.id }},
  .comparison-value-{{ section.id }} * {
    margin: 0;
    font-size: {{ value_size_mobile }}px;
    color: {{ value_color }};
    line-height: {{ value_height }}%;
    text-transform: unset;
  }

  .comparison-view-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-left: auto;
    margin-right: auto;
    margin-top: {{ view_mt_mobile }}px;
    font-size: {{ view_size_mobile }}px;
    color: {{ view_color }};
    line-height: {{ view_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    border: 0px;
    background-color: transparent;
    padding: {{ view_padding_vertical_mobile }}px {{ view_padding_horizontal_mobile }}px;
    border-radius: {{ view_radius }}px;
    transition: all 0.25s ease 0s;
    cursor: pointer;
    font-weight: 700;
    font-family: unset;
  }

  .comparison-view-{{ section.id }}:hover {
    color: {{ view_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-view-{{ section.id }} svg {
    width: 10px;
    height: 10px;
  }

  .comparison-view-{{ section.id }} svg path {
    stroke: {{ view_icon_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-view-{{ section.id }}:hover svg path {
    stroke: {{ view_icon_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .comparison-toggle-icon-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 24px;
    height: 24px;
    border-radius: 50%;
    background: {{ view_icon_bg }};
    transition: all 0.25s ease 0s;
  }

  .comparison-view-{{ section.id }}:hover .comparison-toggle-icon-{{ section.id }} {
    background: {{ view_icon_bg_hover }};
    transition: all 0.25s ease 0s;
  }

  .comparison-view-{{ section.id }}.active .comparison-toggle-icon-{{ section.id }} {
    transform: rotate(180deg);
    transition: all 0.25s ease 0s;
  }

  .comparison-row-{{ section.id }}--sticky {
    visibility: hidden;
    opacity: 0;
    z-index: 1;
    background: {{ table_bg }};
    padding-block: 20px;
    border-bottom: 1px solid {{ row_border_color }};
    transition: opacity .2s, visibility .2s, top .2s;
    position: fixed;
    top: 120px;
    border-top-width: 0 !important;
  }

  .comparison-row-{{ section.id }}--sticky.is-visible {
    opacity: 1;
    visibility: visible;
  }

  .comparison-row-{{ section.id }}--sticky .comparison-button-{{ section.id }} {
    display: none !important;
  }

  .comparison-tag-{{ section.id }} {
    margin: 0;
    font-size: {{ tag_size_mobile }}px;
    color: {{ tag_color }};
    line-height: {{ tag_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: {{ tag_bg_color }};
    padding: {{ tag_padding_vertical_mobile }}px {{ tag_padding_horizontal_mobile }}px;
    border: {{ tag_border_thickness }}px solid {{ tag_border_color }};
    border-radius: {{ tag_radius }}px;
    position: absolute;
    top: 10px;
    left: 10px;
    z-index: 2;
  }

  @media(min-width: 768px) {
    .comparison-row-{{ section.id }} {
      grid-template-columns: 140px repeat({{ values_column_count }}, 1fr);
    }

    .comparison-product-item-{{ section.id }}:first-child {
      grid-column-start: 2;
    }

    .comparison-heading-{{ section.id }} {
      position: static;
    }

    .comparison-value-{{ section.id }} {
      grid-row: auto;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-body-{{ section.id }} {
      grid-template-columns: calc(35% - {{ body_gap | times: 0.5 }}px) calc(65% - {{ body_gap | times: 0.5 }}px);
      gap: {{ body_gap }}px;
    }

    .comparison-table-{{ section.id }} {
      margin: 0px;
      border-radius: {{ table_radius }}px;
      padding: {{ table_padding_vertical }}px {{ table_padding_horizontal }}px;
    }

    .comparison-inner-table-{{ section.id }} {
      margin: 0px;
      padding: 0px;
    }

    .comparison-row-{{ section.id }} {
      grid-template-columns: 180px repeat({{ values_column_count }}, 1fr);
      gap: {{ row_gap }}px;
    }

    .comparison-title-{{ section.id }} {
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
    }

    .comparison-price-{{ section.id }} {
      margin-top: {{ price_mt }}px;
    }

    .comparison-price-{{ section.id }} * {
      font-size: {{ price_size }}px;
    }

    .comparison-button-{{ section.id }} {
      display: flex !important;
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .comparison-row-body-{{ section.id }} {
      padding: {{ row_padding_vertical }}px 0px;
    }

    .comparison-row-{{ section.id }}--product {
      padding-bottom: {{ row_padding_vertical }}px;
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-value-{{ section.id }},
    .comparison-value-{{ section.id }} * {
      font-size: {{ value_size }}px;
    }

    .comparison-view-{{ section.id }} {
      display: flex !important;
      margin-top: {{ view_mt }}px;
      padding: {{ view_padding_vertical }}px {{ view_padding_horizontal }}px;
      font-size: {{ view_size }}px;
    }

    .comparison-row-{{ section.id }}--sticky .comparison-item-{{ section.id }} {
      grid-template-columns: 100px 1fr;
    }

    .comparison-tag-{{ section.id }} {
      font-size: {{ tag_size }}px;
      padding: {{ tag_padding_vertical }}px {{ tag_padding_horizontal }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .comparison-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .comparison-price-{{ section.id }} * {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .comparison-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if view_custom %}
  <style>
    .comparison-view-{{ section.id }} {
      font-family: {{ view_font.family }}, {{ view_font.fallback_families }} !important;
      font-weight: {{ view_font.weight }};
      font-style: {{ view_font.style }};
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if value_custom %}
  <style>
    .comparison-value-{{ section.id }} * {
      font-family: {{ value_font.family }}, {{ value_font.fallback_families }};
      font-weight: {{ value_font.weight }};
      font-style: {{ value_font.style }};
    }
  </style>
{% endif %}

{% if tag_custom %}
  <style>
    .comparison-tag-{{ section.id }} {
      font-family: {{ tag_font.family }}, {{ tag_font.fallback_families }} !important;
      font-weight: {{ tag_font.weight }};
      font-style: {{ tag_font.style }};
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .comparison-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .comparison-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .comparison-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if view_style == "non_outline" or view_style == "non_outline_arrow" %}
  <style>
    .comparison-view-{{ section.id }} {
      background-color: {{ view_bg_color }};
    }

    .comparison-view-{{ section.id }}:hover {
      background-color: {{ view_bg_hover_color }};
    }
  </style>
{% elsif view_style == "outline" or view_style == "outline_arrow" %}
  <style>
    .comparison-view-{{ section.id }} {
      background-color: {{ view_bg_color }};
      border: {{ view_border_thickness }}px solid {{ view_border_color }};
    }

    .comparison-view-{{ section.id }}:hover {
      background-color: {{ view_bg_hover_color }};
      border: {{ view_border_thickness }}px solid {{ view_border_hover_color }};
    }
  </style>
{% elsif view_style == "link" %}
  <style>
    .comparison-view-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if button_hide_mobile %}
  <style>
    .comparison-button-{{ section.id }} {
      display: none;
    }
  </style>
{% endif %}

{% if table_full %}
  <style>
    @media(min-width: 1024px) {
      .comparison-body-{{ section.id }} {
        grid-template-columns: 1fr;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
    {% if block.type == "heading" %}
        <style>
            {{  block.settings.heading_font | font_face: font_display: 'swap' }}

            .comparison-heading-{{ block.id }} {
                text-align: {{ block.settings.heading_align_mobile }};
                margin-top: {{ block.settings.heading_mt_mobile }}px;
            }

            .comparison-heading-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.heading_size_mobile }}px;
                color: {{ block.settings.heading_color }};
                line-height: {{ block.settings.heading_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .comparison-heading-{{ block.id }} {
                    text-align: {{ block.settings.heading_align }};
                    margin-top: {{ block.settings.heading_mt }}px;
                    max-width: {{ block.settings.heading_width }}%;
                }

                .comparison-heading-{{ block.id }} * {
                    font-size: {{ block.settings.heading_size }}px;
                }
            }
        </style>

        {% if block.settings.heading_custom %}
            <style>
                .comparison-heading-{{ block.id }} * {
                    font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
                    font-weight: {{ block.settings.heading_font.weight }};
                    font-style: {{ block.settings.heading_font.style }};
                }
            </style>
        {% endif %}

      {% if block.settings.heading_align == "center" %}
        <style>
          @media(min-width: 1024px) {
            .comparison-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: auto;
            } 
          }
        </style>
      {% elsif block.settings.heading_align == "right" %}
        <style>
          @media(min-width: 1024px) {
            .comparison-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: 0px;
            } 
          }
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
            .comparison-heading-{{ block.id }} {
              margin-left: 0px;
              margin-right: 0px;
            } 
          }
        </style>
      {% endif %}

    {% elsif block.type == "text" %}
        <style>
            {{ block.settings.text_font | font_face: font_display: 'swap' }}

            .comparison-text-{{ block.id }} {
                text-align: {{ block.settings.text_align_mobile }};
                margin-top: {{ block.settings.text_mt_mobile }}px;
            }

            .comparison-text-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.text_size_mobile }}px;
                color: {{ block.settings.text_color }};
                line-height: {{ block.settings.text_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .comparison-text-{{ block.id }} {
                    text-align: {{ block.settings.text_align }};
                    margin-top: {{ block.settings.text_mt }}px;
                }

                .comparison-text-{{ block.id }} * {
                    font-size: {{ block.settings.text_size }}px;
                }
            }
        </style>

        {% if block.settings.text_custom %}
            <style>
                .comparison-text-{{ block.id }} {
                    font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
                    font-weight: {{ block.settings.text_font.weight }};
                    font-style: {{ block.settings.text_font.style }};
                }
            </style>
        {% endif %}

    {% elsif block.type == "sub_heading" %}

      <style>
            {{ block.settings.sub_heading_font | font_face: font_display: 'swap' }}

            .comparison-subheading-{{ block.id }} {
                text-align: {{ block.settings.sub_heading_align_mobile }};
                margin-top: {{ block.settings.sub_heading_mt_mobile }}px;
            }

            .comparison-subheading-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.sub_heading_size_mobile }}px;
                color: {{ block.settings.sub_heading_color }};
                line-height: {{ block.settings.sub_heading_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .comparison-subheading-{{ block.id }} {
                    text-align: {{ block.settings.sub_heading_align }};
                    margin-top: {{ block.settings.sub_heading_mt }}px;
                }

                .comparison-subheading-{{ block.id }} * {
                    font-size: {{ block.settings.sub_heading_size }}px;
                }
            }
        </style>

        {% if block.settings.sub_heading_custom %}
            <style>
                .comparison-subheading-{{ block.id }} * {
                    font-family: {{ block.settings.sub_heading_font.family }}, {{ block.settings.sub_heading_font.fallback_families }};
                    font-weight: {{ block.settings.sub_heading_font.weight }};
                    font-style: {{ block.settings.sub_heading_font.style }};
                }
            </style>
        {% endif %}

    {% elsif block.type == "button" %}

        <style>

          {{  block.settings.button_font | font_face: font_display: 'swap' }}

          .comparison-button-{{ block.id }} {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
            width: 100%;
            max-width: fit-content;
            margin: 0;
            margin-top: {{ block.settings.button_mt_mobile }}px;
            font-size: {{ block.settings.button_size_mobile }}px;
            color: {{ block.settings.button_color }};
            line-height: {{ block.settings.button_height }}%;
            text-align: center;
            text-transform: unset;
            text-decoration: none;
            padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
            border-radius: {{ block.settings.button_radius }}px;
            transition: all 0.25s ease 0s;
            position: relative;
            font-weight: 700;
            z-index: 2;
          }

          .comparison-button-{{ block.id }}:hover {
            color: {{ block.settings.button_hover_color }};
            transition: all 0.25s ease 0s;
          }

          .comparison-button-{{ block.id }} svg {
            width: 14px;
            height: 14px;
          }

          .comparison-button-{{ block.id }} svg path {
            fill: {{ block.settings.button_color }};
            transition: all 0.25s ease 0s;
          }

          .comparison-button-{{ block.id }}:hover svg path {
            fill: {{ block.settings.button_hover_color }};
            transition: all 0.25s ease 0s;
          }

          @media(min-width: 1024px) {
            .comparison-button-{{ block.id }} {
                margin-top: {{ block.settings.button_mt }}px;
                padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
                font-size: {{ block.settings.button_size }}px;
            }
          }
        </style>

        {% if block.settings.button_custom %}
            <style>
              .comparison-button-{{ block.id }} {
                  font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
                  font-weight: {{ block.settings.button_font.weight }};
                  font-style: {{ block.settings.button_font.style }};
              }
            </style>
        {% endif %}

        {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
            <style>
              .comparison-button-{{ block.id }} {
                  background-color: {{ block.settings.button_bg_color }};
              }

              .comparison-button-{{ block.id }}:hover {
                  background-color: {{ block.settings.button_bg_hover_color }};
              }
            </style>
        {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
            <style>
              .comparison-button-{{ block.id }} {
                  background-color: {{ block.settings.button_bg_color }};
                  border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
              }

              .comparison-button-{{ block.id }}:hover {
                  background-color: {{ block.settings.button_bg_hover_color }};
                  border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
              }
            </style>
        {% elsif block.settings.button_style == "link" %}
            <style>
                .comparison-button-{{ block.id }} {
                    max-width: fit-content;
                    padding: 0px;
                }
            </style>
        {% endif %}

      {% if block.settings.button_align_mobile == "center" %}
        <style>
          .comparison-button-{{ block.id }} {
            margin-left: auto;
            margin-right: auto;
          }
        </style>
      {% elsif block.settings.button_align_mobile == "right" %}
        <style>
          .comparison-button-{{ block.id }} {
            margin-left: auto;
          }
        </style>
      {% endif %}

      {% if block.settings.button_align == "center" %}
        <style>
          @media(min-width: 1024px) {
            .comparison-button-{{ block.id }} {
              margin-left: auto;
              margin-right: auto;
            } 
          }
        </style>
      {% elsif block.settings.button_align == "right" %}
        <style>
          @media(min-width: 1024px) {
            .comparison-button-{{ block.id }} {
              margin-left: auto;
              margin-right: 0px;
            } 
          }
        </style>
      {% elsif block.settings.button_align == "left" %}
        <style>
          @media(min-width: 1024px) {
            .comparison-button-{{ block.id }} {
              margin-left: 0px;
              margin-right: 0px;
            } 
          }
        </style>
      {% endif %}

    {% endif %}

{% endfor %}

<div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
