                  </div>
                {%- endif -%}

              {%- for i in (1..20) -%}
                {%- assign heading_key = 'heading_' | append: i -%}
                {%- assign value_key = 'value_' | append: i -%}

                {%- assign heading_value = headings_block.settings[heading_key] -%}

                {%- comment -%}We only output the line if at least one value is defined{%- endcomment -%}
                {%- if heading_value != blank and values_blocks[0].settings[value_key] != blank or values_blocks[1].settings[value_key] != blank or values_blocks[2].settings[value_key] != blank -%}
                  {%- assign outputted_count = outputted_count | plus: 1 -%}

                  <div class="comparison-row-{{ section.id }} {% if forloop.first %}comparison-row-body-first-{{ section.id }}{% endif %} comparison-row-body-{{ section.id }}" {% if outputted_count > max_rows %}hidden{% endif %}>
                    {%- if heading_value != blank -%}
                      <div class="comparison-heading-{{ section.id }}" {% if outputted_count == 0 %}{{ headings_block.shopify_attributes }}{% endif %}><p>{{ heading_value }}</p></div>
                    {%- endif -%}

                    {%- for i in (0..2) -%}
                      {%- if values_blocks[i] != blank -%}
                        {%- assign value = values_blocks[i].settings[value_key] -%}
                        {%- assign stripped_value = value | strip_html | strip -%}

                        <div class="comparison-value-{{ section.id }}" {% if outputted_count == 0 %}{{ values_blocks[i].shopify_attributes }}{% endif %}>
                          {%- if stripped_value == 'FALSE' -%}
                            <svg fill="none" class="icon offset-icon" width="16" height="16" viewBox="0 0 16 16" style="--icon-height: 16px">
                              <path d="m.94 3.06 12 12 2.12-2.12-12-12L.94 3.06Zm2.12 12 12-12L12.94.94l-12 12 2.12 2.12Z" fill="{{ section.settings.chart_true_false_color | default: section.settings.chart_text_color | default: value_color }}"></path>
                            </svg>
                          {%- elsif stripped_value == 'TRUE' -%}
                            <svg fill="none" class="icon offset-icon" width="20" height="16" viewBox="0 0 20 16" style="--icon-height: 16px">
                              <path d="m2 7.6 5.259 5.6L18 2" stroke="{{ section.settings.chart_true_false_color | default: section.settings.chart_text_color | default: value_color }}" stroke-width="3"></path>
                            </svg>
                          {%- else -%}
                            {{- value | default: '' -}}
                          {%- endif -%}
                        </div>
                      {%- endif -%}
                    {%- endfor -%}
                  </div>
                {%- endif -%}
              {%- endfor -%}
            </div>
          {%- if outputted_count > max_rows -%}
            <div class="comparison-toggle-{{ section.id }}">
              <button data-action="toggle-rows" data-view-more="{{ view_all_text | escape }}" data-view-less="{{ view_less_text | escape }}" class="comparison-view-{{ section.id }}">
                <span class="comparison-toggle-text-{{ section.id }}">{{- view_all_text | escape -}}</span>
                {% if view_style == "non_outline_arrow" or view_style == "outline_arrow" or view_style == "link" %}
                  <span class="comparison-toggle-icon-{{ section.id }}">
                    <svg role="presentation" focusable="false" width="8" height="8" viewBox="0 0 8 6">
                      <path d="m1 1.5 3 3 3-3" fill="none" stroke="currentColor" stroke-width="1.5"></path>
                    </svg>
                  </span>
                {% endif %}
              </button>
            </div>
          {%- endif -%}
        </div>

      </div>
    </div>
</div>

<script>
  function initComparisonTable7() {
    let buttonView = document.querySelector('.comparison-view-{{ section.id }}');
    let hiddenRows = document.querySelectorAll('.comparison-row-{{ section.id }}[hidden]');
    if(buttonView) {
      buttonView.addEventListener('click', () => {
        let more_text = buttonView.getAttribute('data-view-more');
        let less_text = buttonView.getAttribute('data-view-less');
        let textElement = buttonView.querySelector('.comparison-toggle-text-{{ section.id }}');

        buttonView.classList.toggle('active');

        if(textElement) {
          if(buttonView.classList.contains('active')) {
            textElement.innerText = less_text;
          } else {
            textElement.innerText = more_text;
          }
        }

        if(hiddenRows) {
          hiddenRows.forEach(el => {
            if(buttonView.classList.contains('active')) {
              el.removeAttribute('hidden');
            } else {
              el.setAttribute('hidden','');
            }
          })
        }

      })
    }

    {% if total_rows_count > 6 %}
    let row = document.querySelector('.comparison-row-body-first-{{ section.id }}');
    let stickyRow = document.querySelector('.comparison-row-{{ section.id }}--sticky');
    let header = document.querySelector('#shopify-section-header');
    let innerTable = document.querySelector('.comparison-inner-table-{{ section.id }}');

    let headerHeight = header.clientHeight;

    let rowWidth = row.clientWidth;

    stickyRow.style.width = rowWidth + "px";

    window.addEventListener('resize', function() {
      let rowWidth = row.clientWidth;
      stickyRow.style.width = rowWidth + "px";
    });

    window.addEventListener('scroll', function() {

      if(header.classList.contains('shopify-section-header-hidden')) {
        stickyRow.style.top = 0 + "px";
      } else {
       stickyRow.style.top = headerHeight + "px"; 
      }

      let rowPosition = row.getBoundingClientRect().top;
      let stickyRowHeight = stickyRow.clientHeight;
      let innerTableBottom = innerTable.getBoundingClientRect().bottom - stickyRowHeight;

      if (rowPosition <= 100 && innerTableBottom > 0) {
          stickyRow.classList.add('is-visible');
      } else {
          stickyRow.classList.remove('is-visible');
      }
    });

    innerTable.addEventListener('scroll', function() {
      const scrollLeft = this.scrollLeft;

      stickyRow.style.marginLeft = `-${scrollLeft}px`;
    });  
    {% endif %}
  }

  document.addEventListener('DOMContentLoaded', initComparisonTable7);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initComparisonTable7);
     document.addEventListener('shopify:section:load', initComparisonTable7);
  }
</script>
