-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  column_title_font | font_face: font_display: 'swap' }}
  {{  column_subtitle_font | font_face: font_display: 'swap' }}
  {{  value_title_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .comparison-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .comparison-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    font-weight: 700;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .comparison-table-{{ section.id }} {
    margin-top: {{ table_mt_mobile }}px;
  }

  .comparison-row-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr 1fr;
    border-bottom: {{ column_border_thickness }}px solid {{ column_border_color }};
  }

  .comparison-row-hidden-{{ section.id }} {
    display: none;
  }

  .comparison-row-hidden-{{ section.id }}.active {
    display: grid;
  }

  .comparison-column-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    gap: 10px;
    background: {{ column_bg_color }};
    padding: {{ column_padding_vertical_mobile }}px {{ column_padding_horizontal_mobile }}px;
    border-radius: {{ column_radius }}px;
    width: 100%;
  }

  .comparison-column-{{ section.id }}:last-child {
    border-left: {{ column_border_thickness }}px solid {{ column_border_color }};
  }

  .comparison-title-{{ section.id }} {
    margin: 0px;
    text-align: {{ column_content_align_mobile }};
    font-size: {{ column_title_size_mobile }}px;
    font-weight: 700;
    color: {{ column_title_color }};
    line-height: {{ column_title_height }}%;
    text-transform: unset;
  }

  .comparison-image-{{ section.id }} {
    margin-top: {{ image_mt_mobile }}px;
    width: 100%;
    overflow: hidden;
    margin-left: auto;
    margin-right: auto;
    border-radius: {{ image_radius }}px;
  }

  .comparison-image-{{ section.id }} img,
  .comparison-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-image-{{ section.id }} svg {
    background: #AFAFAF;
  }

  .comparison-subtitle-{{ section.id }} {
    margin: 0px;
    margin-top: {{ column_subtitle_mt_mobile }}px;
    text-align: {{ column_content_align_mobile }};
    font-size: {{ column_subtitle_size_mobile }}px;
    font-weight: 700;
    color: {{ column_subtitle_color }};
    line-height: {{ column_subtitle_height }}%;
    text-transform: unset;
  }

  .comparison-value-title-{{ section.id }} {
    margin: 0px;
    text-align: {{ column_content_align_mobile }};
    font-size: {{ value_title_size_mobile }}px;
    font-weight: 700;
    color: {{ value_title_color }};
    line-height: {{ value_title_height }}%;
    text-transform: unset;
  }

  .comparison-value-image-{{ section.id }} {
    margin-top: {{ value_image_mt_mobile }}px;
    width: 100%;
    overflow: hidden;
    margin-left: auto;
    margin-right: auto;
    border-radius: {{ value_image_radius }}px;
  }

  .comparison-value-image-{{ section.id }} img,
  .comparison-value-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .comparison-value-image-{{ section.id }} svg {
    background: #AFAFAF;
  }

  .comparison-text-{{ section.id }} {
    margin: 0px;
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ column_content_align_mobile }};
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .comparison-button-{{ section.id }} {
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    cursor: pointer;
    padding: 0px;
    margin-top: {{ button_mt_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .comparison-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .comparison-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .comparison-table-{{ section.id }} {
      margin-top: {{ table_mt }}px;
    }

    .comparison-image-{{ section.id }} {
      margin-top: {{ image_mt }}px;
      max-width: {{ image_width }}%;
    }

    .comparison-column-{{ section.id }} {
      padding: {{ column_padding_vertical }}px {{ column_padding_horizontal }}px;
    }

    .comparison-title-{{ section.id }} {
      text-align: {{ column_content_align }};
      font-size: {{ column_title_size }}px;
    }

    .comparison-subtitle-{{ section.id }} {
      margin-top: {{ column_subtitle_mt }}px;
      text-align: {{ column_content_align }};
      font-size: {{ column_subtitle_size }}px;
    }

    .comparison-value-title-{{ section.id }} {
      text-align: {{ column_content_align }};
      font-size: {{ value_title_size }}px;
    }

    .comparison-value-image-{{ section.id }} {
      margin-top: {{ value_image_mt }}px;
      max-width: {{ value_image_width }}%;
    }

    .comparison-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ column_content_align }};
      font-size: {{ text_size }}px;
    }

    .comparison-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .comparison-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if column_title_custom %}
  <style>
    .comparison-title-{{ section.id }} {
      font-family: {{ column_title_font.family }}, {{ column_title_font.fallback_families }};
      font-weight: {{ column_title_font.weight }};
      font-style: {{ column_title_font.style }};
    }
  </style>
{% endif %}

{% if column_subtitle_custom %}
  <style>
    .comparison-subtitle-{{ section.id }} {
      font-family: {{ column_subtitle_font.family }}, {{ column_subtitle_font.fallback_families }};
      font-weight: {{ column_subtitle_font.weight }};
      font-style: {{ column_subtitle_font.style }};
    }
  </style>
{% endif %}

{% if value_title_custom %}
  <style>
    .comparison-value-title-{{ section.id }} {
      font-family: {{ value_title_font.family }}, {{ value_title_font.fallback_families }};
      font-weight: {{ value_title_font.weight }};
      font-style: {{ value_title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .comparison-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .comparison-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .comparison-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }

    .comparison-versus-icon-{{ section.id }} {
      top: 16%;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .comparison-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
    .comparison-versus-icon-{{ section.id }} {
      top: 10%;
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .comparison-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }    
    .comparison-versus-icon-{{ section.id }} {
      top: 14%;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .comparison-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% endif %}

{% if value_image_ratio_mobile == "portrait" %}
  <style>
    .comparison-value-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }

    .comparison-versus-icon-{{ section.id }} {
      top: 16%;
    }
  </style>
{% elsif value_image_ratio_mobile == "landscape" %}
  <style>
    .comparison-value-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
    .comparison-versus-icon-{{ section.id }} {
      top: 10%;
    }
  </style>
{% elsif value_image_ratio_mobile == "square" %}
  <style>
    .comparison-value-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }    
    .comparison-versus-icon-{{ section.id }} {
      top: 14%;
    }
  </style>
{% endif %}

{% if value_image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .comparison-value-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif value_image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-value-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif value_image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .comparison-value-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} comparison-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  <div class="section-{{ section.id }}-settings">
    {% if heading != blank %}
      <div class="comparison-heading-{{ section.id }}">
        {{ heading }}
      </div>
    {% endif %}
    <div class="comparison-table-{{ section.id }}">
      <div class="comparison-row-{{ section.id }}">
        <div class="comparison-column-{{ section.id }}">
          {% if column_title_left != blank %}
            <p class="comparison-title-{{ section.id }}">{{ column_title_left }}</p>
          {% endif %}
          <div class="comparison-image-{{ section.id }}">
            {% if first_image != blank %}
              <img src="{{ first_image | image_url }}" alt="{{  first_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}  
          </div>
          {% if column_subtitle_left != blank %}
            <p class="comparison-subtitle-{{ section.id }}">{{ column_subtitle_left }}</p>
          {% endif %}
        </div>
        <div class="comparison-column-{{ section.id }}">
          {% if column_title_right != blank %}
            <p class="comparison-title-{{ section.id }}">{{ column_title_right }}</p>
          {% endif %}
          <div class="comparison-image-{{ section.id }}">
            {% if second_image != blank %}
              <img src="{{ second_image | image_url }}" alt="{{ second_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}  
          </div>
          {% if column_subtitle_right != blank %}
            <p class="comparison-subtitle-{{ section.id }}">{{ column_subtitle_right }}</p>
          {% endif %}
        </div>
      </div>
      {% for block in section.blocks %}
        <div class="comparison-row-{{ section.id }} comparison-row-hidden-{{ section.id }}">
          <div class="comparison-column-{{ section.id }}"> 
            {% if block.settings.left_title != blank %}
              <p class="comparison-value-title-{{ section.id }}">{{ block.settings.left_title }}</p>  
            {% endif %}   
            {% if block.settings.left_image != blank %}
              <div class="comparison-value-image-{{ section.id }}">
                <img src="{{ block.settings.left_image | img_url: "500x500" }}" alt="{{ block.settings.left_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </div>  
            {% endif %}   
            {% if block.settings.left_text != blank %}
              <p class="comparison-text-{{ section.id }}">{{ block.settings.left_text }}</p>  
            {% endif %}                                      
          </div>
          <div class="comparison-column-{{ section.id }}">
            {% if block.settings.right_title != blank %}
              <p class="comparison-value-title-{{ section.id }}">{{ block.settings.right_title }}</p>  
            {% endif %}   
            {% if block.settings.right_image != blank %}
              <div class="comparison-value-image-{{ section.id }}">
                <img src="{{ block.settings.right_image | img_url: "500x500" }}" alt="{{ block.settings.right_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </div>  
            {% endif %}    
            {% if block.settings.right_text != blank %}
              <p class="comparison-text-{{ section.id }}">{{ block.settings.right_text }}</p>  
            {% endif %}                                        
          </div>
        </div>
      {% endfor %}
      <button class="comparison-button-{{ section.id }}">
        <span class="comparison-button-more-{{ section.id }}">{{ button_more }}</span>
        <span class="comparison-button-less-{{ section.id }}">{{ button_less }}</span>
      </button>
    </div>    
  </div>
</div>
