       margin-top: {{ label_mt_mobile }}px;
    }

    @media(min-width: 1024px) {
      .contact-field-{{ block.id }}.contact-field-date-{{ block.id }} {
        flex: 0 1 100%;
      }

      .contact-field-{{ block.id }}.contact-field-date-{{ block.id }} input {
        width: {{ block.settings.width }}%;
      }

      .contact-field-{{ block.id }} {
        flex: 0 1 {{ block.settings.width }}%;
