  <style>
    .contact-{{ section.id }} .errors * {
      font-family: {{ error_text_font.family }}, {{ error_text_font.fallback_families }};
      font-weight: {{ error_text_font.weight }};
      font-style: {{ error_text_font.style }};
    }
  </style>
