      font-family: {{ submit_font.family }}, {{ submit_font.fallback_families }};
      font-weight: {{ submit_font.weight }};
      font-style: {{ submit_font.style }};
    }
  </style>
{% endif %}


{% if layout_mobile == 'content_form'%}
  <style>
    .contact-body-{{ section.id }} {
      grid-template-areas: 
        "content"
        "form";     
    }
  </style>
{% elsif layout_mobile == 'form_content' %}
  <style>
    .contact-body-{{ section.id }} {
      grid-template-areas: 
        "form"
        "content";     
    }
  </style>
{% endif %}

{% if layout == 'content_form'%}
  <style>
    @media(min-width: 1024px) {
      .contact-body-{{ section.id }} {
        grid-template-areas: "content form";    
      }
    }    
  </style>
