{%- style -%}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  label_font | font_face: font_display: 'swap' }}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  checkbox_font | font_face: font_display: 'swap' }}
  {{  required_font | font_face: font_display: 'swap' }}
  {{  error_text_font | font_face: font_display: 'swap' }}
  {{  success_text_font | font_face: font_display: 'swap' }}
  {{  submit_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .contact-layout-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .contact-image-{{ section.id }} {
    grid-area: image;
    margin-top: {{ form_padding_vertical_mobile }}px;
    margin-bottom: {{ form_padding_vertical_mobile }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
    position: relative;
  }

  .contact-image-{{ section.id }} img,
  .contact-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .contact-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .contact-image-content-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 40px;
    display: flex;
    flex-direction: column;
    justify-content: {{ image_content_vertical_align_mobile }};
    align-items: {{ image_content_horizontal_align_mobile }};
  }

  .contact-form-{{ section.id }} {
    grid-area: form;
    padding: {{ form_padding_vertical_mobile }}px {{ form_padding_horizontal_mobile }}px;
    background-color: {{ form_bg_color }};
  }

  .contact-fields-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: stretch;
    gap: {{ form_gap_mobile }}px;
    margin-top: {{ form_mt_mobile }}px;
  }

  .contact-success-{{ section.id }} {
    display: block;
    margin: 0;
    margin-top: {{ success_text_mt_mobile }}px;
    font-size: {{ success_text_size_mobile }}px;
    color: {{ success_text_color }};
    line-height: {{ success_text_height }}%;
    text-align: {{ success_text_align_mobile }};
    text-transform: unset;
  }

  .contact-field-label-{{ section.id }} {
    display: block;
    margin-top: {{ label_mt_mobile }}px;
    font-size: {{ label_size_mobile }}px;
    color: {{ label_color }};
    line-height: {{ label_height }}%;
    text-transform: unset;
    font-weight: 400;
  }

  .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
  .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
    margin-top: {{ label_mt_mobile }}px;
  }

  .contact-field-title-{{ section.id }} {
    display: flex;
    align-items: flex-start;
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .contact-field-required-{{ section.id }} {
    margin: 0;
    font-size: {{ required_size_mobile }}px;
    color: {{ required_color }};
    line-height: {{ required_height }}%;
    text-transform: unset;
  }

  .contact-{{ section.id }} .errors ul {
    padding: 0;
    list-style: none;
  }

  .contact-{{ section.id }} .errors * {
    margin: 0;
    margin-top: {{ error_text_mt_mobile }}px;
    font-size: {{ error_text_size_mobile }}px;
    color: {{ error_text_color }};
    line-height: {{ error_text_height }}%;
    text-align: {{ error_text_align_mobile }};
    text-transform: unset;
  }

  .contact-field-input-{{ section.id }} {
    display: block;
    width: 100%;
    margin: 0;
    margin-top: {{ input_mt_mobile }}px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    padding: {{ input_padding_vertical_mobile }}px {{ input_padding_horizontal_mobile }}px;
    border: {{ input_border_thickness }}px solid {{ input_border_color }};
    border-radius: {{ input_radius }}px;
    background-color: {{ input_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .contact-field-input-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    border: {{ input_border_thickness }}px solid {{ input_border_hover_color }};
  }

  .contact-field-input-{{ section.id }}:after,
  .contact-field-input-{{ section.id }}:before {
    display: none;
  }

  .contact-field-input-{{ section.id }}:focus,
  .contact-field-input-{{ section.id }}:focus-visible {
    outline-offset: unset;
    box-shadow: none;
    outline: 0px;
    border-color: {{ input_active_color }};
  }

  .contact-field-input-{{ section.id }}::placeholder {
    color: {{ placeholder_color }};
  }

  .contact-field-select-{{ section.id }} {
    position: relative;
  }

  .contact-field-select-svg-{{ section.id }} {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    right: 15px;
    width: 12px;  
    pointer-events: none;
  }

  .contact-field-select-svg-{{ section.id }} path {
    fill: {{ select_icon_color }};
  }

  .contact-field-input-select-{{ section.id }} {
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    color: {{ input_color }};
  }

  .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
  .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
    margin-top: {{ label_mt_mobile }}px;
  }

  .contact-field-checkbox-{{ section.id }} input[type="checkbox"] {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }

  .contact-field-checkbox-{{ section.id }} label {
    position: relative;
    padding-left: {{ checkbox_size_mobile | plus: 5 }}px;
    cursor: pointer;
    margin: 0;
    font-size: {{ checkbox_size_mobile }}px;
    color: {{ checkbox_color }};
    line-height: {{ checkbox_height }}%;
    text-transform: unset;
  }

  .contact-field-checkbox-{{ section.id }} label:before {
    margin: 0;
    content: '';
    position: absolute;
    {% if checkbox_size_mobile < 20 %}
    left: {{ checkbox_size_mobile | times: 0.13 | round: 0 }}px;
    {% else %}
    left: {{ checkbox_size_mobile | times: 0.18 | round: 0 }}px;
    {% endif %}
    {% if checkbox_size_mobile < 10 %}
    top: {{ checkbox_size_mobile | times: 0.4 | round: 0 }}px;
    {% elsif checkbox_size_mobile < 20 %}
    top: {{ checkbox_size_mobile | times: 0.3 | round: 0 }}px;
    {% elsif checkbox_size_mobile < 30 %}
    top: {{ checkbox_size_mobile | times: 0.25 | round: 0 }}px;
    {% else %}
    top: {{ checkbox_size_mobile | times: 0.18 | round: 0 }}px;
    {% endif %}
    width: {{ checkbox_size_mobile | times: 0.7 | round: 0 }}px;
    height: {{ checkbox_size_mobile | times: 0.7 | round: 0 }}px;
    transform: scale(0);
    z-index: 2;
    background-color: {{ checkbox_checkmar_color }};
    clip-path: polygon(13.29% 49.19%,3.89% 59.8%,40.98% 90%,96.95% 21.23%,86.94% 13.08%,85.97% 12.29%,38.95% 70.07%);
  }

  .contact-field-checkbox-{{ section.id }} label:after {
    transform: translateY(-2px);
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: {{ checkbox_size_mobile }}px;
    height: {{ checkbox_size_mobile }}px;
    margin-top: 3px;
    z-index: 1;
    background-color: {{ checkbox_bg_color }};
    border: 1px solid {{ checkbox_border_color }};
    border-radius: {{ checkbox_radius }}px;
  }

  .contact-field-checkbox-{{ section.id }} input:checked + label:before {
    transform: scale(1) translateY(-2px);
  }

  .contact-field-checkbox-{{ section.id }} input:checked + label:after {
    background-color: {{ checkbox_bg_active_color }};
    border: 1px solid {{ checkbox_border_active_color }};
  }

  .contact-field-inner-{{ section.id }} textarea {
    font-family: Montserrat, sans-serif;
    min-height: 250px;
    resize: vertical;
  }

  .contact-submit-button-{{ section.id }} {
    display: flex;
    width: 100%;
    margin-top: {{ submit_mt_mobile }}px;
  }

  .contact-submit-{{ section.id }} {
    display: block;
    width: 100%;
    word-break: break-word;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-transform: unset;    
    padding: {{ submit_padding_vertical_mobile }}px {{ submit_padding_horizontal_mobile }}px;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    border-radius: {{ submit_radius }}px;
    background-color: {{ submit_bg_color }};
    background-image: {{ submit_bg_gradient }};
    cursor: pointer;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .contact-submit-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ submit_hover_color }};
    background-color: {{ submit_bg_hover_color }};
    background-image: {{ submit_bg_hover_gradient }};
    border: {{ submit_border_thickness }}px solid {{ submit_border_hover_color }};
  }

  input[type="date"]::-webkit-calendar-picker-indicator {
    opacity: 0;
    cursor: pointer;
    position: relative;
    z-index: 2;
  }

  input[type="date"] {
    position: relative;
  }

  .custom-date-icon-{{ section.id }} {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    pointer-events: none;
    width: 16px;
    height: 16px;
    fill: {{ input_color }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .contact-layout-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
    }

    .contact-image-{{ section.id }} {   
      margin-top: {{ form_padding_vertical }}px;
      margin-bottom: {{ form_padding_vertical }}px;  
    }

    .contact-form-{{ section.id }} {
      padding: {{ form_padding_vertical }}px {{ form_padding_horizontal }}px;
    }

    .contact-fields-{{ section.id }} {
      gap: {{ form_gap }}px;
      margin-top: {{ form_mt }}px;
    }

    .contact-field-label-{{ section.id }} {
      margin-top: {{ label_mt }}px;
      font-size: {{ label_size }}px;
    }

    .contact-field-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
    .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
      margin-top: {{ label_mt }}px;
    }

    .contact-field-label-file-{{ section.id }} {
      margin-top: {{ label_mt }}px !important;
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px {{ input_padding_vertical | plus: 50 }}px;
    }

    .contact-field-required-{{ section.id }} {
      font-size: {{ required_size }}px;
    }

    .contact-success-{{ section.id }} {
      margin-top: {{ success_text_mt }}px;
      font-size: {{ success_text_size }}px;
      text-align: {{ success_text_align }};
    }

    .contact-{{ section.id }} .errors * {
      margin-top: {{ error_text_mt }}px;
      text-align: {{ error_text_align }};
      font-size: {{ error_text_size }}px;
    }

    .contact-field-input-{{ section.id }} {
      font-size: {{ input_size }}px;
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
      margin-top: {{ input_mt }}px;
    }

    .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
    .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
      margin-top: {{ label_mt }}px;
    }

    .contact-field-checkbox-{{ section.id }} label {
      padding-left: {{ checkbox_size | plus: 5 }}px;
      font-size: {{ checkbox_size }}px;
    }

    .contact-field-checkbox-{{ section.id }} label:before {
      {% if checkbox_size < 10 %}
      left: {{ checkbox_size | times: 0.15 | round: 0 }}px;
      {% elsif checkbox_size < 20 %}
      left: {{ checkbox_size | times: 0.15 | round: 0 }}px;
      {% else %}
      left: {{ checkbox_size | times: 0.18 | round: 0 }}px;
      {% endif %}    
      {% if checkbox_size_mobile < 10 %}
      top: {{ checkbox_size_mobile | times: 0.4 | round: 0 }}px;  
      {% elsif checkbox_size < 20 %}
      top: {{ checkbox_size | times: 0.3 | round: 0 }}px;
      {% elsif checkbox_size < 30 %}
      top: {{ checkbox_size | times: 0.25 | round: 0 }}px;
      {% else %}
      top: {{ checkbox_size | times: 0.18 | round: 0 }}px;
      {% endif %};
      width: {{ checkbox_size | times: 0.7 | round: 0 }}px;
      height: {{ checkbox_size | times: 0.7 | round: 0 }}px;
    }

