                              <path d="M19 4h-1V2h-2v2H8V2H6v2H5a2 2 0 00-2 2v14a2 2 0 002 2h14a2 2 0 002-2V6a2 2 0 00-2-2zm0 16H5V9h14v11zM7 11h2v2H7v-2zm4 0h2v2h-2v-2zm4 0h2v2h-2v-2z"/>
                            </svg>
                          </div>                        
                         </div>
                      {% when 'text_field' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">              
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">           
                              {{ block.settings.label }}
                            </label>
                          {% endif %}
                          <input type="{% if block.settings.type == "tel" %}tel{% elsif block.settings.type == "email" %}email{% else %}text{% endif %}" {% if block.settings.placeholder != blank %}placeholder="{{ block.settings.placeholder }}"{% endif %} class="contact-field-input-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="{% if block.settings.type == "email" %}contact[email]{% else %}contact[{{ block.settings.name }}]{% endif %}" {% if block.settings.required %}required{% endif %}>
                        </div>
                      {% when 'textarea' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                        
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</label>
                          {% endif %}
                          <textarea {% if block.settings.placeholder != blank %}placeholder="{{ block.settings.placeholder }}"{% endif %} class="contact-field-input-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="{% if block.settings.type == "message" %}contact[message]{% else %}contact[{{ block.settings.name }}]{% endif %}" {% if block.settings.required %}required{% endif %}></textarea>
                        </div>
                      {% when 'dropdown' %}
                        {% assign options = block.settings.dropdown_options | strip | split: ',' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                        
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</label>
                          {% endif %}
                          <div class="contact-field-select-{{ section.id }}">
                            <svg class="contact-field-select-svg-{{ section.id }}" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg"><path d="M512 581.5l273.1-273.1c25-25 65.5-25 90.5 0s25 65.5 0 90.5l-316.7 316.7c-12.9 12.9-30 19.2-47 18.7-16.9 0.4-33.9-5.8-46.8-18.699l-316.8-316.8c-25-25-25-65.5 0-90.5s65.5-25 90.5 0l273.2 273.2z"/></svg>
                            <select class="contact-field-input-{{ section.id }} contact-field-input-select-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="contact[{{block.settings.name}}]" {% if block.settings.required %}required{% endif %} style="cursor: pointer;">
                              {% if block.settings.placeholder  %}
                                <option value = "" disabled selected>{% if block.settings.placeholder != blank %}{{ block.settings.placeholder }}{% endif %}</option>
                              {% endif %}

                              {% for option in options %}
                                <option value = "{{option}}">{{option}}</option>
                              {% endfor %}
                            </select>  
                          </div>

                        </div>
                      {% when 'checkbox' %}
                        {% assign optionscheckbox = block.settings.checkbox_options | strip | split: ',' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                       
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <p class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</p>
                          {% endif %}
                          {% for option in optionscheckbox %}
                            <div class="contact-field-checkbox-{{ section.id }}">
                              <input type="checkbox" id="ContactForm{{ block.settings.name }}{{ forloop.index }}" name="{% if block.settings.use_newsletter and forloop.first %}customer[accepts_marketing]{% else %}contact[{{ block.settings.name }}-{{ option }}]{% endif %}">
                              <label for="ContactForm{{ block.settings.name }}{{ forloop.index }}" class="contact-field-label-{{ block.id }}">{{ option }}</label>
                            </div>
                          {% endfor %}
                        </div>
                    {% endcase %}
                  </div>    
                {% endunless %}                 
              {% endfor %}
            </div>

            <div class="contact-submit-button-{{ section.id }}">
              <input type="submit" class="contact-submit-{{ section.id }}" value="{% if submit != blank %}{{ submit }}{% else %}Submit{% endif %}">
            </div>                 

          {% endform %}
        </div>   
      </div>

      {% for block in section.blocks %}
        {% if block.type == 'bottom_text' %}
          {% if block.settings.text != blank %}
            <div class="contact-bottom-text-{{ block.id }}">
              {{ block.settings.text }}
            </div>
          {% endif %}     
        {% endif %}
      {% endfor %}    
    </div>
</div>
