{%- style -%}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  label_font | font_face: font_display: 'swap' }}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  checkbox_font | font_face: font_display: 'swap' }}
  {{  required_font | font_face: font_display: 'swap' }}
  {{  error_text_font | font_face: font_display: 'swap' }}
  {{  success_text_font | font_face: font_display: 'swap' }}
  {{  submit_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .contact-form-{{ section.id }} {
    grid-area: form;
    padding: 0;
    background-color: {{ form_bg_color }};
  }

  .contact-fields-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: stretch;
    gap: {{ form_gap_mobile }}px;
    margin-top: {{ form_mt_mobile }}px;
  }

  .contact-success-{{ section.id }} {
    display: block;
    margin: 16px 0;
    font-size: {{ success_text_size_mobile }}px;
    color: {{ success_text_color }};
    line-height: {{ success_text_height }}%;
    text-align: {{ success_text_align_mobile }};
    text-transform: unset;
  }

  .contact-field-label-{{ section.id }} {
    display: block;
    margin-top: {{ label_mt_mobile }}px;
    font-size: {{ label_size_mobile }}px;
    color: {{ label_color }};
    line-height: {{ label_height }}%;
    text-transform: unset;
    font-weight: 400;
  }

  .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
  .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
    margin-top: {{ label_mt_mobile }}px;
  }

  .contact-field-title-{{ section.id }} {
    display: flex;
    align-items: flex-start;
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .contact-field-required-{{ section.id }} {
    margin: 0;
    font-size: {{ required_size_mobile }}px;
    color: {{ required_color }};
    line-height: {{ required_height }}%;
    text-transform: unset;
  }

  .contact-{{ section.id }} .errors ul {
    padding: 0;
    list-style: none;
  }

  .contact-{{ section.id }} .errors * {
    margin: 16px 0;
    font-size: {{ error_text_size_mobile }}px;
    color: {{ error_text_color }};
    line-height: {{ error_text_height }}%;
    text-align: {{ error_text_align_mobile }};
    text-transform: unset;
  }

  .contact-field-form-tab-{{ section.id }} {
    width: 100%;
    padding: 3rem;
    border: {{ input_border_thickness | plus: 2 }}px solid {{ input_border_color }};
    border-radius: {{ input_radius }}px;
    margin-top: 36px !important;
  }

  .contact-field-form-tab-{{ section.id }} {
    display: none !important;
  }

  .contact-field-form-tab-{{ section.id }}.active {
    display: flex !important;
  }

  .contact-field-input-{{ section.id }} {
    display: block;
    width: 100%;
    margin: 0;
    margin-top: {{ input_mt_mobile }}px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    border: 0;
    padding: {{ input_padding_vertical_mobile }}px {{ input_padding_horizontal_mobile }}px;
    border-bottom: {{ input_border_thickness }}px solid {{ input_border_color }}; 
    background-color: {{ input_bg_color }};
    transition: all 0.25s ease 0s;
    border-radius: 0;
  }

  .contact-field-input-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    border-color: {{ input_border_hover_color }};
  }

  .contact-field-input-{{ section.id }}:after,
  .contact-field-input-{{ section.id }}:before {
    display: none;
  }

  .contact-field-input-{{ section.id }}:focus,
  .contact-field-input-{{ section.id }}:focus-visible {
    outline-offset: unset;
    box-shadow: none;
    outline: 0px;
    border-color: {{ input_active_color }};
  }

  .contact-field-input-{{ section.id }}::placeholder {
    color: {{ placeholder_color }};
  }

  .contact-field-select-{{ section.id }} {
    position: relative;
  }

  .contact-field-select-svg-{{ section.id }} {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    right: 15px;
    width: 12px;  
  }

  .contact-field-select-svg-{{ section.id }} path {
    fill: {{ select_icon_color }};
  }

  .contact-field-input-select-{{ section.id }} {
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    color: {{ input_color }};
    border: {{ input_border_thickness }}px solid {{ input_border_color }};
    border-radius: {{ input_radius }}px;
  }

  .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
  .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
    margin-top: {{ label_mt_mobile }}px;
  }

  .contact-field-checkbox-{{ section.id }} input[type="checkbox"] {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }

  .contact-field-checkbox-{{ section.id }} label {
    position: relative;
    padding-left: {{ checkbox_size_mobile | plus: 5 }}px;
    cursor: pointer;
    margin: 0;
    font-size: {{ checkbox_size_mobile }}px;
    color: {{ checkbox_color }};
    line-height: {{ checkbox_height }}%;
    text-transform: unset;
  }

  .contact-field-checkbox-{{ section.id }} label:before {
    margin: 0;
    content: '';
    position: absolute;
    {% if checkbox_size_mobile < 20 %}
    left: {{ checkbox_size_mobile | times: 0.13 | round: 0 }}px;
    {% else %}
    left: {{ checkbox_size_mobile | times: 0.18 | round: 0 }}px;
    {% endif %}
    {% if checkbox_size_mobile < 10 %}
    top: {{ checkbox_size_mobile | times: 0.4 | round: 0 }}px;
    {% elsif checkbox_size_mobile < 20 %}
    top: {{ checkbox_size_mobile | times: 0.3 | round: 0 }}px;
    {% elsif checkbox_size_mobile < 30 %}
    top: {{ checkbox_size_mobile | times: 0.25 | round: 0 }}px;
    {% else %}
    top: {{ checkbox_size_mobile | times: 0.18 | round: 0 }}px;
    {% endif %}
    width: {{ checkbox_size_mobile | times: 0.7 | round: 0 }}px;
    height: {{ checkbox_size_mobile | times: 0.7 | round: 0 }}px;
    transform: scale(0);
    z-index: 2;
    background-color: {{ checkbox_checkmar_color }};
    clip-path: polygon(13.29% 49.19%,3.89% 59.8%,40.98% 90%,96.95% 21.23%,86.94% 13.08%,85.97% 12.29%,38.95% 70.07%);
  }

  .contact-field-checkbox-{{ section.id }} label:after {
    transform: translateY(-2px);
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: {{ checkbox_size_mobile }}px;
    height: {{ checkbox_size_mobile }}px;
    margin-top: 3px;
    z-index: 1;
    background-color: {{ checkbox_bg_color }};
    border: 1px solid {{ checkbox_border_color }};
    border-radius: {{ checkbox_radius }}px;
  }

  .contact-field-checkbox-{{ section.id }} input:checked + label:before {
    transform: scale(1) translateY(-2px);
  }

  .contact-field-checkbox-{{ section.id }} input:checked + label:after {
    background-color: {{ checkbox_bg_active_color }};
    border: 1px solid {{ checkbox_border_active_color }};
  }

  .contact-field-inner-{{ section.id }} textarea {
    min-height: 250px;
    resize: vertical;
    border: {{ input_border_thickness }}px solid {{ input_border_color }};
    border-radius: {{ input_radius }}px;
  }

  .contact-submit-button-{{ section.id }} {
    display: flex;
    width: 100%;
    margin-top: {{ submit_mt_mobile }}px;
    justify-content: {{ content_horizontal_align_mobile }};
  }

  .contact-submit-{{ section.id }} {
    display: block;
    width: 100%;
    word-break: break-word;
    max-width: fit-content;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-transform: unset;    
    padding: {{ submit_padding_vertical_mobile }}px {{ submit_padding_horizontal_mobile }}px;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    border-radius: {{ submit_radius }}px;
    background-color: {{ submit_bg_color }};
    background-image: {{ submit_bg_gradient }};
    cursor: pointer;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .contact-submit-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ submit_hover_color }};
    background-color: {{ submit_bg_hover_color }};
    background-image: {{ submit_bg_hover_gradient }};
    border: {{ submit_border_thickness }}px solid {{ submit_border_hover_color }};
  }

  input[type="date"]::-webkit-calendar-picker-indicator {
    opacity: 0;
    cursor: pointer;
    position: relative;
    z-index: 2;
  }

  input[type="date"] {
    position: relative;
  }

  .custom-date-icon-{{ section.id }} {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    pointer-events: none;
    width: 16px;
    height: 16px;
    fill: {{ input_color }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .contact-form-{{ section.id }} {
      padding: 0;
    }

    .contact-fields-{{ section.id }} {
      gap: {{ form_gap }}px;
      margin-top: {{ form_mt }}px;
    }

    .contact-field-label-{{ section.id }} {
      margin-top: {{ label_mt }}px;
      font-size: {{ label_size }}px;
    }

    .contact-field-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
    .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
      margin-top: {{ label_mt }}px;
    }

    .contact-field-label-file-{{ section.id }} {
      margin-top: {{ label_mt }}px !important;
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px {{ input_padding_vertical | plus: 50 }}px;
    }

    .contact-field-required-{{ section.id }} {
      font-size: {{ required_size }}px;
    }

    .contact-success-{{ section.id }} {
      font-size: {{ success_text_size }}px;
      text-align: {{ success_text_align }};
    }

    .contact-{{ section.id }} .errors * {
      text-align: {{ error_text_align }};
      font-size: {{ error_text_size }}px;
    }

    .contact-field-input-{{ section.id }} {
      font-size: {{ input_size }}px;
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
      margin-top: {{ input_mt }}px;
    }

    .contact-field-title-{{ section.id }} + .contact-field-checkbox-{{ section.id }},
    .contact-field-label-{{ section.id }} + .contact-field-checkbox-{{ section.id }} {
      margin-top: {{ label_mt }}px;
    }

    .contact-field-checkbox-{{ section.id }} label {
      padding-left: {{ checkbox_size | plus: 5 }}px;
      font-size: {{ checkbox_size }}px;
    }

    .contact-field-checkbox-{{ section.id }} label:before {
      {% if checkbox_size < 10 %}
      left: {{ checkbox_size | times: 0.15 | round: 0 }}px;
      {% elsif checkbox_size < 20 %}
      left: {{ checkbox_size | times: 0.15 | round: 0 }}px;
      {% else %}
      left: {{ checkbox_size | times: 0.18 | round: 0 }}px;
      {% endif %}    
      {% if checkbox_size_mobile < 10 %}
      top: {{ checkbox_size_mobile | times: 0.4 | round: 0 }}px;  
      {% elsif checkbox_size < 20 %}
      top: {{ checkbox_size | times: 0.3 | round: 0 }}px;
      {% elsif checkbox_size < 30 %}
      top: {{ checkbox_size | times: 0.25 | round: 0 }}px;
      {% else %}
      top: {{ checkbox_size | times: 0.18 | round: 0 }}px;
      {% endif %};
      width: {{ checkbox_size | times: 0.7 | round: 0 }}px;
      height: {{ checkbox_size | times: 0.7 | round: 0 }}px;
    }

    .contact-image-{{ section.id }} {
      aspect-ratio: auto !important;
    }

    .contact-field-checkbox-{{ section.id }} label:after {
      width: {{ checkbox_size }}px;
      height: {{ checkbox_size }}px;
    }

    .contact-submit-button-{{ section.id }} {
      margin-top: {{ submit_mt }}px;    
      justify-content: {{ content_horizontal_align }};
    }

    .contact-submit-{{ section.id }} {      
      font-size: {{ submit_size }}px;
      padding: {{ submit_padding_vertical }}px {{ submit_padding_horizontal }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}


{% if success_text_custom %}
  <style>
    .contact-success-{{ section.id }} {
      font-family: {{ success_text_font.family }}, {{ success_text_font.fallback_families }};
      font-weight: {{ success_text_font.weight }};
      font-style: {{ success_text_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .contact-field-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if label_custom %}
  <style>
    .contact-field-label-{{ section.id }} {
      font-family: {{ label_font.family }}, {{ label_font.fallback_families }};
      font-weight: {{ label_font.weight }};
      font-style: {{ label_font.style }};
    }
  </style>
{% endif %}

{% if required_custom %}
  <style>
    .contact-field-required-{{ section.id }} {
      font-family: {{ required_font.family }}, {{ required_font.fallback_families }};
      font-weight: {{ required_font.weight }};
      font-style: {{ required_font.style }};
    }
  </style>
{% endif %}

{% if input_custom %}
  <style>
    .contact-field-input-{{ section.id }},
    .contact-field-input-{{ section.id }}::placeholder,
    .contact-field-inner--{{ section.id }} textarea {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }} !important;
      font-weight: {{ input_font.weight }} !important;
      font-style: {{ input_font.style }} !important;
    }
  </style>
{% endif %}

{% if checkbox_custom %}
  <style>
    .contact-field-checkbox-{{ section.id }} label {
      font-family: {{ checkbox_font.family }}, {{ checkbox_font.fallback_families }};
      font-weight: {{ checkbox_font.weight }};
      font-style: {{ checkbox_font.style }};
    }
  </style>
{% endif %}

{% if error_text_custom %}
  <style>
    .contact-{{ section.id }} .errors * {
      font-family: {{ error_text_font.family }}, {{ error_text_font.fallback_families }};
      font-weight: {{ error_text_font.weight }};
      font-style: {{ error_text_font.style }};
    }
  </style>
{% endif %}

{% if submit_custom %}
  <style>
    .contact-submit-{{ section.id }} {
      font-family: {{ submit_font.family }}, {{ submit_font.fallback_families }};
      font-weight: {{ submit_font.weight }};
      font-style: {{ submit_font.style }};
    }
  </style>
{% endif %}


{% for block in section.blocks %}
  <style>
    .contact-field-{{ block.id }} {
      flex: 0 1 100%;
    }

    .contact-field-label-{{ block.id }} {
       margin-top: {{ label_mt_mobile }}px;
    }

    @media(min-width: 1024px) {

      .contact-field-{{ block.id }} {
        flex: 0 1 calc({{ block.settings.width }}% - {{ form_gap | divided_by: 2 }}px);
      }

      .contact-field-label-{{ block.id }} {
         margin-top: {{ label_mt }}px;
      }
    }
  </style>

  {% if block.settings.width == 100 %}
    <style>
      @media(min-width: 1024px) {
        .contact-field-{{ block.id }} {
          flex: 0 1 calc({{ block.settings.width }}%);
        }
      }    
    </style>
  {% endif %}

  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .contact-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};         
        }

        .contact-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {

          .contact-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }

          .contact-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .contact-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}   
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .contact-text-{{ block.id }} {
          margin: 0 auto;
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};    
        }

        .contact-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {

          .contact-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_align }};        
          }

          .contact-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .contact-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}  
  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} contact-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">        
      <div class="contact-form-{{ section.id }}">
        {% for block in section.blocks %}
          {% case block.type %}
            {% when 'heading'%}
              {% if block.settings.heading != blank %}
                <div class="contact-heading-{{ block.id }}">
                  {{ block.settings.heading }}
                </div>
                {% endif %}  
              {% else %}
            {% when 'text' %}
              {% if block.settings.text != blank %}
                <div class="contact-text-{{ block.id }}">
                  {{ block.settings.text }}
                </div>
              {% endif %}                      
          {% endcase %}
        {% endfor %} 

        {% form 'contact'%}          
          {% if form.posted_successfully? %}
            {% comment %} Successful message {% endcomment %}
            <p class="contact-success-{{ section.id }}">{{ success_text }}</p>
          {% endif %}    

          {{ form.errors | default_errors }}

          <div class="contact-fields-{{ section.id }}">
            {% assign inside_item = false %} 
            {% for block in section.blocks %}               
                {% if block.type != "heading" and block.type != "text" and block.type != 'submenu' %}
                  <div class="contact-field-{{ section.id }} contact-field-{{ block.id }} {% if block.type == "date" %} contact-field-date-{{ block.id }}{% endif %}">
                    {% case block.type %}
                      {% when 'date' %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.title != blank %}
                            <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">              
                              {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                          {% endif %}
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">                  
                              {{ block.settings.label }}
                            </label>
                          {% endif %}
                          <div class="contact-field-input-date-{{ block.id }}" style="position: relative;">
                            <input type="date" {% if block.settings.placeholder != blank %}placeholder="{{ block.settings.placeholder }}"{% endif %} class="contact-field-input-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="contact[{{ block.settings.name }}]" {% if block.settings.required %}required{% endif %}>
                            <svg class="custom-date-icon-{{ section.id }}" viewBox="0 0 24 24">
                              <path d="M19 4h-1V2h-2v2H8V2H6v2H5a2 2 0 00-2 2v14a2 2 0 002 2h14a2 2 0 002-2V6a2 2 0 00-2-2zm0 16H5V9h14v11zM7 11h2v2H7v-2zm4 0h2v2h-2v-2zm4 0h2v2h-2v-2z"/>
                            </svg>
                          </div>                        
                         </div>
                      {% when 'text_field' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">              
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">           
                              {{ block.settings.label }}
                            </label>
                          {% endif %}
                          <input type="{% if block.settings.type == "tel" %}tel{% elsif block.settings.type == "email" %}email{% else %}text{% endif %}" {% if block.settings.placeholder != blank %}placeholder="{{ block.settings.placeholder }}"{% endif %} class="contact-field-input-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="{% if block.settings.type == "email" %}contact[email]{% else %}contact[{{ block.settings.name }}]{% endif %}" {% if block.settings.required %}required{% endif %}>
                        </div>
                      {% when 'textarea' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                        
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</label>
                          {% endif %}
                          <textarea {% if block.settings.placeholder != blank %}placeholder="{{ block.settings.placeholder }}"{% endif %} class="contact-field-input-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="{% if block.settings.type == "message" %}contact[message]{% else %}contact[{{ block.settings.name }}]{% endif %}" {% if block.settings.required %}required{% endif %}></textarea>
                        </div>
                      {% when 'dropdown' %}
                        {% assign options = block.settings.dropdown_options | strip | split: ',' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                        
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <label for="ContactForm{{ block.settings.name }}" class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</label>
                          {% endif %}
                          <div class="contact-field-select-{{ section.id }}">
                            <svg class="contact-field-select-svg-{{ section.id }}" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg"><path d="M512 581.5l273.1-273.1c25-25 65.5-25 90.5 0s25 65.5 0 90.5l-316.7 316.7c-12.9 12.9-30 19.2-47 18.7-16.9 0.4-33.9-5.8-46.8-18.699l-316.8-316.8c-25-25-25-65.5 0-90.5s65.5-25 90.5 0l273.2 273.2z"/></svg>
                            <select class="contact-field-input-{{ section.id }} contact-field-input-select-{{ section.id }}" id="ContactForm{{ block.settings.name }}" name="contact[{{block.settings.name}}]" {% if block.settings.required %}required{% endif %}>
                              {% if block.settings.placeholder  %}
                                <option value = "" disabled selected>{% if block.settings.placeholder != blank %}{{ block.settings.placeholder }}{% endif %}</option>
                              {% endif %}

                              {% for option in options %}
                                <option value="{{option}}">{{option}}</option>
                              {% endfor %}
                            </select>  
                          </div>

                        </div>
                      {% when 'checkbox' %}
                        {% assign optionscheckbox = block.settings.checkbox_options | strip | split: ',' %}
                        {% if block.settings.title != blank %}
                          <p class="contact-field-title-{{ section.id }} contact-field-title-{{ block.id }}">                       
                            {{ block.settings.title }} {% if block.settings.required %}<span class="contact-field-required-{{ section.id }}">{{ required_text }}</span>{% endif %}</p>
                        {% endif %}
                        <div class="contact-field-inner-{{ section.id }}">
                          {% if block.settings.label != blank %}
                            <p class="contact-field-label-{{ section.id }} contact-field-label-{{ block.id }}">{{ block.settings.label }}</p>
                          {% endif %}
                          {% for option in optionscheckbox %}
                            <div class="contact-field-checkbox-{{ section.id }}">
                              <input type="checkbox" id="ContactForm{{ block.settings.name }}{{ forloop.index }}-{{ block.id }}" name="{% if block.settings.use_newsletter and forloop.first %}customer[accepts_marketing]{% else %}contact[{{ block.settings.name }}-{{ option }}]{% endif %}">
                              <label for="ContactForm{{ block.settings.name }}{{ forloop.index }}-{{ block.id }}" class="contact-field-label-{{ block.id }}">{{ option }}</label>
                            </div>
                          {% endfor %}
                        </div>                       
                    {% endcase %}
                  </div> 
                {% elsif block.type == 'submenu' %}   
                  {% if inside_item %}
                    </div>
                  {% endif %}
                  <div class="contact-field-form-tab-{{ section.id }} contact-fields-{{ section.id }}" data-option="{{ block.settings.option }}">
                  {% assign inside_item = true %}   
                {% endif %}                 
              {% endfor %}
              {% if inside_item %}                 
                </div>
              {% endif %}
          </div>

          <div class="contact-submit-button-wrapper-{{ section.id }}">
            <div class="contact-submit-button-{{ section.id }}">
              <input type="submit" class="contact-submit-{{ section.id }}" value="{% if submit != blank %}{{ submit }}{% else %}Submit{% endif %}">
            </div>  
          </div>                        

        {% endform %}
      </div>       
    </div>
</div>

<script>
function initContactForm6() {
  const selectElement = document.querySelector('.contact-field-input-select-{{ section.id }}');

  if (selectElement) {
    const handleSelectChange = () => {
      const selectedOptionId = selectElement.options[selectElement.selectedIndex].value;
      console.log(selectedOptionId);

      const allTabs = document.querySelectorAll('[data-option]');
      allTabs.forEach(tab => {
        tab.classList.remove('active');
      });

      const targetTab = document.querySelector(`.contact-field-form-tab-{{ section.id }}[data-option="${selectedOptionId}"]`);
      if (targetTab) {
        targetTab.classList.add('active');
      }
    };
    selectElement.addEventListener('change', handleSelectChange);

    handleSelectChange();
  }
}

document.addEventListener('DOMContentLoaded', initContactForm6);

if (Shopify.designMode) {
  document.addEventListener('shopify:section:unload', initContactForm6);
  document.addEventListener('shopify:section:load', initContactForm6);
}
</script>
