{%- style -%}

  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  timer_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .countdown-body-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    gap: {{ body_gap_mobile }}px;
  }

  .countdown-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_position_mobile }};
    justify-content: {{ content_vertical_position_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    border-radius: {{ content_radius }}px;
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
    background-color: {{ content_bg_color }};
    box-sizing: border-box;
  }

  .countdown-subheading-{{ section.id }} {
    text-align: {{ content_horizontal_position_mobile_text }};
  }

  .countdown-subheading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
  }

  .countdown-heading-{{ section.id }} {
    margin-top: {{ heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ content_horizontal_position_mobile_text }};
  }

  .countdown-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .countdown-text-{{ section.id }} {
    width: 100%;
    max-width: {{ text_width_mobile }}%;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ content_horizontal_position_mobile_text }};
  }

  .countdown-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .countdown-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .countdown-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .countdown-image-{{ section.id }} {
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .countdown-image-{{ section.id }} img,
  .countdown-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .countdown-image-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  .countdown-timer-{{ section.id }} {
    margin-top: {{ timer_mt | times: 0.75 | round: 0 }}px;
    display: grid;
    grid-template-columns: repeat(4,1fr);
    gap: {{ timer_gap_mobile }}px;
  }

  .countdown-timer-{{ section.id }} .time-block {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
    border-radius: {{ block_radius }}px;
    border: {{ block_border_thickness }}px solid {{ block_border_color }};
    background-color: {{ block_bg_color }};
  }

  .countdown-timer-{{ section.id }} .time-block__num {
    margin: 0px;
    font-size: {{ timer_numbers_size_mobile }}px;
    color: {{ timer_numbers_color }};
    line-height: {{ timer_numbers_height }}%;
    text-transform: unset;
  }

  .countdown-timer-{{ section.id }} .time-block__unit {
    margin: 0px;
    margin-top: {{ timer_text_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ timer_text_size_mobile }}px;
    color: {{ timer_text_color }};
    line-height: {{ timer_text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-body-{{ section.id }} {
      grid-template-columns: repeat(2, 1fr);
      gap: {{ body_gap }}px;
    }

    .countdown-content-{{ section.id }} {
      align-items: {{ content_horizontal_position }};
      justify-content: {{ content_vertical_position }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .countdown-subheading-{{ section.id }} {
      text-align: {{ content_horizontal_position_text }};
    }

    .countdown-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .countdown-heading-{{ section.id }} {
      margin-top: {{ heading_mt }}px;
      text-align: {{ content_horizontal_position_text }};
    }

    .countdown-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .countdown-text-{{ section.id }} {
      max-width: {{ text_width }}%;
      margin-top: {{ text_mt }}px;
      text-align: {{ content_horizontal_position_text }};
    }

    .countdown-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .countdown-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .countdown-timer-{{ section.id }} {
      margin-top: {{ timer_mt }}px;
      gap: {{ timer_gap }}px;
    }

    .countdown-timer-{{ section.id }} .time-block {
      padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
    }

    .countdown-timer-{{ section.id }} .time-block__unit {
      margin-top: {{ timer_text_mt }}px;
      font-size: {{ timer_text_size }}px;
    }

    .countdown-timer-{{ section.id }} .time-block__num {
      font-size: {{ timer_numbers_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if sub_heading_custom %}
  <style>
    .countdown-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .countdown-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .countdown-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .countdown-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if timer_text_custom %}
  <style>
    .countdown-timer-{{ section.id }} .time-block__unit {
      font-family: {{ timer_text_font.family }}, {{ timer_text_font.fallback_families }};
      font-weight: {{ timer_text_font.weight }};
      font-style: {{ timer_text_font.style }};
    }
  </style>
{% endif %}

{% if timer_numbers_custom %}
  <style>
    .countdown-timer-{{ section.id }} .time-block__num {
      font-family: {{ timer_numbers_font.family }}, {{ timer_numbers_font.fallback_families }};
      font-weight: {{ timer_numbers_font.weight }};
      font-style: {{ timer_numbers_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: 9.6/11; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: 12/7; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .countdown-image-{{ section.id }} {
       aspect-ratio: 9.6/11; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
       aspect-ratio: 12/7; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% endif %}

{% if image_position_mobile == "top" %}
  <style>
    .countdown-content-{{ section.id }} {
      order: 2;
    }
    .countdown-image-{{ section.id }} {
      order: 1;
    }
  </style>
{% else %}
  <style>
    .countdown-content-{{ section.id }} {
      order: 1;
    }
    .countdown-image-{{ section.id }} {
      order: 2;
    }
  </style>
{% endif %}

{% if image_position == "right" %}
  <style>
    @media(min-width: 1024px ){
     .countdown-content-{{ section.id }} {
        order: 1;
      }
      .countdown-image-{{ section.id }} {
        order: 2;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px ){
      .countdown-content-{{ section.id }} {
        order: 2;
      }
      .countdown-image-{{ section.id }} {
        order: 1;
      }
    }
  </style>
{% endif %}

{% if hide_image %}
  <style>
    @media(min-width: 1024px ) {
      .countdown-body-{{ section.id }} {
        grid-template-columns: repeat(1, 1fr) !important;
      }
    }
  </style>
{% endif %}

{% if button_hover %}
  <style>
    .countdown-button-{{ section.id }}:hover {
      transform: translateY(-4px);
    }
  </style>
{% endif %}

{% if timer_gap_mobile == 0 %}
  <style>
    .countdown-timer-{{ section.id }} .time-block:not(:first-child) {
      border-left: 0px;
    }
  </style>
{% endif %}

{% if timer_gap == 0 %}
  <style>
    @media(min-width: 1024px) {
     .countdown-timer-{{ section.id }} .time-block:not(:first-child) {
        border-left: 0px;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .countdown-timer-{{ section.id }} .time-block:not(:first-child) {
        border-left: {{ block_border_thickness }}px solid {{ block_border_color }};
      } 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} countdown-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">

      <div class="countdown-body-{{ section.id }}">
        <div class="countdown-content-{{ section.id }}">

          {% if sub_heading != blank %}
            <div class="countdown-subheading-{{ section.id }}">{{ sub_heading }}</div>
          {% endif %}

          {% if heading != blank %}
            <div class="countdown-heading-{{ section.id }}">{{ heading }}</div>
          {% endif %}

          {% if text != blank %}
            <div class="countdown-text-{{ section.id }}">{{ text }}</div>
          {% endif %}

          <div class="countdown-timer-{{ section.id }}">

            <div class="time-block">
              <span class="time-block__num js-timer-days">00</span>
              <span class="time-block__unit">{{ days_text }}</span>
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-hours">00</span>
              <span class="time-block__unit">{{ hours_text }}</span>
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-minutes">00</span>
              <span class="time-block__unit">{{ minutes_text }}</span>
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-seconds">00</span>
              <span class="time-block__unit">{{ seconds_text }}</span>
            </div>
          </div>

          <style>
            .countdown-timer-completed-text-{{ section.id }} {
              display: none;
            }
          </style>
          <div
            class="countdown-timer-completed-text-{{ section.id }}"
          >
            {{ timer_completed_text }}
          </div>

          {% if button != blank %}
            <a href="{{ button_url }}" class="countdown-button-{{ section.id }}">{{ button }}</a>
          {% endif %}

        </div>

        {% unless hide_image %}
          <div class="countdown-image-{{ section.id }}">
            {% if image != blank %}
              <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
        {% endunless %}
      </div>

    </div>
</div>

<script type="text/javascript">
  (function () {

    function startTimer() {
      const second = 1000,
        minute = second * 60,
        hour = minute * 60,
        day = hour * 24,
        wrapper = document.querySelector(
          '.countdown-timer-{{ section.id }}'
        ),
        countDown = new Date('{{ year }}-{{ month }}-{{ day }}T{{ hour }}:{{ minute }}:00Z').getTime();

      console.log('start timer, end date:' + countDown)
      const interval = setInterval(function () {
        let now = new Date().getTime(),
          distance = countDown - now,
          days = Math.floor(distance / day),
          hours = Math.floor((distance % day) / hour),
          minutes = Math.floor((distance % hour) / minute),
          seconds = Math.floor((distance % minute) / second);

        (wrapper.querySelector('.js-timer-days').innerText = (
          days
        )),
          (wrapper.querySelector('.js-timer-hours').innerText = (
            hours
          )),
          (wrapper.querySelector('.js-timer-minutes').innerText = (
            minutes
          )),
          (wrapper.querySelector('.js-timer-seconds').innerText = (
            seconds
          ));

        if (distance < 0) {
          wrapper.querySelector('.js-timer-days').innerText = '00';
          wrapper.querySelector('.js-timer-hours').innerText = '00';
          wrapper.querySelector('.js-timer-minutes').innerText = '00';
          wrapper.querySelector('.js-timer-seconds').innerText = '00';
          {% if hide_timer_on_completed %}
            wrapper.style.display = 'none';
          {% endif %}
          document.querySelector(
            '.countdown-timer-completed-text-{{ section.id }}'
          ).style.display = 'block';

          clearInterval(interval);
        }

      }, second);
      document.addEventListener('shopify:section:unload', function (event) {
        if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
      });
    }
    startTimer()
    if (window.Shopify && Shopify.designMode) {
      document.addEventListener('shopify:section:load', function (event) {
        if (event.detail.sectionId === '{{ section.id }}') {
          startTimer();
        }
      });
    }
  })();
</script>
