{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign body_gap_mobile = section.settings.body_gap_mobile
  assign body_gap = section.settings.body_gap

  assign image = section.settings.image
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_radius = section.settings.image_radius
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_position = section.settings.image_position
  assign image_position_mobile = section.settings.image_position_mobile
  assign hide_shape = section.settings.hide_shape

  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_maxwidth = section.settings.content_maxwidth
  assign content_horizontal_align_mobile = section.settings.content_horizontal_align_mobile
  assign content_horizontal_align = section.settings.content_horizontal_align

  assign text_align = ""
  if content_horizontal_align == "start"
    assign text_align = "left"
  elsif content_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if content_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .countdown-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ body_gap_mobile }}px;
  }

  .countdown-image-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    position: relative;
  }

  .countdown-image-{{ section.id }} > img:first-child,
  .countdown-image-{{ section.id }} > svg:first-child {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .countdown-image-{{ section.id }} > svg:first-child {
    background-color: #AFAFAF;
  }

  .countdown-desktop-shape-{{ section.id }} {
    display: none;
    position: absolute;
    height: 102%;
    width: auto;
    z-index: 2;
    top: -1%;
  }

  .countdown-mobile-shape-{{ section.id }} {
    position: absolute;
    height: auto;
    width: 100%;
    z-index: 2;
    left: 0;
  }

  .countdown-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .countdown-content-inner-{{ section.id }} {
    width: 100%;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ body_gap }}px;
      align-items: center;
    }

    .countdown-desktop-shape-{{ section.id }} {
      display: block;
    }

    .countdown-mobile-shape-{{ section.id }} {
      display: none;
    }

    .countdown-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .countdown-content-inner-{{ section.id }} {
      max-width: {{ content_maxwidth }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .countdown-image-{{ section.id }}{
     aspect-ratio: 12/10; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .countdown-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .countdown-image-{{ section.id }} {
       aspect-ratio: 9.6/14; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
       aspect-ratio: 12/10; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% if image_position_mobile == "top" %}
  <style>
    .countdown-image-{{ section.id }} {
      order: 1;
    }

    .countdown-content-{{ section.id }} {
      order: 2;
    }

    .countdown-mobile-shape-{{ section.id }} {
      top: -1px;
    }
  </style>
{% else %}
  <style>
    .countdown-image-{{ section.id }} {
      order: 2;
    }

    .countdown-content-{{ section.id }} {
      order: 1;
    }

    .countdown-mobile-shape-{{ section.id }} {
      bottom: -1px;
      transform: rotate(-180deg);
    }
  </style>
{% endif %}

{% if image_position == "left" %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
        order: 1;
      }

      .countdown-content-{{ section.id }} {
        order: 2;
      }

      .countdown-desktop-shape-{{ section.id }} {
        right: -1px;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .countdown-image-{{ section.id }} {
        order: 2;
      }

      .countdown-content-{{ section.id }} {
        order: 1;
      }

      .countdown-content-inner-{{ section.id }} {
        margin-left: auto;
      }

      .countdown-desktop-shape-{{ section.id }} {
        left: -1px;
        transform: rotate(-180deg);
      } 
    }
  </style>
{% endif %}

{% if hide_shape %}
  <style>
    .countdown-desktop-shape-{{ section.id }},
    .countdown-mobile-shape-{{ section.id }} {
      display: none !important;
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == "heading" %}
    <style>
      {{  block.settings.heading_font | font_face: font_display: 'swap' }}

      .countdown-heading-{{ block.id }} {
        text-align: {{ text_align_mobile }};
        margin-top: {{ block.settings.heading_mt_mobile }}px;
      }

      .countdown-heading-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.heading_size_mobile }}px;
        color: {{ block.settings.heading_color }};
        line-height: {{ block.settings.heading_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {

        .countdown-heading-{{ block.id }} {
          text-align: {{ text_align }};
          margin-top: {{ block.settings.heading_mt }}px;
        }

        .countdown-heading-{{ block.id }} * {
          font-size: {{ block.settings.heading_size }}px;
        }
      }
    </style>

    {% if block.settings.heading_custom %}
      <style>
        .countdown-heading-{{ block.id }} {
          font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
          font-weight: {{ block.settings.heading_font.weight }};
          font-style: {{ block.settings.heading_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "text" %}
    <style>
      {{  block.settings.text_font | font_face: font_display: 'swap' }}

      .countdown-text-{{ block.id }} {
        text-align: {{ text_align_mobile }};
        margin-top: {{ block.settings.text_mt_mobile }}px;
      }

      .countdown-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {

        .countdown-text-{{ block.id }} {
          text-align: {{ text_align }};
          margin-top: {{ block.settings.text_mt }}px;
        }

        .countdown-text-{{ block.id }} * {
          font-size: {{ block.settings.text_size }}px;
        }
      }
    </style>

    {% if block.settings.text_custom %}
      <style>
        .countdown-text-{{ block.id }} {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "button" %}

  <style>
    {{  block.settings.button_font | font_face: font_display: 'swap' }}

    .countdown-button-{{ block.id }} {
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;
      width: 100%;
      max-width: {{ block.settings.button_width_mobile }}px;
      margin: 0;
      margin-top: {{ block.settings.button_mt_mobile }}px;
      font-size: {{ block.settings.button_size_mobile }}px;
      color: {{ block.settings.button_color }};
      line-height: {{ block.settings.button_height }}%;
      text-align: center;
      text-transform: unset;
      text-decoration: none;
      padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
      border-radius: {{ block.settings.button_radius }}px;
      transition: all 0.25s ease 0s;
      position: relative;
      font-weight: 700;
      z-index: 2;
    }

    .countdown-button-{{ block.id }}:hover {
      color: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .countdown-button-{{ block.id }} svg {
      width: 14px;
      height: 14px;
    }

    .countdown-button-{{ block.id }} svg path {
      fill: {{ block.settings.button_color }};
      transition: all 0.25s ease 0s;
    }

    .countdown-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    @media(min-width: 1024px) {
      .countdown-button-{{ block.id }} {
        margin-top: {{ block.settings.button_mt }}px;
        max-width: {{ block.settings.button_width }}px;
        padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
        font-size: {{ block.settings.button_size }}px;
      }
    }
  </style>

  {% if block.settings.button_custom %}
    <style>
      .countdown-button-{{ block.id }} {
        font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
        font-weight: {{ block.settings.button_font.weight }};
        font-style: {{ block.settings.button_font.style }};
      }
    </style>
  {% endif %}

  {% if text_align_mobile == "center" %}
    <style>
     .countdown-button-{{ block.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    </style>
  {% elsif text_align_mobile == "right" %}
    <style>
     .countdown-button-{{ block.id }} {
        margin-left: auto;
      } 
    </style>
  {% endif %}

  {% if text_align == "center" %}
    <style>
      @media(min-width: 1024px) {
       .countdown-button-{{ block.id }} {
          margin-left: auto !important;
          margin-right: auto !important;
        } 
      }
    </style>
  {% elsif text_align == "right" %}
    <style>
      @media(min-width: 1024px) {
       .countdown-button-{{ block.id }} {
          margin-left: auto !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
       .countdown-button-{{ block.id }} {
          margin-left: 0px !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% endif %}

  {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
    <style>
      .countdown-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
      }

      .countdown-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
      }
    </style>
  {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
    <style>
      .countdown-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
      }

      .countdown-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
      }
    </style>
  {% endif %}

  {% elsif block.type == "timer" %}

    <style>
      {{  block.settings.number_font | font_face: font_display: 'swap' }}
      {{  block.settings.text_font | font_face: font_display: 'swap' }}
      {{  block.settings.completed_text_font | font_face: font_display: 'swap' }}

      .countdown-timer-{{ block.id }} {
        margin-top: {{ block.settings.timer_mt | times: 0.75 | round: 0 }}px;
        display: flex;
        align-items: stretch;
        gap: {{ block.settings.timer_gap | times: 0.75 | round: 0 }}px;
        justify-content: {{ content_horizontal_align_mobile }};
      }

      .countdown-timer-{{ block.id }} .time-block {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        text-align: center;
        width: 100%;
        max-width: {{ block.settings.item_width_mobile }}px;
        gap: 10px;
        background-color: {{ block.settings.item_bg_color }};
        border-radius: {{ block.settings.item_radius }}px;
        padding: {{ block.settings.item_padding_vertical | times: 0.75 | round: 0 }}px {{ block.settings.item_padding_horizontal | times: 0.75 | round: 0 }}px;
      }

      .countdown-timer-{{ block.id }} .time-block__num {
        margin: 0;
        font-size: {{ block.settings.number_size_mobile }}px;
        color: {{ block.settings.number_color }};
        line-height: {{ block.settings.number_height }}%;
        text-transform: unset;
        font-weight: 700;
      }

      .countdown-timer-{{ block.id }} .time-block__unit {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
      }

      .countdown-timer-completed-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.completed_text_size_mobile }}px;
        color: {{ block.settings.completed_text_color }};
        line-height: {{ block.settings.completed_text_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {
        .countdown-timer-{{ block.id }} {
          margin-top: {{ block.settings.timer_mt }}px;
          gap: {{ block.settings.timer_gap }}px;
          justify-content: {{ content_horizontal_align }};
        }

        .countdown-timer-{{ block.id }} .time-block {
          max-width: {{ block.settings.item_width }}px;
          padding: {{ block.settings.item_padding_vertical }}px {{ block.settings.item_padding_horizontal }}px;
        }

        .countdown-timer-{{ block.id }} .time-block__num {
          font-size: {{ block.settings.number_size }}px;
        }

        .countdown-timer-{{ block.id }} .time-block__unit {
          font-size: {{ block.settings.text_size }}px;
        }

        .countdown-timer-completed-text-{{ block.id }} * {
          font-size: {{ block.settings.text_size }}px;
        }
      }
    </style>

    {% if block.settings.number_custom %}
      <style>
        .countdown-timer-{{ block.id }} .time-block__num {
          font-family: {{ block.settings.number_font.family }}, {{ block.settings.number_font.fallback_families }};
          font-weight: {{ block.settings.number_font.weight }};
          font-style: {{ block.settings.number_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.text_custom %}
      <style>
        .countdown-timer-{{ block.id }} .time-block__unit {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.completed_text_custom %}
      <style>
        .countdown-timer-completed-text-{{ block.id }} * {
          font-family: {{ block.settings.completed_text_font.family }}, {{ block.settings.completed_text_font.fallback_families }};
          font-weight: {{ block.settings.completed_text_font.weight }};
          font-style: {{ block.settings.completed_text_font.style }};
        }
      </style>
    {% endif %}

  {% endif %}

{% endfor %}

