{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign icons_gap = section.settings.icons_gap
  assign icons_gap_mobile = section.settings.icons_gap_mobile
  assign icons_mt = section.settings.icons_mt
  assign icons_mt_mobile = section.settings.icons_mt_mobile

  assign icon_width = section.settings.icon_width
  assign icon_width_mobile = section.settings.icon_width_mobile
-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .countdown-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .countdown-icons-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ content_horizontal_align_mobile }};
    gap: {{ icons_gap_mobile }}px;
  }

  .countdown-icons-{{ section.id }}:not(:first-child) {
    margin-top: {{ icons_mt_mobile }}px;
  }

  .countdown-icon-{{ section.id }} {
    width: {{ icon_width_mobile }}px;
  }

  .countdown-icon-{{ section.id }} img,
  .countdown-icon-{{ section.id }} svg {
    display: block;     
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  @media(min-width: 1024px) {
    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-body-{{ section.id }} {
      flex-direction: rows;
      align-items: {{ content_horizontal_align }};
    }

    .countdown-icons-{{ section.id }} {
      justify-content: {{ content_horizontal_align }};
      gap: {{ icons_gap }}px;
    }

    .countdown-icons-{{ section.id }}:not(:first-child) {
      margin-top: {{ icons_mt }}px;
    }

    .countdown-icon-{{ section.id }} {
      width: {{ icon_width }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}    
    {% when 'icon' %}
      <style>
        .countdown-icon-{{ block.id }} svg path {
          fill: {{ block.settings.icon_color }};
        }
      </style>
    {% when 'heading' %}
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .countdown-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .countdown-heading-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }          

        @media(min-width: 1024px) {
          .countdown-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }

          .countdown-heading-{{ block.id }} * {                
            font-size: {{ block.settings.heading_size }}px;
          }  
        }
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .countdown-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}  
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .countdown-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
          max-width: {{ block.settings.text_max_width_mobile }}%;
        }

        .countdown-text-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }  

        .countdown-text-{{ block.id }} strong {
          color: {{ block.settings.text_active_color }};
        }

        @media(min-width: 1024px) {
          .countdown-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{ content_align }};
            max-width: {{ block.settings.text_max_width }}%;
          }

          .countdown-text-{{ block.id }} * {                
            font-size: {{ block.settings.text_size }}px;
          }  
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .countdown-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'timer' %}              
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}
        {{  block.settings.number_font | font_face: font_display: 'swap' }}
        {{  block.settings.completed_text_font | font_face: font_display: 'swap' }}
        {{  block.settings.timer_text_font | font_face: font_display: 'swap' }}

        .countdown-{{ block.id }} {
          margin-top: {{ block.settings.timer_mt_mobile }}px;
          display: flex;
          align-items: center;
          gap: {{ block.settings.timer_gap_mobile }}px;
          flex-wrap: wrap;
          min-width: none;
        }

        .countdown-{{ block.id }} .time-block {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-direction: column;
          text-align: center;
          position: relative;      
        }   

        .countdown-{{ block.id }} .time-block__num {
          margin: 0;
          font-size: {{ block.settings.number_size_mobile }}px;
          color: {{ block.settings.number_color }};
          line-height: {{ block.settings.number_height }}%;
          text-transform: unset;
          font-weight: 700;
        }   

        .countdown-{{ block.id }} .line {
          display: block !important;
          width: {{ block.settings.timer_border_thickness }}px;
          height: 30px;
          margin-top: auto;
          margin-bottom: auto;
          background-color: {{ block.settings.timer_border_color }};
        }

        .countdown-{{ block.id }} .time-block__text {
          margin-top: {{ block.settings.timer_text_mt_mobile }}px;
        }

        .countdown-{{ block.id }} .time-block__unit {
          margin: 0;   
          font-size: {{ block.settings.timer_text_size_mobile }}px;
          color: {{ block.settings.timer_text_color }};
          line-height: {{ block.settings.timer_text_height }}%;
          text-transform: unset;
        }

        .countdown-completed-text-{{ block.id }} {
          display: none;
          position: relative;              
        }

        .countdown-completed-text-{{ block.id }} * {
          margin: 16px 0;
          font-size: {{ block.settings.completed_text_size_mobile }}px;
          color: {{ block.settings.completed_text_color }};
          line-height: {{ block.settings.completed_text_height }}%;
          text-transform: unset;
        }

        @media (min-width: 1024px) {
          .countdown-{{ block.id }} {
            margin-top: {{ block.settings.timer_mt }}px;
            gap: {{ block.settings.timer_gap }}px;         
          }

          .countdown-{{ block.id }} .time-block {
            min-width: {% if block.settings.number_size > 70 %}120px{% elsif block.settings.number_size > 40 %}90px{% else %}45px{% endif %}; 
          }

          .countdown-{{ block.id }} .line {
            height: 70px;
          }

          .countdown-{{ block.id }} .time-block__num {
            font-size: {{ block.settings.number_size }}px;
          }

          .countdown-{{ block.id }} .time-block__text {
            margin-top: {{ block.settings.timer_text_mt }}px;
          }

          .countdown-{{ block.id }} .time-block__unit {         
            font-size: {{ block.settings.timer_text_size }}px;
          }

          .countdown-completed-text-{{ block.id }} * {
            font-size: {{ block.settings.completed_text_size }}px;
          }
        }        
      </style>      
      {% if block.settings.number_custom %}
        <style>
          .countdown-{{ block.id }} .time-block__num {
            font-family: {{ block.settings.number_font.family }}, {{ block.settings.number_font.fallback_families }};
            font-weight: {{ block.settings.number_font.weight }};
            font-style: {{ block.settings.number_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.timer_text_custom %}
        <style>
          .countdown-{{ block.id }} .time-block__unit {
            font-family: {{ block.settings.timer_text_font.family }}, {{ block.settings.timer_text_font.fallback_families }};
            font-weight: {{ block.settings.timer_text_font.weight }};
            font-style: {{ block.settings.timer_text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.completed_text_custom %}
        <style>
          .countdown-completed-text-{{ block.id }} * {
            font-family: {{ block.settings.completed_text_font.family }}, {{ block.settings.completed_text_font.fallback_families }};
            font-weight: {{ block.settings.completed_text_font.weight }};
            font-style: {{ block.settings.completed_text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.wrapper_align_mobile == "center" %}
        <style>
          .countdown-wrapper-{{ block.id }} {
            align-items: center;
          }
        </style>
      {% elsif block.settings.wrapper_align_mobile == "left" %}
        <style>
          .countdown-wrapper-{{ block.id }} {
            align-items: start;
          }
        </style>
      {% else %}
        <style>
          .countdown-wrapper-{{ block.id }} {
            align-items: end;
          }
        </style>
      {% endif %} 

      {% if block.settings.wrapper_align == "center" %}
        <style>
          @media (min-width: 1024px) {
            .countdown-wrapper-{{ block.id }} {
              align-items: center;
            }
          }          
        </style>
      {% elsif block.settings.wrapper_align == "left" %}
        <style>
          @media (min-width: 1024px) {
            .countdown-wrapper-{{ block.id }} {
              align-items: start;
            }
          }          
        </style>
      {% else %}
        <style>
          @media (min-width: 1024px) {
            .countdown-wrapper-{{ block.id }} {
              align-items: end;
            } 
          }          
        </style>
      {% endif %} 

  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} countdown-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="countdown-body-{{ section.id }}">
        {% assign is_open = false %}
        {% for block in section.blocks %}
          {% case block.type %}
            {% when 'icon' %}               
              {% if is_open == false %}
                <div class="countdown-icons-{{ section.id }}">
                {% assign is_open = true %}
              {% endif %}

              <div class="countdown-icon-{{ section.id }} countdown-icon-{{ block.id }}">
                {% if block.settings.custom_icon != blank %}
                  <img src="{{ block.settings.custom_icon | image_url }}" alt="{{ block.settings.custom_icon.alt }}">
                {% else %}
                  <svg xmlns="http://www.w3.org/2000/svg" version="1.1" xmlns:xlink="http://www.w3.org/1999/xlink" width="512" height="512" x="0" y="0" viewBox="0 0 100 100" style="enable-background:new 0 0 512 512" xml:space="preserve" class=""><g><path d="M93.04 30 69.69 6.65 49.84 26.5 30 6.65 6.65 30 26.5 49.85 6.65 69.7 30 93.05 49.85 73.2 69.7 93.05 93.05 69.7 73.2 49.85 93.05 30zM66.19 49.85 49.84 66.2 33.49 49.85 49.84 33.5z" fill="#000000" opacity="1" data-original="#000000" class=""></path></g></svg>
                {% endif %}
              </div>                   

              {% assign next_index = forloop.index0 | plus: 1 %}
              {% if next_index < section.blocks.size and section.blocks[next_index].type != 'icon' %}
                </div>
                {% assign is_open = false %}
              {% elsif forloop.last %}
                </div>
              {% endif %}
            {% when 'heading' %}
              {% if block.settings.heading != blank %}
                <div class="countdown-heading-{{ block.id }}">{{ block.settings.heading }}</div>
              {% endif %}

            {% when 'text' %}
              {% if block.settings.text != blank %}
                <div class="countdown-text-{{ block.id }}">{{ block.settings.text }}</div>
              {% endif %}             
            {% when 'timer' %}             
              <div class="countdown-{{ block.id }}">        
                <div class="time-block">
                  <span class="time-block__num js-timer-days">00</span>
                  <div class="time-block__text">
                    <span class="time-block__unit">{{ block.settings.days_text }}</span>
                  </div>              
                </div>
                <div class="line"></div>        
                <div class="time-block">
                  <span class="time-block__num js-timer-hours">00</span>
                  <div class="time-block__text">
                    <span class="time-block__unit">{{ block.settings.hours_text }}</span>
                  </div>              
                </div>
                <div class="line"></div>               
                <div class="time-block">
                  <span class="time-block__num js-timer-minutes">00</span>
                  <div class="time-block__text">
                    <span class="time-block__unit">{{ block.settings.minutes_text }}</span>
                  </div>                   
                </div>
                <div class="line"></div>               
                <div class="time-block">
                  <span class="time-block__num js-timer-seconds">00</span>
                  <div class="time-block__text">
                    <span class="time-block__unit">{{ block.settings.seconds_text }}</span>
                  </div>            
                </div>
              </div>
              <div class="countdown-completed-text-{{ block.id }}">
                {{ block.settings.completed_text }}
              </div>                    
          {% endcase %}
        {% endfor %}
      </div>
    </div>
</div>

{% for block in section.blocks %}
  {% if block.type == 'timer' %}
    <script>
      function initCountDownTimer5() {    
        const offset = new Date().getTimezoneOffset();  
        function startTimer() {
          const second = 1000,
            minute = second * 60,
            hour = minute * 60,
            day = hour * 24,
            wrapper = document.querySelector(
              '.countdown-{{ block.id }}'
            ),
            countDown = new Date('{{ block.settings.year }}-{{ block.settings.month }}-{{ block.settings.day }}T{{ block.settings.hour }}:{{ block.settings.minute }}:00Z').getTime();

            const localDate = new Date(countDown);  
            console.log('Local date: ', localDate.toString()); 
          console.log('start timer, end date:' + countDown)
          const interval = setInterval(function () {
            let now = new Date().getTime(),
              distance = countDown - now,
              days = Math.floor(distance / day),
              hours = Math.floor((distance % day) / hour),
              minutes = Math.floor((distance % hour) / minute),
              seconds = Math.floor((distance % minute) / second);

            (wrapper.querySelector('.js-timer-days').innerText = (
              days
            )),
              (wrapper.querySelector('.js-timer-hours').innerText = (
                hours
              )),
              (wrapper.querySelector('.js-timer-minutes').innerText = (
                minutes
              )),
              (wrapper.querySelector('.js-timer-seconds').innerText = (
                seconds
              ));

            if (distance < 0) {
              wrapper.querySelector('.js-timer-days').innerText = '00';
              wrapper.querySelector('.js-timer-hours').innerText = '00';
              wrapper.querySelector('.js-timer-minutes').innerText = '00';
              wrapper.querySelector('.js-timer-seconds').innerText = '00';
              {% if block.settings.hide_timer_on_completed %}
                wrapper.style.display = 'none';   
                document.querySelector(
                '.countdown-completed-text-{{ block.id }}'
              ).style.display = 'block';         
              {% endif %}      


              clearInterval(interval);
            }

          }, second);
          document.addEventListener('shopify:section:unload', function (event) {
            if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
          });
        }
        startTimer();
        if (window.Shopify && Shopify.designMode) {
          document.addEventListener('shopify:section:load', function (event) {
            if (event.detail.sectionId === '{{ section.id }}') {
              startTimer();
            }
          });
        }
      }

      document.addEventListener('DOMContentLoaded', initCountDownTimer5);

      if (Shopify.designMode) {
        document.addEventListener('shopify:section:unload', initCountDownTimer5);
        document.addEventListener('shopify:section:load', initCountDownTimer5);
      }
    </script>
  {% endif %}
{% endfor %}
