liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_height = section.settings.layout_height
  assign layout_fixed_height = section.settings.layout_fixed_height

  assign image = section.settings.image
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_padding = section.settings.image_padding
  assign image_padding_mobile = section.settings.image_padding_mobile

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign lazy_load_setting = 'lazy'
  unless lazy
    assign lazy_load_setting = 'eiger'
  endunless
-%}

{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .countdown-body-{{ section.id }} {
    display: grid;
    position: relative;
    grid-template-columns: 1fr;    
  }

  .countdown-image-{{ section.id }} {
    grid-area: image;
    position: relative; 
    overflow: hidden;
    padding: {{ image_padding_mobile }}px;
  }

  .countdown-image-{{ section.id }} img,
  .countdown-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ image_radius }}px;
  }

  .countdown-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .countdown-content-{{ section.id }} {
    padding: 2rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;    
    }   

    .countdown-image-{{ section.id }} {
      padding: {{ image_padding }}px;
      aspect-ratio: auto !important;
    }

    .countdown-content-{{ section.id }} {
      padding: 5rem;
      justify-content: {{ content_vertical_align }};
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .countdown-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .countdown-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .countdown-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .countdown-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}


{% if layout_height == "full_screen" %}
  <style>

    @media(min-width: 1024px) {      
      .section-{{ section.id }}-settings {
        height: 100%;
      }

      .countdown-{{ section.id }} {
        height: 95vh;
      }

      .countdown-body-{{ section.id }} {
        height: 95vh;
      }
    }
  </style>
{% elsif layout_height == 'fixed_height' %}  
  <style>      
    @media(min-width: 1024px) {
      .section-{{ section.id }}-settings {
        height: 100%;
      }

      .countdown-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
      }

     .countdown-body-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
     }
    }
  </style>
{% endif %}

{% if layout_mobile == "image_content" %}
  <style>
    .countdown-body-{{ section.id }} {
      display: flex;
      flex-direction: column;
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_image" %}
    <style>
      .countdown-body-{{ section.id }} {
        display: flex;
        flex-direction: column-reverse;
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "image_content" %}
  <style>
    @media (min-width: 1024px) {
      .countdown-body-{{ section.id }} {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-template-areas: "image content";
      }
    }
  </style>
{% else %}  
  {% if layout == "content_image" %}
    <style>
      @media (min-width: 1024px) {
        .countdown-body-{{ section.id }} {
          display: grid;
          grid-template-columns: 1fr 1fr;
          grid-template-areas: "content image";
        }
      }
    </style>
  {% endif %}  
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'sub_heading' %}
      <style>
        {{  block.settings.sub_heading_font | font_face: font_display: 'swap' }}

        .countdown-subheading-{{ block.id }} {
          display: flex;
          align-items: center;
          gap: 12px;
          justify-content: {{ content_horizontal_align_mobile }};
          margin-top: {{ block.settings.sub_heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .countdown-subheading-{{ block.id }}:before {
          content: "";
          display: block;
          width: 6px;
          height: 6px;
          border-radius: 50%;
          background-color: {{ block.settings.sub_heading_color }};
        }

        .countdown-subheading-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.sub_heading_size_mobile }}px;
          color: {{ block.settings.sub_heading_color }};
          line-height: {{ block.settings.sub_heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }          

        @media(min-width: 1024px) {
          .countdown-subheading-{{ block.id }} {
            justify-content: {{ content_horizontal_align }};
            margin-top: {{ block.settings.sub_heading_mt }}px;
            text-align: {{ content_align }};
          }

          .countdown-subheading-{{ block.id }} * {                
            font-size: {{ block.settings.sub_heading_size }}px;
          }  
        }
      </style>

      {% if block.settings.sub_heading_custom %}
        <style>
          .countdown-subheading-{{ block.id }} * {
            font-family: {{ block.settings.sub_heading_font.family }}, {{ block.settings.sub_heading_font.fallback_families }};
            font-weight: {{ block.settings.sub_heading_font.weight }};
            font-style: {{ block.settings.sub_heading_font.style }};
          }
        </style>
      {% endif %}
    {% when 'heading' %}
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .countdown-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .countdown-heading-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }          

        @media(min-width: 1024px) {
          .countdown-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }

          .countdown-heading-{{ block.id }} * {                
            font-size: {{ block.settings.heading_size }}px;
          }  
        }
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .countdown-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}  
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .countdown-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .countdown-text-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }  

        .countdown-text-{{ block.id }} strong {
          color: {{ block.settings.text_active_color }};
        }

        @media(min-width: 1024px) {
          .countdown-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{ content_align }};
          }

          .countdown-text-{{ block.id }} * {                
            font-size: {{ block.settings.text_size }}px;
          }  
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .countdown-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'scrolling_text' %}
      <style>
        {{ block.settings.scrolling_text_font | font_face: font_display: 'swap' }}

        .countdown-wrap-{{ block.id }} {                
          position: absolute;
          max-height: fit-content;      
          -ms-flex-wrap: nowrap;
          flex-wrap: nowrap;
          z-index: 0;        
          background-attachment: scroll !important;        
          padding: {{ block.settings.scrolling_padding_horizontal_mobile }}px;
          background-color: {{ block.settings.scrolling_bg_color }};
          overflow: hidden;
        }

        .countdown-wrap-mobile-{{ block.id }} {
          position: relative;
          display: -webkit-box;
          display: -ms-flexbox;
          display: flex;
          transform-origin: left;
          margin-bottom: 0;
        }

        .countdown-wrap-desktop-{{ block.id }} {
          display: none;
          transform-origin: left;
          left: 50%;
          transform: rotate(90deg) translateX(-10vh);      
        }

        .countdown-list-{{ block.id }} {
          display: -webkit-box;
          display: -ms-flexbox;
          display: flex;
          white-space: nowrap;
          height: fit-content;
          background-attachment: scroll !important;
          -webkit-animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity_mobile }}s infinite linear;
          animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity_mobile }}s infinite linear;
          flex-shrink: 0;
        }

        .countdown-{{ block.id }} {
          margin: 0;    
          font-weight: 700;
          font-size: {{ block.settings.scrolling_text_size_mobile }}px;
          color: {{ block.settings.scrolling_text_color }};
          line-height: {{ block.settings.scrolling_text_height }}%;
          margin-right: {{ block.settings.scrolling_gap_mobile }}px;
          text-transform: unset;
          word-wrap: break-word;
          background-attachment: scroll !important;
          position: relative;
          flex-shrink: 0;
        }  

        @media (min-width: 1024px) {        
          .countdown-wrap-mobile-{{ block.id }} {         
            display: none;
          }

          .countdown-wrap-desktop-{{ block.id }} {
            display: -webkit-box;
            display: -ms-flexbox;
            display: flex;
            padding: {{ block.settings.scrolling_padding_horizontal }}px;
          }

          .countdown-list-{{ block.id }} {
            -webkit-animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity }}s infinite linear;
            animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity }}s infinite linear;        
          }

          .countdown-{{ block.id }} {
            font-size: {{ block.settings.scrolling_text_size }}px;
            margin-right: {{ block.settings.scrolling_gap }}px;
          }  
        }
      </style>

      {% if block.settings.scrolling_text_custom %}
        <style>
          .countdown-{{ block.id }} {
            font-family: {{ block.settings.scrolling_text_font.family }}, {{ block.settings.scrolling_text_font.fallback_families }};
            font-weight: {{ block.settings.scrolling_text_font.weight }};
            font-style: {{ block.settings.scrolling_text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.reverse_direction %}
        <style>          
          @keyframes ticker{{ block.id | replace: '-', '' }} {
            0% {
              transform: translateX(-50%);
            }
            100% {
              transform: translateX(0%);
            }
          }
        </style>
      {% else %}
        <style>      
          @keyframes ticker{{ block.id | replace: '-', '' }} {
            0% {
              transform: translateX(0%);
            }
            100% {
              transform: translateX(-50%);
            }
          }
        </style>
      {% endif %}

      {% if block.settings.pause_hover %}
        <style>
          .countdown-wrap-{{ block.id }}:hover .countdown-list-{{ block.id }} {
            animation-play-state: paused;
          }
        </style>
      {% endif %}

      {% if layout_mobile == 'content_image' %}
        <style>
          .countdown-wrap-mobile-{{ block.id }} {
            top: 0;        
          }
        </style>
      {% else %}
        <style>
          .countdown-wrap-mobile-{{ block.id }} {
            bottom: 0;        
          }
        </style>
      {% endif %}

    {% when 'button' %}  
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .contdown-button-wrapper-{{ block.id }} {
          display: flex;
          justify-content: {{ content_horizontal_align_mobile }};
        }

        .countdown-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .countdown-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .countdown-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .countdown-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .countdown-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .countdown-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .contdown-button-wrapper-{{ block.id }} {
            display: flex;
            justify-content: {{ content_horizontal_align }};
          }

          .countdown-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.use_shadow %}
        <style>
          .countdown-button-{{ block.id }}:hover {
            -webkit-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            -moz-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
          }
        </style>
      {% endif %}  

      {% if block.settings.button_custom %}
        <style>
          .countdown-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .countdown-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }

          .countdown-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .countdown-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }

          .countdown-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings. button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .countdown-button-{{ block.id }} {
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;
          }
        </style>
      {% endif %}  

    {% when 'timer' %}              
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}
        {{  block.settings.number_font | font_face: font_display: 'swap' }}
        {{  block.settings.completed_text_font | font_face: font_display: 'swap' }}
        {{  block.settings.timer_text_font | font_face: font_display: 'swap' }}

        .countdown-{{ block.id }} {
          margin-top: {{ block.settings.timer_mt_mobile }}px;
          display: flex;
          align-items: center;
          justify-content: {{ content_horizontal_align_mobile }};
          gap: {{ block.settings.timer_gap_mobile }}px;
          flex-wrap: wrap;
          min-width: 0;
          padding: 30px 0px;
          border-top: {{ block.settings.timer_border_thickness }}px solid {{ block.settings.timer_border_color }};
          border-bottom: {{ block.settings.timer_border_thickness }}px solid {{ block.settings.timer_border_color }};
        }

        .countdown-{{ block.id }} .time-block {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-direction: column;
          text-align: center;
          position: relative;      
        }   

        .countdown-{{ block.id }} .time-block__num {
          margin: 0;
          font-size: {{ block.settings.number_size_mobile }}px;
          color: {{ block.settings.number_color }};
          line-height: {{ block.settings.number_height }}%;
          text-transform: unset;
          font-weight: 700;
        }   

        .countdown-{{ block.id }} .time-block__text {
          margin-top: {{ block.settings.timer_text_mt_mobile }}px;
        }

        .countdown-{{ block.id }} .time-block__unit {
          margin: 0;   
          font-size: {{ block.settings.timer_text_size_mobile }}px;
          color: {{ block.settings.timer_text_color }};
          line-height: {{ block.settings.timer_text_height }}%;
          text-transform: unset;
        }

        .countdown-completed-text-{{ block.id }} {
          display: none;
          position: relative;              
        }

        .countdown-completed-text-{{ block.id }} * {
          margin: 16px 0;
          font-size: {{ block.settings.completed_text_size_mobile }}px;
          color: {{ block.settings.completed_text_color }};
          line-height: {{ block.settings.completed_text_height }}%;
          text-transform: unset;
        }

        @media (min-width: 1024px) {
          .countdown-{{ block.id }} {
            justify-content: {{ content_horizontal_align }};
            margin-top: {{ block.settings.timer_mt }}px;
            gap: {{ block.settings.timer_gap }}px;         
          }

          .countdown-{{ block.id }} .time-block {
            min-width: {% if block.settings.number_size > 70 %}120px{% elsif block.settings.number_size > 40 %}90px{% else %}45px{% endif %}; 
          }

          .countdown-{{ block.id }} .time-block__num {
            font-size: {{ block.settings.number_size }}px;
          }

          .countdown-{{ block.id }} .time-block__text {
            margin-top: {{ block.settings.timer_text_mt }}px;
          }

