{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign content_width = section.settings.content_width
  assign full_width = section.settings.full_width

  assign bg_image = section.settings.bg_image
  assign bg_image_mobile = section.settings.bg_image_mobile

  assign position = section.settings.position

  assign hide_close = section.settings.hide_close

  assign year = section.settings.year
  assign month = section.settings.month
  assign day = section.settings.day
  assign days_text = section.settings.days_text
  assign hour = section.settings.hour
  assign hours_text = section.settings.hours_text
  assign minute = section.settings.minute
  assign minutes_text = section.settings.minutes_text
  assign seconds_text = section.settings.seconds_text
  assign hide_timer_on_completed = section.settings.hide_timer_on_completed
  assign hide_section_on_completed = section.settings.hide_section_on_completed

  assign timer_padding_horizontal = section.settings.timer_padding_horizontal
  assign timer_padding_horizontal_mobile = section.settings.timer_padding_horizontal_mobile
  assign timer_padding_vertical = section.settings.timer_padding_vertical
  assign timer_padding_vertical_mobile = section.settings.timer_padding_vertical_mobile
  assign timer_bg_color = section.settings.timer_bg_color
  assign timer_border_thickness = section.settings.timer_border_thickness
  assign timer_border_color = section.settings.timer_border_color
  assign timer_radius = section.settings.timer_radius
  assign timer_bg_color = section.settings.timer_bg_color

  assign number_custom = section.settings.number_custom
  assign number_font = section.settings.number_font
  assign number_size = section.settings.number_size
  assign number_size_mobile = section.settings.number_size_mobile
  assign number_height = section.settings.number_height
  assign number_color = section.settings.number_color
  assign number_bg_color = section.settings.number_bg_color
  assign number_padding = section.settings.number_padding
  assign number_radius = section.settings.number_radius

  assign timer_text_custom = section.settings.timer_text_custom
  assign timer_text_font = section.settings.timer_text_font
  assign timer_text_size = section.settings.timer_text_size
  assign timer_text_size_mobile = section.settings.timer_text_size_mobile
  assign timer_text_height = section.settings.timer_text_height
  assign timer_text_color = section.settings.timer_text_color

  assign completed_text = section.settings.completed_text
  assign completed_text_custom = section.settings.completed_text_custom
  assign completed_text_font = section.settings.completed_text_font
  assign completed_text_size = section.settings.completed_text_size
  assign completed_text_size_mobile = section.settings.completed_text_size_mobile
  assign completed_text_height = section.settings.completed_text_height
  assign completed_text_color = section.settings.completed_text_color

  assign text = section.settings.text
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_link_color = section.settings.text_link_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height

  assign close_color = section.settings.close_color
-%}

{%- style -%}
  {{  number_font | font_face: font_display: 'swap' }}
  {{  completed_text_font | font_face: font_display: 'swap' }}
  {{  timer_text_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    position: relative;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .timer-close-{{ section.id }} {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 9999;
    cursor: pointer;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 4px;
  }

  .timer-close-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .timer-close-{{ section.id }} svg rect {
    fill: {{ close_color }};
  }

  .countdown-timer-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .countdown-timer-text-{{ section.id }} {
    max-width: 100%;
    text-align: center;
  }

  .countdown-timer-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .countdown-timer-text-{{ section.id }} a,
  .countdown-timer-text-{{ section.id }} a * {
    color: {{ text_link_color }} !important;
    text-decoration: none !important;
  }

  .countdown-timer-timer-{{ section.id }} {
    display: flex;
    position: relative;
    align-items: center;  
    background-color: {{ timer_bg_color }};
    gap: 15px;
    padding: {{ timer_padding_vertical_mobile }}px {{ timer_padding_horizontal_mobile }}px;
    border: {{ timer_border_thickness }}px solid {{ timer_border_color }};
    border-radius: {{ timer_radius }}px;
  }

  .countdown-timer-timer-{{ section.id }} .time-block {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    width: 100%;
    gap: 6px;
    text-wrap: nowrap
  }

  .countdown-timer-timer-{{ section.id }} .time-block__num {
    display: flex;
    gap: 4px;
    margin: 0;
    font-size: {{ number_size_mobile }}px;
    color: {{ number_color }};
    line-height: {{ number_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .countdown-timer-timer-{{ section.id }} .time-block__num .digit {
    padding: {{ number_padding }}px;
    border-radius: {{ number_radius }}px;
    background-color: {{ number_bg_color }};
  }

  .countdown-timer-timer-{{ section.id }} .time-block__unit {
    margin: 0;
    font-size: {{ timer_text_size_mobile }}px;
    color: {{ timer_text_color }};
    line-height: {{ timer_text_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-align: center;
  }

  .countdown-timer-completed-text-{{ section.id }} {
    display: none;
    position: relative;    
  }

  .countdown-timer-completed-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ completed_text_size_mobile }}px;
    color: {{ completed_text_color }};
    line-height: {{ completed_text_height }}%;
    text-transform: unset;
  }

  .countdown-timer-timer-{{ section.id }} .time-block__text {
    display: flex;
    align-items: center;
    gap: 4px;
  }

  @media(min-width: 1024px) {
    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;      
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-timer-content-{{ section.id }} {
      flex-direction: row;
    }

    .countdown-timer-text-{{ section.id }} {
      max-width: 40%;
      text-align: left;
    }

    .countdown-timer-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .countdown-timer-timer-{{ section.id }} {
      padding: {{ timer_padding_vertical }}px {{ timer_padding_horizontal }}px;
      gap: 10px;
    }

     .countdown-timer-timer-{{ section.id }} .time-block {
       flex-direction: row;
     }

    .countdown-timer-timer-{{ section.id }} .time-block__num {
      font-size: {{ number_size }}px;
    }

    .countdown-timer-timer-{{ section.id }} .time-block__unit {
      font-size: {{ timer_text_size }}px;
      text-align: left;
    }

    .countdown-timer-completed-text-{{ section.id }} * {
      font-size: {{ completed_text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if position == "fixed_bottom" %}
  <style>
    .countdown-timer-{{ section.id }} {
      position: fixed;
      left: 0;
      bottom: 0;
      z-index: 999;
      width: 100%;
    }
  </style>
{% endif %}

{% if bg_image != blank %}
  <style>
    .countdown-timer-{{ section.id }} {
      background-image: url('{{ bg_image | image_url }}') !important;
      background-repeat: no-repeat;
      background-size: cover;
      background-position: center;
    }
  </style>
{% endif %}

{% if bg_image_mobile != blank %}
  <style>
    @media(max-width: 1024px) {
      .countdown-timer-{{ section.id }} {
        background-image: url('{{ bg_image_mobile | image_url }}') !important;
        background-repeat: no-repeat;
        background-size: cover;
        background-position: center;
      }
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .countdown-timer-{{ section.id }} .time-block__num {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
      font-style: {{ number_font.style }};
    }
  </style>
{% endif %}

{% if timer_text_custom %}
  <style>
    .countdown-timer-{{ section.id }} .time-block__unit {
      font-family: {{ timer_text_font.family }}, {{ timer_text_font.fallback_families }};
      font-weight: {{ timer_text_font.weight }};
      font-style: {{ timer_text_font.style }};
    }
  </style>
{% endif %}

{% if completed_text_custom %}
  <style>
    .countdown-timer-completed-text-{{ section.id }} * {
      font-family: {{ completed_text_font.family }}, {{ completed_text_font.fallback_families }};
      font-weight: {{ completed_text_font.weight }};
      font-style: {{ completed_text_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .countdown-timer-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if position == "class" %}
  <template id="countdown-timer-template-{{ section.id }}">
{% endif %}
  <div class="section-{{ section.id }} countdown-timer-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">  
      {% unless hide_close %}
        <div class="timer-close-{{ section.id }}">
          <svg viewBox="0 0 22 24" fill="none" xmlns="http://www.w3.org/2000/svg" class="w-5 text-white"><rect x="1.25391" y="19.4238" width="25" height="4" transform="rotate(-45 1.25391 19.4238)" fill="currentColor"></rect><rect x="4.08203" y="1.74609" width="25" height="4" transform="rotate(45 4.08203 1.74609)" fill="currentColor"></rect></svg>
        </div>
      {% endunless %}
      <div class="countdown-timer-content-{{ section.id }}">
          <div class="countdown-timer-timer-{{ section.id }}">        
            <div class="time-block">
              <span class="time-block__num js-timer-days">
                <span class="digit">0</span>
                <span class="digit">0</span>
              </span>
              <div class="time-block__text">
                <span class="time-block__unit">{{ days_text }}</span>
              </div>              
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-hours">
                <span class="digit">0</span>
                <span class="digit">0</span>
              </span>
              <div class="time-block__text">
                <span class="time-block__unit">{{ hours_text }}</span>
              </div>              
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-minutes">
                <span class="digit">0</span>
                <span class="digit">0</span>
              </span>
              <div class="time-block__text">
                <span class="time-block__unit">{{ minutes_text }}</span>
              </div>                   
            </div>
            <div class="time-block">
              <span class="time-block__num js-timer-seconds">
                <span class="digit">0</span>
                <span class="digit">0</span>
              </span>
              <div class="time-block__text">
                <span class="time-block__unit">{{ seconds_text }}</span>
              </div>                   
            </div>
          </div>
          <div class="countdown-timer-completed-text-{{ section.id }}">
            {{ completed_text }}
          </div>
          {% if text != blank %}
            <div class="countdown-timer-text-{{ section.id }}"> 
              {{ text }}
            </div>      
          {% endif %}
        </div>
      </div>
    </div>
{% if position == "class" %}
</template>
{% endif %}

<div id="countdown-timer-local-target-{{ section.id }}"></div>

<script>
  function initCountdownTimerBar3() {
  setTimeout(() => {
    {% if position == "class" %}
      let cssTarget = '{{ section.settings.insert_css_class_target }}';
      let productForm = cssTarget
        ? document.querySelector(cssTarget)
        : document.querySelector('#countdown-timer-local-target-{{ section.id }}');

      if (!productForm) return;

      const templateContent = document.querySelector(
        '#countdown-timer-template-{{ section.id }}'
      ).content;

      productForm.insertAdjacentElement(
        'beforebegin',
        templateContent.firstElementChild
      );
    {% endif %}

    let section = document.querySelector('.countdown-timer-{{ section.id }}');

    let close = document.querySelector('.timer-close-{{ section.id }}');

    if(close) {
      close.addEventListener('click', () => {
        section.style.display = "none";
      })
    }

    function startTimer() {
      const second = 1000,
        minute = second * 60,
        hour = minute * 60,
        day = hour * 24,
        wrapper = document.querySelector(
          '.countdown-timer-timer-{{ section.id }}'
        ),
        countDown = new Date('{{ year }}-{{ month }}-{{ day }}T{{ hour }}:{{ minute }}:00Z').getTime();

      console.log('start timer, end date:' + countDown);

      function formatNumberToDigits(number) {
        // Pad the number with leading zeros to ensure at least 2 digits
        const paddedNumber = number.toString().padStart(2, '0');
        let digits = '';

        // Convert each digit to the span format
        for(let i = 0; i < paddedNumber.length; i++) {
          digits += `<span class="digit">${paddedNumber[i]}</span>`;
        }

        return digits;
      }

      const interval = setInterval(function () {
        let now = new Date().getTime(),
          distance = countDown - now,
          days = Math.floor(distance / day),
          hours = Math.floor((distance % day) / hour),
          minutes = Math.floor((distance % hour) / minute),
          seconds = Math.floor((distance % minute) / second);

        wrapper.querySelector('.js-timer-days').innerHTML = formatNumberToDigits(days);
        wrapper.querySelector('.js-timer-hours').innerHTML = formatNumberToDigits(hours);
        wrapper.querySelector('.js-timer-minutes').innerHTML = formatNumberToDigits(minutes);
        wrapper.querySelector('.js-timer-seconds').innerHTML = formatNumberToDigits(seconds);

        if (distance < 0) {
          const zeroDigits = '<span class="digit">0</span><span class="digit">0</span>';
          wrapper.querySelector('.js-timer-days').innerHTML = zeroDigits;
          wrapper.querySelector('.js-timer-hours').innerHTML = zeroDigits;
          wrapper.querySelector('.js-timer-minutes').innerHTML = zeroDigits;
          wrapper.querySelector('.js-timer-seconds').innerHTML = zeroDigits;

          {% if hide_timer_on_completed %}
            wrapper.style.display = 'none';   
            document.querySelector(
              '.countdown-timer-completed-text-{{ section.id }}'
            ).style.display = 'block';         
          {% endif %}

          {% if hide_section_on_completed %}
            section.style.display = 'none';
          {% endif %}

          clearInterval(interval);
        }
      }, second);

      document.addEventListener('shopify:section:unload', function (event) {
        if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
      });
    }

    startTimer();

    if (window.Shopify && Shopify.designMode) {
      document.addEventListener('shopify:section:load', function (event) {
        if (event.detail.sectionId === '{{ section.id }}') {
          startTimer();
        }
      });
    }
  }, 500);
}

document.addEventListener('DOMContentLoaded', initCountdownTimerBar3);

</script>
