{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal = section.settings.margin_horizontal
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign content_width = section.settings.content_width
  assign full_width = section.settings.full_width
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign position = section.settings.position
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_mt_mobile = section.settings.content_mt_mobile

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign hide_close = section.settings.hide_close

  assign close_color = section.settings.close_color
-%}

{%- style -%}
  
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;    
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    position: relative;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .countdown-timer-content-{{ section.id }} {
    position: relative;
    display: flex;
    align-items: {{ content_horizontal_align_mobile }};
    gap: 20px;
    justify-content: space-between;
    flex-direction: column;
  }

  .timer-close-{{ section.id }} {
    position: absolute;
    top: -{{ content_mt_mobile | plus: 4 }}px;
    right: 0px;
    z-index: 9999;
    cursor: pointer;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 4px;
  }

  .timer-close-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .timer-close-{{ section.id }} svg rect {
    fill: {{ close_color }};
  }

  @media(min-width: 1024px) {
    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;      
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .countdown-timer-content-{{ section.id }} {  
      flex-direction: row;
      align-items: center;
    }

    .timer-close-{{ section.id }} {    
      top: 50%;
      right: -24px;
      transform: translateY(-50%);    
    }

  }
  
{%- endstyle -%}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if position == "fixed_bottom" %}
  <style>
    .countdown-timer-{{ section.id }} {
      position: fixed;
      left: 0;
      bottom: 0;
      z-index: 999;
      width: calc(100% - {{ margin_horizontal_mobile | times: 2 }}rem);
    }

    @media(min-width: 1024px) {
      .countdown-timer-{{ section.id }} {
        width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
      }
    }
  </style>
{% endif %}

{% if hide_close == false %}
  <style>
    .countdown-timer-content-{{ section.id }} {
      margin-top: {{ content_mt_mobile }}px;
    }

    @media(min-width: 1024px) {
      .countdown-timer-content-{{ section.id }} {
        margin-top: 0;
        margin-right: 24px;
      }
    }
  </style>

{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'copy_button' %}
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}
        {{  block.settings.success_text_font | font_face: font_display: 'swap' }}

        .countdown-button-{{ block.id }} {
          position: relative;
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
          background-color: {{  block.settings.button_bg_color }};
          border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
        }

        .countdown-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .countdown-button-{{ block.id }}:hover {
          background-color: {{  block.settings.button_bg_hover_color }};
          border: {{  block.settings.button_border_thickness }}px solid {{ block.settings. button_border_hover_color }};
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .countdown-button-{{ block.id }} svg {
          width: {{ block.settings.copy_icon_size_mobile }}px;
          height: {{ block.settings.copy_icon_size_mobile }}px;
        }

        .countdown-button-{{ block.id }} svg path {
          stroke: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .countdown-button-{{ block.id }}:hover svg path {
          stroke: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .countdown-copy-success-{{ block.id }} {
          margin: 0;
          display: none;
          padding: 6px 8px;
          border-radius: {{ block.settings.success_text_radius }}px;
          border: {{ block.settings.success_text_border_thickness }}px solid {{ block.settings.success_text_border_color }};
          position: absolute;
          top: 80%;
          left: 8%;
          background-color: {{ block.settings.success_text_bg_color }};
          font-size: {{ block.settings.success_text_size_mobile }}px;
          color: {{ block.settings.success_text_color }};
          line-height: {{ block.settings.success_text_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          font-weight: 400;
        }

        .countdown-copy-success-{{ block.id }}.active {
          display: block;
        }

        @media (min-width: 1024px) {
          .countdown-button-{{ block.id }} {
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }

          .countdown-button-{{ block.id }} svg {
            width: {{ block.settings.copy_icon_size }}px;
            height: {{ block.settings.copy_icon_size }}px;
          }

          .countdown-copy-success-{{ block.id }} {
            font-size: {{ block.settings.success_text_size }}px;        
          }
        }
      </style>

      {% if block.settings.use_shadow %}
        <style>
          .countdown-button-{{ block.id }}:hover {
            -webkit-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            -moz-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
          }
        </style>
      {% endif %}  

      {% if block.settings.button_custom %}
        <style>
          .countdown-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.success_text_custom %}
        <style>
          .countdown-copy-success-{{ block.id }} {
            font-family: {{ block.settings.success_text_font.family }}, {{ block.settings.success_text_font.fallback_families }};
            font-weight: {{ block.settings.success_text_font.weight }};
            font-style: {{ block.settings.success_text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .countdown-text-{{ block.id }} {
          text-align: {{ content_align_mobile }};
          max-width: {{ block.settings.text_max_width_mobile }}%;
        }

        .countdown-text-{{ block.id }} * {          
          margin: 0;            
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }  

        .countdown-text-{{ block.id }} strong {
          color: {{ block.settings.text_accent_color }};
          background-color: {{ block.settings.text_accent_bg_color }};
          padding: 5px;
          border-radius: {{ block.settings.text_accent_roundness }}px;
        }

        @media(min-width: 1024px) {
          .countdown-text-{{ block.id }} {
            text-align: {{ block.settings.text_align }};
            max-width: {{ block.settings.text_max_width }}%;
          }

          .countdown-text-{{ block.id }} * {                
            font-size: {{ block.settings.text_size }}px;
          }  
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .countdown-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

    {% when 'timer' %}      
      <style>
        {{  block.settings.number_font | font_face: font_display: 'swap' }}
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        {{  block.settings.completed_text_font | font_face: font_display: 'swap' }}
        
        .countdown-timer-{{ block.id }} {
          display: flex;
          align-items: stretch;
          gap: {{ block.settings.timer_gap_mobile}}px;

        }

        .countdown-timer-{{ block.id }} .time-block {
          display: flex;
          flex-direction: column;
          align-items: stretch;
          justify-content: start;
          text-align: center; 
          width: {{ block.settings.item_width_mobile }}px;              
        }

        .countdown-timer-{{ block.id }} .time-block__num {
          margin: 0;          
          border-radius: {{ block.settings.item_radius }}px; 
          border: {{ block.settings.item_border_thickness }}px solid {{  block.settings.item_border_color }};
          padding: {{ block.settings.item_padding_vertical_mobile }}px 4px;
          background-color: {{ block.settings.item_bg_color }};
          font-size: {{ block.settings.number_size_mobile }}px;
          color: {{ block.settings.number_color }};
          line-height: {{ block.settings.number_height }}%;
          text-transform: unset;
        }
        
        .countdown-timer-{{ block.id }} .time-block__unit {
          margin: 0;
          margin-top: {{ block.settings.text_mt_mobile }}px;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          word-break: break-word;
        }

        .countdown-timer-completed-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.completed_text_size_mobile }}px;
          color: {{ block.settings.completed_text_color }};
          line-height: {{ block.settings.completed_text_height }}%;
          text-transform: unset;
          text-align: {{ content_align_mobile }};
        }

        @media(min-width: 1024px) {
          .countdown-timer-{{ block.id }} {
            gap: {{ block.settings.timer_gap }}px;
          }

          .countdown-timer-{{ block.id }} .time-block {
            width: {{ block.settings.item_width }}px;      
          }

          .countdown-timer-{{ block.id }} .time-block__num { 
            font-size: {{ block.settings.number_size }}px;
            padding: {{ block.settings.item_padding_vertical }}px 4px;
          }

          .countdown-timer-{{ block.id }} .time-block__unit {
            margin-top: {{ block.settings.text_mt }}px;
            font-size: {{ block.settings.text_size }}px;
          }

          .countdown-timer-completed-text-{{ block.id }} * {
            font-size: {{ block.settings.completed_text_size }}px;
          }
        }
      </style>

      {% if block.settings.number_custom %}
        <style>
          .countdown-timer-{{ block.id }} .time-block__num {
            font-family: {{ block.settings.number_font.family }}, {{ block.settings.number_font.fallback_families }};
            font-weight: {{ block.settings.number_font.weight }};
            font-style: {{ block.settings.number_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.text_custom %}
        <style>
          .countdown-timer-{{ block.id }} .time-block__unit {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.completed_text_custom %}
        <style>
          .countdown-timer-completed-text-{{ block.id }} * {
            font-family: {{ block.settings.completed_text_font.family }}, {{ block.settings.completed_text_font.fallback_families }};
            font-weight: {{ block.settings.completed_text_font.weight }};
            font-style: {{ block.settings.completed_text_font.style }};
          }
        </style>
      {% endif %}
    
    {% else %}
      
  {% endcase %}
{% endfor %}

{% if position == "class" %}
<template id="countdown-timer-template-{{ section.id }}">
{% endif %}
  <div class="section-{{ section.id }} countdown-timer-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">  
      <div class="countdown-timer-content-{{ section.id }}">
        {% unless hide_close %}
          <div class="timer-close-{{ section.id }}">
            <svg viewBox="0 0 22 24" fill="none" xmlns="http://www.w3.org/2000/svg" class="w-5 text-white"><rect x="1.25391" y="19.4238" width="25" height="4" transform="rotate(-45 1.25391 19.4238)" fill="currentColor"></rect><rect x="4.08203" y="1.74609" width="25" height="4" transform="rotate(45 4.08203 1.74609)" fill="currentColor"></rect></svg>
          </div>
        {% endunless %}

        {% for block in section.blocks %}
          {% case block.type %}
            {% when 'copy_button' %}              
              {% if block.settings.code != blank %}                
                <div class="countdown-button-{{ block.id }}">
                  <p class="countdown-button-inner-{{ block.id }}">
                    {% if block.settings.code != blank %}
                      <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18" fill="none">
                        <path d="M3.64973 14.3333V14.9661C3.64973 16.0894 4.56034 17 5.68363 17H14.9661C16.0894 17 17 16.0894 17 14.9661V5.70054C17 4.57725 16.0894 3.66664 14.9661 3.66664H14.3503M1 12.2994V3.0339C1 1.91058 1.91061 1 3.0339 1H12.3164C13.4397 1 14.3503 1.91058 14.3503 3.0339V12.2994C14.3503 13.4227 13.4397 14.3333 12.3164 14.3333H3.0339C1.91061 14.3333 1 13.4227 1 12.2994Z" stroke="#121212" stroke-width="1.8" stroke-miterlimit="10" stroke-linecap="round" stroke-linejoin="round"/>
                      </svg>
                      {{ block.settings.copy_text }}
                    {% endif %}              
                  </p>
                  {% if block.settings.code != blank %}
                    <p class="coutdown-copy-code-{{ section.id }}" style="display: none;">{{ block.settings.code }}</p>
                  {% endif %}
                  {% if block.settings.success_text != blank %}
                    <p class="countdown-copy-success-{{ section.id }} countdown-copy-success-{{ block.id }}">{{ block.settings.success_text }}</p>
                  {% endif %}
                </div>                  
               
              {% endif %} 

            {% when 'timer' %}
              <div class="countdown-timer-{{ block.id }}">

                <div class="time-block">
                  <span class="time-block__num js-timer-days">00</span>
                  <span class="time-block__unit">{{ block.settings.days_text }}</span>
                </div>
                <div class="time-block">
                  <span class="time-block__num js-timer-hours">00</span>
                  <span class="time-block__unit">{{ block.settings.hours_text }}</span>
                </div>
                <div class="time-block">
                  <span class="time-block__num js-timer-minutes">00</span>
                  <span class="time-block__unit">{{ block.settings.minutes_text }}</span>
                </div>
                <div class="time-block">
                  <span class="time-block__num js-timer-seconds">00</span>
                  <span class="time-block__unit">{{ block.settings.seconds_text }}</span>
                </div>
              </div>
    
              <style>
                .countdown-timer-completed-text-{{ block.id }} {
                  display: none;
                }
              </style>

              <div
                class="countdown-timer-completed-text-{{ block.id }}"
              >
                {{ block.settings.timer_completed_text }}
              </div>
            
            {% when 'text' %}
              {% if block.settings.text != blank %}
                <div class="countdown-text-{{ block.id }}">{{ block.settings.text }}</div>
              {% endif %} 
            
            {% else %}
              
          {% endcase %}
        {% endfor %}
      </div>     
    </div>
  </div>
{% if position == "class" %}
</template>
{% endif %}

<div id="countdown-timer-local-target-{{ section.id }}"></div>

    <script>
      function initCountdownTimerBar5() {
        setTimeout(() => {
        {% for block in section.blocks %}
          {% case block.type %} 
            {% when 'timer' %}
                {% if position == "class" %}
                  let cssTarget = '{{ section.settings.insert_css_class_target }}';
                  let productForm = cssTarget
                    ? document.querySelector(cssTarget)
                    : document.querySelector('#countdown-timer-local-target-{{ section.id }}');
              
                  if (!productForm) return;
              
                  const templateContent = document.querySelector(
                    '#countdown-timer-template-{{ section.id }}'
                  ).content;
              
                  productForm.insertAdjacentElement(
                    'beforebegin',
                    templateContent.firstElementChild
                  );
                {% endif %}

                let section = document.querySelector('.countdown-timer-{{ section.id }}');

                let close = document.querySelector('.timer-close-{{ section.id }}');

                if(close) {
                  close.addEventListener('click', () => {
                    section.style.display = "none";
                  })
                }

                function startTimer() {
                  const second = 1000,
                    minute = second * 60,
                    hour = minute * 60,
                    day = hour * 24,
                    wrapper = document.querySelector(
                      '.countdown-timer-{{ block.id }}'
                    ),
                    countDown = new Date('{{  block.settings.year }}-{{  block.settings.month }}-{{  block.settings.day }}T{{  block.settings.hour }}:{{  block.settings.minute }}:00Z').getTime();

                  console.log('start timer, end date:' + countDown)
                  const interval = setInterval(function () {
                    let now = new Date().getTime(),
                      distance = countDown - now,
                      days = Math.floor(distance / day),
                      hours = Math.floor((distance % day) / hour),
                      minutes = Math.floor((distance % hour) / minute),
                      seconds = Math.floor((distance % minute) / second);

                    (wrapper.querySelector('.js-timer-days').innerText = (
                      days
                    )),
                      (wrapper.querySelector('.js-timer-hours').innerText = (
                        hours
                      )),
                      (wrapper.querySelector('.js-timer-minutes').innerText = (
                        minutes
                      )),
                      (wrapper.querySelector('.js-timer-seconds').innerText = (
                        seconds
                      ));

                    if (distance < 0) {
                      wrapper.querySelector('.js-timer-days').innerText = '00';
                      wrapper.querySelector('.js-timer-hours').innerText = '00';
                      wrapper.querySelector('.js-timer-minutes').innerText = '00';
                      wrapper.querySelector('.js-timer-seconds').innerText = '00';
                      {% if block.settings.hide_timer_on_completed %}
                        wrapper.style.display = 'none';
                      {% endif %}
                      document.querySelector(
                        '.countdown-timer-completed-text-{{ block.id }}'
                      ).style.display = 'block';

                      clearInterval(interval);
                    }

                  }, second);
                  document.addEventListener('shopify:section:unload', function (event) {
                    if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
                  });
                }

                startTimer()
                if (window.Shopify && Shopify.designMode) {
                  document.addEventListener('shopify:section:load', function (event) {
                    if (event.detail.sectionId === '{{ section.id }}') {
                      startTimer();
                    }
                  });
                }

            {% when 'copy_button' %}
              const copyButton = document.querySelector('.countdown-button-{{ block.id }}');
              const copyCodeElement = document.querySelector('.coutdown-copy-code-{{ section.id }}');
              const copyCodeText = document.querySelector('.countdown-copy-success-{{ block.id }}');
                  
              if (copyButton && copyCodeElement) {
                copyButton.addEventListener('click', function() {
                  const textarea = document.createElement('textarea');
                  textarea.value = copyCodeElement.textContent;
                  
                  document.body.appendChild(textarea);
                  
                  textarea.select();
                  document.execCommand('copy');
                  
                  document.body.removeChild(textarea);

                  copyCodeText.classList.add('active');

                  setTimeout(() => {
                    copyCodeText.classList.remove('active');
                  }, 2000)
                });
              }

          {% endcase %}
        {% endfor %}        
        }, 500);
      }
      document.addEventListener('DOMContentLoaded', initCountdownTimerBar5);  
    </script>
