liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign body_gap = section.settings.body_gap
  assign body_gap_mobile = section.settings.body_gap_mobile

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_border_thickness = section.settings.content_border_thickness
  assign content_border_color = section.settings.content_border_color
  assign content_bg_color = section.settings.content_bg_color
  assign content_radius = section.settings.content_radius

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align_mobile = section.settings.heading_align_mobile
  assign heading_align = section.settings.heading_align

  assign item_padding_vertical_mobile = section.settings.item_padding_vertical_mobile
  assign item_padding_vertical = section.settings.item_padding_vertical
  assign item_padding_horizontal_mobile = section.settings.item_padding_horizontal_mobile
  assign item_padding_horizontal = section.settings.item_padding_horizontal
  assign item_radius = section.settings.item_radius
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_border_color = section.settings.item_border_color
  assign item_bg_color = section.settings.item_bg_color
  assign item_mt = section.settings.item_mt
  assign item_mt_mobile = section.settings.item_mt_mobile

  assign question_size = section.settings.question_size
  assign question_size_mobile = section.settings.question_size_mobile
  assign question_color = section.settings.question_color
  assign question_custom = section.settings.question_custom
  assign question_font = section.settings.question_font
  assign question_height = section.settings.question_height

  assign answer_size = section.settings.answer_size
  assign answer_size_mobile = section.settings.answer_size_mobile
  assign answer_color = section.settings.answer_color
  assign answer_custom = section.settings.answer_custom
  assign answer_font = section.settings.answer_font
  assign answer_height = section.settings.answer_height
  assign answer_padding_top = section.settings.answer_padding_top

  assign icon_color = section.settings.icon_color
  assign icon_size = section.settings.icon_size
  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_border_thickness = section.settings.icon_border_thickness
  assign icon_border_color = section.settings.icon_border_color

  assign image_show_mobile = section.settings.image_show_mobile
  assign images_gap = section.settings.images_gap
  assign images_gap_mobile = section.settings.images_gap_mobile

  assign image_radius = section.settings.image_radius
  assign image_height = section.settings.image_height
  assign image_height_mobile = section.settings.image_height_mobile
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_count = 0

  for block in section.blocks
    if block.type == 'image'
      assign image_count = image_count | plus: 1 
    endif
  endfor
-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  question_font | font_face: font_display: 'swap' }}
  {{  answer_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .faq-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ body_gap_mobile }}px;
  }

  .faq-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    background-color: {{ content_bg_color }};
    border-radius: {{ content_radius }}px;
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
  }

  .faq-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
    width: 100%;
  }

  .faq-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    font-weight: 700;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .faq-items-{{ section.id }} {
    width: 100%;
  }

  .faq-item-{{ section.id }}:first-child {
    margin-top: {{ item_mt_mobile }}px;
  }

  .faq-item-{{ section.id }} {
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    border-radius: {{ item_radius }}px;
    background-color: {{ item_bg_color }};
    width: 100%;
    position: relative;
  }

  .faq-item-{{ section.id }}:not(:last-child) {
    border-bottom: {{ item_border_thickness }}px solid {{ item_border_color }};
  }

  .faq-toggle-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    cursor: pointer;
    gap: 12px;
  }

  .faq-icon-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    flex: 0 0 {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
    border-radius: 50%;
    border: {{ icon_border_thickness }}px solid {{ icon_border_color }};
  }

  .faq-icon-{{ section.id }} svg {
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .faq-icon-{{ section.id }} svg g {
    fill: {{ icon_color }};
  }

  .faq-icon-{{ section.id }} svg:last-child {
    display: none;
  }

  .faq-icon-{{ section.id }}.active svg:first-child {
    display: none;
  }

  .faq-icon-{{ section.id }}.active svg:last-child {
    display: block;
  }

  .faq-question-{{ section.id }} {
    margin: 0px;
    font-size: {{ question_size_mobile }}px;
    color: {{ question_color }};
    line-height: {{ question_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .faq-answer-{{ section.id }} {
    transition: all 0.2s ease-in-out;
    overflow: hidden;
  }

  .faq-answer-{{ section.id }} * {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .faq-answer-{{ section.id }} p {
    min-height: 10px;
  }

  .faq-answer-{{ section.id }} p:not(:first-child ) {
    margin-top: 10px;
  } 

  .faq-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;
    width: calc(100% - {{ item_padding_horizontal_mobile | times: 2 }}px);
    padding-top: {{ answer_padding_top }}px;
  }

  .faq-height-cal-{{ section.id }} * {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .faq-height-cal-{{ section.id }} p {
    min-height: 10px;
  }

  .faq-height-cal-{{ section.id }} p:not(:first-child ) {
    margin-top: 10px;
  }

  .faq-image-{{ section.id }} {
    overflow: hidden;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
  }

  .faq-image-{{ section.id }} img,
  .faq-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .faq-image-{{ section.id }} svg {
    background: #AEAEAE;
  }

  .faq-image-column-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    gap: {{ images_gap_mobile }}px;
  }

  .faq-image-column-{{ section.id }} .faq-image-{{ section.id }} {
    width: 100%
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .faq-body-{{ section.id }} {
      grid-template-columns: repeat(3, 1fr);
      gap: {{ body_gap }}px;
    }

    .faq-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .faq-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }    

    .faq-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .faq-item-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
    }

    .faq-item-{{ section.id }}:first-child {
      margin-top: {{ item_mt }}px;
    }

    .faq-question-{{ section.id }} {
      font-size: {{ question_size }}px;
    }

    .faq-answer-{{ section.id }} * {
      font-size: {{ answer_size }}px;
    }

    .faq-icon-{{ section.id }} {
      flex: 0 0 {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .faq-icon-{{ section.id }} svg {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .faq-height-cal-{{ section.id }} {
      width: calc(100% - {{ item_padding_horizontal | times: 2 }}px);
    }

    .faq-height-cal-{{ section.id }} * {
      font-size: {{ answer_size }}px;
    }

    .faq-image-column-{{ section.id }} {
      gap: {{ images_gap }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .faq-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if question_custom %}
  <style>
    .faq-question-{{ section.id }} {
      font-family: {{ question_font.family }}, {{ question_font.fallback_families }};
      font-weight: {{ question_font.weight }};
      font-style: {{ question_font.style }};
    }
  </style>
{% endif %}

{% if answer_custom %}
  <style>
    .faq-answer-{{ section.id }} * {
      font-family: {{ answer_font.family }}, {{ answer_font.fallback_families }};
      font-weight: {{ answer_font.weight }};
      font-style: {{ answer_font.style }};
    }
  </style>
{% endif %}


{% if image_count == 5 %} 
  <style>
    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(1),
    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(4) {
      max-height: 150px;
    }

    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(2),
    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(3) {
      width: calc(50% - ({{ images_gap_mobile }}px / 2)) !important;
    }

    @media (min-width: 1024px) {
      .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(2),
      .faq-image-column-{{ section.id }} .faq-image-{{ section.id }}:nth-child(3) {
        width: calc(50% - ({{ images_gap }}px / 2)) !important;
      }
    }
  </style>
{% endif %}

{% if image_count < 5 %} 
  <style>
    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }} {
      min-height: {{ image_height_mobile }}px;
    }
  </style>
{% endif %}  

{% if image_count >= 3 %} 
  <style>
    .faq-image-column-{{ section.id }} .faq-image-{{ section.id }} {
      min-height: {{ image_height_mobile }}px;
    }

    @media(min-width: 1024px) {
      .faq-image-column-{{ section.id }} .faq-image-{{ section.id }} {
        min-height: auto;
        max-height: {{ image_height }}px !important;
      }
    }
  </style>
{% endif %}  

{% if image_count < 2 %} 
  <style>
    .faq-body-{{ section.id }} {
      grid-template-columns: 1fr;
    }
    @media (min-width: 1024px) {
      .faq-body-{{ section.id }} {
        grid-template-columns: repeat(2, 1fr);
      }
    }
  </style>
{% endif %} 

{% unless image_show_mobile %} 
  <style>
    .faq-image-{{ section.id }} {
      display: none;
    }

    @media (min-width: 1024px) {
      .faq-image-{{ section.id }} {
        display: block;
      }
    }
  </style>
{% endunless %}

<div class="section-{{ section.id }} faq-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="faq-body-{{ section.id }}">
        <div class="faq-content-{{ section.id }}">
          {% if heading != blank %}
            <div class="faq-heading-{{ section.id }}">
              {{ heading }}
            </div>
          {% endif %}
          <div class="faq-items-{{ section.id }}">
            {% for block in section.blocks %}
              {% case block.type %}
                {% when 'item' %}              
                  <div class="faq-item-{{ section.id }}">        
                    <div class="faq-toggle-{{ section.id }}">
                      <p class="faq-question-{{ section.id }}">{{ block.settings.question }}</p>
                      <div class="faq-icon-{{ section.id }}">
                        <svg viewBox="0 -0.5 9 9" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                          <defs>

                          </defs>
                          <g id="Page-1" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                            <g id="Dribbble-Light-Preview" transform="translate(-345.000000, -206.000000)" fill="#000000">
                                <g id="icons" transform="translate(56.000000, 160.000000)">
                                  <polygon id="plus_mini-[#1523]" points="298 49 298 51 294.625 51 294.625 54 292.375 54 292.375 51 289 51 289 49 292.375 49 292.375 46 294.625 46 294.625 49"></polygon>
                                </g>
                            </g>
                          </g>
                        </svg>
                        <svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 485.064 485.064"
                          xml:space="preserve">
                          <g>
                            <g>
                              <path d="M458.736,181.097H26.334C11.793,181.097,0,192.884,0,207.425v70.215c0,14.541,11.787,26.328,26.334,26.328h432.402
                                c14.541,0,26.328-11.787,26.328-26.328v-70.215C485.07,192.884,473.283,181.097,458.736,181.097z"/>
                            </g>
                          </g>
                        </svg>
                      </div>
                    </div>
                    <div class="faq-answer-{{ section.id }}" style="display: none; height: 0px; padding-top: 0px; padding-bottom: 0px;">
                        {{ block.settings.answer }}
                      </div>
                    <div class="faq-height-cal-{{ section.id }}">
                      {{ block.settings.answer }}
                    </div>

                  </div>          
              {% endcase %}
            {% endfor %}
          </div>
        </div> 
        {% if image_count > 0 %}
          {% for block in section.blocks %}
            {% if block.type == 'image' %}
              <div class="faq-image-{{ section.id }}">
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" alt="{{  block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}  
              </div>
              {% break %}
            {% endif %}            
          {% endfor %}          
        {% endif %} 
        {% if image_count > 1 %}
          <div class="faq-image-column-{{ section.id }}">
            {% assign first_image = true %}
            {% for block in section.blocks %}
              {% if block.type == 'image' %}
                {% unless first_image %}
                  <div class="faq-image-{{ section.id }}">
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | image_url }}" alt="{{  block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}  
                  </div>
                {% endunless %}  
                {% if first_image %}
                  {% assign first_image = false %}
                {% endif %}                          
              {% endif %}     
            {% endfor %}
          </div>
        {% endif %}
      </div>
    </div>
</div>

<script>
  function initFaq12() {
    const answerPaddingTop = {{ answer_padding_top }};
    const answerPaddingBottom = 0;

    const buttons = document.querySelectorAll(
      '.faq-toggle-{{ section.id }}'
    );
    const answers = document.querySelectorAll(
      '.faq-answer-{{ section.id }}'
    );
    const answersHeightCalElements = document.querySelectorAll(
      '.faq-height-cal-{{ section.id }}'
    );
    const faqIcons = document.querySelectorAll(
      '.faq-icon-{{ section.id }}'
    ); 

    let activeIndex = -1;

    function openAnswer(index) {
      console.log(answersHeightCalElements[index]);
      const height = answersHeightCalElements[index].offsetHeight;

      answers[index].style.display = 'block';
      setTimeout(() => {
        answers[index].style.paddingTop = `${answerPaddingTop}px`;
        answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
        answers[index].style.height = `${height}px`;
      });

      // Добавляем класс active к иконке
      faqIcons[index].classList.add('active');
    }

    function closeAnswer(index) {
      answers[index].style.paddingTop = 0;
      answers[index].style.paddingBottom = 0;
      answers[index].style.height = 0;

      setTimeout(() => {
        answers[index].style.display = 'none';
      }, 300);

      // Убираем класс active с иконки
      faqIcons[index].classList.remove('active');
    }

    function toggleAnswer(index) {
      if (answers[index].style.display === 'none') {
        openAnswer(index);
        activeIndex = index;
      } else {
        closeAnswer(index);
        activeIndex = -1;
      }
    }

    function closeAllAnswerBut(index) {
      answers.forEach((answer, i) => {
        if (i !== index) {
          closeAnswer(i);
        }
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener('click', () => {
        toggleAnswer(index);
        closeAllAnswerBut(index);
      });
    });

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (activeIndex !== -1) {
          const height = answersHeightCalElements[activeIndex].offsetHeight;
          answers[activeIndex].style.height = `${height}px`;
        }
      }, 250);
    });
  }

  document.addEventListener('DOMContentLoaded', initFaq12);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initFaq12);
    document.addEventListener('shopify:section:load', initFaq12);
  }
</script>
